INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9789570DACD7C419A6F2D47DD68F8DA', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 2, NULL, NULL, 2, NULL, 0, 'Once the oven is hot, add the vegetables to a large baking sheet in a single layer. Roast for 20-25 minutes until the vegetables are cooked.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D62C50EF3FDCEB4D951A81C1D0CBFC15', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 3, NULL, NULL, 2, NULL, 0, 'Take the pastry out of the fridge, remove from the wrapper and sit on a plate to bring to room temperature while the vegetables roast.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D81BE1AEB3469F47A5F4433195F3DC2D', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 4, NULL, NULL, 2, NULL, 0, 'Whizz up the pate ingredients in a food processor until you have a smooth pate.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1BB1FD41BDEC6A41B9D4B2ADB502E323', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 5, NULL, NULL, 2, NULL, 0, 'Once the vegetables are roasted, unroll the pastry on a baking sheet and add the pate in a flat sausage shape up the central third of the pastry, lengthwise.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB448EEDCA19B74085776807CF1CA66A', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 6, NULL, NULL, 2, NULL, 0, 'Heap the vegetables on top of the pate.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC1F9EC93CA02944B8C74E37380F98FA', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the Parmesan over the vegetables.');
