INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2779E5B6267E464797DCCE006C7B70D1', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 9, NULL, NULL, 2, NULL, 0, 'Fold the top and bottom pieces of the pastry over the vegetables, then brush a little water on the tips of your pastry strips, then fold them one at a time across the middle until the filling is completely covered, with just a little showing.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50E98FC886C74F44A3FA1AD7146D29BA', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 10, NULL, NULL, 2, NULL, 0, 'Brush the pastry with an egg wash or olive oil and bake in the oven for 20-25 minutes until golden and crisp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2F01A6CD51F22441981C546B4D8D61F8', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', X'F9A7174E46AE5F4FA3B31DAB8ACF2186', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D31E1145383707499E40BBB950A36A44', X'8B931FE2DFE43A4488D2023174047B14', X'8B931FE2DFE43A4488D2023174047B14', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 190c/170c fan/375f/gas mark 5.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'325F1CB73F3DF445AB109B8046E70DD1', X'8B931FE2DFE43A4488D2023174047B14', X'8B931FE2DFE43A4488D2023174047B14', 1, NULL, NULL, 2, NULL, 0, 'Toss the prepared onions and red pepper in olive oil, then season with salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7434C3841597F4A9767809BD8CA0E08', X'8B931FE2DFE43A4488D2023174047B14', X'8B931FE2DFE43A4488D2023174047B14', 2, NULL, NULL, 2, NULL, 0, 'Brush the aubergine with harissa paste and the courgette with pesto (or the other way around).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E0F2438779F2C148B2E1ED53289EF555', X'8B931FE2DFE43A4488D2023174047B14', X'8B931FE2DFE43A4488D2023174047B14', 3, NULL, NULL, 2, NULL, 0, 'Place the vegetables in a single layer on one or two baking trays, sheet pans or roasting pans. I needed two.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'24865F56A1E29444B69954C2AE1A0CAF', X'8B931FE2DFE43A4488D2023174047B14', X'8B931FE2DFE43A4488D2023174047B14', 4, NULL, NULL, 2, NULL, 0, 'Roast in your pre-heated oven for 20 minutes, then add the cherry tomatoes and garlic and roast for another 10 minutes. Keeping an eye on them to make sure they don''t brown too much.');
