INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3ABAEB3D1A2F31479EEE01510E422091', X'992F9E192D49EB40B06A28A358B34929', X'992F9E192D49EB40B06A28A358B34929', 1, NULL, NULL, 2, NULL, 0, 'Transfer into a cereal bowl, glass or jar, cover and chill in the fridge overnight.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E2FD4E3FEAE49F459B2F84C10F2B4375', X'992F9E192D49EB40B06A28A358B34929', X'992F9E192D49EB40B06A28A358B34929', 2, NULL, NULL, 2, NULL, 0, 'That''s it! It''s ready to enjoy for breakfast. You can take it out 10 minutes before eating it to take the chill off if you prefer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CB56CA8CC465604A9ADF751AD35FCD16', X'992F9E192D49EB40B06A28A358B34929', X'992F9E192D49EB40B06A28A358B34929', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'192EEEA5F66B094A8DD904644E6CB84D', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200c/180c fan/400f/gas mark 6 and take the pastry out of the fridge. Remove it from the box and place the rolled up pastry on a plate and let it come to room temperature so it doesn''t crack when it is unrolled.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0AED56244700C44A90F0D4101E07170', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 1, NULL, NULL, 2, NULL, 0, 'In a large frying pan saute the onion and garlic in 1 tbsp olive oil until it is soft, then add the mushrooms and cook them gently until they are soft as well. Season with salt and pepper then add thyme, red wine and then crumble in the half a stock cube. Gently cook until all the juices are gone, then turn of the heat.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D1023A35DCBAD74D91006257C03D2C56', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 2, NULL, NULL, 2, NULL, 0, 'In a blender or food processor and whizz up a third of the mushrooms with the oats and nuts until you have a pate texture. Set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E354756179A1B545916CFEF95DA28DEA', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 3, NULL, NULL, 2, NULL, 0, 'Add the lentils to the rest of the mushroom mixture in the frying pan and mix well.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'00702EACCE827E42B4A90C930F5446EF', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 4, NULL, NULL, 2, NULL, 0, 'Rinse the spinach in a colander, then wilt the spinach by pouring a kettleful of boiling water over it. Refresh it with cold water, squeeze out as much water as you can then pat dry on kitchen paper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A965268D87A45542981709EE4DF256AA', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 5, NULL, NULL, 2, NULL, 0, 'Carefully unroll the pastry and place it on a large baking tray, still on the baking parchment it was rolled in or on a baking sheet.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A16139B6C80F134F99F55A9233763063', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 6, NULL, NULL, 2, NULL, 0, 'Spread the hummus along one (long) side of the pastry leaving a border. Top with a layer of wilted spinach and the mushroom pate. Next top with the mushroom and lentil mixture.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A72AB32563682549825C4D6F873CA46B', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 7, NULL, NULL, 2, NULL, 0, 'Brush a little water around the edges of the pastry. Pull the pastry over the filling and seal to make a large sausage roll shape. Crimp the edges with a fork and use a knife to cut diagonal slits in the top to let out steam. Brush with the rest of the olive oil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5CA192B1CED58C4C8B0E40649E0773AB', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 8, NULL, NULL, 2, NULL, 0, 'Bake or 20-25 minutes until golden and crisp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08A5451090397147A86AB90F68AA6C1F', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 9, NULL, NULL, 2, NULL, 0, 'Serve slices with roast potatoes, vegetables and onion gravy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26FF44628FC92544B2715C9627520772', X'1FFDDBE64EA96740B9993CD4E073B33D', X'1FFDDBE64EA96740B9993CD4E073B33D', 10, NULL, NULL, 2, NULL, 0, 'Enjoy');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E2F8ED19212CD341A2E179909C94D751', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 0, NULL, NULL, 2, NULL, 0, 'In a large pot saute the onion, garlic and celery in olive oil until soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D3E4DBDD63EC947BB9617A843EC35E6', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 1, NULL, NULL, 2, NULL, 0, 'Add the carrots and pepper and cook gently for a few minutes, then add the mushrooms and cook for another couple of minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EEE355EA024CD04EA43A93B6CD8EDBE8', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 2, NULL, NULL, 2, NULL, 0, 'Add the meat-free mince, then pour in the wine and stock, black pepper and stir well.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9424031D29C3894E81AFCAD1B1B20E56', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 3, NULL, NULL, 2, NULL, 0, 'Add the tomato puree and brown sauce to add flavour and cook for 25 to 30 minutes, adding a little more stock if you think it needs it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD6049C9BEE8434FB9852B1AA1F28703', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 4, NULL, NULL, 2, NULL, 0, 'While the savoury mince is cooking you can boil or steam and then mash the potatoes with some dairy-free spread, salt and pepper. Towards the end of cooking pre-heat the oven to 220c/200c fan/425f/gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'450A93FA18F19B409E5E6F42F7012A4F', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 5, NULL, NULL, 2, NULL, 0, 'Pour the savoury mince into a large ovenproof dish and bake for 20 minutes until the potato topping is golden and crispy at the edges.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F221A8C40CEAC4B8CA7CA74BA60CC97', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 6, NULL, NULL, 2, NULL, 0, 'Serve with steamed vegetables.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6EBB71E312835640A90F15974EB64D04', X'4DFE15E28B25AC4CA169E6B3172C5E9A', X'4DFE15E28B25AC4CA169E6B3172C5E9A', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C343A48439F0994B8AE0559C6908B5C8', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 220c/200c fan/425f/Gas 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C88B2CA7F6FF174ABD5C9735E8F25E99', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 1, NULL, NULL, 2, NULL, 0, 'Place the potatoes, onion wedges and sausages in a large ovenproof dish and drizzle with the oil. Use your (clean hands) to toss the ingredients in the oil, then spread out in an even layer across the dish and bake for 20-25 mins until the ingredients are cooked and starting to brown at the edges.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'218480A0380C3D4DB22F763E13ED4DA7', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the chopped tomatoes with the tomato puree, sugar and spices then mix well.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'48DC6C6925B4EE459FC9F428110EAF89', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 3, NULL, NULL, 2, NULL, 0, 'Add the drained beans and mix in.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'028C7AE0A797D14287617977C77D1ED7', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 4, NULL, NULL, 2, NULL, 0, 'Remove the dish from the oven and carefully, cut the sausages in half, being careful not to burn yourself, then mix in the tomato bean mixture and pop back in the oven for 30 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DFCAEC2591CBB84CA4D3B19BC9BA2EE7', X'D589BB42866A1F458F7355C4BE47C02A', X'D589BB42866A1F458F7355C4BE47C02A', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'52C90BB386D519479C2C1BE05E9BFF34', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 0, NULL, NULL, 2, NULL, 0, 'Wilt spinach in a colander, Place it in the sink and pour a kettle full of boiling water over the spinach, then pour over cold water and squeeze as much water out as possible then dry off with kitchen paper and chop.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7A6B9FA995B08949B78F713EC76FAFD8', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 1, NULL, NULL, 2, NULL, 0, 'In a bowl mix together the passata, the tomato puree, oregano, salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1629410791E4AC4EA09FCA528C3961A3', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 2, NULL, NULL, 2, NULL, 0, 'In another bowl mix the cream cheese with the spinach and parmesan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0DE692E138E684BAF6520DF7D9CF287', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 3, NULL, NULL, 2, NULL, 0, 'Fill the pasta with the cream cheese mix.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C92849BFA858EB4C8B7AD5404A59D76A', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 4, NULL, NULL, 2, NULL, 0, 'Spread the bottom of the slow cooker bowl with some tomato sauce, then place the filled pasta in a single layer across the sauce, then top with more sauce. You can do this in one or two layers of pasta depending on the size of your bowl. Just top each layer with sauce.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA26D7310D5E9743A37028C5AB9F5EA1', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 5, NULL, NULL, 2, NULL, 0, 'Top with a layer of mozzarella and then a good grating of parmesan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2FE5165A9D38544DBE1AECFF61A8C670', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 6, NULL, NULL, 2, NULL, 0, 'Set the slow cooker to high and cook for 3 hours.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D45135320719254FB2FA29BB5575A4C5', X'EED7C66F6F36BA4E97643554D42987A9', X'EED7C66F6F36BA4E97643554D42987A9', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C04BCF0117EDDA469A9694AE61D5F7DF', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 150c/130c fan/ 300f/gas mark 2.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD7B5F247050FD41B79106055EBC8BA4', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 1, NULL, NULL, 2, NULL, 0, 'Line a large baking tray (or two standard trays) with a baking sheet or greaseproof paper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9627524D2D84B4A83DEC7D50D3D3DF8', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 2, NULL, NULL, 2, NULL, 0, 'Beat the dairy-free spread in a stand mixer (or in a bowl with a hand mixer) to soften it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E3651CACB49E24291671F2F28F0DA8E', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 3, NULL, NULL, 2, NULL, 0, 'In another bowl mix the spices into the flour and add it into the soft spread a bit at a time, starting at a slow speed so you don''t coat your kitchen in a layer of white powder. Once that has been completely incorporated. Follow it with the cornflour (cornstarch) and then with the sugar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DD21276791D1584AADA2E0BBCC4BB951', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 4, NULL, NULL, 2, NULL, 0, 'While it is beating add a little milk if you think it needs it and beat until it all comes together into a ball of dough. It may seem very dry and unlikely to stick together at all, but give it time. You might need to remove it from the bowl near the end and knead the last dry bits in by hand.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'667331A63FBA61488536EE1F114F35EC', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface, sprinkle the dough with just a touch more flour, and roll it out to a thickness of ½ cm to 1 cm. If you have trouble rolling it out smoothly or find it is too sticky to work with, let it chill in the fridge for 30 minutes or so before continuing.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'77ED0EC39E350C4E8475BDD4EEE28912', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 6, NULL, NULL, 2, NULL, 0, 'Cut into cookies with a cookie cutter or a glass and place on the prepared baking tray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BAB553E53F304C4193F233A963CCBA3E', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 7, NULL, NULL, 2, NULL, 0, 'Bake for 20-25 minutes until the edges just begin to take on some colour, but you really don''t want to let them get too golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0FBCB6AC9F16A54B9638FFFC5A564844', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 8, NULL, NULL, 2, NULL, 0, 'Leave the cookies to cool on the tray for a few minutes then move to a cooling rack to continue cooling.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B147B8519C22C540BE9B9AE718A326BF', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 9, NULL, NULL, 2, NULL, 0, 'Dust with icing sugar before serving.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F7F098FF4BA4AC408E063680D06DFEE6', X'E17387DDC2EE4B449DAFA42A85FA7BB5', X'E17387DDC2EE4B449DAFA42A85FA7BB5', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BFB6F24D6A95BF44BC79E7EC7324B825', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 0, NULL, NULL, 2, NULL, 0, 'Saute the onion and garlic in the olive oil until soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3A400366FF524942A3023C8EC88AFBDC', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 1, NULL, NULL, 2, NULL, 0, 'Add the chipotle pasta, cinnamon and cumin and cook gently for a couple of minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8AD15D9F85560844B6749039F2A13136', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 2, NULL, NULL, 2, NULL, 0, 'Add the agave nectar, vinegar, tomato passata and a good grinding of salt and pepper. Cook for a few minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'82D1C71C4E2102499106101EBC611B37', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 3, NULL, NULL, 2, NULL, 0, 'Whizz the mixture in a blender (or with a stick blender) until smooth.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'99604FC17682FC459A5A768B5396ED0D', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 4, NULL, NULL, 2, NULL, 0, 'Decant into a sterilised jar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EEDB0DA3BFD7E544B37239B0113739E6', X'D5BB09C9E3AC9241891D408EB1E3750D', X'D5BB09C9E3AC9241891D408EB1E3750D', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0679B11C02D77348A32E2A9764F10CC2', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180c/160c fan/350f/gas mark 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25C639DB892EC7478874C9CCB40D2887', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 1, NULL, NULL, 2, NULL, 0, 'Pour a third of the tomato sauce into a large casserole dish and spread to cover the base.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E4310C30FB7654F8678534CEE9F98DF', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 2, NULL, NULL, 2, NULL, 0, 'Cover with sheets of lasagne in a single layer (no need to cook the lasagne sheets first), breaking some extra pieces to fill any gaps.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D7711646801E3848B623CB4C4A88CDAC', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 3, NULL, NULL, 2, NULL, 0, 'Mix together the cream cheese with a few tbsp of the grated parmesan and a good grinding of pepper. If you are making vegetarian lasagne you can used a mixture of cream cheese and ricotta cheese.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF6545A1B49960429D22B8995CA7D7D7', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 4, NULL, NULL, 2, NULL, 0, 'Spread half of the cream cheese mixture across the lasagne to cover it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC4DE987D79D2B429CA3021846A6B678', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 5, NULL, NULL, 2, NULL, 0, 'Top with a handful of spinach and half the corn, then top with another layer of lasagne.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'57FAE4920207B042B21F487DA8A1A6E7', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 6, NULL, NULL, 2, NULL, 0, 'Cover with another third of the jar of tomato sauce and top with another layer of lasagne.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'86A72A1F5481CD4F84227C6520BADE99', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 7, NULL, NULL, 2, NULL, 0, 'Next add the rest of the cream cheese, smooth across the pasta and top again with spinach and corn, then top with another layer of lasagne.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1A5F061163F094E803DCAA7239A1AE1', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 8, NULL, NULL, 2, NULL, 0, 'Pour on the rest of the tomato sauce and spread evenly, then top with grated mozzarella. If you are using vegan mozzarella, spray the top with a fine mist of olive oil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4693086B848D642BB76D28AA28F71CE', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 9, NULL, NULL, 2, NULL, 0, 'Top with a sprinkling of Parmesan and bake in the oven for 30 minutes until cooked through and golden. If it starts to brown too quickly, cover with foil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4C23A95B96235041AEDFF3F590B06B82', X'D72413E50C57CC4E92EF02578424E72F', X'D72413E50C57CC4E92EF02578424E72F', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF6544F81A198147BE59352548137A60', X'13D1992174B7E94AB7AEBFAB0C74AE47', X'13D1992174B7E94AB7AEBFAB0C74AE47', 0, NULL, NULL, 2, NULL, 0, 'Cut the tomatoes in half. Scoop out the seeds with a teaspoon and discard, then chop the tomatoes finely and place in a large bowl.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C66D86D36FD907418E96797BFE074BD8', X'13D1992174B7E94AB7AEBFAB0C74AE47', X'13D1992174B7E94AB7AEBFAB0C74AE47', 1, NULL, NULL, 2, NULL, 0, 'Finely chop the red onion, chilli and fresh coriander and add to the bowl.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B8B46E96117CE46A6A8BCBF7D13EB16', X'13D1992174B7E94AB7AEBFAB0C74AE47', X'13D1992174B7E94AB7AEBFAB0C74AE47', 2, NULL, NULL, 2, NULL, 0, 'Crush the garlic and add it to the other ingredients with the lime juice and salt, then mix well.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9A2B41722AB634085994E038A361E7D', X'13D1992174B7E94AB7AEBFAB0C74AE47', X'13D1992174B7E94AB7AEBFAB0C74AE47', 3, NULL, NULL, 2, NULL, 0, 'Cover and place in the fridge until you are ready to serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22D08E76DEF48C4BB88C56F6234A9AA0', X'13D1992174B7E94AB7AEBFAB0C74AE47', X'13D1992174B7E94AB7AEBFAB0C74AE47', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8956206665D5AD498F4E5FE6BC9A5CC7', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 0, NULL, NULL, 2, NULL, 0, 'Carefully peel the skin off the mushrooms and remove the stalk.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1AB2D106B2658444A002F9F0DD73A8BE', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 1, NULL, NULL, 2, NULL, 0, 'Brush the outside of the mushrooms with a little oil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD96A9A24999DC438BF276DB30C0BEDE', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 2, NULL, NULL, 2, NULL, 0, 'Add a heaped tablespoon of paste to each mushroom and spread across the inside with a teaspoon.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69632F997404D54A959C17ED943E5D8F', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 3, NULL, NULL, 2, NULL, 0, 'Cut the mozzarella (or vegan mozzarella) into 8 slices and place one slice in each mushroom.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B234737FEA4AF047B7237B1D37B83251', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 4, NULL, NULL, 2, NULL, 0, 'Slice each cherry tomato into 3 slices and top each mushroom with 3 slices.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'71BDACBF68C38742B73B4789AB1821C3', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle each mushroom with a little oregano and a grinding of salt and black pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'53F280BA70508D42A9717D2DAFDDD202', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 6, NULL, NULL, 2, NULL, 0, 'Place the mushrooms on a pre-heated BBQ, directing onto the cooking grate and cook until the mushrooms are juicy and tender and the mozzarella has melted. Depending on the heat, this will take 5-10 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F679177DDBB3FF438E9AA2214B31E69A', X'FA68F2D1C792FF44891DDF1C1407B70A', X'FA68F2D1C792FF44891DDF1C1407B70A', 7, NULL, NULL, 2, NULL, 0, 'Serve and Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'09B11BB92882A7418D5154EBB16D98D0', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 0, NULL, NULL, 2, NULL, 0, 'Make your marinade. Add all the ingredients to a bowl from the ground turmeric down to the black pepper and stir together into a paste.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BFB4161790C699428997C1C242B19FAE', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 1, NULL, NULL, 2, NULL, 0, 'Add the yogurt and water to make a good consistency for coating tofu.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DD62F4B7C5B89E44A7F39C2253DBFE26', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 2, NULL, NULL, 2, NULL, 0, 'Press your tofu for half an hour or so, then cube it and gently coat the tofu in the marinade. Pop the tofu in an airtight container and pop in the fridge for a few hours.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'67CA0C80DB851449A730226E59AD527C', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 3, NULL, NULL, 2, NULL, 0, 'Soak wooden skewers in water for half an hour before you make the skewers, this will prevent them from burning.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A78784781A42F141A2A4844968B223F3', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 4, NULL, NULL, 2, NULL, 0, 'Heat your grill or BBQ and then thread the tofu and vegetables onto 12 skewers in a rainbow order.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FB9C80E77B34F94CAAD7F034528C4369', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 5, NULL, NULL, 2, NULL, 0, 'Cook for a few minutes on each side until cooked and starting to char and crisp at the edges.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A35DD3F3D2CB6343B1498CA22C777147', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 6, NULL, NULL, 2, NULL, 0, 'Serve with toasted pitta bread, salad leaves, cherry tomatoes and hummus.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D465DC34D45B1429D51680C5AA3BFCF', X'83DE5AA4BC946E4CB66842B0FB6D37F6', X'83DE5AA4BC946E4CB66842B0FB6D37F6', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F75BC74AAD38443B7364BE28B0117FB', X'B3C765C01CB54F4CA1B61242924A6662', X'B3C765C01CB54F4CA1B61242924A6662', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients together, except for the yogurt and water. This will make your paste. Keep in the fridge until ready to use.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B040371EC82A494795694637D0FDD06D', X'B3C765C01CB54F4CA1B61242924A6662', X'B3C765C01CB54F4CA1B61242924A6662', 1, NULL, NULL, 2, NULL, 0, 'Once you are ready to marinade, add the yogurt to the paste and then enough water to get the right consistency, approximately 2-3 tbsp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'306B7A44C975FA4EA7F76A691567BFFF', X'B3C765C01CB54F4CA1B61242924A6662', X'B3C765C01CB54F4CA1B61242924A6662', 2, NULL, NULL, 2, NULL, 0, 'Mix your tofu, halloumi or vegetables in the marinade, spoon into an air tight container and leave in the fridge to soak up the flavours for a few hours or overnight. The cook in a grill pan or on a heated BBQ.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'694E2B20B27C5F48A0EB2B493BC3D601', X'B3C765C01CB54F4CA1B61242924A6662', X'B3C765C01CB54F4CA1B61242924A6662', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5A10073264F6C2429C0EF9A5E3088174', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 0, NULL, NULL, 2, NULL, 0, 'Wash, hull and slice the strawberries, then add them to a medium to large pan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'29C2FB0C07EF0748B7E6E413F464055F', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 1, NULL, NULL, 2, NULL, 0, 'Add the maple syrup, a squeeze of lemon juice and the vanilla extract.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'411673B678E0A442ACEF1A920D72FAE4', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 2, NULL, NULL, 2, NULL, 0, 'Cook on a medium heat until it comes to the boil. About 10 minutes, then, mash the strawberries with a potato masher and leave to cook for another few minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50CB6A842E7FDA4197C7017C5BC40339', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 3, NULL, NULL, 2, NULL, 0, 'Take off the heat, then stir in the chia seeds.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5A02E6F2D951C14C9DC4E58A043A2769', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 4, NULL, NULL, 2, NULL, 0, 'Pour into sterilised jars using a jam funnel, all the way to the brim, then add the lid and secure tightly.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E13E1B5640BC14CB9C8600C753CC0A6', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 5, NULL, NULL, 2, NULL, 0, 'Leave the jam jars out until it cools, then pop it in the fridge. The chia seeds will thicken the jam as it cool, but it is a soft-set jam.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A37DF45C1962E24E8E76043DE029065D', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 6, NULL, NULL, 2, NULL, 0, 'This jam can be kept in the fridge and used for a week after it has been opened.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCEA5617FB3FE047B0FF1A5DDB20CE0C', X'6EF54C676704C04C921D657DA77C2A7C', X'6EF54C676704C04C921D657DA77C2A7C', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8C192CD6C8404A469B0824226FF25D73', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 180c/160c fan/ 350f/gas mark 4 and line two baking sheets with non-stick paper or mats (I love my Silpat liners).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F8876A8DCA9364CB08BE4B66071DA64', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 1, NULL, NULL, 2, NULL, 0, 'Whizz up the oats and flax seeds in a blender or food processor for a finer consistency. I used pulse, I didn''t want them too fine.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50E02396646D74459AAFB140095CB090', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 2, NULL, NULL, 2, NULL, 0, 'Mash the bananas into a soft mush in a bowl and mix in the maple syrup.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'920230A477EF22438FFCB9B0EE5EFD08', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 3, NULL, NULL, 2, NULL, 0, 'Pour the oats into a large bowl and mix in the cocoa.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2E7BF4E1A8C3194BB77C22BC796E75F2', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 4, NULL, NULL, 2, NULL, 0, 'Mix the bananas into the oats, then stir in the chocolate chips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'630DAD8C06598641AFDEF07D430A942E', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 5, NULL, NULL, 2, NULL, 0, 'Scoop tbsp of the mixture, roll into a ball and press into a cookie shape. Do this for all of the mixture and place then, well spaced apart on the cookie sheets and bake for 20 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'899FA1E32C53154C830B717848B1399C', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 6, NULL, NULL, 2, NULL, 0, 'Take out of the oven. Once they have cooled a little move them to a cooling rack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6255DC419E8C8B40ADB3CAED357DD791', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 7, NULL, NULL, 2, NULL, 0, 'Go on, eat one or two whilst they are still warm.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70BE57E39F8F4A49BB87318EDE80A401', X'1F5D3B93F709F2418E9D0D62CCDE45D9', X'1F5D3B93F709F2418E9D0D62CCDE45D9', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F9F0F3B9AB35047B148BC5CEC45510E', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 0, NULL, NULL, 2, NULL, 0, 'Take the puff pastry out on the box and leave it on a plate to come to room temperature for 15 minutes before your make your pizzas.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8EF28BD3AE52D94B9C65D93BD8BDE447', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 200c/180c fan/gas mark 6.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'134AB375E4C69F40AD91EA6FC2D9F299', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 2, NULL, NULL, 2, NULL, 0, 'Carefully unroll the pastry. Use a small bowl to cut circles out of the pastry. Press down and give it a wiggle. If you need to you can use a knife, but the bowls should be enough. Place the circles onto 2 baking trays, leaving a space between each pastry circle.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EFD0EFFE133B97408956FBC1697EBBB8', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 3, NULL, NULL, 2, NULL, 0, 'Top each pastry circle with a dollop of tomato puree and spread around carefully, leaving a small border around the edge with no puree.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF92C9AB06F522438BB81D13E40E0730', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 4, NULL, NULL, 2, NULL, 0, 'Top with a slice of mozzarella and slices of tomato.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40EE807553EED04C8CD925C5918FCCA1', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle each pizza with a little oregano.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'85A1883C13D20C4EBD061157FEB4B070', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 6, NULL, NULL, 2, NULL, 0, 'Bake the pizzas for 15 minutes until the pastry is golden and the cheese has melted. Serve with some salad and garlic bread.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'553219FB2AF62148ACC6534A30F2ED5F', X'553D6FF38EEF71429E9BA1FA71565A11', X'553D6FF38EEF71429E9BA1FA71565A11', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4C6B3BAF769B0843AC1CD8FEBD410799', X'20D0D85A81D0A94FB5E1E863B53AD87F', X'20D0D85A81D0A94FB5E1E863B53AD87F', 0, NULL, NULL, 2, NULL, 0, 'In a large pan, saute the leek and garlic until the leek is soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'20C99B54CF49C045817CDDF6D881F18C', X'20D0D85A81D0A94FB5E1E863B53AD87F', X'20D0D85A81D0A94FB5E1E863B53AD87F', 1, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and cook until they are soft too. I like to season them with black pepper while they are cooking as they absorb flavours.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D420E12F4EF93D408E3FDBF9BC57DB15', X'20D0D85A81D0A94FB5E1E863B53AD87F', X'20D0D85A81D0A94FB5E1E863B53AD87F', 2, NULL, NULL, 2, NULL, 0, 'Pour in the rice and mix well to coat.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B545901C7DEC0148B0282B4FF7E5F440', X'20D0D85A81D0A94FB5E1E863B53AD87F', X'20D0D85A81D0A94FB5E1E863B53AD87F', 3, NULL, NULL, 2, NULL, 0, 'Pour in the wine cook gently until most of the wine has absorbed.');
