INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9158EC629A9E0E4BAB1FC6C6E3DF42CF', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 3, NULL, NULL, 2, NULL, 0, 'Unroll the pastry, if it is on baking paper, transfer it straight onto your baking tray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2428150019D29543AF4BB4D442214F82', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 4, NULL, NULL, 2, NULL, 0, 'Score a border around the edge of your tart, not cutting all the way through, then brush the border with olive oil and fill the inside of the tart with pesto. Spreading it evenly.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C1F5BDA498B5E848B525D585B3DE1F78', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 5, NULL, NULL, 2, NULL, 0, 'Slice your tomatoes and place them over your tart to fill it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB00E57E9934A3478D08433A68DE2EAB', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 6, NULL, NULL, 2, NULL, 0, 'Scatter the onion across the tart.');
