INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0590E82F0DF0C741BB192CAB892A127E', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 11, NULL, NULL, 2, NULL, 0, 'Serve warm with salad or vegetables.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BF02FD337B89414D9C914A6BE95229F2', X'E425B7F365FE0945AEF9915CD4EDEA45', X'E425B7F365FE0945AEF9915CD4EDEA45', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C71910337C52EF4FB2E2945EEAC630FD', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 0, NULL, NULL, 2, NULL, 0, 'Saute the onion and garlic in the olive oil until soft, then add the spices and let them cook gently for a minute or two.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'453D8CAC70802046AF8EBD9735434711', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 1, NULL, NULL, 2, NULL, 0, 'Add the onion mixture, beans, cheese, tomato puree and peppers to a food processor and whizz until the mixture starts to come together. Don''t whizz it until it is completely smooth, you do want some texture left.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3E39A35F00B1124B9277C07ED12A5B7A', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 2, NULL, NULL, 2, NULL, 0, 'Scrape the mixture into a bowl with the oats and season with salt and pepper. Mix well and then shape into six balls before pressing them gently into patties.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6B4CBB2FD3D2AC49B09B0E023EFC06A0', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 3, NULL, NULL, 2, NULL, 0, 'The burgers can be cooked on a BBQ, grill pan or in the oven. I do find burgers are a little dry when cooked in the oven so I prefer the other methods. I grilled them on a grill pan with just a light spray of oil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E771B8C8C925F418CFDDB3220BBC558', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 4, NULL, NULL, 2, NULL, 0, 'Serve the burgers on toasted buns (inside), with your favourite toppings.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'622A8E2407E55C428083650B4E6C29FB', X'C04D87BE7BDB7C4FAFB763946444FDB7', X'C04D87BE7BDB7C4FAFB763946444FDB7', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'896C329AE6FB9C468BA44F356C976285', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180c/160c fan/350f/gas mark 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BEA6B18DA35CE0449BBF7F85B7356BC2', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 1, NULL, NULL, 2, NULL, 0, 'Line a 2 lb loaf tin. I like using loaf tin liners instead of cutting and fitting baking paper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'598D5E7900861A428A5051703B5C91DC', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 2, NULL, NULL, 2, NULL, 0, 'Mash the bananas in a bowl until mushy, then stir in the vanilla extract, sugar and milk. You can add a little more milk when you are folding the dry and wet ingredients together, if you think it needs it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0BF4F99B760D7F4D9DE4BED89FE02736', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 3, NULL, NULL, 2, NULL, 0, 'Once the wet ingredients are well mixed, add the cocoa powder, flour, baking powder and salt.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E972505CAC3D4D48BEAE5249AC53EA1C', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 4, NULL, NULL, 2, NULL, 0, 'Gently fold the wet and dry ingredients together until just combined. Don''t over mix, just enough until it''s mixed, then pour in the loaf tin.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB4BFAD2DA17A244946B2BC3095099B7', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 5, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 1 hour.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18F83E8D0E65FD43B1CD908C049A5890', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 6, NULL, NULL, 2, NULL, 0, 'Do a skewer test before the end of baking time, if it comes out clean it''s ready. Keep an eye on it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1AB94EA66A6A3F4DB276707F767C31E2', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 7, NULL, NULL, 2, NULL, 0, 'Leave to cool in the tin for a little while, then move to a rack to continue cooling.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D0F1344F1EA3047A2F8A659982237A6', X'0DDDFE8038F46A448E6BE2A80B38BED3', X'0DDDFE8038F46A448E6BE2A80B38BED3', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B8D7A45DB61B646859C61DFEA743871', X'796FF38DBCF4D646B871F8F701309B2E', X'796FF38DBCF4D646B871F8F701309B2E', 0, NULL, NULL, 2, NULL, 0, 'Spread the tortilla with cream cheese, then pile the spinach and basil in a line across the middle horizontally.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2EBBDFC89931B4A91A32A6EE132706D', X'796FF38DBCF4D646B871F8F701309B2E', X'796FF38DBCF4D646B871F8F701309B2E', 1, NULL, NULL, 2, NULL, 0, 'Skin, stone and slice the avocado and add slices to the wrap, toss the rest in lemon juice and save for salads.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C5BEC00B4070AB4697924F51843F4824', X'796FF38DBCF4D646B871F8F701309B2E', X'796FF38DBCF4D646B871F8F701309B2E', 2, NULL, NULL, 2, NULL, 0, 'Slice your cherry tomatoes and add them, then season with black pepper and top with a good drizzle of sweet chilli sauce.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0826D2A357F5D943A7BBED808C190BA9', X'796FF38DBCF4D646B871F8F701309B2E', X'796FF38DBCF4D646B871F8F701309B2E', 3, NULL, NULL, 2, NULL, 0, 'Fold the sides of the tortilla in, then roll the top towards you firmly over the filling, tucking in the sides as you go. Cut in half and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2F82CD5D7F71BB448B9AFDD082712C15', X'796FF38DBCF4D646B871F8F701309B2E', X'796FF38DBCF4D646B871F8F701309B2E', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A4DECDAC094414489506D648FC15602C', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 150ºC/300ºF/gas 2.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C77CCFDA0A17164088C548EAAAEB9B19', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 1, NULL, NULL, 2, NULL, 0, 'Line and grease two square 20cm x 20cm brownie tins.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'910908E1E08D88478101E04C4D25DEF3', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 2, NULL, NULL, 2, NULL, 0, 'In a small pan, melt the butter (or dairy free spread), brown sugar, golden syrup and lemon peel.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B695124F8B97A4A8D6D71287779E030', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 3, NULL, NULL, 2, NULL, 0, 'Pour the oats into a large bowl, then pour in the melted butter and sugar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'881CF2B1E23B924E8D6516748E19CD70', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 4, NULL, NULL, 2, NULL, 0, 'Divide the mixture into the two tins and press down until smooth, leaving some of the mixture in the bowl to add to the top as a crumble.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'242651FE472D9840ABB97EBCA7DFF251', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 5, NULL, NULL, 2, NULL, 0, 'Spread the jam over the top of the oaty mix, then top with the blueberries and sprinkle the remaining oaty mixture in between the blueberries. Leave some of the jam showing.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'34B06B1EDA11D24E8BEE870EA9D3BB2A', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 6, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes. It will be starting to turn golden at the edges when you take it out and quite soft, but it will firm up as it cools.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'177EB12C9600A94ABC617AE6CA5CB05E', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 7, NULL, NULL, 2, NULL, 0, 'Leave in the tins to cool for a while, then remove from the tins and place on a cooling racks until completely cool.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39C86A5925134B4596893E1ADCD57FDF', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 8, NULL, NULL, 2, NULL, 0, 'Cut into bars and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9E410AB02B25EC4F9A9C8E37CCAD455A', X'6012BE918B39C548A9CFAF6E7AFCC8E6', X'6012BE918B39C548A9CFAF6E7AFCC8E6', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B21BBAC391F1B842A1097C1AA9B769B1', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 0, NULL, NULL, 2, NULL, 0, 'Peel your carrot, then grate coarsely.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E263B2077D4F140A9F0EF128FEB5F68', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 1, NULL, NULL, 2, NULL, 0, 'Roughly chop the cashews.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E44B7CCD6E9604A97815BF8F7BA465A', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 2, NULL, NULL, 2, NULL, 0, 'Spread your tortilla with hummus, then lay the spinach across the middle of the wrap, top with the grated carrot and nuts.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33F0CCF5C0E33D45AC83B3F59D67A799', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 3, NULL, NULL, 2, NULL, 0, 'To wrap your tortilla, fold in the sides, roll the top of the tortilla towards you wrapping it firmly around the filling, tucking in the edges as you go.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'43FE150AD819DE4C87B5F0B7566AEF87', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 4, NULL, NULL, 2, NULL, 0, 'Cut your wrap in half and serve with a few crisps (chips) and some apple wedges, with some dressed salad or wrapped in foil if you are on the go.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'180571EC4E51054D84B7D64A92CE63EC', X'2A44B79789D92547A9317A6AF0DE6CBC', X'2A44B79789D92547A9317A6AF0DE6CBC', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56E4E918539CA6499EC35F78D366C07C', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 0, NULL, NULL, 2, NULL, 0, 'Take the pastry out of the fridge and remove from the box 10-15 minutes before you want to use it. Preheat the oven to 220°C/200°C fan/gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E07BBBFE3EC4B4DB6E2A40FF9B1DC06', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 1, NULL, NULL, 2, NULL, 0, 'Blanch the spinach. Place it in a colander in the sink, then pour over a kettle full of boiling water to wilt the spinach. Refresh with cold water, then squeeze out as much water as you can. Use kitchen paper to make sure there is no more liquid, then sprinkle over the cumin and set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08F1478CDEE1EF48B5FF572C6FDC4CD4', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 2, NULL, NULL, 2, NULL, 0, 'Mix the cream cheese with the parmesan and black pepper. Set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C2DC18ACE556D64092C97494C26B6DD5', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 3, NULL, NULL, 2, NULL, 0, 'Place the pastry flat on your work surface. Using a cup, glass or pastry cutter, cut out 12-14 small circles, re-rolling the scaps of pastry if you need to.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8A83C324187FF49856D3EEBB22DF168', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 4, NULL, NULL, 2, NULL, 0, 'Place a little spinach one teaspoon of cheese mixture onto each circle of pastry, then rub a little water around the outside edges. Take one side and stretching it slightly, lift it over the filling and press around the edge to seal. Press a fork around the edges to form crimping marks, then prick the top of each pastry.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'650FB128035F6A4D887C42D8A9F8DAF6', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 5, NULL, NULL, 2, NULL, 0, 'Brush the parcels with olive oil and sprinkle some sesame seeds over each pastry. Bake for 15-20 minutes, until the parcels are golden and crisp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD5ABC5099ABC147A38FA97D8F0D8D19', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', X'C8B62AA0AF8B2E49BE4EAB03393F4C01', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7FB6AC8673C4704F9B818A84D08106F2', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 0, NULL, NULL, 2, NULL, 0, 'Spread the tortilla with the cream cheese, leaving a border around the edge.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'85110CACC47CCA48BF16BB958CE2BD02', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 1, NULL, NULL, 2, NULL, 0, 'Spread the salad leaves and slices of pepper in a line across the middle of the tortilla.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F32607B633739548BFCBD1CA2687D252', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 2, NULL, NULL, 2, NULL, 0, 'Top the salad leaves with the beans, salsa and grated cheese.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4EC399F03EDFD847A7EBC38323F7F07A', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 3, NULL, NULL, 2, NULL, 0, 'Tuck the sides in and roll the tortilla towards you over the filling firmly, folding in the edges as you go.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'112A7D0FBA748B4399F9467AD7DFE0D4', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 4, NULL, NULL, 2, NULL, 0, 'Cut the filled tortilla in half and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EC0489F1DE67F04CA6A12AF926E165EC', X'0DA13486D558B242B0BE474C8C902660', X'0DA13486D558B242B0BE474C8C902660', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31FA4364CC60D04C90800DF0161DBAB4', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 220c/200c fan/425f/Gas 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3D3C75CD8AA2847882F40E0AC5D5757', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 1, NULL, NULL, 2, NULL, 0, 'In a large pan, saute the onion and garlic in some olive oil until soft and translucent, then add the spices and mix in.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7CCC4E8274DBBB409ADD8C11844D49A5', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 2, NULL, NULL, 2, NULL, 0, 'While the onions are cooking spread the prepared peppers and onions across a baking tray. Spray them with a little oil and season with black pepper, then pop in the oven roast for 20-25 minutes until soft but not too charred.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E04EF764598D04685807C5191A7D9B9', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 3, NULL, NULL, 2, NULL, 0, 'Once the onions are soft and you''ve added the spices, add the tomatoes, tomato puree and wine, then cook on a medium heat, stirring occasionally for 20 minutes until the sauce has thickened. Season with salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4C243E42AA3BB418B9F2A94C2C4F72C', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 4, NULL, NULL, 2, NULL, 0, 'While the vegetables and sauce are cooking bring your pasta water (in a large pan) to the boil. Add the pasta 12 minutes (or less, check the packet) before the end of cooking time, stirring from time to time to prevent sticking.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63D1934C877BFC448AE1F5421B2D181C', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 5, NULL, NULL, 2, NULL, 0, 'Stir the roast vegetables into the tomato sauce and pour the drained pasta into the sauce, stirring well to coat.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C75AE3FFE2E18545BBB682A3087444BB', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 6, NULL, NULL, 2, NULL, 0, 'Serve topped with veggie or vegan style Parmesan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'46612ABBB43C3C41BFBB8E7840AB1538', X'E3CB746C05F47E41AFECCF850DD46464', X'E3CB746C05F47E41AFECCF850DD46464', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'81F296BEAB2C4B4DB479EBE03BE92081', X'8DE3804949FED84B9233C668BAED720D', X'8DE3804949FED84B9233C668BAED720D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 220 °C /200°C fan/ gas mark 7 and prepare the vegetables.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1043486511B3D548896B627C851FA223', X'8DE3804949FED84B9233C668BAED720D', X'8DE3804949FED84B9233C668BAED720D', 1, NULL, NULL, 2, NULL, 0, 'Spread the vegetables and tofu onto an oven tray, season with salt and pepper and spray with olive oil. Roast in the oven for 20 - 25 minutes until the vegetables and tofu are golden and crispy at the edges. Leave to cool.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0FB23A2EB61634CADC9C4C9C945372A', X'8DE3804949FED84B9233C668BAED720D', X'8DE3804949FED84B9233C668BAED720D', 2, NULL, NULL, 2, NULL, 0, 'Spread two slices of bread with mustard, top with salad leaves, half of the roasted mixture and add some pepper slices.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F635AFDAF514094489CA4E4B8759C975', X'8DE3804949FED84B9233C668BAED720D', X'8DE3804949FED84B9233C668BAED720D', 3, NULL, NULL, 2, NULL, 0, 'Spread the last two slices of bread with cream cheese (veggie or vegan) and top the sandwiches. Cut in half and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD6FBA338E09B147AEB61E1FDDF7E514', X'8DE3804949FED84B9233C668BAED720D', X'8DE3804949FED84B9233C668BAED720D', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AEB69E3D50552348B578CE1A47365661', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 220c/200c fan/gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B9B20964DC61CC4B9A3D917DF718F842', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 1, NULL, NULL, 2, NULL, 0, 'Sieve the flour into a large bowl, add the dairy free spread and gently rub them together with your finger tips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C41D0951F80C644DBD61055F28A6C046', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 2, NULL, NULL, 2, NULL, 0, 'Gently mix in the salt and sugar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A318D72D609AE645A83BDF0CEF5E84F5', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 3, NULL, NULL, 2, NULL, 0, 'Whisk the yoghurt and milk together in a jug and then pour the liquid into the dry mix and cut into the mixture with a knife until it starts to come together.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'812A6AF817736640B6085C1E03F8F9FD', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 4, NULL, NULL, 2, NULL, 0, 'Finish bringing the dough together with your hands (don''t over do it), then roll out to 3-4 cm thick on a floured surface.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E9216439126A0469C9A5A6D72583D25', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 5, NULL, NULL, 2, NULL, 0, 'Cut into circles with a cookie cutter and place on a baking sheet.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'14F88940C13DEA468978357C00011A05', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 6, NULL, NULL, 2, NULL, 0, 'Brush with almond milk and bake for 12-15 minutes until well risen and starting to turn golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'71BDC6AD1BEB284CA5F4860E87885EC5', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven and cool on a rack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'02972072D4C5CC4A9641EDE6868DC034', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 8, NULL, NULL, 2, NULL, 0, 'Serve with dairy-free spread and jam while still warm.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'14E193347B709942ACC70E614B7E3177', X'DC9705954BDF5B4D971D7660CC6EA635', X'DC9705954BDF5B4D971D7660CC6EA635', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39F4A9A0E579FB4A9545BC996201915A', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 0, NULL, NULL, 2, NULL, 0, 'Cook the gnocchi according to the packet instructions, drain and pat dry in a clean tea towel, then fry the gnocchi in a frying pan in one tablespoon of olive oil until crisp and golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3204930F547E1499BC9C22E3ACE0BA6', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 1, NULL, NULL, 2, NULL, 0, 'While the gnocchi is frying saute the mushrooms and garlic in a large frying pan with one tablespoon of olive oil until the mushrooms are tender, seasoning well with salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'95BA6E528830714BA84A4B92E735C663', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 2, NULL, NULL, 2, NULL, 0, 'Add some of the herbs (reserve some for decoration), the wine and stock and continue cooking gently until most of the liquid is almost gone.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'259E2F9B6D965244BABA7783C12C2949', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 3, NULL, NULL, 2, NULL, 0, 'Add the yogurt and cream and stir well, until the sauce is warmed through. Taste for seasoning and add more if it needs it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B21F97A7E799046B3FC433C00C183B5', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 4, NULL, NULL, 2, NULL, 0, 'Mix the gnocchi through the sauce and serve topped with a sprinkle of herbs. Serve with a side salad and garlic bread. It might not seem like a big portion, but it’s very filling.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6B983395AAF13842B0158F238C4743D0', X'B55CDB5FF9A2FE4D8204A24B418221E2', X'B55CDB5FF9A2FE4D8204A24B418221E2', 5, NULL, NULL, 2, NULL, 0, 'Enjoy');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'410CFF41EE230441BC0B85F7547BDD1B', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 0, NULL, NULL, 2, NULL, 0, 'Blanch the frozen peas and beans in boiling water for a minute, drain and pat dry.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D15282D0D045D45A3CF20033D13B354', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 1, NULL, NULL, 2, NULL, 0, 'Add all the ingredients apart from the breadcrumbs and oil to a blender or food processor and whizz until you have a textured batter.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C78B50058386144B5C7B45AF5F39DC3', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 2, NULL, NULL, 2, NULL, 0, 'Form the batter into balls and roll in the breadcrumbs.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'410B40F87BA02A4980B2DC5FF0C33797', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 3, NULL, NULL, 2, NULL, 0, 'Heat enough oil in a wok to cover the falafel. Once the oil is really hot, carefully lower a few of the falafel in at a time and cook for a few minutes until the coating is brown and crunchy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C338F880785AF4DB2C6F6CBA35BFD47', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 4, NULL, NULL, 2, NULL, 0, 'Once cooked remove from the oil carefully (I used a metal slotted spoon) and place on kitchen paper to drain off the excess oil. Keep cooking in batches until they are all cooked.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EEFE990CBBCCA04084F05E04B667B864', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 5, NULL, NULL, 2, NULL, 0, 'To make the dip, chop the cucumber and mint and mix with the yogurt. Season with salt.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B8AB689D386817448858BE9ACA52B445', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 6, NULL, NULL, 2, NULL, 0, 'Serve the falafel with salad and dip or on a wrap with salad and hummus.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'175955FB6BC86048A4EAC69C9475150F', X'81D37F9D5653EF45AD97F93306E4008A', X'81D37F9D5653EF45AD97F93306E4008A', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'41BB206BAC4CAF47BA9268AE29FC7CD0', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180c/160c fan/gas mark 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC0C617FA803674384089174BB42A0BB', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 1, NULL, NULL, 2, NULL, 0, 'Line two x 2lb loaf tins with baking parchment (greaseproof paper) or loaf tin baking inserts. Little blobs of dairy free spread will stick the liners to the tin, anywhere the paper is bulging. You will thank me for this tip when you are pouring the mixture in.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC47EC7C3001C64DB7E2F5CC0322087E', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 2, NULL, NULL, 2, NULL, 0, 'Sift the flour, bicarbonate of soda, ginger and mixed spice into a large bowl. Stir in the sugar and flax powder until well combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5FD7768DE9DC1B429257925C7D5855CC', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 3, NULL, NULL, 2, NULL, 0, 'Whisk the treacle, almond milk, olive oil and mashed banana in a large jug or bowl.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9E6DE5D59EFED4A87E610B38542449D', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 4, NULL, NULL, 2, NULL, 0, 'Add the treacle mixture to the flour and fold in gently until just combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C7F137F4DE190C4993C4A88BC4FE6E86', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 5, NULL, NULL, 2, NULL, 0, 'Pour half the mixture into each of the loaf tins, top with a long slice of banana for decoration and bake for 40 - 45 minutes, or until a cake skewer inserted into the centre comes out clean.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0EBAF109937BE04E9F6373C0F312FA02', X'8368D9039B92584C8E3D239A10A02FF1', X'8368D9039B92584C8E3D239A10A02FF1', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and leave in the tins for 5 minutes, then turn out onto a wire rack and allow it to cool completely.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BF465261F8F2D4EBA4DD4CBBAE9C791', X'12B51F9E8A766C419342024ACD2AD6BB', X'12B51F9E8A766C419342024ACD2AD6BB', 0, NULL, NULL, 2, NULL, 0, 'Spread the tortilla with pesto.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8AAE4088140F1C4FA096872AA5752797', X'12B51F9E8A766C419342024ACD2AD6BB', X'12B51F9E8A766C419342024ACD2AD6BB', 1, NULL, NULL, 2, NULL, 0, 'Add your filling along the top half of the tortilla, first salad leaves, then cucumber, red pepper, sundried tomatoes and baby beets.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51874C008CC88A4E961FC300BD4153DF', X'12B51F9E8A766C419342024ACD2AD6BB', X'12B51F9E8A766C419342024ACD2AD6BB', 2, NULL, NULL, 2, NULL, 0, 'Fold the sides of the tortilla in a little then holding them firmly, roll the tortilla from the top over the filling and towards you.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'819BBEA864CA194F86956AF16AE5AAD2', X'12B51F9E8A766C419342024ACD2AD6BB', X'12B51F9E8A766C419342024ACD2AD6BB', 3, NULL, NULL, 2, NULL, 0, 'Chop in half and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08615DB6D5D61A4BAFE7B9E6A7B90B75', X'12B51F9E8A766C419342024ACD2AD6BB', X'12B51F9E8A766C419342024ACD2AD6BB', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDC46B52C1D5B546B74AEA75822B4301', X'4CFAAAB95A170549AE0D9021F3BF61A7', X'4CFAAAB95A170549AE0D9021F3BF61A7', 0, NULL, NULL, 2, NULL, 0, 'First make your salad dressing by mixing up the yogurt, garlic and herbs in a bowl. Season with salt and pepper to taste.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'57F2F2D90C1FE248A057A60545D8F2D3', X'4CFAAAB95A170549AE0D9021F3BF61A7', X'4CFAAAB95A170549AE0D9021F3BF61A7', 1, NULL, NULL, 2, NULL, 0, 'Layer your salad in a bowl. Salad leaves, cucumber, beetroot and grapes, Spoon on dollops of cream cheese, scatter over the nuts and then add a drizzle of the dressing.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CB34432DDB9164393D18611B37AD98C', X'4CFAAAB95A170549AE0D9021F3BF61A7', X'4CFAAAB95A170549AE0D9021F3BF61A7', 2, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'75D5E2C3C2C7714A9753437C3CED7146', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 0, NULL, NULL, 2, NULL, 0, 'Place all pancake ingredients in a blender or food processor and blend until the mixture is smooth. If you don''t have a food processor or blender, you can use a stick blender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'716292E015B7464FA473E950074A5FA5', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 1, NULL, NULL, 2, NULL, 0, 'Heat a non-stick frying pan and test a little of the pancake batter to see if the pan is hot enough. It should puff up and start to bubble a little, then it is ready to flip. Use 2 heaped tablespoons of batter for each pancake.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E3FCA1D06D0EF34082900881BF57C4E1', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 2, NULL, NULL, 2, NULL, 0, 'Cook each pancake for 2 to 3 minutes then flip and cook the other side for a couple of minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F39CD53C2C9AA94DACDA581438A69F11', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 3, NULL, NULL, 2, NULL, 0, 'To make the chocolate sauce whisk together the cocoa powder, maple syrup and coconut milk in a small pan and bring to the boil. Reduce to a simmer and cook for a couple of minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7F19A3856A606479939C50C22984966', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 4, NULL, NULL, 2, NULL, 0, 'Mix together arrowroot or cornstarch with the water. Add this mixture to the pot and whisk well. This will help to thicken the sauce. It will take a couple of minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90A1663FF8A05444A97DB33EA42E71B7', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 5, NULL, NULL, 2, NULL, 0, 'Serve the pancakes topped with a drizzle of chocolate sauce . To make them extra special add a scattering of hemp seeds and desiccated coconut for a bit of crunch and blackberries for adding flavour.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'10268185A61704429948E0516F2907EA', X'0DECB2B0C34C6A43B2A6B368C142AD37', X'0DECB2B0C34C6A43B2A6B368C142AD37', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'504948ACBEAA4C4F8CE19A631E0F0027', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 0, NULL, NULL, 2, NULL, 0, 'In a large pan or deep large frying pan, saute the onion until soft, then add the mushrooms and cook for a few more minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB68C67436246A43843D324F7472A671', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 1, NULL, NULL, 2, NULL, 0, 'Stir in the spices and the veggie mince, then add the beans, stock, tomatoes, tomato puree, wine, cocoa, salt and pepper. Stir well.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D81809A78503B645AE69AFE28A4EB5CF', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 2, NULL, NULL, 2, NULL, 0, 'Bring to a simmer, then reduce the heat and leave to cook for 25- 30 minutes, stirring occasionally until the sauce is thick.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'685FD16DF88C3F4C8EB4A2AE1CCBE85B', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 3, NULL, NULL, 2, NULL, 0, 'Warm the tortillas in the oven or in the microwave for a few seconds.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A8989F21EAA7A409255C7A500C7A971', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 4, NULL, NULL, 2, NULL, 0, 'Layer up the wraps and serve. Each tortilla is spread with a generous layer of yoghurt/sourcream/creme fraiche, then topped with salad leaves, chilli and grated vegan cheddar. before they are rolled.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69E14FC45DB32F4384832EE5F157ACB2', X'85F151A434934844BFBAD0F6D8C092EA', X'85F151A434934844BFBAD0F6D8C092EA', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCEF4A00D0B4E340A96E7618A18186DB', X'AE867B355FF9E242874832E5A9C09F65', X'AE867B355FF9E242874832E5A9C09F65', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 220 °C /200°C fan/ gas mark 7 and prepare the vegetables.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2857607F7A87C5449A00913283828C93', X'AE867B355FF9E242874832E5A9C09F65', X'AE867B355FF9E242874832E5A9C09F65', 1, NULL, NULL, 2, NULL, 0, 'Spread the vegetables into one or two oven trays, season and spray with olive oil, then pop in the oven and cook for 20 - 25 minutes until golden and crisping at the edges.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E7DD8770E6337438F345073C107EF55', X'AE867B355FF9E242874832E5A9C09F65', X'AE867B355FF9E242874832E5A9C09F65', 2, NULL, NULL, 2, NULL, 0, 'Prepare your tacos. If you have a gas hob, turn a ring on just above low and hold pop the tortilla on it to let it char. Use tongs to move it around and then turn it over and do the other side. Just a little charring round the edges and across the middle is enough to leave it soft, but warm with a little crispness. If you like them crisper, leave them longer. If you don''t have a gas hob, then you could warm and crisp them under the grill (broiler in USA), but do keep an eye on them.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDD8EB4B1C7AF64CAF028D4F1237570A', X'AE867B355FF9E242874832E5A9C09F65', X'AE867B355FF9E242874832E5A9C09F65', 3, NULL, NULL, 2, NULL, 0, 'Build your tacos now. Spread with tapenade, top with salad leaves, vegetables and tofu, then fold in half and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40EF8F0F88963D459A7F1C0D840D29BC', X'AE867B355FF9E242874832E5A9C09F65', X'AE867B355FF9E242874832E5A9C09F65', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA7DA396444A5846A7969D023B43820D', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 220 °C /200°C fan/ gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4AA3A7B1E9DC349B96B56EA01BC82BC', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 1, NULL, NULL, 2, NULL, 0, 'Place the vegetables on the tray and drizzle with a little olive oil and season with salt and pepper. Toss with your hands and then try to lay the vegetables in one layer if you can. Roast for 25-30 minutes, keeping an eye on them. They should be charred at the edges and soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1D8A2FB46BC2C342B0C7705B06217B4E', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 2, NULL, NULL, 2, NULL, 0, 'In a small glass or bowl mix the salad dressing ingredients together, whisking with a fork.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18645C87D136E64F9F74F8A832F6750F', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 3, NULL, NULL, 2, NULL, 0, 'It''s time to plate up your salad. First cover the plate with a layer or salad leaves, then top with roast vegetables, beetroot, sundried tomatoes and olives.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2E1BAC3FC07B3545A031A34E891F24BC', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 4, NULL, NULL, 2, NULL, 0, 'Drizzle your dressing over the salad and dig in.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C8FC8B0D6051BA439A7851D02C7BBC6E', X'B69967BC86C41B449EA3A82F2180508A', X'B69967BC86C41B449EA3A82F2180508A', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D534F63C0B27A43BC70F4A77BF9651C', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 0, NULL, NULL, 2, NULL, 0, 'Fry the sausage in a little olive oil until cooked, brown and crisp. Set aside to cool, then cut into generous pieces.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9D896EF16DA2A84F9A98200D9F03954F', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 1, NULL, NULL, 2, NULL, 0, 'In a large pot or casserole dish saute the onion and garlic in olive oil until soft. Stir in the spices and allow to heat through to bring out the flavour, then add in the mushrooms and cook until soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'054FC3A35535E1489AB0BA13DE0FE272', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 2, NULL, NULL, 2, NULL, 0, 'Add the beans, tomatoes, wine, stock and tomato puree. Bring to the boil, reduce to a simmer and cook gently for 20-30 minutes until the stew has thickened.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6911311726273D429E09878ECC749E1A', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 3, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E9BA1942AC31649A4963B8CA99F337B', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 4, NULL, NULL, 2, NULL, 0, 'Serve over mashed potato with some veg on the side.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'324E914828A11A4EAA9DDC8910A1D498', X'DA387B2711B4194883875629B6D23661', X'DA387B2711B4194883875629B6D23661', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E564A211F312204C9A68F02B6E7DBC48', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 0, NULL, NULL, 2, NULL, 0, 'In a large pan saute the onion, garlic and ginger in the olive oil until soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C1A0BA72BA5C5D4686C6BD90921C0EF7', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 1, NULL, NULL, 2, NULL, 0, 'Add the sweet potato, carrots and butternut squash and cook gently for 5 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0C66A3B61B6024E98DA9F32DC1CC2A7', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 2, NULL, NULL, 2, NULL, 0, 'Add the spices and stir, then add the stock. Bring to the boil, then reduce to a simmer and cooker for 20-30 minutes until the vegetables are tender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FDD7DF2A4A945E41B43CB7BFCF22A4A2', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 3, NULL, NULL, 2, NULL, 0, 'Stir in the coriander and coconut milk and blend a little to thicken, but don''t blend completely smooth.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'428AC024E8C9D9438DF1E10292478385', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 4, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2331AF773093445B9B41821EC19CB31', X'1508872C7A8CE646BE79DD61C2C5FB42', X'1508872C7A8CE646BE79DD61C2C5FB42', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCC515A13F883840BD956B5C8182CE4E', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 0, NULL, NULL, 2, NULL, 0, 'Line a loose-bottom cake tin with greaseproof paper or coat with baking spray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A69D370AC431F74FA986B645A1F86CD5', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 1, NULL, NULL, 2, NULL, 0, 'Melt the dairy free spread. While this is melting, crush the biscuits and mix together the melted spread with the biscuit crumbs. Press into the base of the cake tin and pop in the fridge to chill.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80F26EB946F54A46A7240EDEF5CFFDAD', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 2, NULL, NULL, 2, NULL, 0, 'Take the tin of coconut milk out of the fridge, turn it upside down and open it. Pour the coconut water into a glass (you can use this for smoothies) and whisk the thick coconut cream with the sugar and vanilla paste until thick. If it is too thick, add a little coconut milk.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA8111B713F3E849BF63488C34EE62DE', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 3, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until soft, then whip into the whipped coconut cream and spread over the cheesecake base then return to the fridge until you are ready to serve it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B65E3FF77304174B94B6A79A7E28A404', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 4, NULL, NULL, 2, NULL, 0, 'When you are ready to serve, loosen the edge with a knife dipped in hot water, then carefully remove from the tin.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'91B6BB939E90D44EB63E1501EFA7D519', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 5, NULL, NULL, 2, NULL, 0, 'Top with some caramel (optional), sliced banana and grated chocolate, then serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'48E1BC53FD590148802B40C7291540C8', X'D23CA2CF22825948AC67DA9425C884A8', X'D23CA2CF22825948AC67DA9425C884A8', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DDA3D261FA0544419B5F89992ED86934', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180c/160c fan/350/gas mark 4, then grease two 18 cm cake tins and line the bottom of each with baking paper. I find baking spray works the best for easy release.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D1470557FF98DC4AA977D50B1408A680', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl mix the flour, cocoa, sugar, bicarbonate of soda and salt together.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F4E21E67007B1A488C02B14DF27BDD9A', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the milk, melted spread, flax eggs and vanilla extract, then pour into the dry ingredients. Whisk until smooth, then add the hot coffee and fold in with a spatula');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FFD4A56DA4E4AD42872624D5CBF6E650', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 3, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared cake tins and bake in the centre of the oven for 35-40 minutes. A skewer should come out clean.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'06C3D933B8B76B419F8911AFA2742F4F', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 4, NULL, NULL, 2, NULL, 0, 'Cool the cakes in the tins until nearly cool, then ease out of the tins, gently peel of the baking paper and leave to completely cool.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE85F09504126C4A96C9D27BDB987DD7', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 5, NULL, NULL, 2, NULL, 0, 'Whip the coconut solids with the icing sugar and ½ tsp of vanilla extract.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF587FFBB9E37E46BDEB6EC8DD08E1F8', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 6, NULL, NULL, 2, NULL, 0, 'Spread a whole tub of chocolate buttercream between the layers, then topped with the coconut whipped cream and raspberries.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD5E95AAB96B2E47B76A1827B1EEE958', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', X'FE2FA9C1CF81594EBBE5EEDA894D1C48', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F33BC0DB1B55264BB56651E99714DDEF', X'60BBD31B8760C0439CB3C5548E4EF038', X'60BBD31B8760C0439CB3C5548E4EF038', 0, NULL, NULL, 2, NULL, 0, 'Drain the chickpeas into a bowl so you can save the aquafaba (chickpea water). You add some in this recipe.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D5CA36D4E84C44EA9DF7F99C7CA3D09', X'60BBD31B8760C0439CB3C5548E4EF038', X'60BBD31B8760C0439CB3C5548E4EF038', 1, NULL, NULL, 2, NULL, 0, 'Add all the ingredients to a blender and pulse for a few seconds until starting to come together in a thick paste but still with texture.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B032C7FAFBDFA94383580721FF5E565F', X'60BBD31B8760C0439CB3C5548E4EF038', X'60BBD31B8760C0439CB3C5548E4EF038', 2, NULL, NULL, 2, NULL, 0, 'Taste for seasoning, then layer up your sandwich. I spread pesto on the bottom slice of bread, a layer of spinach leaves, chickpea filler, grated carrot, sliced tomato and a top slice of bread spread with mango chutney.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC4284DC7461AF4A876F6C8F9841CAE3', X'60BBD31B8760C0439CB3C5548E4EF038', X'60BBD31B8760C0439CB3C5548E4EF038', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E6CBDBB0F39FEA42A03082ED24463B27', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 0, NULL, NULL, 2, NULL, 0, 'Saute the onion in 1 tbsp of olive oil until soft, then set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E27CCE302793C43A08994769D0AE77E', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 1, NULL, NULL, 2, NULL, 0, 'Cook the sausages in 1 tbsp of oil until brown, turning frequently.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8A482583B238B498B46773E2541B72A', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 2, NULL, NULL, 2, NULL, 0, 'Add the sausages and onions to the slow cooker then add all the other ingredients and stir.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BFECAB3E18DA07449C7FC0F712660D05', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 3, NULL, NULL, 2, NULL, 0, 'Set your slow cooker to high and cook for 2-3 hours or set it to low for 4-6 hours.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B9F261C8202B844A86E56A760136476', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 4, NULL, NULL, 2, NULL, 0, 'If you don''t have a slow cooker cook the same way until the sauce has reduced and thickened.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF965E3B9B07BC408E913E24F33DD079', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 5, NULL, NULL, 2, NULL, 0, 'Serve with mashed potatoes and steamed vegetables.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBB1C8AB7575EC42AB40203F56C23A4A', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', X'AF6BF9C4794E4C4D8E71AFE6474F7B39', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25C64695DF8BFE4C93736F49480C8B32', X'D80FFE954BA47D479D5769297F1FBAFC', X'D80FFE954BA47D479D5769297F1FBAFC', 0, NULL, NULL, 2, NULL, 0, 'If using an ice-cream maker, freeze the bowl ahead of time, according to the manufacturer’s instructions.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3B7BC55400F3E4EB8D6B6236BAD68AC', X'D80FFE954BA47D479D5769297F1FBAFC', X'D80FFE954BA47D479D5769297F1FBAFC', 1, NULL, NULL, 2, NULL, 0, 'Purée the beetroot with a hand blender until smooth, then set aside. In a small saucepan over a medium-low heat, combine the sugar and water, stirring occasionally until completely dissolved. This could take about 5 minutes. Remove from the heat and leave to cool slightly.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A96B4F31FADAE04D953DA6E137561418', X'D80FFE954BA47D479D5769297F1FBAFC', X'D80FFE954BA47D479D5769297F1FBAFC', 2, NULL, NULL, 2, NULL, 0, 'In a food processor, blend the cooled sugar syrup with the puréed beetroot, lemon juice and vanilla until smooth.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE496A1CE958754FBAADFFCEF60DA31B', X'D80FFE954BA47D479D5769297F1FBAFC', X'D80FFE954BA47D479D5769297F1FBAFC', 3, NULL, NULL, 2, NULL, 0, 'If using an ice-cream maker, pour the cooled mixture into the pre-frozen bowl of the ice-cream maker and churn until thick, according to the manufacturer’s instructions, then either serve immediately or pour into a freezer-safe dish to store in the freezer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F1A4912EF8AE2499F3B085F8FB475EA', X'D80FFE954BA47D479D5769297F1FBAFC', X'D80FFE954BA47D479D5769297F1FBAFC', 4, NULL, NULL, 2, NULL, 0, 'If not using an ice-cream maker, pour the cooled mixture into a large freezer-safe dish and freeze, mixing it well with a fork every half an hour over the next 4 hours. Transfer to the fridge for 15 minutes to soften before serving.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1FE0E3621F0C5048B8FEAE65604181BC', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 200C/fan 180C /gas 5.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'391B65EB4EA85C40A6A9A7802A040F5F', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 1, NULL, NULL, 2, NULL, 0, 'Sieve the flour into a large mixing bowl, add the salt and blobs of the Trex. Yes that is a technical term.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4FACBE5831C69540B8A30B622B61049E', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 2, NULL, NULL, 2, NULL, 0, 'Use your fingertips to rub together the flour and Trex until it resembles breadcrumbs.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7767E6123017CB43B5983799715089CE', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 3, NULL, NULL, 2, NULL, 0, 'Pour in 4 tbsp of chilled water and cut through it with a knife until it starts to come together. Add another tbsp water if you think it needs it, then bring it together with your hands. Don''t knead it or you will make it tough. Wrap it in clingfilm and pop it in the fridge while you prepare the fillings.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ADFC252E071B39449EC7AE59761D81C1', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 4, NULL, NULL, 2, NULL, 0, 'Cook your apples in a small pan with the sugar for a few minutes until they have started to soften.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CAA4FF29AE02394682C7FBA9EAA24FBD', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 5, NULL, NULL, 2, NULL, 0, 'Empty the haggis into a clean bowl and break it up with a knife, add the spices and pepper and mix through.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C1CC475E298302409EDF213F41AD1473', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 6, NULL, NULL, 2, NULL, 0, 'Take your pastry out of the fridge and divide it into four. Shape piece of pastry into a sausage shape then roll into a long fat rectangle. Cut a strip of pastry from the end and make a wee divider wall with it 3/4 ways up your pastry. Spread the centre of the long section with mustard and the smaller section with jam (at either side of the divide). Don''t take it to the edges, leave a border. Top the mustard with the haggis and the jam with the apple, not too much though. You need to be able to wrap the pastry around the filling.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'728C5EC065EE5B48B23B7A8D3413A132', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 7, NULL, NULL, 2, NULL, 0, 'Wet the edges of the pastry with water then roll the pastry over the filling and keep rolling until you have a sausage roll shape. Place the fold underneath.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22C65DF4088F1549A7F3340F151A8E7B', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 8, NULL, NULL, 2, NULL, 0, 'Pierce the pastry with the tip of the knife to let the steam out and make a little mark or pastry shape to show where the sweet end is. I made little hearts.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22455A004A54AA4BBC6AC24F87EB07DF', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 9, NULL, NULL, 2, NULL, 0, 'Place the clangers on a floured baking tray and brush with melted dairy free spread.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'94A18C99FDF5CD4BBF4252688B78A4A5', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 10, NULL, NULL, 2, NULL, 0, 'Bake for 20-25 minutes until golden and crisp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'128787E109CB39478D123F58A59A4F48', X'2329F66FCBD7174589A5FD496EB21EE2', X'2329F66FCBD7174589A5FD496EB21EE2', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF6D629F057A3D4781255957397F5E56', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 240C/fan 220C /gas 8.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'03223740505C9C4C8657CA19E90F0170', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 1, NULL, NULL, 2, NULL, 0, 'If you are using chilled pizza dough, pop it out of the box and onto a plate to come to room temperature.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3A5CFDCD43188408CAA5E0B00A406F4', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 2, NULL, NULL, 2, NULL, 0, 'Place your vegetables (all except the tomatoes) in a roasting tin and scatter over a few small blobs of Trex. If you are using oil, toss the vegetables in it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'969A057FE58C644087BECC371FC88271', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 3, NULL, NULL, 2, NULL, 0, 'While the vegetables are roasting whizz up some sauce. If you are using jarred sauce, you can skip this step.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6DE8885F978C224BB7497AE33A7714F7', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 4, NULL, NULL, 2, NULL, 0, 'Turn the veg, being careful not to burn yourself.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'88D3B1E50283074A8654FC39589D05F4', X'5149B455D6A7994098ECC4E711D21B82', X'5149B455D6A7994098ECC4E711D21B82', 5, NULL, NULL, 2, NULL, 0, 'Roll out the dough on a baking tray, top with sauce, the roasted vegetables, then the tomatoes.');
