INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'336E435AB8188547935BDE259C9F2162', X'48380009596BC24BB77733F0C1B0ACA7', X'48380009596BC24BB77733F0C1B0ACA7', 1, NULL, NULL, 2, NULL, 0, 'Place a double sheet of clingfilm on your countertop and scoop the cream cheese onto it in a log shape. cover the log in cling film and twist the ends, then gently shape the log.');
