INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E8EF6D7EDF0FCF48B662873928A50786', X'E44B8A73B6ED1546BCBBA2655CCCEC2F', X'E44B8A73B6ED1546BCBBA2655CCCEC2F', 5, NULL, NULL, 2, NULL, 0, 'Pop the cheesecake in the fridge for a few hours to set.');
