INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A249F7E08A04E498FE672341D220C90', X'BA4627A0FF113E4C97B4F8111AF000F3', X'BA4627A0FF113E4C97B4F8111AF000F3', 1, NULL, NULL, 2, NULL, 0, 'Add the spices and stock.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'019A49AF13B58943BBC30A92448AD755', X'BA4627A0FF113E4C97B4F8111AF000F3', X'BA4627A0FF113E4C97B4F8111AF000F3', 2, NULL, NULL, 2, NULL, 0, 'Set the slow cooker to low (75-80 degrees for 8 hours) or cook in a casserole on the hob for 45 minutes - 1 hour. If you are cooking on the hob, sauté the onion and garlic until soft before adding the other ingredients and add more stock.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90EB0DF1DF1EA844A9AC7E4228A3E317', X'BA4627A0FF113E4C97B4F8111AF000F3', X'BA4627A0FF113E4C97B4F8111AF000F3', 3, NULL, NULL, 2, NULL, 0, 'Check the casserole near the end of cooking to see if it needs longer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E5A232884BCC25489995E1BCB32B3BF7', X'BA4627A0FF113E4C97B4F8111AF000F3', X'BA4627A0FF113E4C97B4F8111AF000F3', 4, NULL, NULL, 2, NULL, 0, 'Serve with crusty bread, green vegetables or well-cooked vegan sausages.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'28A7653371B0DF498C1559B4CF7F39CA', X'BA4627A0FF113E4C97B4F8111AF000F3', X'BA4627A0FF113E4C97B4F8111AF000F3', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B468C444916DB4B85B417472A3100F2', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 0, NULL, NULL, 2, NULL, 0, 'Rub your frying pan with a little oil and heat on a medium to high heat until the pan is really hot.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1992DC37791BC49A52D9ECD0337E1FE', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 1, NULL, NULL, 2, NULL, 0, 'Sift your flour into a bowl and add the salt.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8DDC0EB0B94234488C6BE8218F5B45BD', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 2, NULL, NULL, 2, NULL, 0, 'In a jug, whisk your milk, agave nectar, oil and vanilla extract until well combined. Make a well in the middle of your flour and pour in most of the wet mixture. You want it to be like thick pouring cream, you may not need all the liquid.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E40276462E405841B4D8AC3F016719D3', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 3, NULL, NULL, 2, NULL, 0, 'Try a teaspoon full of your batter in your frying pan, to test if it is hot enough. It should bubble up quickly, flip, then cook on the other side for just a few seconds. You should have a wee golden pancake.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'61E18CB7EA4A954CBC518CF8099E47E7', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 4, NULL, NULL, 2, NULL, 0, 'Cook the rest of your pancakes. Each pancake is made from a generous tablespoon of batter. They will take about 3 minutes each. Turn them when they start to bubble. You can have a sneak peek underneath using a palette knife to check it they are golden underneath and ready to flip.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84854DCA5F0145489B261550FA125A4C', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 5, NULL, NULL, 2, NULL, 0, 'Serve the pancakes warm with maple syrup.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C0D208E7510C2418EF2E0833C93E9CF', X'84C9128E898C5E45B312678A0D6AF7BB', X'84C9128E898C5E45B312678A0D6AF7BB', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'30980C4C0C7BF14685A1A121D7BA3455', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 220 c/200 c fan/425 f/gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B556B1A7C99B45478F4CF4F9F91A8819', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 1, NULL, NULL, 2, NULL, 0, 'Gently fry the burgers in the oil for a few minutes on each side until well cooked.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9A4DE20FF938004CA4488A6A31E7F563', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 2, NULL, NULL, 2, NULL, 0, 'Mix the cream cheese with the herbs and season with black pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A3DF6F3FCE11964EB02F2F5F9C68A6A8', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 3, NULL, NULL, 2, NULL, 0, 'Cut eight circles of puff pastry. Use a saucer or small bowl. It needs to be a bit bigger than the burgers, a bit more than 1 cm all round so the filling will fit in.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'01B144A9189DE148ADDC562AB1943D47', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 4, NULL, NULL, 2, NULL, 0, 'Put the pastry circles on a baking sheet and spread four of the circles with mustard (leaving a border) and rest with a thick layer of soft cheese (leaving a border again).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8F681F109FFAFA4DB328DC2EF1854647', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 5, NULL, NULL, 2, NULL, 0, 'Top the mustard with a burger then dampen around the edge of the pastry with a little water to help it bind. Add a pastry lid, cheese side down and seal the pastry using the prongs of a fork. Cut a couple of slits in the top to let out steam.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'847E63107C91BF43B7F19489F9CA3FEA', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 6, NULL, NULL, 2, NULL, 0, 'Cut shapes from the leftover pastry to decorate the top of the pies and stick on with a little water (optional).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E321D4333DEF134FBDA3AA3310740249', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 7, NULL, NULL, 2, NULL, 0, 'Brush each pie with a little milk and bake for 15-20 minutes until the pastry is golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E4D0CAF108196F4EB9A3F6CC9646AE04', X'CE995C15D9928246BE21066C325FC6A7', X'CE995C15D9928246BE21066C325FC6A7', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31502C3EA8A27048997FA48605E80B1B', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 190c/170c fan/375f/gas mark 5.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F5DA775EA12A9345BE5B4C1A87A6C8C7', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 1, NULL, NULL, 2, NULL, 0, 'Line a 20 cm (8-inch) square or round baking tin');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C92745D149B62E4BA7415D99F9618147', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 2, NULL, NULL, 2, NULL, 0, 'Mix together the topping ingredients and set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1169B39FECCA70498C69246D1839943B', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl mash the bananas until gooey, whisk in the eggs, vanilla extract and sugar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'162B3BD07B802C438A977F6B2A7A705B', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 4, NULL, NULL, 2, NULL, 0, 'Sieve the flour, cinnamon and baking powder in another bowl, then fold into the banana mix. Then gently fold in the chocolate chips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD4FDD1DDB8A8E449D033B3822A94210', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 5, NULL, NULL, 2, NULL, 0, 'Pour into your tin and bake in the oven for 35 to 40 minutes. A skewer should come out clean, when you test it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4595B76B6513DC42B9BD8CA2B0E8AB12', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 6, NULL, NULL, 2, NULL, 0, 'When it comes out of the oven top with the cinnamon sugar and leave to cool in the pan for 10 minutes before removing from the tin and finishing the cooling on a rack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4F5B053DDE071D4AA4E88526A032A857', X'0D79D31EABFBFA4090B30220DF4612AA', X'0D79D31EABFBFA4090B30220DF4612AA', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D215AC93CE81B49B6F7C6E143CCC82A', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 0, NULL, NULL, 2, NULL, 0, 'Slice the cheese into 5mm slices, lengthwise.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6E297CB45F1AD24E82C4E23BFF68F128', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large non-stick frying pan over a medium-high heat. Fry the cheese until golden underneath, then use tongs to turn the cheese over and cook until golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'32F09270B246854BBB561A3225E63658', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 2, NULL, NULL, 2, NULL, 0, 'Squeeze the lemon juice over the cheese while it''s frying.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'58F909950D1DCB4B9028FF4192B1CED1', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 3, NULL, NULL, 2, NULL, 0, 'Place the cheese on warmed plates, scatter over the fresh herbs, olives, lemon zest and season with black pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E968383E812AB84F8D845FF9EA20A038', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'07A57968890D914F82B6BB9B27B802E5', X'D807D930A44A5B449C740C2191B0C6D8', X'D807D930A44A5B449C740C2191B0C6D8', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C5AED43F964F54082CCAE2EE5FE5D8D', X'D94D833A7186834996706CE440B19CA6', X'D94D833A7186834996706CE440B19CA6', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients bar the cola cube in a cocktail shaker (or a in a flask).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3ADEA3564B110248B69D096DB9E8F480', X'D94D833A7186834996706CE440B19CA6', X'D94D833A7186834996706CE440B19CA6', 1, NULL, NULL, 2, NULL, 0, 'Pour into a cocktail glass containing a cola cube.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4FCBA4EABDB62E4DAE9B433AB13F797F', X'D94D833A7186834996706CE440B19CA6', X'D94D833A7186834996706CE440B19CA6', 2, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E07979A27F1EA4CAB7FF03DF0D2D16E', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, saute the onion, garlic and chilli in the olive oil until translucent.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D5E9B717D055F45A756BDCE36A52953', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 1, NULL, NULL, 2, NULL, 0, 'Add the turnip, carrot and cook for a few minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9DD8683534B8AC40A240E15E644BF1BC', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 2, NULL, NULL, 2, NULL, 0, 'Add the stock and cook gently for a further 30 minutes..');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'36A59B6C4BB7EB41A273FCD570381E47', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 3, NULL, NULL, 2, NULL, 0, 'Blend the soup and add the coriander, salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'417525A93B160F458B9C09F65AB22535', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 4, NULL, NULL, 2, NULL, 0, 'Serve topped with crumbled feta cheese (optional).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0DA53FE3AD1A5D48B611410F5AD55BA4', X'51184D76AB2AC943907D5D411F985E5B', X'51184D76AB2AC943907D5D411F985E5B', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2A2EC1A54FF034FAD9208346A48F1B9', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 180c/160c fan/ 356f/gas 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'312781475719B646B6CD96FC2B17265B', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 1, NULL, NULL, 2, NULL, 0, 'Place the peppers on a baking tray and roast for approximately 20-25 minutes until beginning to char.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'273F121CC02D8A458C18F5FFC997C665', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 2, NULL, NULL, 2, NULL, 0, 'Place the peppers in a bowl and cover it with cling film, then leave the peppers to cool. Once cool peel off the skin, discard the seeds and cut the pepper in half lengthwise.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9FF3DD1CB6319A46A50140C8F76F5BD6', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 3, NULL, NULL, 2, NULL, 0, 'While the peppers are cooling saute the mushrooms in olive oil until soft, seasoning with salt and pepper as they are cooking. Set aside to cool.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'325EFCCB652FF643A2FA977E6644128B', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 4, NULL, NULL, 2, NULL, 0, 'Mix the ricotta with the cooled mushrooms, mustard, onion powder, 2 tbsp of the grated parmesan, salt and pepper and the herbs if you are adding them.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3E3AB99B46621A449044915C37A795DB', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 5, NULL, NULL, 2, NULL, 0, 'Fill each pepper with the creamy filling. Secure with a tooth pick.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC2174AB43547F4CAA3777E8A5568B75', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 6, NULL, NULL, 2, NULL, 0, 'Place the peppers on a baking tray and sprinkle with the parmesan cheese, then grill for approximately five minutes until the cheese has melted.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B28E62C0ADF6C948A315919BB2351B2E', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', X'88C1AC7614F3AA48B651DC3E0C0E8EE7', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC0B2BAB2580EF4E8DF0F999C05A7D0C', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 0, NULL, NULL, 2, NULL, 0, 'Line a 23 cm cake tin with baking paper, but grease it too or spray with cake release spray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4936600D5F0A8E41A7FEBC9AE5F5A3B0', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 1, NULL, NULL, 2, NULL, 0, 'Gently melt the butter or dairy free spread in a small pan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'308DF2D37503C149AC544CCE5D601B9C', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 2, NULL, NULL, 2, NULL, 0, 'Crush the biscuits in a bowl then pour in the melted butter and mix well before spreading over the base of the tin in an even layer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DC077B753CA29441B4CF8373613CF30E', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 3, NULL, NULL, 2, NULL, 0, 'Break the chocolate into squares and put them in into a heatproof bowl, with the liquid glucose and rum. Place the bowl over a pan of gently simmering water (make sure the base of the bowl doesn''t touch the water or the chocolate could seize). Leave it until the chocolate has melted. Stir it then take it off the heat and leave it to cool a little. Remember that bowl will be hot so use an oven glove.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4AE38505C279F4190B5969A2C658192', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 4, NULL, NULL, 2, NULL, 0, 'In another bowl beat the double cream until just beginning to thicken, then fold half of the cream into the chocolate mixture, then fold that mixture into the rest of the cream. When it is blended and smooth, spoon it into your prepared cake tin. Tap the tin gently to even the mixture out, then cover with clingfilm and chill overnight in the fridge.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6B7A1B0663B45F46807771F16060D0AE', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 5, NULL, NULL, 2, NULL, 0, 'Just before serving, run a warm knife round the edge of the tin to loosen the torte and remove from the mould really carefully.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7276BC835D8764894D86DA1E0586889', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 6, NULL, NULL, 2, NULL, 0, 'Dust with cocoa powder before serving.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1094011820A1D74A83C43A9E37D89AEA', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 7, NULL, NULL, 2, NULL, 0, 'Serve with berries and whipped cream (or coconut cream).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5038F746E7839D46805D905859E5481F', X'C529E541E68E2F43B7E3EFCB1981629D', X'C529E541E68E2F43B7E3EFCB1981629D', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4DD736EB10635341A9F979C05389F253', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 0, NULL, NULL, 2, NULL, 0, 'Saute the onion and garlic in the olive oil for a few minutes, add the carrots and cook until tender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D4D73A79751B3489EBD7E59A734033B', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 1, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and cook for another few minutes. Add the soya mince, give it a good mix in and then add the vegetable stock.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'59D2F1C82CD1ED44B11BC3C5211C557D', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 2, NULL, NULL, 2, NULL, 0, 'Cook down for 20 minutes and then add the worcestershire sauce and tomato puree.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'390FBF467FA8F747A6C4F46B365E40BE', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 3, NULL, NULL, 2, NULL, 0, 'Cook for a further 10 minutes and season with salt and pepper. Go easy on the salt as the stock can be quite salty.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47065585F378E441A8BC8B23F97CE86F', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 4, NULL, NULL, 2, NULL, 0, 'Spoon into an ovenproof dish and top with mashed potato. Brown under the grill.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0EBDF8F5128EE4DA16E342CEE153176', X'073C625045ED254996D6DD8CBFA05063', X'073C625045ED254996D6DD8CBFA05063', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A437504CF5AC6B4387B26E52824F198D', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 190c/375f/Gas 5.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6969913A4000A84DBBAB5948A6D8F996', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 1, NULL, NULL, 2, NULL, 0, 'Saute the cherry tomatoes in a hot pan (no oil) until the skin begins to crack, then pour in the balsamic vinegar and cook for a minute more. Remove from the pan and set aside.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'432345C94DD8EB4AB91860D02E505BFD', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 2, NULL, NULL, 2, NULL, 0, 'Roll out a sheet of pastry on a pre-heated oven tray. Score a line all the way around the pastry a couple of centimetres in from the edge, to make a border. This will allow the edge of the pastry to puff up.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76CF39AF885B174081ADE0062B32A888', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 3, NULL, NULL, 2, NULL, 0, 'Spread the pesto on the pastry, being careful not to go over your scored line. Next, scatter the tomatoes and feta and basil across the pastry.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'792D012BC348F7498494B5F7460CE960', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 4, NULL, NULL, 2, NULL, 0, 'Brush around the edge with milk (or oil) for a golden finish. Finally, drizzle with a little olive oil, then bake for 15 - 20 minutes until the pastry is golden and puffy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'67B03DC1E745FD4FA884F265F0954BF5', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 5, NULL, NULL, 2, NULL, 0, 'Serve with a dressed salad and baby potatoes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0345E63F6A32C49973837BF291F8135', X'17FBE05FED77FC41BFF7125EFF311880', X'17FBE05FED77FC41BFF7125EFF311880', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2A53566B2555EB4F8D9FA282C916E476', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 220c/200fan/425f/gas mark 7.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CFEC1E44F8C07F448580BE15B5875B79', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 1, NULL, NULL, 2, NULL, 0, 'Sieve the flour, baking powder, salt and mustard in a large mixing bowl, mix together and then rub in the butter with your fingertips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'588AA25DFF37DA45BA137722267266E1', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 2, NULL, NULL, 2, NULL, 0, 'Knead lightly and roll out to 1 inch/2 ½ cm thickness.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68531738A6E36A4091C37E15B842E2A3', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 3, NULL, NULL, 2, NULL, 0, 'Cut out rounds with a cookie cutter, being careful not to twist the cutter as you remove it or they won''t rise straight.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E39625874546A24197B8E537EBD07D20', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 4, NULL, NULL, 2, NULL, 0, 'Place the scones on a lightly floured baking tray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'53F954A4F6E0494E97B8B3B8FE7875DF', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 5, NULL, NULL, 2, NULL, 0, 'Brush the scones with egg (or milk) and sprinkle with cheddar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F8407B8F198D6D49A047ADF6B70D7CF6', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 6, NULL, NULL, 2, NULL, 0, 'Bake for 10-15 minutes until well risen and golden.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B4DEFADA1804846A40C07F82785BDDE', X'352024866FBAF649A16CA97046ED91DF', X'352024866FBAF649A16CA97046ED91DF', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1766A076D8CB3D45AAF28D1D33E0667F', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut halves or quarters depending how big they are. Cut the core out of the cabbage and chop it. It doesn''t have to be finely chopped. Cook the potatoes and the cabbage in salted water in separate pans .');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FBF867FB1156454BAC1D231F9787404B', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 1, NULL, NULL, 2, NULL, 0, 'Bring the pans to a boil, then reduce the heat to simmer and cook until the potatoes and cabbage are tender, then drain them.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0BD66C00CDDB6F4DBA836563112B5096', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 2, NULL, NULL, 2, NULL, 0, 'Mash the potato with butter and milk for a smooth finish. Stir in the fresh herbs and the cabbage and season with salt and pepper. I like lots of pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5A15C545F9E937439AAD76E4A0DAF69E', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 3, NULL, NULL, 2, NULL, 0, 'Pile into an ovenproof dish and top with grated cheddar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6055C40632629C4588BD80F6DBC2B807', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 4, NULL, NULL, 2, NULL, 0, 'Bake at 350°F/180°C or Gas mark 4 for 25-30 minutes, until the cheese golden and the dish it hot all the way through.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3627EC2DFB86B48A63D5F2E3C08C6CE', X'A919E58488E59045943C03C7B24D32AC', X'A919E58488E59045943C03C7B24D32AC', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E56D66C12E6344E9395E49DCF9F4026', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 160c/140c fan/gas mark 3.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'16273128797D834FA4C34E977F3EAA28', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 1, NULL, NULL, 2, NULL, 0, 'Using a potato peeler to peel the parsnip into long strips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F980ECE6399AB46B9405C250ED5B89F', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 2, NULL, NULL, 2, NULL, 0, 'Place the parsnip strips into a bowl and drizzle with honey. Toss the strips in the honey until well coated, then place on a baking tray. I bake my parsnips on a non-stick mat placed on a baking tray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98EEE8012BDAD744BD0DA70951901023', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 3, NULL, NULL, 2, NULL, 0, 'Grind black pepper over the parsnips, then sprinkle over some sea salt.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8250F72F420C4F4A8D1F847DA428F5F9', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10-15 minutes until browning at the edges. The parsnip strips will crisp up as they cool.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'751AD1A1B8D3304EADE57C59E73DA2B7', X'91FC1DF57BFBB149B3BE15B54F6C4475', X'91FC1DF57BFBB149B3BE15B54F6C4475', 5, NULL, NULL, 2, NULL, 0, 'Enjoy, then peel another parsnip and start again.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'727B6762401F8D47B56EE7C650D61204', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 0, NULL, NULL, 2, NULL, 0, 'Rub your frying pan with a little oil and heat on a medium heat until the pan is really hot.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9ACBFC44CAAA7C4DBF2754B55B9A3080', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 1, NULL, NULL, 2, NULL, 0, 'Sift your flour into a large bowl, then pour in the sugar and salt and gently mix together.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'527FFF90FBCDE94B8CF8CDD76DDF465D', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 2, NULL, NULL, 2, NULL, 0, 'Whisk your milk and egg together. Make a well in the middle of your flour and pour the egg mixture in. Whisk the flour into the egg until well combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8449B581DEE8D14088F74380C4716F8A', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 3, NULL, NULL, 2, NULL, 0, 'Try a teaspoon full of your batter in your frying pan, to test if it is hot enough. It should bubble up in less than a minute, then cook on the other side for just a few seconds. You should have a teeny golden pancake.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DD561264035A0F47936F1DD56E9E3ABF', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 4, NULL, NULL, 2, NULL, 0, 'Now cook off the rest of your batter. Each pancake is made from a tablespoon of batter. They will take about 3 minutes each. Turn them when they start to bubble.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9B30FF330B4B4446937CD0FE75907767', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 5, NULL, NULL, 2, NULL, 0, 'You can have a sneak peek underneath using a palette knife to check it they are ready to turn. I can cook 3 at a time in my frying pan. You have to make sure there is room between them.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70FACB9F5140B84DAB7751CE88D48A67', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 6, NULL, NULL, 2, NULL, 0, 'Serve the pancakes with your favourite toppings. I love maple syrup and bananas with mine.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9743F5DBB242740ABD0810A214F90A7', X'5042C8197D945241955B8C91D443473A', X'5042C8197D945241955B8C91D443473A', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2312AFD63F75EB48BBB57ADB76604430', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl add the salt to the flour and oatmeal.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'573F49943195D249A260BA8E241FA024', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl mix the yeast with a little of the warm liquid and add the sugar. Allow the mixture to become frothy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F54587FD0627884890CD535E04298984', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 2, NULL, NULL, 2, NULL, 0, 'Mix the dry ingredients with the yeast liquid to make a batter.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90577BA0EAEE3344A4D2221B6291DB90', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 3, NULL, NULL, 2, NULL, 0, 'Cover the batter with a clean cloth and leave in a dry place for an hour.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'367A8D513A0EA2428C9754EE9404A025', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 4, NULL, NULL, 2, NULL, 0, 'Bake the oatcake on a well-greased griddle or large frying pan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FC1C33A587E67A43BCAEDC2252E9AB57', X'CD39EBA63E603D40826BB9718BC572E3', X'CD39EBA63E603D40826BB9718BC572E3', 5, NULL, NULL, 2, NULL, 0, 'Add enough batter to make an oatcake about 8-9 inches across. It will gradually become covered in holes as it cooks and the surface will change from wet and shiny to dull when it is ready to turn, after about 3 minutes. Turn the oatcake and cook for another 2-3 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'34FFE152C38A4844A7027B93D55EDA20', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 0, NULL, NULL, 2, NULL, 0, 'First make the puree. Place the apricots, sugar, lemon juice, lemon peel and water in a pot. Bring to the boil, then reduce the heat and simmer gently for 10 minutes until the apricots are soft. Set aside to cool a little before whizzing up in a food processor or with a stick blender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2342B686EB56724A9C1C1D8191EB87B8', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 1, NULL, NULL, 2, NULL, 0, 'Next make a chocolate sauce, In another pot over a low to medium heat, heat the double cream and break the chocolate into it. Stir the chocolate until it has melted, then remove form the heat and add the cointreau if you are using it. You could add orange juice instead or leave it plain.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'965938E07F2F0F419E2890493B5C5D64', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 2, NULL, NULL, 2, NULL, 0, 'Pour the melted chocolate into 4 bowls or glasses and pop into the fridge.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0A17EAB6C928A245B2F704121A45AA7C', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 3, NULL, NULL, 2, NULL, 0, 'Now make the fool base. Whisk the whipping cream until firm. If you like, you may add a little icing sugar, to sweeten it up, but as you are adding some apricot puree, you really don''t need to.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'57FB6525D8BC9E4291C7DCDC41AF7F8D', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 4, NULL, NULL, 2, NULL, 0, 'Swirl apricot puree through the double cream, leaving some to top the finished dish with.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C5C3CD9E9E33894E8FDFFF6DC948A3E7', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 5, NULL, NULL, 2, NULL, 0, 'Spoon apricot fool on top of the chocolate sauce in each of your bowls/glasses and top with some of the remaining apricot puree.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3887C7445182954C86E4DA7715934ACA', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 6, NULL, NULL, 2, NULL, 0, 'Chill well before serving.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A11CF184E203CC4CA624A3F847E37C46', X'D4C576015002124D8681A47185118B59', X'D4C576015002124D8681A47185118B59', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'440B3414FC95E8449754020175FEA8CE', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a wide, shallow pan and add the cumin seeds, followed by the onion.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E69B3A72801C204D9F58405239E55DB3', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 1, NULL, NULL, 2, NULL, 0, 'Sauté for about 5 minutes, until the onion is soft, then add the ginger-garlic paste and fry for a few seconds longer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'55791CF1ABDB1044A4521368DAA7FFF5', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 2, NULL, NULL, 2, NULL, 0, 'Add the green chillies and potatoes and sauté over a high heat for a couple of minutes. Tip in the cauliflower, turmeric and salt, mix well, then reduce the heat. Cover the pan and cook for about 10 minutes, stirring occasionally to prevent sticking.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5827DA1A74681E4B82E714FF45764E5D', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 3, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and garam masala and cook for about 5 minutes, until the vegetables are completely tender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63841B8083B4284D9D5EE472363C8A4F', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle in the chopped coriander and the ginger, squeeze over the lime juice and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1BDECF1A7878BD47B1E0B56EBF9F4122', X'D4754B29F815054383FD452934AE9847', X'D4754B29F815054383FD452934AE9847', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDD1AC539EF822498D31259541A93C3B', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 0, NULL, NULL, 2, NULL, 0, 'Mix flour and oil in a bowl and rub lightly. Add water gradually and knead it at the same time until a soft dough is formed. Cover and leave it for 10 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1508E3E363AA2847BE342FFDE98E83AF', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 1, NULL, NULL, 2, NULL, 0, 'Divide the dough into 12 small portions. Dip one portion at a time in the plain flour and roll it with a rolling pin on the board, dipping it in the dry flour frequently to prevent sticking.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C16F4FDC95E2C846ADB426A6473DBB0B', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 2, NULL, NULL, 2, NULL, 0, 'In the meantime heat a griddle/frying pan and put the rolled chapati in the pan. Cook one side for 20 to 30 seconds, turn it over and then cook the other side.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F72A9ED3F5B9947A07E7CEC105966DC', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 3, NULL, NULL, 2, NULL, 0, 'Now remove the pan and put the chapati on a high flame for few seconds and soon it will puff up.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64778B37C3DC844F9716E01B82DAD4D5', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 4, NULL, NULL, 2, NULL, 0, 'Using a tong, turn the chapati over and wait for few seconds until brown spots appear this side.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6DB74CE1C466EB4BB6A6149F47DC762C', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 5, NULL, NULL, 2, NULL, 0, 'Remove from flame and put it on a kitchen towel.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B53EFDF3DC892E459ACBC330BF76DB5E', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 6, NULL, NULL, 2, NULL, 0, 'Repeat the process with rest of the flour balls.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'148F311FCB40CC4FAFDF381B9E743FEE', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 7, NULL, NULL, 2, NULL, 0, 'Serve these hot with any curry.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE4EB3912886BF46A63E473EAE4CBA58', X'90BDD9E2DADF2848AD21CCB92F0C5F70', X'90BDD9E2DADF2848AD21CCB92F0C5F70', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B3F395D3AF1C944B971ABF6B159A5AAF', X'C34A7494F1C56F4088B373A6D8DBD1A2', X'C34A7494F1C56F4088B373A6D8DBD1A2', 0, NULL, NULL, 2, NULL, 0, 'Dry roast the seeds in a hot pan for a few minutes. Don''t allow to burn. Grind the spices in a pestle and mortar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'404FBF1016167A4F8945B348593CC40C', X'C34A7494F1C56F4088B373A6D8DBD1A2', X'C34A7494F1C56F4088B373A6D8DBD1A2', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large, heavy-based pan. Add the spices and allow to sizzle for a few minutes before adding the rinsed and chopped potatoes. The oil will sparks, so put the lid on until it settles, then stir occasionally for the next 5 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6700CE045D817943AF25669B11B370B3', X'C34A7494F1C56F4088B373A6D8DBD1A2', X'C34A7494F1C56F4088B373A6D8DBD1A2', 2, NULL, NULL, 2, NULL, 0, 'Stir in the salt, turmeric and chilli flakes. Then pour in the coconut milk and stir to deglaze the pan. Bring to the boil and simmer gently, with the lid on for 15 minutes or until the potatoes are tender.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B1FBBA2B5528D4CB9E2E5648A0AE261', X'C34A7494F1C56F4088B373A6D8DBD1A2', X'C34A7494F1C56F4088B373A6D8DBD1A2', 3, NULL, NULL, 2, NULL, 0, 'Rinse the spinach and then add to the pan. There is no need to chop it, it will wilt down as it cooks. Stir well before serving.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E979A760B587104B9328A8884AE422C7', X'C34A7494F1C56F4088B373A6D8DBD1A2', X'C34A7494F1C56F4088B373A6D8DBD1A2', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CA96E3D00885949934B695D214BA0B2', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 0, NULL, NULL, 2, NULL, 0, 'Heat a non-stick frying pan on your gas or electric hob.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D541990A7431094288CD0972DD9993BC', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 1, NULL, NULL, 2, NULL, 0, 'In a jug whisk together 1 large egg and half a cup of milk.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDC77244E46605478510229B45B8D2D4', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 2, NULL, NULL, 2, NULL, 0, 'Sieve the flour and cocoa powder into a large bowl with the sugar and salt, pour in the egg mixture and whisk in until well combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F9C3D2EA93F2B042A37E833CB317F823', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 3, NULL, NULL, 2, NULL, 0, 'Pour 1 tbsp of batter into the pan for each pancake. Use the back of your spoon to smooth it gently into a circle. Each pancake with take a couple of minutes, once they have a lot of bubbles over the surface, flip it. It only needs seconds on the other side to cook.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A85F047292F4D46BA52448E4A237496', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 4, NULL, NULL, 2, NULL, 0, 'Keep going until you have used all your batter, but don''t try to cook too many at once, give them space in the pan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'14BBF0B1E38A984F88B4686586A56622', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 5, NULL, NULL, 2, NULL, 0, 'Whip the cream until thick, then swirl some lemon curd through it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33EA7660AB7728479269A1543C3A5D18', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 6, NULL, NULL, 2, NULL, 0, 'Once they are a little cooler, stack the pancakes with a filling of cream between each layer. Top with a dollop of cream and drizzle curd from the top.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8DB2DC6DC4EE094DAB6BA75928D03408', X'AB0D0CA12AF30C408B50789261E26BFB', X'AB0D0CA12AF30C408B50789261E26BFB', 7, NULL, NULL, 2, NULL, 0, 'Tuck in and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'74BC3AB972371547AA16E4AC1ABEFE65', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 0, NULL, NULL, 2, NULL, 0, 'First make your pancakes (this is up to you, you could make the fluff first) and heat a non-stick frying pan.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70EF4B4718987E468A6F2BF2EC886597', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 1, NULL, NULL, 2, NULL, 0, 'In a jug, whisk together 1 large egg and half a cup of milk');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D91FEDBA4CC39F4CAA3061BA9ED4D00C', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 2, NULL, NULL, 2, NULL, 0, 'Sieve the flour into a large bowl with the sugar and salt, pour in the egg mixture and whisk in until well combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2F7B64B1EA2DD45AA5E96ECB69B0567', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 3, NULL, NULL, 2, NULL, 0, 'Pour 1 tbsp of batter into the pan for each pancake. Use the back of your spoon to smooth them into a circle. Each pancake will take a couple of minutes, once they have a lot of bubbles over the surface, have a peek underneath. If it is golden, flip it. It only needs seconds on the other side to cook.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33A9EB472E526646B770EBF5DA291589', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 4, NULL, NULL, 2, NULL, 0, 'To make the fluff you need a glass bowl that will fit over a pan to make a ban marie. Add a little hot water to the pan and simmer. Make sure the bottom of the bowl does not touch the water.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C15AD7854AAD3B4BBD2F210914CD994B', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 5, NULL, NULL, 2, NULL, 0, 'To the bowl add the egg whites, sugar, salt, syrup and vanilla extract. Whisk for 10 minutes until you have a thick glossy mixture, then take of the heat and continue whisking until the mixture has turned into thick, white and glossy fluff.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDF9ECD9FCA4954C8C38E54CCF170E8C', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 6, NULL, NULL, 2, NULL, 0, 'Melt the chocolate in a bowl over the pan you whisked the fluff over. Just heat the water again and add a little more if required.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'124ACB4ECE25744B88FC06EA838F4B70', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 7, NULL, NULL, 2, NULL, 0, 'Stack you pancakes, sandwiching them together with fluff and finish off with a drizzle of melted chocolate just before you serve them. Remember to tell guests if you have added a toothpick to secure the stack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9C523D62DAAA04D96C52512335B694B', X'A1E8DD680E4B584B85E497CCAA8EBC3A', X'A1E8DD680E4B584B85E497CCAA8EBC3A', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F871F0B39505BC47A51155BF67A50546', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 0, NULL, NULL, 2, NULL, 0, 'Drain the tofu, then wrap it in kitchen paper and tea towels. Weight with heavy books and leave for half an hour to drain the excess liquid from the tofu.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC1E521C6EEDB240AEDFE6B0A2F809F5', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 1, NULL, NULL, 2, NULL, 0, 'Unwrap the tofu and slice into fingers.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7C84DAFB48602741A4A9E2AC2ACE4CE7', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 2, NULL, NULL, 2, NULL, 0, 'Mix the breadcrumbs with the spices and pour onto a plate. Dip the tofu fingers in the breadcrumbs until well coated.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3714CFD001769848A20DDE417F49566C', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 3, NULL, NULL, 2, NULL, 0, 'Bake or fry in a little oil until golden and crunchy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4B68AD88F6A0C049BBFC68A4CFDB8B66', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 4, NULL, NULL, 2, NULL, 0, 'Serve with ketchup for dipping.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3693D16FB28C2E43A1FC5AA713D3C867', X'AB34621591C43A4DA92E1C868BABD1AE', X'AB34621591C43A4DA92E1C868BABD1AE', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E364AE91CB75464EBDC257A5B457BDA3', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 0, NULL, NULL, 2, NULL, 0, 'Mix up your flax eggs. Each flax egg is 1 tbsp of freshly ground flax seeds (linseeds) with 3 tbsp of water. You need two for this recipe. Mix together and pop in the fridge to thicken.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3502A8413474684AB208B97E2F69CD5B', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350f/180c/160c fan/gas mark 4 and spray your donut pan with cake release spray.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D35F91C5B417894299B45DC0CA245498', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 2, NULL, NULL, 2, NULL, 0, 'Sieve the dry ingredients together in a bowl and gently mixed together.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8C31C1A6222F3D47BF24D79D79C56BC5', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 3, NULL, NULL, 2, NULL, 0, 'Mash the banana and mix in the melted spread, vanilla extract and flax eggs.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7745C602A4FE541B4E4F0E1833CC8F6', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 4, NULL, NULL, 2, NULL, 0, 'Fold the wet ingredients into the dry ingredients with a spatula, then fold in the chocolate chips.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'44F054114993F84389E54DAA271347C4', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 5, NULL, NULL, 2, NULL, 0, 'Pour some of the mixture into each ring of the donut pan, only filling to just a little less than ¾ full. Bake for 7 or 8 minutes until a skewer comes out mostly clean. Remember that chocolate cake benefits from being slightly under cooked. You will have to do the donuts in three batches unless you have more trays.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9A9264A37904D49BB1A1A455E757AD3', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 6, NULL, NULL, 2, NULL, 0, 'When the donuts have cooled a little slide out of the pan and cool on a rack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DD05E0CF4839E845ACC370D6A373D165', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 7, NULL, NULL, 2, NULL, 0, 'Once the donuts are cool, melt some dark chocolate and spoon some over each donut, then top with sprinkles.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBBB07D2809ADD4094B16E4998418508', X'3E224EDF70F073419D5D0ECC84DF1992', X'3E224EDF70F073419D5D0ECC84DF1992', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2299B1314560E40A9F4AB6DE8004CF5', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 0, NULL, NULL, 2, NULL, 0, 'Rinse the lentils really well under cold running water, then add to a large heavy based pot with all the ingredients bar the fresh spinach, fresh coriander and black pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2D3424FC3A19BE4892B86969DCC239C2', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 1, NULL, NULL, 2, NULL, 0, 'Bring to the boil, reduce to a gently simmer, cover and cook for 15 minutes, stirring occasionally.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F599E5986AEC6A49B1AF32FDBFA343F2', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 2, NULL, NULL, 2, NULL, 0, 'Add the spinach and cook for another 5-10 minutes or until the lentils are soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E0389B0DDD7672499F83FA4AE88C9688', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 3, NULL, NULL, 2, NULL, 0, 'Add the coriander and stir through. You may add a little more stock or water, if you feel it needs it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3A1F4A9B499E846BC8F4F527E043FFE', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 4, NULL, NULL, 2, NULL, 0, 'Season with black pepper and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8622EF5F98CCDF468C939E877921628D', X'2E4731DAD821614DA619B680F35C6EB4', X'2E4731DAD821614DA619B680F35C6EB4', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'149CC62235677D499BB813B765B257DF', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 130c/110c fan/gas mark ½.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA522905CBC35D49AEF7FCB6C0D27E09', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 1, NULL, NULL, 2, NULL, 0, 'Slice two small apples very finely, either with a mandoline or a knife.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'35FCC8452130654EA65E86F639280077', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 2, NULL, NULL, 2, NULL, 0, 'Mix together coconut oil and agave nectar. If the coconut oil is a little hard, heat it a little to soften it.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CFFA8BC1FED4A643B767829F16C9188E', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 3, NULL, NULL, 2, NULL, 0, 'Place the apple slices on a lined baking sheet and brush with the oil mixture, then dust with cinnamon.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EC2C2657EA72ED4F836EF542ED48C690', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 4, NULL, NULL, 2, NULL, 0, 'Bake for approximately 2 hours. Check after 1½ hours. The time these will take to bake depends on how thinly you cut the apples.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'65BEFACF09C4A34E8E666A8AC607E71E', X'E7150783BEC68B4185727FC4D144F0FF', X'E7150783BEC68B4185727FC4D144F0FF', 5, NULL, NULL, 2, NULL, 0, 'Leave to cool and crisp up on the tray. Once cool, these will keep in an airtight tub for a few days.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98A585FD5CDB1342947253D0613B6562', X'8CBB2DDCA0375A4388E19E4F250B6EDB', X'8CBB2DDCA0375A4388E19E4F250B6EDB', 0, NULL, NULL, 2, NULL, 0, 'Once you''ve rinsed the beans, reserve a quarter (roughly) to add later and pop the rest in a large bowl.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'74A03C298D43D042832838383185A88E', X'8CBB2DDCA0375A4388E19E4F250B6EDB', X'8CBB2DDCA0375A4388E19E4F250B6EDB', 1, NULL, NULL, 2, NULL, 0, 'Mash the beans into a paste, then add the spices, herbs oats and whole beans and mix until well combined.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'75EE734AC390B94F90202D0C23BF8925', X'8CBB2DDCA0375A4388E19E4F250B6EDB', X'8CBB2DDCA0375A4388E19E4F250B6EDB', 2, NULL, NULL, 2, NULL, 0, 'Shape the burger mixture into 6-8 burgers, depending on how big you like them.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'870E27BAE473B642A5846B63EAC5F094', X'8CBB2DDCA0375A4388E19E4F250B6EDB', X'8CBB2DDCA0375A4388E19E4F250B6EDB', 3, NULL, NULL, 2, NULL, 0, 'These are now ready to cook. I like to fry them in a spray of rapeseed oil but they should bake fine too. If you don''t need them right away either chill them for later or freeze them on a tray, then pop them in a freezer bag once they are frozen.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CDE8ADB82DE1A4BA6BF8D7B2593C77F', X'8CBB2DDCA0375A4388E19E4F250B6EDB', X'8CBB2DDCA0375A4388E19E4F250B6EDB', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'591D77122232B044A0B58BF1665F0F92', X'CC1F82E1334CA547A161068E1C59A70E', X'CC1F82E1334CA547A161068E1C59A70E', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a heavy bottomed pan and gently saute the onion, garlic and parsley until the onion is soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6CBE821EE92C914DB6222E15CA7D75C4', X'CC1F82E1334CA547A161068E1C59A70E', X'CC1F82E1334CA547A161068E1C59A70E', 1, NULL, NULL, 2, NULL, 0, 'Add the rice and stir for a couple of minutes before adding the stock. Cook the rice until all the stock has been absorbed and the rice is firm to the bite but not raw in the middle of the grain.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B3969909E8033E4084F7034F3A33E9A2', X'CC1F82E1334CA547A161068E1C59A70E', X'CC1F82E1334CA547A161068E1C59A70E', 2, NULL, NULL, 2, NULL, 0, 'Add the cheddar, combine and season, then pour into a shallow cling filmed tray approximately 2 cm in depth. Cover with cling film and allow to fully cool in the fridge.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C732CF122D244F4AA61ADC734CBD4F3A', X'CC1F82E1334CA547A161068E1C59A70E', X'CC1F82E1334CA547A161068E1C59A70E', 3, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 180c/160c fan/350f/gas mark 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FCDDE985D5B62A408538CE9789A04CD4', X'CC1F82E1334CA547A161068E1C59A70E', X'CC1F82E1334CA547A161068E1C59A70E', 4, NULL, NULL, 2, NULL, 0, 'Pour the breadcrumbs, flour and beaten egg into three separate bowls. Divide the risotto mixture into 6 - 8 cakes and coat in the flour, egg and then breadcrumbs, ensuring each time that they are completely covered before the next tray.');
