INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'256A2E3704C5AB4CBF0BDC230560244D', X'A4A84CBEDB9F0941B9B1AB32535430B5', X'A4A84CBEDB9F0941B9B1AB32535430B5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 160c/140c fan/gas mark 3 and line a brownie pan or baking tray with greaseproof paper. I like to use foil backed parchment as it moulds to the pan nicely and is less fiddly.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B59D4FAEE6DD834EA879A27A1B279472', X'A4A84CBEDB9F0941B9B1AB32535430B5', X'A4A84CBEDB9F0941B9B1AB32535430B5', 1, NULL, NULL, 2, NULL, 0, 'In a small pan melt together the margarine. Let it cool a little then stir in the mashed banana and maple syrup');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4EE19F427A312840A48957A1C34B6E32', X'A4A84CBEDB9F0941B9B1AB32535430B5', X'A4A84CBEDB9F0941B9B1AB32535430B5', 2, NULL, NULL, 2, NULL, 0, 'Measure the oats into a large bowl, mix in the pineapple and chocolate.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD96AF7AD1B6A44E9A8D5283DDFB7997', X'A4A84CBEDB9F0941B9B1AB32535430B5', X'A4A84CBEDB9F0941B9B1AB32535430B5', 3, NULL, NULL, 2, NULL, 0, 'Pour the wet ingredients into the oats and stir until well combined. If the mixture seems a little wet, you may add more oats.');
