INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0835A1E9958DA4CA9631EE316ED405B', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until a skewer inserted into one of the cakes comes out clean.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'757F0A84ACAB454F86D91137B2F8A4FF', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 6, NULL, NULL, 2, NULL, 0, 'Leave to cook on a rack.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'15555D9775707F439BD04978EFA8F64D', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 7, NULL, NULL, 2, NULL, 0, 'Once they are totally cool, scoop out some of each cake and fill each one with one tsp of clotted cream and jam, then add a lid back on.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4657343497536446A00B31E29ED1C189', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 8, NULL, NULL, 2, NULL, 0, 'Whip the cream with vanilla and pipe onto each cupcake.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E673042E54F41F46ACF8439FB96FAE6F', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 9, NULL, NULL, 2, NULL, 0, 'Top each cupcake with a raspberry.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7D302B12813804B94334F9FE5CD8CE8', X'3E715DF0EFBD54409A48D2E4B26C9BB5', X'3E715DF0EFBD54409A48D2E4B26C9BB5', 10, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B94B72F1B73574BB6E63B6AA40F9FF2', X'8C8195CA4D358347B8198703C97BF695', X'8C8195CA4D358347B8198703C97BF695', 0, NULL, NULL, 2, NULL, 0, 'Whizz up all the ingredients, but only 1 tsp of oil and 1 tbsp flour. Reserve the rest of the flour and oil. I blended my ingredients in my power blender, but you could use a food processor.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'92A92EACAB893A41AA7D11DFD46F2CEF', X'8C8195CA4D358347B8198703C97BF695', X'8C8195CA4D358347B8198703C97BF695', 1, NULL, NULL, 2, NULL, 0, 'Fold the other 2 tablespoons of flour into the mix and then pop in the fridge for half an hour to firm up.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E8B61B9E499FA47833ADF40D72FD349', X'8C8195CA4D358347B8198703C97BF695', X'8C8195CA4D358347B8198703C97BF695', 2, NULL, NULL, 2, NULL, 0, 'Heat the 4 tablespoons of oil in a frying pan or wok.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7348EC17E7E418418DDC73F328EAEA5C', X'8C8195CA4D358347B8198703C97BF695', X'8C8195CA4D358347B8198703C97BF695', 3, NULL, NULL, 2, NULL, 0, 'Roll the falafel mix into balls with floured hands and lightly fry in the olive oil until golden. You can bake these, but I wanted them ready quickly and to be super crisp.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'92C4B95EE8368F4CB9DEF472BB283B7C', X'8C8195CA4D358347B8198703C97BF695', X'8C8195CA4D358347B8198703C97BF695', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D5087E392BCB8E45AC8F9A9839FA76F8', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 0, NULL, NULL, 2, NULL, 0, 'If you are using homemade caramel, make it first.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A4FDD2AC3F998449830BCD1E90AEEE64', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 1, NULL, NULL, 2, NULL, 0, 'Pour the sugar into a clean and dry, deep heavy-based pan and cook over a medium to low heat for about 10 minutes, until it begins to melt. Do not stir! You will ruin the caramel.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3F0F8D0CD4FE5945851583B6CEA14D4F', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 2, NULL, NULL, 2, NULL, 0, 'Warm the milk in the microwave or in a pan. Mix it with the melted dairy-free spread in a jug or bowl. Whisk in the cream of tartar, if using.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D377391C5CA474690729951B68635F4', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 3, NULL, NULL, 2, NULL, 0, 'When about a third of the sugar is melted, remove from the heat and stir gently with a clean, dry wooden spoon to incorporate the unmelted sugar. Continue to stir for about a minute until all the sugar has melted and it is smooth with no grains.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3FAEF1A6F2F367479AE88C8CA050BA90', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 4, NULL, NULL, 2, NULL, 0, 'Carefully pour in 1–2 tablespoons of the milk mixture, but beware as the caramel will bubble and splutter. Stir to combine, then carefully add the rest of liquid and salt, to taste (add more for a salted caramel sauce).');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FE5A8D79558EED4EA4FF03B93821E758', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 5, NULL, NULL, 2, NULL, 0, 'If it has split or is lumpy, return to a low heat and melt again, but only very briefly or it may become more like bitter toffee than caramel.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'96A28F6F6B3B95449DB193CE6FCA775F', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly. Set aside until the tart is ready.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2579F2D67BE87C409EC096B29E8C8C68', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 7, NULL, NULL, 2, NULL, 0, 'If not using immediately pour into a clean, heatproof jar with a lid while still hot; it will thicken considerably as it cools. Store in the fridge for up to 2 weeks. Bring up to room temperature to soften.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D35736C8E6C66408921EBA4000BACDB', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 8, NULL, NULL, 2, NULL, 0, 'Heat the oven to 200ºc/180ºc/400ºf/gas mark 6.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D50BA2D49EE8524880C11BDD8135E950', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 9, NULL, NULL, 2, NULL, 0, 'Roll out the pastry to about 2 mm thick and so it fits a 25 cm loose bottomed tart tin. Line the tin with the pastry, trimming the overhanging edges to form a neat edge (save the trimmings for another recipe). Chill for 15 minutes in the fridge or freezer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B22B96C94BB9AD4881B87DE602B688C5', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 10, NULL, NULL, 2, NULL, 0, 'Cover the pastry with greaseproof paper, fitting into the edges, then top with with ceramic baking beans, dried beans or rice and blind bake for 15 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DAA5FF0A02476541A6E3C139C29ACEA2', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 11, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the 1 tablespoon milk, the oil and golden syrup or agave nectar.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4577606D5349704C94A1FEE4C297FD12', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 12, NULL, NULL, 2, NULL, 0, 'Remove the pastry case from the oven and carefully lift out the paper and beans.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D05C5EC33345244896596A7272D39BB9', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 13, NULL, NULL, 2, NULL, 0, 'Reduce the oven to 170ºc/150ºC fan/340°F/gas mark 3½.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40DFCA63228A1A46A285425DC688CA19', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 14, NULL, NULL, 2, NULL, 0, 'Prick the base of the tart with a fork, then brush with the milk wash and bake for a further 15–20 minutes, spinning the tart around halfway through for even baking, until the case is golden brown and quite crisp. Set aside while you make the filling.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F7FE603165AD446B02D98B910AE8B5E', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 15, NULL, NULL, 2, NULL, 0, 'Melt the chocolate, milk, vanilla and sugar in a heatproof bowl set over a pan of just simmering water (making sure the bottom of the bowl doesn''t touch the water or the chocolate might seize and become grainy) until the chocolate is melted. Remove from the heat and allow to cool slightly.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'029FAE66448A574785EC0CE612B488A2', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 16, NULL, NULL, 2, NULL, 0, 'Spread the caramel sauce (homemade recipe below), smoothly over the bottom of the cooled pastry case, then spoon over the warm chocolate ganache carefully, trying not to disturb the caramel layer. Use a spatula to gently spread the filling out. Chill for 2 hours until set. (Don’t worry if it looks like it has separated slightly.)');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F60D1D4BAE26F1468CE2013984D3FF79', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 17, NULL, NULL, 2, NULL, 0, 'When ready to serve, transfer from the tin to a serving plate, dust with the cocoa and sprinkle the almonds on top.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0527824DB0EDD54D9897B7E9F81BEFE4', X'E76E53B831DF5949A26DDBF7268F7244', X'E76E53B831DF5949A26DDBF7268F7244', 18, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'315718339AB65843B49262EFE713C84A', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, saute the onion and garlic in the olive oil until soft.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2409B1AEED978C4CAF7DEA4E1688DB8E', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 1, NULL, NULL, 2, NULL, 0, 'Add the carrots and butternut squash and cook gently for a few minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'508D6A51F6FF8944BD86659977B0D1B6', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 2, NULL, NULL, 2, NULL, 0, 'Add the beans, tomatoes, wine, stock, tomato puree and herbs and cook for 20 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B040966E657BB4D8F083FA55BD2BD3F', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 3, NULL, NULL, 2, NULL, 0, 'Add the spinach and pasta and cook for another 8-10 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D0EBE0C3853F64FA2E319201B0EE50D', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 4, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper and stir in pesto.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'43A028074AAB1949888AA861700E2B99', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 5, NULL, NULL, 2, NULL, 0, 'Serve in bowls topped with some pesto and grated cheese.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'281A386390C21646838ED534FE37B35A', X'B45C900DC923BF43885B24BB069EC8D4', X'B45C900DC923BF43885B24BB069EC8D4', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'605BAE3EBAB68A40AB2E62FB3767102C', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 0, NULL, NULL, 2, NULL, 0, 'Line a square brownie pan with non-stick baking paper or foil backed greaseproof paper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'078DC5828DCFAB408D12BECCD40DECDD', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 1, NULL, NULL, 2, NULL, 0, 'Whizz up biscuits and cornflakes in a food processor or pop them in a large freezer bag and crush them with a rolling pin. Leave a bit of texture, you don''t want it to be too fine.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'695DDFE62881C14BBAEE57D2DCB696E1', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 2, NULL, NULL, 2, NULL, 0, 'Melt the dark chocolate in a bain marie (a bowl over simmering water, making sure the bottom of the bowl doesn''t touch the water, as the chocolate will seize) with the peanut butter.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A16A09A3E968944D9B93B93F22DFC1F4', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl mix together the biscuit mixture and melted chocolate together until well mixed, then press into the lined brownie pan. Chill in the fridge.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ED7078508CF53249931CA1154FC94169', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 4, NULL, NULL, 2, NULL, 0, 'Melt the second batch of chocolate (as above) and spread over the tiffin base and chill.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F1DBAF3773464F488B2C8AA09DC76722', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 5, NULL, NULL, 2, NULL, 0, 'Once set cut into bars or squares and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D8C1AEE5E72C2742A31DEB3C59B324C8', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 6, NULL, NULL, 2, NULL, 0, 'Will keep in the fridge in an airtight container for a week.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3EECAF61EF998F4ABE2AF0C462A12E38', X'EADB162920BA95428C4280758D2B99C9', X'EADB162920BA95428C4280758D2B99C9', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0077699174DA1C47BEE1F7B34172CD6A', X'AC4C4CE207D21F439525309D738B0CA0', X'AC4C4CE207D21F439525309D738B0CA0', 0, NULL, NULL, 2, NULL, 0, 'Add all the vegetables to a large bowl.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F5F525A98075A45B55253C711EF528C', X'AC4C4CE207D21F439525309D738B0CA0', X'AC4C4CE207D21F439525309D738B0CA0', 1, NULL, NULL, 2, NULL, 0, 'Add the mayo, fresh dill and season with salt and pepper, then mix well. Taste to check the seasoning.');
