INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F6609CFCE513234BB38972D4D0FB487D', X'53CAD9B4AF859C40BFB7D3074F3AF75C', X'53CAD9B4AF859C40BFB7D3074F3AF75C', 3, NULL, NULL, 2, NULL, 0, 'Top with fresh basil leaves, then top with the rest of the mozzarella.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E1E0ADC087CB74D8E5E280702CF3BBC', X'53CAD9B4AF859C40BFB7D3074F3AF75C', X'53CAD9B4AF859C40BFB7D3074F3AF75C', 4, NULL, NULL, 2, NULL, 0, 'Finish with a grinding of black pepper, top with the other tortilla and press down. Grill until the underneath is starting to crisp and turn golden, then turn over and finish the other side. This will only take 2 or 3 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4DE88FCBF89B064EADB5599B1F278763', X'53CAD9B4AF859C40BFB7D3074F3AF75C', X'53CAD9B4AF859C40BFB7D3074F3AF75C', 5, NULL, NULL, 2, NULL, 0, 'Remove from the pan, slice into wedges and serve.');
