INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3DE3E187158547478E4757A414D143D7', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 1, NULL, NULL, 2, NULL, 0, 'Add the spices and stir well, then add the carrots and cook gently for a few minutes,');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1CEA47E7F0B1C541BC6B13FCB3516C82', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 2, NULL, NULL, 2, NULL, 0, 'Add the rest of the vegetables and cook gently for a few minutes before adding the stock.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E702DAA2E6348341B5DCEB1165991D82', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 3, NULL, NULL, 2, NULL, 0, 'Cook at a gentle simmer for 30 minutes until the vegetables are soft and the flavours have combined and developed.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'11B193ECAE34EE45BEED06530565810A', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 4, NULL, NULL, 2, NULL, 0, 'Mash some of the vegetables with a potato masher or an electric hand blender. Leave lets of vegetables for texture. I mash some, just to thicken the soup.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E13448CF4B770944B496FF219808A374', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 5, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper and serve.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39DFF886EB1FA141AC0D3D27A5AAFF53', X'55C5E356E186524F99D29DEC9E33DDC5', X'55C5E356E186524F99D29DEC9E33DDC5', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA7120051EA7A3429EB1873DC91BBA66', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl mix together the passata, tomato puree, garlic, oregano, chilli powder, salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D27FBDBDB7B354E8251A5F621A9487D', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 1, NULL, NULL, 2, NULL, 0, 'Spray your slow cooker pan with no-stick spray or rub with a little oil to prevent sticking.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6E825AFDB0950B47907B85247BA8CF2E', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 2, NULL, NULL, 2, NULL, 0, 'Place the chopped potatoes in the bottom of your slow cooker and top with an even layer of the tomato mixture.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D67E05B6A7324A40823EB7FF0C5C479D', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 3, NULL, NULL, 2, NULL, 0, 'Top with an even layer of mozzarella, then top with slices of tomato and fresh basil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7975BEAFE036A4BA7ED233784FDE228', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 4, NULL, NULL, 2, NULL, 0, 'Set your slow cooker to high for 4 hours. If your potatoes are not tender, give them longer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'66C82E8CC27BF84C8A8E4C4DE4C7A9D3', X'6AAD04CEC2C5324B96E30271662F3C5A', X'6AAD04CEC2C5324B96E30271662F3C5A', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C5BEC8114748344AB9C5541506CDE2D', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 0, NULL, NULL, 2, NULL, 0, 'Heat the coconut oil and 1 tablespoon of the olive oil in a large heavy-based frying pan over a medium heat. When hot, add the aubergines and cook, stirring, for about 5 minutes, then add the onion and a pinch of salt and sauté for 15 minutes, stirring regularly, until the aubergines are soft, tender and browning and the onion is translucent and also beginning to brown. If the pan seems dry at any point, add another splash of olive oil.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7BE49098DBE16B449E0226853476E48D', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 1, NULL, NULL, 2, NULL, 0, 'Add the garlic, olives, red pepper paste, tomato purée, paprika, fennel seeds, sunflower seeds and pine nuts and cook, stirring, for 3 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B8BC57F012BFC459D348276A1E19CC5', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small bowl, mix together the ground flaxseeds and water in a bowl and allow to soak for 5 minutes.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'12732FA565AC134793B5F6EE6570AA16', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 3, NULL, NULL, 2, NULL, 0, 'Tip the aubergine mixture into a food processor with the rosemary, parsley, lemon zest and 50g of the cooked Puy lentils. Blitz briefly, to form a rough purée, but don’t go too far – the balls need texture, and too much whizzing will make them mushy.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'49C9C49C9BF8CC4B87E6A5823D84A19A', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 4, NULL, NULL, 2, NULL, 0, 'Transfer the purée to a bowl and add the rest of the lentils, 5 tablespoons of the breadcrumbs, the soaked flaxseeds, the gram flour, baking powder, a generous pinch of salt and some black pepper. Mix well with a spoon, then place in the fridge to firm up for an hour.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D473B03DC18EC54DBF326F9194F46CD3', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 5, NULL, NULL, 2, NULL, 0, 'When ready to cook, heat a splash of oil in a clean nonstick frying pan over a medium heat. First, cook a nugget of the mixture to check its texture and seasoning. If it seems too wet, add more of the breadcrumbs, and add more salt and pepper, if necessary.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DE9C77D8A2E9CC4FAC92A3DB70160AF7', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 6, NULL, NULL, 2, NULL, 0, 'Using a tablespoon, scoop out pieces of mixture and form into 16 firm, golf-ball-sized ‘meatballs’ using 2 spoons or floured hands. Add the remaining 2 tablespoons oil to the pan and fry the balls gently, in batches, turning often, until they are deep brown and quite crisp on the outside. Remove with a slotted spoon and keep warm while you cook the rest.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD568A67B0147C43B0AFEE7181049700', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the pasta in boiling salted water until al dente. To make the sauce, heat the oil in a pan over a low heat. Add the bruised garlic and allow the flavour to infuse into the oil (don’t let it brown) for about 10 minutes. Remove the garlic and add the passata. Bring to a simmer, adding a pinch of salt and pepper.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF69691296120649BFE086889C7C2C3C', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 8, NULL, NULL, 2, NULL, 0, 'Drain the pasta. Add the basil to the tomato sauce, then add the pasta and toss well to coat it in the sauce.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F273C83DA05B345AE6DE9EE1A16BA24', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 9, NULL, NULL, 2, NULL, 0, 'Serve the pasta topped with the warm aubergine balls.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DEBD57D3A68D56438AC572B04BE4757B', X'3E47A0C8559F214FA8B1836F80E7A976', X'3E47A0C8559F214FA8B1836F80E7A976', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'65964F90BA28C7409D3927D3DD849825', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 180°c/Fan 160°c/350°f/Gas 4.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C8FEE60E4DD459428759B2576E24FD6F', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 1, NULL, NULL, 2, NULL, 0, 'Rinse the kale in a colander.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'00BD5B8D51DA7247893E7D89D057E1E7', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 2, NULL, NULL, 2, NULL, 0, 'Dry with a clean tea towel and kitchen paper until the kale is completely dry. This is the most important step for crispy kale.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'468EE7B0F717A947AF8847F9D67F460F', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 3, NULL, NULL, 2, NULL, 0, 'Cut the woody stem of the kale with a small knife or scissors.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B891917D5C365409162DC7FFE17D66E', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 4, NULL, NULL, 2, NULL, 0, 'Lay the kale on a baking sheet in a single layer.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8FC5ACE3D52D414391054FC842E2B118', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 5, NULL, NULL, 2, NULL, 0, 'Whisk the oil and vinegar and pour over the kale, then massage into the kale.');
INSERT INTO ` VWR INTERNAL Tinnedtomatoes-2 recipeInstructions`  (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'29D795F1D56CC84AA79A0FA2EAB11F1C', X'52DC8CECA54CBD4A8A08B734DB55623C', X'52DC8CECA54CBD4A8A08B734DB55623C', 6, NULL, NULL, 2, NULL, 0, 'Bake for 12-15 minutes, checking on it after 10 minutes. You want it crispy but not charred as it will just be bitter and nasty.');
