INSERT INTO `VWR INTERNAL Vegetarianventures` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'044A759DE559834FB2F84B1A722AFE50', X'2594459E7A01F748A9593240C3C897A8', NULL, 0, 'https://www.vegetarianventures.com/category/all-2/', NULL, 1, NULL, 0);
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D259AAE3E62E940AEF756C008D9DECE', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6EDCD4DBCC22EA4ABFC396D68F26211E', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the paddle attachment, cream together the butter and 2/3 cup sugar. With the stand mixer running on low, add in the vanilla extract and salt. Next, add in the flour slowly and mix until the mixture comes together to one big dough ball.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D721514C15B79A4F9644364234ABCA92', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 2, NULL, NULL, 2, NULL, 0, 'Place coarse sugar in a shallow dish and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8FFC4C7E3C1B9B4B963E050657A6B58E', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 3, NULL, NULL, 2, NULL, 0, 'Scoop heaping tablespoons of dough and use clean hands to shape 1-inch balls. Roll the ball in the coarse sugar until completely coated and transfer the dough balls to the prepared baking sheet, 2 inches apart.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F08E60CA7E0F9E4B86AE71B29E62F3F7', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 4, NULL, NULL, 2, NULL, 0, 'Use a wine cork or your clean thumb to press a small indentation into each dough ball. Transfer to the freeze and let chill for at least 10 minutes (this will make sure the cookies don''t spread too much while baking).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'850E4F322F221C4DB7B50304E7613683', X'85BA58059AF4C346B99F97A25F12D660', X'85BA58059AF4C346B99F97A25F12D660', 5, NULL, NULL, 2, NULL, 0, 'Bake for 14 - 18 minutes or until browned on the bottom. Remove from oven and use the cork to press down on the indent again if it puffed up while baking. Transfer to a wire rack and let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5A4891DAE387004295F97BE2ECA36852', X'FCFDA6C7A4604643900F2E4AEB8B24DD', X'FCFDA6C7A4604643900F2E4AEB8B24DD', 0, NULL, NULL, 2, NULL, 0, 'While the cookies are baking, combine the brown sugar, cardamom, and unsalted butter in a small saucepan. Cook over medium heat, stirring constantly, until the mixture begins to boil. Once boiling, continue to cook for one minute, stirring constantly. Remove from heat and stir in the cream and orange extract. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D967540BA5EE61418BBD0E0C992FB898', X'FCFDA6C7A4604643900F2E4AEB8B24DD', X'FCFDA6C7A4604643900F2E4AEB8B24DD', 1, NULL, NULL, 2, NULL, 0, 'Pour the caramel sauce into the indents of the cookie and sprinkle with a tiny bit of coarse sea salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2106647FE27B8B4B8B60D6AC21D500B4', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, dissolve yeast in warm water and set aside for at least 10 minutes. Meanwhile, put milk in a small saucepan and heat over medium heat for 2-3 minutes or just until it starts to bubble around the edges. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'386BD98CD28ADE428E8BA97DCAEB233A', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl fitted with a dough attachment on a stand mixer, whisk together milk, sugar, 1/3 cup melted butter, salt, and egg. Add 2 cups of flour and mix until smooth. Add yeast mixture and mix, slowly adding in the remaining dough, until it forms a dough ball. Turn mixer speed up to medium-high and mix for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A3C8B135D595F4698E1AD19977BA108', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 2, NULL, NULL, 2, NULL, 0, 'Transfer to a well-greased bowl, cover, and let rise until doubled in size (about an hour).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'743E1790B1860B41B2C622A253EDA35B', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour dried fruit and rum into a small bowl and let soak while the dough rises. Once the dough has risen, strain out the rum and reserve it for another use. In another small bowl, mix together brown sugar, cinnamon, cardamom, nutmeg, and orange zest. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'493C2A31297B5B41ACD699766B2F04C8', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 4, NULL, NULL, 2, NULL, 0, 'Once doubled in size, punch down the dough and transfer to a floured work surface. Roll out into a 15 by 9 inch rectangle. Spread all but 1 Tbsp of the 1/4 cup melted butter all over the dough. Sprinkle brown sugar mixture, rum soaked fruit, candied fruit, and almonds evenly over the dough.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B0B339C40DB5674FA24A421E29BAFD87', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 5, NULL, NULL, 2, NULL, 0, 'Beginning at the long side, roll up the dough and pinch edge together to seal. Cut into 12 even slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'72348949D02A3A449106B3FED9AB5846', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 6, NULL, NULL, 2, NULL, 0, 'Grease a rectangular baking dish with remaining melted butter and place roll in prepared pan (4 rows of 3 each). Cover and let rise for an additional 45 minutes or until doubled in size. Meanwhile, preheat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C21132A23E618143AADAD50072A49E12', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 7, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until browned on top. Remove from oven and let cool slightly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9CD3F09A676298488BEB59A7BFC13AD4', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 8, NULL, NULL, 2, NULL, 0, 'While the rolls are cooling, whisk together the frosting ingredients until smoothed. The frosting should be spreadable and not runny. If it ends up runny then add more powdered sugar.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'20A5260A4020384BBBA206464B5FF36B', X'218086275ABCC44793A30F5BA8A8C1BC', X'218086275ABCC44793A30F5BA8A8C1BC', 9, NULL, NULL, 2, NULL, 0, 'When ready to serve, lather the rolls with frosting and sprinkle with candied fruit and orange zest.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDC43DE60070124D9EF6D571B97BC641', X'6ABBED010D26824B8E3103021EC022BD', X'6ABBED010D26824B8E3103021EC022BD', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the cocoa powder, powdered sugar, and salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'982DA820DDC8684287D4501128E03B54', X'6ABBED010D26824B8E3103021EC022BD', X'6ABBED010D26824B8E3103021EC022BD', 1, NULL, NULL, 2, NULL, 0, 'Fold in the chocolate chunk pieces.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'435332477967004E93DDBF39C6AE3287', X'6ABBED010D26824B8E3103021EC022BD', X'6ABBED010D26824B8E3103021EC022BD', 2, NULL, NULL, 2, NULL, 0, 'Break up the rum sugar with your clean hands or a large wooden spoon. Sprinkle with the rum sugar over the cookies and set aside to cool. Cookies will set as they cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22BFE6765E46EA48BA2C2245B90C0F0B', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and line two baking sheets with parchment paper. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D52CB49172AC4544BC4CC1BF87BB10B2', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 1, NULL, NULL, 2, NULL, 0, 'Add the caraway seeds to a small dry skillet and toast over medium heat for 2-3 minutes or until starting to brown and they become fragrant. Remove from heat, transfer to a cutting board, and roughly chop the seeds. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3624FA5FC2D8EC4C9EC9FBD246DD61E0', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 2, NULL, NULL, 2, NULL, 0, 'Make the topping by combining 1/3 cup granulated sugar and 1 tsp ground cinnamon in a shallow bowl. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'035D27AD478CE84FAD83707577A88C8C', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the all-purpose flour, rye flour, half of the caraway seeds, cream of tartar, 1 tsp cinnamon, baking soda, and salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E70477617914874C9482F9DF11276EDE', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 4, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with a paddle attachment, beat the butter and 1 cup granulated sugar on high speed until light and creamy, about 3 minutes. Add the egg and vanilla extract and beat until combined, another 1-2 minutes. Turn the mixer speed down to low and slowly add in the flour mixture just until everything is combined, scraping down the sides when needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'701C1B6C501D5F4D9CA5199E9ACC16E7', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 5, NULL, NULL, 2, NULL, 0, 'Roll the cookie dough into roughly 1 1/2 Tbsp balls (I recommend slightly overfilling a Tbsp measuring spoon with the batter and using floured hands to roll the dough between your palms) and then roll the dough balls in the cinnamon mixture before transferring to a prepared baking sheet. Arrange the cookies to be at least 3 inches apart on the baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'14199620E3FA354D8D1547AB63F74DC9', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 6, NULL, NULL, 2, NULL, 0, 'Bake for 10 minutes or until the bottoms are starting to darken. When they are still warm, use the back of a spoon to slightly press down the center to flatten a bit. Sprinkle with the remaining caraway seed pieces. Let cookies cool on baking sheet for 10 minutes and then transfer to a wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'532533911C1544429BECD897F3673336', X'BCB21CAE9CD2D844AEF33A870E0D4658', X'BCB21CAE9CD2D844AEF33A870E0D4658', 7, NULL, NULL, 2, NULL, 0, 'Enjoy right away or store in an airtight container at room temperature for a few days.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'34A8BF134D45D648A1A3904A36E83072', X'9BC4858B150FE54C832BB510F563E3D4', X'9BC4858B150FE54C832BB510F563E3D4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. Place 1 Tbsp sugar in a small shallow bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22BA950B378B4B47BF21228F125B3C81', X'9BC4858B150FE54C832BB510F563E3D4', X'9BC4858B150FE54C832BB510F563E3D4', 1, NULL, NULL, 2, NULL, 0, 'In a stand mixer bowl fitted with the paddle attachment, beat together the butter and sugar on medium speed until fluffy and pale in color, about 3 minutes. Beat in egg, almond extract (if using), and orange zest until incorporated, scraping down the sides of the bowl as needed. Beat in sour cream until incorporated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7717993650A5046924C95077CBCB29D', X'9BC4858B150FE54C832BB510F563E3D4', X'9BC4858B150FE54C832BB510F563E3D4', 2, NULL, NULL, 2, NULL, 0, 'In a separate mixing bowl, whisk together all-purpose flour, almond flour, cardamom, baking powder, salt, and baking soda. Slowly pour into the mixer bowl while the mixer is running on low speed and mix until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA6F494F2F9D564BB66B8E60252F2641', X'9BC4858B150FE54C832BB510F563E3D4', X'9BC4858B150FE54C832BB510F563E3D4', 3, NULL, NULL, 2, NULL, 0, 'Drop tablespoons of dough onto prepared baking sheet, keeping 2 inches in between each cookie. Wet the bottom of a glass with water and dip it into the sugar in the shallow bowl. Press into each cookie to flatten, re-wetting and dipping in sugar after each cookie to keep it from sticking.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F8844136415F33488C3889D780967582', X'9BC4858B150FE54C832BB510F563E3D4', X'9BC4858B150FE54C832BB510F563E3D4', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10 to 12 minutes or until starting to brown around edges. Remove from oven and let cool for a few minutes before transferring onto a cooling rack. Let cool completely before adding frosting.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA41DF1B9E35E14E833BB1DBE84BA70E', X'EFFAED9511870541B86BFB4CAF96650B', X'EFFAED9511870541B86BFB4CAF96650B', 0, NULL, NULL, 2, NULL, 0, 'In a stand mixer fitted with the whisk attachment, cream butter until smooth and fluffy, about 3 minutes. Gradually beat in powdered sugar and vanilla extract until incorporated. Add milk, 1 Tbsp at a time, and beat for an additional 3 minutes or until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'32D2D39563902540A1C2527E0F692857', X'A9EAF103E652BF4BA737B229EF1DBD89', X'A9EAF103E652BF4BA737B229EF1DBD89', 0, NULL, NULL, 2, NULL, 0, 'Use a peeler to remove off long strips of the orange peel until all of the outer layer is removed. Use a sharp pairing knife to slice peel into 1/4 inch wide strips.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A507729193F6964B8B394D479C256EDF', X'A9EAF103E652BF4BA737B229EF1DBD89', X'A9EAF103E652BF4BA737B229EF1DBD89', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat 1 cup sugar and 1 cup water to a boil over high heat. Add orange peel strips and let boil until translucent, about 10 minutes. Remove peels with a slotted spoon and transfer to a wire rack, separating each piece so they don’t stick together as they dry.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E0659E7B191774F9E7B4661E16F87D5', X'A9EAF103E652BF4BA737B229EF1DBD89', X'A9EAF103E652BF4BA737B229EF1DBD89', 2, NULL, NULL, 2, NULL, 0, 'When ready to assemble. frost each cookie using a spatula and add 3-5 strips of orange peel (depending on how many your orange yielded).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C1E2133A29E65D4BB33160C3EA6B9B18', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the all-purpose flour, salt, and 1/2 tsp sugar. Use cleans hands to press the butter into the dough until they are the size of pebbles. Add the ice cold water and use your clean hands to knead the water into the dough until a cohesive ball has formed. Flatten into a disc and wrap tightly in plastic wrap. Chill in the fridge for at least 2 hours or overnight.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'754B3107C043B743A5E052601D170F6E', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'57C9371D20157F499D4E99790DCFDD54', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 2, NULL, NULL, 2, NULL, 0, 'Remove the dough from the plastic wrap and transfer to a flour dusted surface. Use a rolling pin to roll out into roughly a 12 inch circle. Use cookie cutters to cut out pie dough cookies into your favorite shapes. You should be able to press the scraps together and re-roll them to cut out more shapes. You''ll end up with between 15-20 depending on how big your cutter is. Transfer them to a single layer on your baking sheet and stick in the freezer to chill for 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9955886FE52958478411B14A4720C56F', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the cinnamon and 1 Tbsp sugar. In another small bowl, beat an egg with 1 tsp of water to create an egg wash.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E55335687ED3DD42887EF2C969D6C940', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 4, NULL, NULL, 2, NULL, 0, 'Brush the egg wash over the chilled cookies and then sprinkle the cinnamon-sugar mixture evenly over the pie cookies. Bake for 15 to 20 minutes (time will vary depending on the size of your cookies so check often). Add pecan halves to another baking sheet and place in the oven for the last 5 minutes of the baking time on the cookies so they can toast. Transfer cookies to a wire rack and let cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DEF726F4591BA9488B593649FC2F950D', X'37BC45F407F6464BA05090ACE3951D19', X'37BC45F407F6464BA05090ACE3951D19', 5, NULL, NULL, 2, NULL, 0, 'When ready to serve your sundaes, place cookies on a plate and pecans, caramel, whipped cream in their own serving bowls. Set these bowls and plates up near your container of Hudsonville Ice Cream along with serving bowls and utensils and let your guests assemble themselves. Enjoy right away before the ice cream melts!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'38542371C81EC943A0F1621369079A09', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line 2 baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6154542F894BCC4C9F56364856499D3F', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a mixer fitted with a paddle attachment, cream together the butter and sugar until light and fluffy. Add the egg, sour cream, and vanilla and mix for another 3 minutes on medium or until fully incorporated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'68250D142336BD4A9677C09069CC77E3', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a medium mixing bowl, whisk together the flour, baking soda, cinnamon, nutmeg, and salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA8A7636A1EA9346B90DB2D3A2216729', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 3, NULL, NULL, 2, NULL, 0, 'Turn the mixer down to low and slowly pour in the dry ingredients until completely combined and a stiff dough has formed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9EDD82832DE7314DB2D23BC4FCD88CD9', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 4, NULL, NULL, 2, NULL, 0, 'Transfer the dough to a floured surface and roll the dough out to 1/2 inch thick. Use cookie cutters to cut out desired shapes and transfer to prepared baking sheets.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'737381D3C7BC074E86AB2E567E05C9B1', X'ABD11D555318E341A9897446DB4BDFE3', X'ABD11D555318E341A9897446DB4BDFE3', 5, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes (depending on the size of your cookies) and then transfer to a wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E176037EC8F712479226A3809E0995E6', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', 0, NULL, NULL, 2, NULL, 0, 'While the cookies cool, transfer the cranberries and orange juice to a medium saucepan. Heat over medium the cranberries burst and release their juices. Remove from heat and stir in the zest from 2 oranges.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D238A2ADA020847A94B9F6CA98ECDB8', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', 1, NULL, NULL, 2, NULL, 0, 'Strain through a fine mesh strainer into a medium mixing bowl (use the back of a spoon to scrape the pulp off the bottom of the fine mesh strainer) and whisk in the powdered sugar until completely smooth.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9412593B98CF9E4DA59FF717628F04BF', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', X'3C2D8508BBB3C14D8FC4D9BF53DA2112', 2, NULL, NULL, 2, NULL, 0, 'Dip the cookies into the glaze and then transfer back onto the cooling rack to let the glaze harden.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3B9CE81870A4442925B90A6A2817A29', X'1432868947DB3342BAA4E1946364BB97', X'1432868947DB3342BAA4E1946364BB97', 0, NULL, NULL, 2, NULL, 0, 'Stir the sugar, salt, and water together in a small bowl until the sugar and salt are fully dissolved. Place the bowl in the freezer so the liquid can chill down and be ice cold when making the dough.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6505308DCDD98044972C23B57EA85E4F', X'1432868947DB3342BAA4E1946364BB97', X'1432868947DB3342BAA4E1946364BB97', 1, NULL, NULL, 2, NULL, 0, 'Mix the flours together in a large mixing bowl and add the pieces of butter. Use your clean hands to squeeze the butter into the flour until you have pea size pieces of butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F25CA7558756D346A29763EF2EC0F292', X'1432868947DB3342BAA4E1946364BB97', X'1432868947DB3342BAA4E1946364BB97', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the sugar water mixture of the flour and mix, again with your clean hands, until a shaggy dough has formed and all the flour is moistened by the water. It will be crumbly at first but will come together the more you mix it. Keep mixing until a shaggy ball has been formed. Flatten the mound into a 1 inch thick disk and wrap in plastic wrap. Stick in the fridge to chill for at least 2 hours or overnight.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF3CB11C2E3EFD4296F46868E0B52538', X'1432868947DB3342BAA4E1946364BB97', X'1432868947DB3342BAA4E1946364BB97', 3, NULL, NULL, 2, NULL, 0, 'Once chilled, transfer your disk onto a floured surface and roll the dough out into a 12 inch circle. Transfer to a 9 inch pie pan, letting the extra dough hang off the sides. Roll the extra dough hanging off the edge underneath itself until it lays on the rim of the dish. To make a crimped edge, place a piece of the edge between your thumbs and a index finger to create a crimp look and repeat this process all the way around. Alternatively, you can use a fork to make fringe decoration around the crust. Transfer to the freezer to let chill while the oven preheats.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'489CCCE539914E498A1F1C5AE9EE0CE6', X'1432868947DB3342BAA4E1946364BB97', X'1432868947DB3342BAA4E1946364BB97', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees. Once the oven is ready, remove pie crust from the freezer and top with parchment paper followed by pie weights (I always used dried beans). Blind bake for 20 minutes, carefully remove the parchment paper and pie weights, and bake for another 10 to 15 minutes or until the bottom of the crust looks fully baked (shouldn''t be wet looking at all). Transfer to a wire rack and let cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D09081B51739224CB41B09A2836F0F69', X'34A5957C1A4BAA4D80201630C2B151A4', X'34A5957C1A4BAA4D80201630C2B151A4', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together 1 1/4 cup sugar, cornstarch, and 1/4 tsp salt. Whisk in the egg yolks, whole egg, and 1/4 cup plus 2 Tbsp water. Whisk in the lemon juice then pass the mixture through a fine mesh strainer into a small saucepan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A5836CFF980A084B87A903F466D895F7', X'34A5957C1A4BAA4D80201630C2B151A4', X'34A5957C1A4BAA4D80201630C2B151A4', 1, NULL, NULL, 2, NULL, 0, 'Place the pan over medium high heat and stir constantly with a rubber spatula, scraping the sides to prevent burning, for 5 to 8 minutes or until the mixture starts to thicken. Reduce the heat to the lowest setting, continue to stir constantly, and cook until the consistency is thick but still pourable, about 5 more minutes. Remove from heat and stir in 3 Tbsp cold butter until melted.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69CB8A71681EB84CAF394A6CD067E9CB', X'34A5957C1A4BAA4D80201630C2B151A4', X'34A5957C1A4BAA4D80201630C2B151A4', 2, NULL, NULL, 2, NULL, 0, 'Pour the hot filling into your blind-baked pie crust and use the spatula to ease the filling into an even layer.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4B582558DC65304ABD38D71D2F4CBA92', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 0, NULL, NULL, 2, NULL, 0, 'Using a clean and dry bowl fitted on a stand mixer with the whisk attachment, beat the egg whites and cream of tartar on high until they are white, foamy, and hold soft peaks.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C3F28F6E3029184E89326897AFBA0D75', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small saucepan, stir 1/4 cup water and 1 cup sugar over high heat until the sugar has dissolved and the syrup reaches a strong boil (a candy thermometer should reach 240 degrees). Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A87F09176384FD4BAA4C354BCF289D63', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 2, NULL, NULL, 2, NULL, 0, 'With the stand mixer running, slowly pour the hot sugar mixture in a thin stream into the foamy egg whites. Continue beating until the meringue holds the pattern of the whisk and the texture is voluminous and silky. Add the vanilla, salt, and poppyseeds, and beat just until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'308806142797784C8608F8F2AFEB4A33', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 3, NULL, NULL, 2, NULL, 0, 'Pile the meringue on top of the lemon curd and use the back of a hot, wet spoon to create a wavy texture with peaks and valleys.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA6EC22887604B49B0F65A5FB40DD6E5', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 4, NULL, NULL, 2, NULL, 0, 'Set an oven rack in the middle and place the pie on it so that the pie is 3 to 5 inches from the top. Turn the broiler on high and broil just until the golden brown, about 1 to 3 minute. Watch closely as broilers vary widely and could take less or more time.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE67AC00AB391C4D9C505C14AE03B2DD', X'69664F2FBB7D634A8B8945521F1D13CB', X'69664F2FBB7D634A8B8945521F1D13CB', 5, NULL, NULL, 2, NULL, 0, 'Transfer the pie to the fridge and chill for at least 4 hours. The pie will keep for up to 4 days, covered in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'520D0E8464C2AA4F8864444FC71039F7', X'7B2A98857FAA8C40BF9876E3B0841643', X'7B2A98857FAA8C40BF9876E3B0841643', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees F and line a baking sheet with parchment paper. In a medium bowl, whisk together the flour, baking soda, and fine salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'72E055D3FAEC5540BD91620C4472C7FB', X'7B2A98857FAA8C40BF9876E3B0841643', X'7B2A98857FAA8C40BF9876E3B0841643', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with a paddle attachment, beat the butter and brown sugar on medium-high speed until pale and fluffy. Add tahini, egg, and vanilla and beat until incorporated. Reduce speed to low and add in the dry ingredients beating until combined. Use a spatula to fold in the chocolate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'525610E34C0C3C489736514C61C13AAA', X'7B2A98857FAA8C40BF9876E3B0841643', X'7B2A98857FAA8C40BF9876E3B0841643', 2, NULL, NULL, 2, NULL, 0, 'Use a 1-inch scoop to scoop out dough into balls onto prepared baking sheet, keeping 2 inches between each cookie. Repeat with the rest of the dough and then sprinkle thick sea salt over the cookies.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A2C6F03E16FA4F4DA1C4343E8ECCACD6', X'7B2A98857FAA8C40BF9876E3B0841643', X'7B2A98857FAA8C40BF9876E3B0841643', 3, NULL, NULL, 2, NULL, 0, 'If baking right away, transfer to preheated oven and bake for 12 to 15 minutes or until brown around the edges. Let cool on pan for 10 minutes and then serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3C9ECF1860E1E4BA7FAF59C790E174B', X'7B2A98857FAA8C40BF9876E3B0841643', X'7B2A98857FAA8C40BF9876E3B0841643', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, transfer dough balls into the freezer for one hour and then transfer all the dough into a freezer bag, seal, and store in the freezer for up to 3 months. When wanting one, just bake at 375 for 15 to 18 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F217458EB649EE4D921188945B18DA31', X'BDB7D4D0AAB90341AFD797F4340DB5E4', X'BDB7D4D0AAB90341AFD797F4340DB5E4', 0, NULL, NULL, 2, NULL, 0, 'Scatter 2 cups cranberries and rosemary sprigs in a bundt cake pan and fill halfway with water. Transfer to the freeze and let freeze overnight.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6FBA01A2294354F870508A86D129EE7', X'2FC9952D67B3AC4AB4C80DBD30ACA533', X'2FC9952D67B3AC4AB4C80DBD30ACA533', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients for the simple syrup into a medium saucepan and bring to a simmer over medium heat. Stir until the sugar is dissolved and remove from heat. Cover and let steep for at least 30 minutes. Strain before using. This syrup can be made up to 3 days ahead of time.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78A532D406BC0B42B8EFC358E73031B3', X'4F04FCEE467F5B4B84E065AFD9DE6FFA', X'4F04FCEE467F5B4B84E065AFD9DE6FFA', 0, NULL, NULL, 2, NULL, 0, 'Pour cranberry simple syrup, orange juice, vodka, and sparkling water into a punch bowl. Run bundt cake under warm water until the ice ring has broken free from the bundt cake. Transfer to punch bowl, add a few orange slices, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0D3A5B2A9B56C640944F236BAA01C048', X'8C73C18AD174F34C8E6BA17961C1E82A', X'8C73C18AD174F34C8E6BA17961C1E82A', 0, NULL, NULL, 2, NULL, 0, 'In a small saucepan, stir together the orange juice, orange zest, 1 chamomile tea bag (or chamomile flowers, if using), sugar, and 1/2 cup filtered water and cook over medium heat. Simmer until the sugar has completely dissolved and the mixture has thickened, about 5 minutes. Remove from heat and set aside to cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD03D902CD3D2A4CB928C9419F6F1AE1', X'8C73C18AD174F34C8E6BA17961C1E82A', X'8C73C18AD174F34C8E6BA17961C1E82A', 1, NULL, NULL, 2, NULL, 0, 'Once cooled, strain through a fine mesh strainer into a airtight container. This will keep for one week in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'503768B9DB1218428D49743AD95205DD', X'4B602D2E0A467740958FEEAFE22745BC', X'4B602D2E0A467740958FEEAFE22745BC', 0, NULL, NULL, 2, NULL, 0, 'In a large mug, stir together 1 Tbsp simple syrup, boiling water, brandy, and lemon juice. Add chamomile tea bag and let steep for 5 minutes. Remove tea and garnish with a lemon wedge and rosemary. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC264FFFFEACA548A677A79652404273', X'BCA0B5AC0470944490611A03B00CCC56', X'BCA0B5AC0470944490611A03B00CCC56', 0, NULL, NULL, 2, NULL, 0, 'Combine all of the chai concentrate ingredients, except the black tea, into a small saucepan and bring to a boil over high heat. Once boiling, reduce heat to medium-low, cover, and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'16D986BE6136BE4393FD943AD47455F4', X'BCA0B5AC0470944490611A03B00CCC56', X'BCA0B5AC0470944490611A03B00CCC56', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tea bags, and let steep for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5CFD0D3DE081BD4E982AE6D41A779C55', X'BCA0B5AC0470944490611A03B00CCC56', X'BCA0B5AC0470944490611A03B00CCC56', 2, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture through a strainer and discard the spices (or save some for garnish). Let cool to room temperature.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C542B003D3E7A41B00E4570F40401E1', X'BCA0B5AC0470944490611A03B00CCC56', X'BCA0B5AC0470944490611A03B00CCC56', 3, NULL, NULL, 2, NULL, 0, 'Pour 1.5 ounces of spiced rum into 4 cocktail glasses. Add 1/4 cup of both the chai concentrate and apple cider into each glass and garnish with a star anise. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'61D12F7ED723334C9943EDB723ABF147', X'BCA0B5AC0470944490611A03B00CCC56', X'BCA0B5AC0470944490611A03B00CCC56', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, you can warm the cider and chai in a small saucepan and serve the cocktails warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CAE0CC7FCCA42A4F9EB14A6F39B32059', X'A4D1C9664948FF419DCA1431514CFCD7', X'A4D1C9664948FF419DCA1431514CFCD7', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium-high heat, combine 1 cup of water, sugar, cranberries, and orange juice and bring to a boil. Immediately lower the heat to low and simmer, stirring often, until the sugar has dissolved, about 3 minutes. Remove from heat and let sit for 20 minutes. Strain through a fine mesh strainer and discard any solids. Use the syrup right away or store in an airtight container in the fridge for up to a 1 week.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7A8B3D5717095F4B92573FE36D7291F6', X'39EC88118ECE414B8D3FE4D99693542C', X'39EC88118ECE414B8D3FE4D99693542C', 0, NULL, NULL, 2, NULL, 0, 'Combine the tequila, triple sec, simple syrup, and lime juice in a large pitcher and stir.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33E1BD168B90E94E8FEE4962B009CA24', X'39EC88118ECE414B8D3FE4D99693542C', X'39EC88118ECE414B8D3FE4D99693542C', 1, NULL, NULL, 2, NULL, 0, 'Pour salt on a small plate and rub 8 glasses with one of the orange slice garnishes. Dip the rim of the glasses into the salt and fill each glass with your garnish options (orange slices, rosemary sprig, and/or cranberries).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D17696F11C2BD94DAD8E548A75A4CF53', X'39EC88118ECE414B8D3FE4D99693542C', X'39EC88118ECE414B8D3FE4D99693542C', 2, NULL, NULL, 2, NULL, 0, 'Right before serving, add ice and sparkling water to the pitcher. Pour into prepared glasses and enjoy!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'107E779965B41948B93D2E4D61FF5497', X'E44D851392C256479108085FF728A249', X'E44D851392C256479108085FF728A249', 0, NULL, NULL, 2, NULL, 0, 'Place the butter and brown sugar in a large deep saucepan. Cook over medium heat until the butter is melted, whisking to combine. Continue to let simmer until the mixture reaches 300 degrees with a candy thermometer, about 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4ECE40116E70E4481EF348C06375010', X'E44D851392C256479108085FF728A249', X'E44D851392C256479108085FF728A249', 1, NULL, NULL, 2, NULL, 0, 'Once at 300 degrees, remove from heat and whisk in the water (being very careful as it will bubble and steam). Then whisk in the cream, milk, and salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'746935652E861A499A681DCAA20D8F66', X'E44D851392C256479108085FF728A249', X'E44D851392C256479108085FF728A249', 2, NULL, NULL, 2, NULL, 0, 'Gently reheat over low heat until warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2BD31559618B5C438D2BC9590F0D1BDC', X'E44D851392C256479108085FF728A249', X'E44D851392C256479108085FF728A249', 3, NULL, NULL, 2, NULL, 0, 'Divide scoops of Hudsonville ice cream between 4-6 mugs (depending on how many people you are serving) and pour the Hot Buttered Rum over the ice cream right before serving.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7CEF4FE7FC5950428BF0367469F2223D', X'06E6B09885AC23489A31A8DB6EEC0D45', X'06E6B09885AC23489A31A8DB6EEC0D45', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the olive oil, lemon juice, cinnamon, garlic cloves, tahini, and salt in a food processor until smooth. With the processor running, add in the chickpeas, 1/2 cup at a time, until smooth. Next, add in the butternut squash and pulse until completely smooth. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'737176419BAEF74CA3C07E629E268BB1', X'06E6B09885AC23489A31A8DB6EEC0D45', X'06E6B09885AC23489A31A8DB6EEC0D45', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, melt the butter over medium heat. Once melted, watch closely and swirl the pan often until it bubbles and specks of brown butter start to form. Remove from heat and add in sage leaves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'791F0DED9B31294CA4A4F9B47CFC457E', X'06E6B09885AC23489A31A8DB6EEC0D45', X'06E6B09885AC23489A31A8DB6EEC0D45', 2, NULL, NULL, 2, NULL, 0, 'To assemble: Transfer hummus to a small serving bowl and use the back of the spoon to create an indent in the center. Pour browned butter and sage leaves over the hummus. Sprinkle with a dash of salt and pepper. Serve right away with crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F4125A0D90E224CB6EF41514ADC0D2D', X'7AF67FD94819C44CB8396C9CF61E5C3B', X'7AF67FD94819C44CB8396C9CF61E5C3B', 0, NULL, NULL, 2, NULL, 0, 'Using a food processor fitted with an S-blade, combine beans, beet pieces, tahini, maple syrup, lemon juice, salt, and pepper and process until completely smooth, 3 to 5 minutes. Season with more salt, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'455266FFBB51ED4385B69E3DB03317BD', X'7AF67FD94819C44CB8396C9CF61E5C3B', X'7AF67FD94819C44CB8396C9CF61E5C3B', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl, drizzle olive oil and hazelnut over it and then sprinkle with rosemary. Serve with fresh bread, crackers, or veggies.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E60BDD646DCECA4C95E633B7427495F5', X'D57C27EE605C694CBD054C92047005F8', X'D57C27EE605C694CBD054C92047005F8', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3F22DA007A3FF47BE285E97F7978E49', X'D57C27EE605C694CBD054C92047005F8', X'D57C27EE605C694CBD054C92047005F8', 1, NULL, NULL, 2, NULL, 0, 'Add cream cheese, pumpkin, cheddar cheese, paprika, salt, and pepper to a food processor and process until completely combined and a smooth paste remains.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'590FA7A8B6E28B41A2D4E92A8FAD1E97', X'D57C27EE605C694CBD054C92047005F8', X'D57C27EE605C694CBD054C92047005F8', 2, NULL, NULL, 2, NULL, 0, 'Using clean and damp hands, divide the dough in half and each half into a circle using the palm of your hands. Transfer to prepared baking sheet and place in the freezer to chill for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'37F72A650B315A488EF7C81D2A9E2ADE', X'D57C27EE605C694CBD054C92047005F8', X'D57C27EE605C694CBD054C92047005F8', 3, NULL, NULL, 2, NULL, 0, 'Mix together the sesame seeds and poppy seeds in a shallow rimmed dish. Remove chilled balls from freezer and roll each ball in the seed mixture until completely coated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A3962ED2E8A8A48994FEFBEF67A764D', X'D57C27EE605C694CBD054C92047005F8', X'D57C27EE605C694CBD054C92047005F8', 4, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish to serve right away or store in an airtight container in the fridge until ready to serve. Serve with veggies, chips, and crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'193FB367E4976841995DA341747AA401', X'9DECA014B5857E48AC3F6D669CE3DEB5', X'9DECA014B5857E48AC3F6D669CE3DEB5', 0, NULL, NULL, 2, NULL, 0, 'Cut the goat cheese log into 1 ounce rounds (you should have 8 pieces total). Dip each piece of the goat cheese slice into the beaten egg followed by the breadcrumbs (you may need to press the goat cheese slightly into the breadcrumbs to help them stick), making sure both sides of the slices are coated with egg and breadcrumbs. Transfer to a plate and stick in the freezer to chill for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FED46C81D0C91D4DB63003BF257F73F0', X'4E774812041B7143A81064B61B05687B', X'4E774812041B7143A81064B61B05687B', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the vinegar, mustard, maple syrup, dash of salt and dash of pepper. Slowly stream in the olive oil while whisking to incorporate it into the dressing. Add the golden raisins and toss so they are coated with the dressing. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7CA8D62F8D343F4A8C30A24A53315D13', X'4E774812041B7143A81064B61B05687B', X'4E774812041B7143A81064B61B05687B', 1, NULL, NULL, 2, NULL, 0, 'In a small frying pan, toast the pepita seeds over medium-low until just starting to brown, about 2 minutes. Remove from heat and transfer to the large mixing bowl with the dressing.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63DCC082776B924F98A65D383329E01A', X'4E774812041B7143A81064B61B05687B', X'4E774812041B7143A81064B61B05687B', 2, NULL, NULL, 2, NULL, 0, 'Using the same pan, warm 1 Tbsp brown sugar and 1 Tbsp water over medium-low heat, whisking often, until the sugar is melted, about 30 seconds to 1 minute. Add the pecans and toss to coat. Remove from heat and splash in another tsp of water to deglaze the pan. Sprinkle a little salt over the pecans and toss one more time. Set them on a plate to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C02CE7FD9C64F4BAAA5324D6F4700DB', X'4E774812041B7143A81064B61B05687B', X'4E774812041B7143A81064B61B05687B', 3, NULL, NULL, 2, NULL, 0, 'Wipe the pan clean and put back on the stovetop over medium heat. Pour in 1 Tbsp olive oil and add enough goat cheese rings to fill the pan but not crowd it (none should be touching). Cook for for 30 seconds to 1 minute or until turning golden brown. Carefully flip and cook for another 30 seconds to 1 minute on that side until golden brown. Transfer to a plate and repeat with the rest of the goat cheese slices, adding more olive oil (1 Tbsp at a time) as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'475A84B9A1DD864EB9FEA0F35041278A', X'4E774812041B7143A81064B61B05687B', X'4E774812041B7143A81064B61B05687B', 4, NULL, NULL, 2, NULL, 0, 'Add the apple slices and lettuce mix to the mixing bowl with the dressing and toss until everything is coated. Transfer to a large serving platter or bowl and top with the brown sugar pecans and goat cheese fritters. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F9BB2E651AD8048B1C0912DF64A4D8F', X'A16598A5A2E29349AC5CE479BAE11972', X'A16598A5A2E29349AC5CE479BAE11972', 0, NULL, NULL, 2, NULL, 0, 'Transfer the cream cheese, goat cheese, honey, and orange zest into a food processor with a blade attachment (alternatively you could blend this with a stand mixer if you don''t have a food processor). Turn the food processor on and blend for 20 seconds or until everything is incorporated and the mixture is light and fluffy. Remove the blade, re-cover, and stick in the freezer to chill for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D8360F440004A449A8DA6AB5A84D4571', X'A16598A5A2E29349AC5CE479BAE11972', X'A16598A5A2E29349AC5CE479BAE11972', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, transfer the pecans and rosemary to a small shallow bowl and toss to combine. In a separate small shallow bowl, add the almonds and thyme and toss to combine. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'482BF513CD478C499A32B8E2D72C3C7D', X'A16598A5A2E29349AC5CE479BAE11972', X'A16598A5A2E29349AC5CE479BAE11972', 2, NULL, NULL, 2, NULL, 0, 'Once chilled, use a tsp measuring spoon to scoop out 2 tsp each of mixture so that you end up with about 35 balls. Use clean hands to roll the balls between your hands (wetting them if the cheese is sticking) to form circular balls. Roll half the balls in the pecan mixture and half in the almond one (you''ll have one extra since it''s an odd number – go ahead and roll the last one in whichever mixture sounds better to you).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'890E6631DC77FD409D9BECB1B9B03A56', X'A16598A5A2E29349AC5CE479BAE11972', X'A16598A5A2E29349AC5CE479BAE11972', 3, NULL, NULL, 2, NULL, 0, 'Serve right away or cover and chill until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D80DDD00CFA5341B1B63833D08AC5A7', X'2736F499A251BA47A10447CF8BB7AB68', X'2736F499A251BA47A10447CF8BB7AB68', 0, NULL, NULL, 2, NULL, 0, 'Place a large serving platter on your appetizer table or where ever you plan to serve it. Arrange the cheeses in different corners of the platter (you want to make sure none are touching so the flavors don''t mingle).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9B08543C9EB7D243B5BA5C9900954127', X'2736F499A251BA47A10447CF8BB7AB68', X'2736F499A251BA47A10447CF8BB7AB68', 1, NULL, NULL, 2, NULL, 0, 'Place mustard, honey, fig spread, olives, and mini cheese bites onto their own small plates or bowls and then place in different areas on the platter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3A7EC1BE724E364C9FD9E6C0822BCA1C', X'2736F499A251BA47A10447CF8BB7AB68', X'2736F499A251BA47A10447CF8BB7AB68', 2, NULL, NULL, 2, NULL, 0, 'Use the vegetables, fruit, crackers, and nuts to fill out the rest of the platter. Add a few sprigs of the rosemary for garnish and then finish the platter off by adding cheese knives and spoons near foods that need utensils to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90B73E06AA6BE240989BAC65F788EA97', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', 0, NULL, NULL, 2, NULL, 0, 'Combine all of the chai concentrate ingredients, except the black tea, into a small saucepan and bring to a boil over high heat. Once boiling, reduce heat to medium-low, cover, and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'866722729DEE19429A6D7AF3D5C468E7', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tea bags, and let steep for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D4F638E63A2BB42997A67A033F0EFEB', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', 2, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture through a strainer and discard the spices (or save some for garnish). Let cool to room temperature.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C2985EDDF7BA4498325D135E9238E3A', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', 3, NULL, NULL, 2, NULL, 0, 'Pour 1.5 ounces of spiced rum into 4 cocktail glasses. Add 1/4 cup of both the chai concentrate and apple cider into each glass and garnish with a star anise. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'807723345DDCD240BEE77072DE89FBE1', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', X'DE3E4BA9D940A649B9FBE1D3A40C7D4F', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, you can warm the cider and chai in a small saucepan and serve the cocktails warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB99DCBF1BCEFE45A978EF452C73DA31', X'5267D373A4C2254CBFFE4A84032E5DF5', X'5267D373A4C2254CBFFE4A84032E5DF5', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium-high heat, combine 1 cup of water, sugar, cranberries, and orange juice and bring to a boil. Immediately lower the heat to low and simmer, stirring often, until the sugar has dissolved, about 3 minutes. Remove from heat and let sit for 20 minutes. Strain through a fine mesh strainer and discard any solids. Use the syrup right away or store in an airtight container in the fridge for up to a 1 week.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A61F1CC66F8B344A87AA2AFF193DA54B', X'683CF0851F7FA14CA9053614A23FF20B', X'683CF0851F7FA14CA9053614A23FF20B', 0, NULL, NULL, 2, NULL, 0, 'Combine the tequila, triple sec, simple syrup, and lime juice in a large pitcher and stir.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76D80D50411351439AB1D8E1E6D642FE', X'683CF0851F7FA14CA9053614A23FF20B', X'683CF0851F7FA14CA9053614A23FF20B', 1, NULL, NULL, 2, NULL, 0, 'Pour salt on a small plate and rub 8 glasses with one of the orange slice garnishes. Dip the rim of the glasses into the salt and fill each glass with your garnish options (orange slices, rosemary sprig, and/or cranberries).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F159ADB9A0E8845AECD158817A1275E', X'683CF0851F7FA14CA9053614A23FF20B', X'683CF0851F7FA14CA9053614A23FF20B', 2, NULL, NULL, 2, NULL, 0, 'Right before serving, add ice and sparkling water to the pitcher. Pour into prepared glasses and enjoy!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5BB4C0ABAB7B7144B9679A095B39A62E', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 0, NULL, NULL, 2, NULL, 0, 'Prepare the ziti according to the package direction. Drain and yet aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BBD8D4E00341E64381647D018AB8006F', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 1, NULL, NULL, 2, NULL, 0, 'While the pasta is cooking, prepare the caramelized onions: Preheat a large saucepan over medium heat. Add onions, 3 Tbsp olive oil, and a tiny pinch of baking soda (Isa says this will help speed up the caramelization process). Saute, stirring often, for 15 minute or until deeply caramelized. Turn off heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7ABFC093843284C9EA96C4516DEAE2A', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9x13-inch baking dish with olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5BB767E2F366154088A82A18CA663988', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 3, NULL, NULL, 2, NULL, 0, 'To make the cashew ricotta: Combine cashews, lemon juice, 2 Tbsp olive oil, garlic cloves, 1 1/2 tsp dried thyme, and 1 1/2 tsp salt in a high speed blender or food processor until a finely chopped mixture is created that looks similar to ricotta.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'77CEC2754CA1414A91C859F27B328891', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together cashew ricotta, maple syrup, paprika, thyme, salt, nutmeg, cayenne, pumpkin puree, milk, and black pepper. Add caramelized onions and pasta and stir until completely combined. Transfer to the prepared basking dish and smooth on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBC1AA41688888458C6A543B0D6D09FD', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 5, NULL, NULL, 2, NULL, 0, 'Prepare the breadcrumbs: Combine bread crumbs, walnuts, nutritional yeast, sage, rosemary, and salt. Drizzle with olive oil and toss so that the oil has completely coated the mixture. Sprinkle crumbs over the casserole.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'311451149D28F24B95A09BAD3A42C1D8', X'5F1F8A2185B59E4AB2B09388A6979E90', X'5F1F8A2185B59E4AB2B09388A6979E90', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, uncovered, or until the crumb is golden brown. Remove from oven, let cool for 10 minutes, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A19FB71F96F25F4F970A9A7034C77826', X'932B3C09E41EC54F8578F8F5306D30EA', X'932B3C09E41EC54F8578F8F5306D30EA', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the all-purpose flour, sage, and salt. Use clean hands to work the butter into the dry ingredients until they are the size of small peas. Drizzle in the ice water and stir until the dough is evenly moistened. Use your hands to press the dough into a ball. Gently press into a disk, wrap in plastic wrap, and let chill in the fridge for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD1ADAFFA785EB4597C2D4AC2A71AFDD', X'88D9D4B669C3A6418F52ACDABCE90DBA', X'88D9D4B669C3A6418F52ACDABCE90DBA', 0, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large saucepan of salted water to a boil over high heat. Add in the potato slices and cook until a fork easily pierces the slices, about 5 to 10 minutes depending on the size of the potato slices. Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1D211C840BFCF544A306459764016D98', X'88D9D4B669C3A6418F52ACDABCE90DBA', X'88D9D4B669C3A6418F52ACDABCE90DBA', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the ricotta, 1/4 tsp salt, dash of pepper, and 1 large egg until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC672B5F1A5D024FB8A0E124EBC7CDAC', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees and lightly grease a baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FDF03A911FCF3843A77960D2AF797AD1', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 1, NULL, NULL, 2, NULL, 0, 'On a lightly floured clean surface, roll out the dough into a 12` circle. Transfer the dough to the prepared baking sheet. Spread the ricotta mixture over the dough, leaving a 2` outer ring bare.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DC7ADBEF25FAB54A8FD45D69FD3F5E41', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 2, NULL, NULL, 2, NULL, 0, 'Top the ricotta with slices of potato and apples, overlapping as needed to make them all fit.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6ED2151D7489334CA24AA05496BB15B8', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 3, NULL, NULL, 2, NULL, 0, 'Fold the bare outer parts over the filling to create a crust. Whisk the remaining egg with 1 Tbsp water in a small bowl and brush over the edges exposed dough.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B8048C87ADE83A43ACBE8C2D4DA35D17', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 4, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the crust is golden brown and the filling is starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8CF5DEBB4945FD42AACC8B89E852DD95', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, transfer the apple cider to a medium saucepan and bring to a simmer over medium-high heat. Let simmer, stirring often, until the cider has reduced by half, about 10 minutes. Add the mustard and continue to simmer for another 2 minutes. Remove from heat and stir in the butter which should help thicken up the glaze a bit.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A099EB147C7BD14A8029FC13D425236F', X'CE88EB1D580A1E4D8D927B7CF709D445', X'CE88EB1D580A1E4D8D927B7CF709D445', 6, NULL, NULL, 2, NULL, 0, 'Remove galette from the oven and liberally brush the glaze all over the galette. Let cool for 10 minutes before cutting. Garnish with sage, sprinkle with flaky salt, slice and serve the remaining glaze on the side.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1405E685968E764EB4C0EFECC4D587E0', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 0, NULL, NULL, 2, NULL, 0, 'Bring a large stockpot of salted water to a boil over high heat. Cook the pasta just shy for a minute or two from what the directions say on the package (it''ll finish cooking in the oven). Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F8916A5427F094384A92A6D76BE860B', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 13×9 inch baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84C1A965C098364DB346D81927595538', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 2, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium heat, melt 4 Tbsp butter and then whisk the 4 Tbsp all-purpose flour into the butter until a thick paste as formed. While whisking, slowly pour the milk into the mixture so that the butter mixture gets incorporated into the milk. Cook for about 10 minutes, stirring often, or until the mix has thickened enough that it coats the pack of a spoon. Remove from heat and stir in the brown sugar, nutmeg, 1/2 tsp salt, and pumpkin puree. Pour the cooked pasta into the pumpkin sauce and stir until coated (alternatively, if your saucepan isn''t big enough, pour into a large mixing bowl and toss until all the pasta is coated).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5560CB0C983AE144BBF749B531C142D4', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the ricotta, large eggs, 1/2 tsp salt, 1/4 tsp black pepper, minced garlic, and 2 Tbsp chopped sage.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6978DA2BC02D78489126BD6BC45AA7D1', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 4, NULL, NULL, 2, NULL, 0, 'Pour about 1/3 of the pumpkin pasta mixture into your prepared baking dish and smooth out into an even layer (no need to be exact). Using about half of the ricotta mixture, place large dollops of the mixture over the pumpkin pasta and then top with half of the torn mozzarella. Layer another 1/3 of the pumpkin pasta over that and top with remaining ricotta. Add the final layer of pumpkin pasta and finish with the remaining mozzarella.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B22420030D69F74BAACD69A439938E0B', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the top is starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'765B87BA48708646A8C19960A6308DDF', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small skillet, heat the remaining 2 Tbsp unsalted butter over medium heat for a few minutes until brown specks start to form. Add the sage leaves and let them fry for 30 seconds or until just starting to turn dark. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CCA6EF1A48EF814DBAFB1A0F4A9AF6A9', X'F18F252C2D6CF1438DCE303F6CBE7964', X'F18F252C2D6CF1438DCE303F6CBE7964', 7, NULL, NULL, 2, NULL, 0, 'While the bake is still warm, pour the brown butter and sage over the top of the bake and serve. Feel free to sprinkle red pepper flakes on the top if you''d like a little extra heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2667C76EA02885478928AA6C2547221B', X'BE3DF261B4623D45B33C3FAB1601090A', X'BE3DF261B4623D45B33C3FAB1601090A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. Toss Brussels sprouts with 1 Tbsp olive oil and a dash of salt and pepper. Transfer to prepared baking sheet and bake for 30 minutes or until crispy on the outside. Remove from oven and let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9FAF4401C67D444295621ACE8B836E5C', X'BE3DF261B4623D45B33C3FAB1601090A', X'BE3DF261B4623D45B33C3FAB1601090A', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dressing by whisking together the olive oil, vinegar, maple syrup, and dijon mustard in a small bowl. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C06D5DC4315C264D95C9898B9FB04977', X'BE3DF261B4623D45B33C3FAB1601090A', X'BE3DF261B4623D45B33C3FAB1601090A', 2, NULL, NULL, 2, NULL, 0, 'Slice the apples into thin slices and toss with lemon juice.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9D960D0555E764A84A38F7B772EE50F', X'BE3DF261B4623D45B33C3FAB1601090A', X'BE3DF261B4623D45B33C3FAB1601090A', 3, NULL, NULL, 2, NULL, 0, 'Once the Brussels sprouts are cooled, slice them into thin strips. In a big salad bowl, toss together the Brussels sprouts, sliced shallots, dried cranberries, pecans, Gorgonzola cheese, and chopped apples.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E8E39DF0C172F0418D5A5F555C2EC978', X'BE3DF261B4623D45B33C3FAB1601090A', X'BE3DF261B4623D45B33C3FAB1601090A', 4, NULL, NULL, 2, NULL, 0, 'Pour dressing over the salad, toss, and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DBF5402092F9DF4CBADAD11AFBBF195F', X'58CD1F0A9EEBF54A8535DA4A14CFF498', X'58CD1F0A9EEBF54A8535DA4A14CFF498', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees. Cut the squash into 1/2 inch rounds and use a spoon to scoop out the pulp-y inside from each round. If you end up with some squash pieces that are thicker than others, cut the thicker pieces in half to make moons. Transfer to a baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78582F69F3156C4F829DAC64FEE53D7C', X'58CD1F0A9EEBF54A8535DA4A14CFF498', X'58CD1F0A9EEBF54A8535DA4A14CFF498', 1, NULL, NULL, 2, NULL, 0, 'Drizzle olive oil, salt, smoked paprika, cinnamon, and black pepper over the squash and toss to coat. Spread the squash out into an even layer (this may take 2 baking sheets depending on the size) and bake for 20-25 minutes, flipping halfway through, until the squash is soft and caramelized.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2FDAE2DDAA0C68409B65CB828E75C246', X'58CD1F0A9EEBF54A8535DA4A14CFF498', X'58CD1F0A9EEBF54A8535DA4A14CFF498', 2, NULL, NULL, 2, NULL, 0, 'Remove from oven and transfer to a serving platter (if serving right away. If not, let cool and then store in an airtight container until ready to serve). Tear the burrata into bite size pieces and arrange around the squash. Sprinkle the chopped sage over it all and drizzle with a little olive oil, flaky sea salt, and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'307ED3216A07714A9E36B88F59067E4B', X'58CD1F0A9EEBF54A8535DA4A14CFF498', X'58CD1F0A9EEBF54A8535DA4A14CFF498', 3, NULL, NULL, 2, NULL, 0, 'Serve as a side to be enjoyed with forks and knives or as an appetizer as a topping for crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84326B331BB37943BD8CC00C1032703C', X'A26066CED5C3FD4785CF33643D25AD13', X'A26066CED5C3FD4785CF33643D25AD13', 0, NULL, NULL, 2, NULL, 0, 'In a small saucepan over medium heat, melt the butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D7A6702E0CB5434D984CB4CC4A7A8DA0', X'A26066CED5C3FD4785CF33643D25AD13', X'A26066CED5C3FD4785CF33643D25AD13', 1, NULL, NULL, 2, NULL, 0, 'Add onion, garlic, sage, salt, and pepper and cook, stirring often, until everything is softened, about 8 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'498BA082D4F6E3468CF7EAE3C4007F89', X'A26066CED5C3FD4785CF33643D25AD13', X'A26066CED5C3FD4785CF33643D25AD13', 2, NULL, NULL, 2, NULL, 0, 'Add flour and, stirring often, cook until it turns the color of a milky cup of coffee, about 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9B5793C77AE47D4B8837174AAE448BDD', X'A26066CED5C3FD4785CF33643D25AD13', X'A26066CED5C3FD4785CF33643D25AD13', 3, NULL, NULL, 2, NULL, 0, 'Next, while whisking constantly, slowly pour in the stock and the cider.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'86ECC7B2D6773E4B9A6B9C4ED50CDA10', X'A26066CED5C3FD4785CF33643D25AD13', X'A26066CED5C3FD4785CF33643D25AD13', 4, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a bowl and then lower heat and simmer, stirring often, until thickened, about 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76DAFAE518D89C4988997171DF8F2BA0', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D7DC5617FF8EEC42A63DE84FF7A37D5B', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 1, NULL, NULL, 2, NULL, 0, 'Warm olive oil and 1/2 Tbsp butter in a large oven proof skillet over medium-high heat. Add the carrots and cook for about 10 minutes, stirring every few minutes, or until they begin to develop color all over. Add salt, pepper, and cumin and sauté for another 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9599FD162266C94E9D27F09DFBF2433A', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 2, NULL, NULL, 2, NULL, 0, 'Transfer to the oven and let roast for 45 minutes, tossing halfway through.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'207A9CCDC899EA4EAFF7043AA38B311D', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, add the remaining butter to a small skillet and warm over medium heat. Heat for 5-7 minutes, stirring often, until the butter has turned golden, there are bits of speckled brown butter heavily covering the bottom of the pan, and it smells nutty. Remove from heat and immediately whisk in the brown sugar. Set aside to cool for a few minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D503390FF4C3C7438DD50D64106CB338', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 4, NULL, NULL, 2, NULL, 0, 'Once it''s cool enough to touch (if it''s too hot, the butter may splatter), whisk in the vinegar and raisins and set aside so they can quick pickle while the carrots cook.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F82E274847B6454D96E36F049D0D6962', X'745ED8B2F1305B4D8D98ED1BB0251E17', X'745ED8B2F1305B4D8D98ED1BB0251E17', 5, NULL, NULL, 2, NULL, 0, 'When ready to serve, whisk together the ricotta, lemon zest, and a dash of salt and pepper. Spread into an even layer on a serving tray. Top with the carrots and the pickled raisins and their brown butter juices. Darnish with dill and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EC4BBD65C5833E47A83EA8E9D6117763', X'9AB7352929EACF419ACE35E562429BB8', X'9AB7352929EACF419ACE35E562429BB8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450 degrees and line a baking sheet with parchment paper. In a large bowl, toss Brussels sprouts with 2 Tbsp olive oil, salt, and a turn of freshly ground black pepper. Transfer to prepared baking sheet and spread out into a single layer. Roast for 20 to 25 minutes, tossing halfway through, or until browned on most sides.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE1E7A12C3E4B04A8E948CAEF796E701', X'9AB7352929EACF419ACE35E562429BB8', X'9AB7352929EACF419ACE35E562429BB8', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine honey, buffalo sauce, and butter in a small saucepan and cook over medium heat, whisking constantly, until the butter has melted and the glaze has thickened slightly, about 3 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B4818C6BB703584EB4FC68DDAADFF433', X'9AB7352929EACF419ACE35E562429BB8', X'9AB7352929EACF419ACE35E562429BB8', 2, NULL, NULL, 2, NULL, 0, 'When Brussels sprouts are done, transfer to a large mixing bowl and toss in the glaze until completely coated with the glaze. Taste and add more salt or pepper, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5923EAAA80E9D4458595BDCF4AD6FB5B', X'9AB7352929EACF419ACE35E562429BB8', X'9AB7352929EACF419ACE35E562429BB8', 3, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish and garnish with scallion slices and lemon zest (if using). Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'79F92F0AFC06AE46A0E7E4D7FC24D7C1', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 9×13 inch baking pan with butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A874C2038E81734B898BF226D918A5D0', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 1, NULL, NULL, 2, NULL, 0, 'Tear large pieces of country bread into blender. Cover and pulse 3-4 times or until large breadcrumbs have formed. Transfer to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26CBCF079831C84EBD8CBD9EAA71AEB9', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 2, NULL, NULL, 2, NULL, 0, 'Rinse blender and then add milk, 4 Tbsp melted butter, cornstarch, and eggs into blender. Blend until completely incorporated, about 5 seconds. Add frozen corn, cheese, and cream-style corn and pulse twice to incorporate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DB0062DF5BC1E945A9B556474A60A896', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 3, NULL, NULL, 2, NULL, 0, 'Pour mixture into prepared baking pan and bake for 45 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'674E5343CDA8BC42AACFE6D28902B4DA', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat 4 Tbsp butter in a large skillet over medium until the butter has turned down brown and smells nutty, about 4 minutes (watch closely as it will turn past once it starts!). Remove from heat and whisk in chili garlic sauce and honey. Pour corn flakes and breadcrumbs and toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E35A3A536702C3489E01EFEB2D2AFF87', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 5, NULL, NULL, 2, NULL, 0, 'When the casserole has 10 minutes left, remove from oven, sprinkle crunchy topping over the casserole and put back in the oven for the remaining time.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8673F6760CDDF745B308CE1EC8C97943', X'F63231761866544DAB53653D67DF77AA', X'F63231761866544DAB53653D67DF77AA', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool slightly before serving. Enjoy!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'59A191D901E3544CB9DF2CCA6F075243', X'2DC26F24CB9E41488B83663F69F8BE67', X'2DC26F24CB9E41488B83663F69F8BE67', 0, NULL, NULL, 2, NULL, 0, 'Stir the sugar, salt, and water together in a small bowl until the sugar and salt are fully dissolved. Place the bowl in the freezer so the liquid can chill down and be ice cold when making the dough.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9DF10188ED69D14D879C86CDE11F2A15', X'2DC26F24CB9E41488B83663F69F8BE67', X'2DC26F24CB9E41488B83663F69F8BE67', 1, NULL, NULL, 2, NULL, 0, 'Mix the flours together in a large mixing bowl and add the pieces of butter. Use your clean hands to squeeze the butter into the flour until you have pea size pieces of butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5AED33C748914B4781E7589D11A4E1D0', X'2DC26F24CB9E41488B83663F69F8BE67', X'2DC26F24CB9E41488B83663F69F8BE67', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the sugar water mixture of the flour and mix, again with your clean hands, until a shaggy dough has formed and all the flour is moistened by the water. It will be crumbly at first but will come together the more you mix it. Keep mixing until a shaggy ball has been formed. Flatten the mound into a 1 inch thick disk and wrap in plastic wrap. Stick in the fridge to chill for at least 2 hours or overnight.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4837FFD3E98BD444A3329CD3AEAD18C5', X'2DC26F24CB9E41488B83663F69F8BE67', X'2DC26F24CB9E41488B83663F69F8BE67', 3, NULL, NULL, 2, NULL, 0, 'Once chilled, transfer your disk onto a floured surface and roll the dough out into a 12 inch circle. Transfer to a 9 inch pie pan, letting the extra dough hang off the sides. Roll the extra dough hanging off the edge underneath itself until it lays on the rim of the dish. To make a crimped edge, place a piece of the edge between your thumbs and a index finger to create a crimp look and repeat this process all the way around. Alternatively, you can use a fork to make fringe decoration around the crust. Transfer to the freezer to let chill while the oven preheats.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39A84CCEFA6FCA4E820998C2DFE7F9FE', X'2DC26F24CB9E41488B83663F69F8BE67', X'2DC26F24CB9E41488B83663F69F8BE67', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees. Once the oven is ready, remove pie crust from the freezer and top with parchment paper followed by pie weights (I always used dried beans). Blind bake for 20 minutes, carefully remove the parchment paper and pie weights, and bake for another 10 to 15 minutes or until the bottom of the crust looks fully baked (shouldn''t be wet looking at all). Transfer to a wire rack and let cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FFF744D397E9A14C8EBA2FA35AEE6551', X'C06F0F7A294814438AEC981D394E6371', X'C06F0F7A294814438AEC981D394E6371', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together 1 1/4 cup sugar, cornstarch, and 1/4 tsp salt. Whisk in the egg yolks, whole egg, and 1/4 cup plus 2 Tbsp water. Whisk in the lemon juice then pass the mixture through a fine mesh strainer into a small saucepan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'04F3BD70DD431E489FE27761A2799694', X'C06F0F7A294814438AEC981D394E6371', X'C06F0F7A294814438AEC981D394E6371', 1, NULL, NULL, 2, NULL, 0, 'Place the pan over medium high heat and stir constantly with a rubber spatula, scraping the sides to prevent burning, for 5 to 8 minutes or until the mixture starts to thicken. Reduce the heat to the lowest setting, continue to stir constantly, and cook until the consistency is thick but still pourable, about 5 more minutes. Remove from heat and stir in 3 Tbsp cold butter until melted.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D6E34B0FA04A54AB631EEBE984BCD84', X'C06F0F7A294814438AEC981D394E6371', X'C06F0F7A294814438AEC981D394E6371', 2, NULL, NULL, 2, NULL, 0, 'Pour the hot filling into your blind-baked pie crust and use the spatula to ease the filling into an even layer.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'416DCC57A2F161428CD49B38A5D58BCE', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 0, NULL, NULL, 2, NULL, 0, 'Using a clean and dry bowl fitted on a stand mixer with the whisk attachment, beat the egg whites and cream of tartar on high until they are white, foamy, and hold soft peaks.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA6CFA6A097A3A4EA4C0B6D04C181A11', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small saucepan, stir 1/4 cup water and 1 cup sugar over high heat until the sugar has dissolved and the syrup reaches a strong boil (a candy thermometer should reach 240 degrees). Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6C72F52C32A9149A0E52CE2E342E235', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 2, NULL, NULL, 2, NULL, 0, 'With the stand mixer running, slowly pour the hot sugar mixture in a thin stream into the foamy egg whites. Continue beating until the meringue holds the pattern of the whisk and the texture is voluminous and silky. Add the vanilla, salt, and poppyseeds, and beat just until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'818A81DE7DCB174793923F231385596E', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 3, NULL, NULL, 2, NULL, 0, 'Pile the meringue on top of the lemon curd and use the back of a hot, wet spoon to create a wavy texture with peaks and valleys.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'325EE6D3D995844B856DAB776E5D7E9F', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 4, NULL, NULL, 2, NULL, 0, 'Set an oven rack in the middle and place the pie on it so that the pie is 3 to 5 inches from the top. Turn the broiler on high and broil just until the golden brown, about 1 to 3 minute. Watch closely as broilers vary widely and could take less or more time.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63F3460B3A939943845602B48C771C14', X'07AC6B9D046DA9428A8BDE5A0C26F404', X'07AC6B9D046DA9428A8BDE5A0C26F404', 5, NULL, NULL, 2, NULL, 0, 'Transfer the pie to the fridge and chill for at least 4 hours. The pie will keep for up to 4 days, covered in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F29C1E6EF2A864B8F51088821DF80BA', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 10` round baking dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4235DDEABF14E049BE0C73A386D230CE', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the flours, baking soda, 1 tsp cinnamon, 1/2 tsp nutmeg, and 1/2 tsp salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7B340FCFC42894E9B71D2CF70FF952A', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 2, NULL, NULL, 2, NULL, 0, 'In the mixing bowl of a stand attachment, cream together 1/2 cup butter and 1/2 cup brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80CF62F91DF5F84282666953DA758471', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, one at a time, beating after each addition.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F18AB0C50C67244B8739D42A2AC2443', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 4, NULL, NULL, 2, NULL, 0, 'With the mixer on low, alternative between adding the flour mixture, maple syrup, and Greek yogurt. Beat in vanilla extra.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'03B4A7E71A60C24EB3D3C6FE72BDBE5E', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 5, NULL, NULL, 2, NULL, 0, 'Turn mixer up to medium and beat until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'087152D2A62C254BAC3947873D951BDB', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 6, NULL, NULL, 2, NULL, 0, 'Fold in apple chunks and transfer to greased baking dish. Spread out evenly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C201763B96C8D441A26DAC3E78DF4010', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 7, NULL, NULL, 2, NULL, 0, 'Combine 1/2 cup brown sugar, 1/2 cup all-purpose flour, walnuts, and cinnamon in a small bowl. Use your clean hands to work the cold butter into it until a coarse consistency has formed. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7F296D1F82EF2C4B9A05958A56071376', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 8, NULL, NULL, 2, NULL, 0, 'Grab 1/4 thinly sliced apple (trying to keep the pieces grouped together) and lightly press it down on top of the batter. Repeat with the rest of the apple slices, alternating directions and filling any gaps with extra slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05184AD7BDAF8F4C9468F7E0A7C1496F', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the brown sugar topping mixture over the top and transfer to the oven.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2FA504E50FC18A4EA10256301E001899', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 to 35 minutes or until browned completely on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D92D8287F9491E4DB97F46986AE71431', X'806FF517F467E94E827A0BD15BA5B816', X'806FF517F467E94E827A0BD15BA5B816', 11, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool on an wire rack for at least ten minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0784BDA80BAC9B4A8312DE16FDC0E18B', X'8D28E85D63260C48B9FE469C5FC42167', X'8D28E85D63260C48B9FE469C5FC42167', 0, NULL, NULL, 2, NULL, 0, 'Cut the goat cheese log into 1 ounce rounds (you should have 8 pieces total). Dip each piece of the goat cheese slice into the beaten egg followed by the breadcrumbs (you may need to press the goat cheese slightly into the breadcrumbs to help them stick), making sure both sides of the slices are coated with egg and breadcrumbs. Transfer to a plate and stick in the freezer to chill for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DDF0C3C0C9329F488543AFE4E71E1D1E', X'53FD79FAF30F8E4083923DE81B3FB004', X'53FD79FAF30F8E4083923DE81B3FB004', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the vinegar, mustard, maple syrup, dash of salt and dash of pepper. Slowly stream in the olive oil while whisking to incorporate it into the dressing. Add the golden raisins and toss so they are coated with the dressing. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63AB7C0EF23AD34E994EFB668D8DDEAD', X'53FD79FAF30F8E4083923DE81B3FB004', X'53FD79FAF30F8E4083923DE81B3FB004', 1, NULL, NULL, 2, NULL, 0, 'In a small frying pan, toast the pepita seeds over medium-low until just starting to brown, about 2 minutes. Remove from heat and transfer to the large mixing bowl with the dressing.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2A7C39BA9C8CAB4F850BFF80FF8AD900', X'53FD79FAF30F8E4083923DE81B3FB004', X'53FD79FAF30F8E4083923DE81B3FB004', 2, NULL, NULL, 2, NULL, 0, 'Using the same pan, warm 1 Tbsp brown sugar and 1 Tbsp water over medium-low heat, whisking often, until the sugar is melted, about 30 seconds to 1 minute. Add the pecans and toss to coat. Remove from heat and splash in another tsp of water to deglaze the pan. Sprinkle a little salt over the pecans and toss one more time. Set them on a plate to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3E209E58A208B449769EDD8842F6E86', X'53FD79FAF30F8E4083923DE81B3FB004', X'53FD79FAF30F8E4083923DE81B3FB004', 3, NULL, NULL, 2, NULL, 0, 'Wipe the pan clean and put back on the stovetop over medium heat. Pour in 1 Tbsp olive oil and add enough goat cheese rings to fill the pan but not crowd it (none should be touching). Cook for for 30 seconds to 1 minute or until turning golden brown. Carefully flip and cook for another 30 seconds to 1 minute on that side until golden brown. Transfer to a plate and repeat with the rest of the goat cheese slices, adding more olive oil (1 Tbsp at a time) as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CDDE61F3F54D7C4C835EEEDE14E17789', X'53FD79FAF30F8E4083923DE81B3FB004', X'53FD79FAF30F8E4083923DE81B3FB004', 4, NULL, NULL, 2, NULL, 0, 'Add the apple slices and lettuce mix to the mixing bowl with the dressing and toss until everything is coated. Transfer to a large serving platter or bowl and top with the brown sugar pecans and goat cheese fritters. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F8FA14AB9C5564297E14E626686D559', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees. Toss the apples with the melted butter and apple cider vinegar in a large 12 inch baking dish and spread into an even layer.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FEBBF44F57979A43913D4B4C94C9AAF4', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the two sugars, cinnamon, cardamom, nutmeg, and salt. Sprinkle over the apples and toss until all the apples are coated with the mixture.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C609C2A660759641A2DF5A9DAEE33B5A', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 2, NULL, NULL, 2, NULL, 0, 'Bake for 40 to 45 minutes, tossing halfway through, or until the apple pieces are so soft that they fall apart when poked. Remove from the oven and let cool slightly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8EA19B65DA83B347B3A65FF13C441ECC', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 3, NULL, NULL, 2, NULL, 0, 'To create a chunky butter, use a potato masher or large fork to smash the apples into a chunky butter. To create a smooth butter, carefully transfer the apples (along with all the juices) to a blender and blend on low until smooth (don''t turn the mixture up too high and keep the lid loose so that the steam can escape since the mixture will be hot.)');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9585A734C6B16B468CCC93FEE9EE35B4', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 4, NULL, NULL, 2, NULL, 0, 'Taste your apple butter to decide if it''s the consistency you''d like it. If you''d like it thicker, transfer to a small saucepan and cook over medium-low heat for 10 to 15 minutes, stirring often, until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C523F17D4055F540BCC1AAF48F0D2AE7', X'214394E4CF15864E94DF82231289A8EA', X'214394E4CF15864E94DF82231289A8EA', 5, NULL, NULL, 2, NULL, 0, 'Serve right away or let cool completely before transferring to an airtight container and storing in the fridge for up to 3 days.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'591253359F316A4DB0992303DC9331B8', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees and place a 12 piece muffin tin into the oven to pre-heat with it.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F56EBADBEC4AA4BB4D7AEC8750D66F9', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 1, NULL, NULL, 2, NULL, 0, 'In a microwave safe bowl, heat the milk for about 1 minute or just until it''s room tempature.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'193E9FB13F574C4F89562770E70EC820', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 2, NULL, NULL, 2, NULL, 0, 'Starting with the liquid ingredients, add milk, 2 Tbsp melted butter, eggs, all-purpose flour, rye flour, and salt to a high speed blender and bled until the batter is completely smooth.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EBE8E5E5F48F93428C47AC0081BF805B', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 3, NULL, NULL, 2, NULL, 0, 'Being careful not to burn yourself but working quickly, use an oven smitten to remove the muffin tin from the oven and use a pastry brush to brush each muffin cup with remaininng 2 Tbsp butter. Add 1/3 cup of batter to each muffin cup and bake for 22-25 minutes or until puffed and starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3AC1B083B787845A02FE7DC3337E3FC', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and use a knife to poke a small hole in the middle of each popover. Add in 1 tsp of shredded cheddar cheese to each popover and set aside for a few minutes to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE858AA5BAC2764E92E9EBA956408F5F', X'CE21C8E964B8C345910A2C7B8FA607C7', X'CE21C8E964B8C345910A2C7B8FA607C7', 5, NULL, NULL, 2, NULL, 0, 'When ready to serve, tear the popover in half and smear on 1 tsp apple butter over the melted cheddar cheese. Enjoy!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'72B027A0CCC60A409B2357754433683F', X'27473D27B9307C4193B47C18E9748C45', X'27473D27B9307C4193B47C18E9748C45', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 and grease a 9` circle pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6104BC6E03C5E94C93F4A832726E8BA3', X'27473D27B9307C4193B47C18E9748C45', X'27473D27B9307C4193B47C18E9748C45', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together cornmeal, flour, baking powder, cinnamon, and salt. Make a well in the center of the dry ingredients and add in pumpkin puree, yogurt, honey, butter, and eggs. Whisk together the wet ingredients in the center until combined. Use a spatula to fold the outer dry ingredients into the wet ones just until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC6DB92DE9F0464B89008C1E471D2BA8', X'27473D27B9307C4193B47C18E9748C45', X'27473D27B9307C4193B47C18E9748C45', 2, NULL, NULL, 2, NULL, 0, 'Transfer batter to prepared pan. Lay granny smith apple slices over the top and gently press in (I fanned them out to create a pretty pattern but you can do whatever you’d prefer).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'034746C20B3032438F576B580481765D', X'27473D27B9307C4193B47C18E9748C45', X'27473D27B9307C4193B47C18E9748C45', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes or until the center comes out clean. Cool before slicing into pieces (like you would a cake) and serve with your favorite soup. Drizzle with honey (if desired).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8E272F2FB617BD4682BE85AD9E3584C3', X'5A4ECA66F151F841AB35A55A3D72AB53', X'5A4ECA66F151F841AB35A55A3D72AB53', 0, NULL, NULL, 2, NULL, 0, 'Combine all of the chai concentrate ingredients, except the black tea, into a small saucepan and bring to a boil over high heat. Once boiling, reduce heat to medium-low, cover, and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80B29A489C8AC54AA849BD360B795328', X'5A4ECA66F151F841AB35A55A3D72AB53', X'5A4ECA66F151F841AB35A55A3D72AB53', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tea bags, and let steep for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4FF2DE33635FB64B81CDBDA12299EB08', X'5A4ECA66F151F841AB35A55A3D72AB53', X'5A4ECA66F151F841AB35A55A3D72AB53', 2, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture through a strainer and discard the spices (or save some for garnish). Let cool to room temperature.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D55C4623EDE8744B3B4D5EFF5A7A3F5', X'5A4ECA66F151F841AB35A55A3D72AB53', X'5A4ECA66F151F841AB35A55A3D72AB53', 3, NULL, NULL, 2, NULL, 0, 'Pour 1.5 ounces of spiced rum into 4 cocktail glasses. Add 1/4 cup of both the chai concentrate and apple cider into each glass and garnish with a star anise. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'53AD9B0A25B5CE45A989F88F3C04433D', X'5A4ECA66F151F841AB35A55A3D72AB53', X'5A4ECA66F151F841AB35A55A3D72AB53', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, you can warm the cider and chai in a small saucepan and serve the cocktails warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5AD80B895F26CE4BA69C8F7AC616FDD5', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line two baking sheets with parchment paper. Remove frozen puff pastry from the freezer and let thaw at room temperature for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E33B9563A3B84479D7A11792050482B', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 1, NULL, NULL, 2, NULL, 0, 'Add almond butter to a food processor and turn it on. With it running, slowly pour in Oregon Chai Tea Latte Concentrate and continue to process until completely blended. The end result should be smooth and spreadable but not runny. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD3460D66CD3AD4DA135D6A2C2266275', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 2, NULL, NULL, 2, NULL, 0, 'Core the apples and thinly slice them (I kept each side of the apples slices grouped together so I could easily fan them onto the tarts (see photos). Squirt the lemon juice over the apple slices to keep them from browning.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F2735DDD0AF0F743931F8583C5FBE877', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 3, NULL, NULL, 2, NULL, 0, 'Unfold the puff pastry sheets so that they both lay flat. Use a 3` biscuit cutter to cut circular tart bases and transfer them to prepared baking sheets.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DAF596315CEA78429B0ADB6DB30462C6', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 4, NULL, NULL, 2, NULL, 0, 'Place 1 Tbsp. almond butter mixture into the center of each round pastry piece and then top with 1/4 of apple slices (fanning them as scene in the photo above). Dust each pastry with a dash of cinnamon.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5391ED3F38998E4EA9E2C1EF111DF4A2', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 5, NULL, NULL, 2, NULL, 0, 'Brush the egg wash around the edges of the tarts and place in the freezer to chill for 10 minutes (you''ll want your pastry to be very cold when it goes into the oven to avoid spreading).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E3724690A719244EBAD2423C799F4378', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 6, NULL, NULL, 2, NULL, 0, 'Bake for 20-25 minutes or until the edges are browned and golden. Remove from oven and let cool for 5-10 minutes. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D1AD7B6A51762F4A8A4E15E041D89E83', X'3F444142F392994EA37C47D6ABCAC052', X'3F444142F392994EA37C47D6ABCAC052', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the powdered sugar and 2 Tbsp Oregon Chai Tea Latte Concentrate. If needed, whisk in an additional 1 or 2 Tbsp of concentrate until you end up with a thick but pour-able glaze. Drizzle the glaze evenly over the tarts and serve tarts warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'16E2980B7144744A9692E6C99E2CB3B7', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 10` round baking dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18818582CB489E47821465B0BE7B7D79', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the flours, baking soda, 1 tsp cinnamon, 1/2 tsp nutmeg, and 1/2 tsp salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4318229383888441B71020554DCCD936', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 2, NULL, NULL, 2, NULL, 0, 'In the mixing bowl of a stand attachment, cream together 1/2 cup butter and 1/2 cup brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B520E2ADB668A5419B05D4ABBF4FA628', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, one at a time, beating after each addition.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'746C3501AD6F7646928E33E1F52A550B', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 4, NULL, NULL, 2, NULL, 0, 'With the mixer on low, alternative between adding the flour mixture, maple syrup, and Greek yogurt. Beat in vanilla extra.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9F07B00BAD9FA49B5F76864850DDF80', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 5, NULL, NULL, 2, NULL, 0, 'Turn mixer up to medium and beat until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'287BDA10A339334EBC052880CCB86892', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 6, NULL, NULL, 2, NULL, 0, 'Fold in apple chunks and transfer to greased baking dish. Spread out evenly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7276F66DADB78746A5F761FDF5FAD08C', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 7, NULL, NULL, 2, NULL, 0, 'Combine 1/2 cup brown sugar, 1/2 cup all-purpose flour, walnuts, and cinnamon in a small bowl. Use your clean hands to work the cold butter into it until a coarse consistency has formed. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9D4BAD855AB1714EA83452ECF935468F', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 8, NULL, NULL, 2, NULL, 0, 'Grab 1/4 thinly sliced apple (trying to keep the pieces grouped together) and lightly press it down on top of the batter. Repeat with the rest of the apple slices, alternating directions and filling any gaps with extra slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA37B8BB55070546A7A3BFC823A15D97', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the brown sugar topping mixture over the top and transfer to the oven.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C18C4926F6DF64EB16556CBFE786882', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 to 35 minutes or until browned completely on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B5A6B76341019945AE8903F57900A704', X'1B2BE3138A085E468269DCC7CD4901F9', X'1B2BE3138A085E468269DCC7CD4901F9', 11, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool on an wire rack for at least ten minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'44BE7ED47FF2B3478FA54F84B9ECD035', X'EB76637F64389B49ABBAF533FCAE27C6', X'EB76637F64389B49ABBAF533FCAE27C6', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the all-purpose flour, sage, and salt. Use clean hands to work the butter into the dry ingredients until they are the size of small peas. Drizzle in the ice water and stir until the dough is evenly moistened. Use your hands to press the dough into a ball. Gently press into a disk, wrap in plastic wrap, and let chill in the fridge for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'19501E71DAF0E045B92E7293B4AB6A6F', X'60B81F7B5B892544BEEA9CA8B5FC58DE', X'60B81F7B5B892544BEEA9CA8B5FC58DE', 0, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large saucepan of salted water to a boil over high heat. Add in the potato slices and cook until a fork easily pierces the slices, about 5 to 10 minutes depending on the size of the potato slices. Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC713E1B2953C341A2FC97128DEA3368', X'60B81F7B5B892544BEEA9CA8B5FC58DE', X'60B81F7B5B892544BEEA9CA8B5FC58DE', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the ricotta, 1/4 tsp salt, dash of pepper, and 1 large egg until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'828B21A99A77D54DAC9750E7C9483C35', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees and lightly grease a baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'219C790B10554849877830D0D3CEB95A', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 1, NULL, NULL, 2, NULL, 0, 'On a lightly floured clean surface, roll out the dough into a 12` circle. Transfer the dough to the prepared baking sheet. Spread the ricotta mixture over the dough, leaving a 2` outer ring bare.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18E4D3F4420A9E4EA01DFA35D5268E46', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 2, NULL, NULL, 2, NULL, 0, 'Top the ricotta with slices of potato and apples, overlapping as needed to make them all fit.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A58924F855C7D9428C30DE3D3F0957E5', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 3, NULL, NULL, 2, NULL, 0, 'Fold the bare outer parts over the filling to create a crust. Whisk the remaining egg with 1 Tbsp water in a small bowl and brush over the edges exposed dough.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'862D6E8AF647844682708C734D5DC0B9', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 4, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the crust is golden brown and the filling is starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40A4898A51CAC7439B098385E9534A3D', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, transfer the apple cider to a medium saucepan and bring to a simmer over medium-high heat. Let simmer, stirring often, until the cider has reduced by half, about 10 minutes. Add the mustard and continue to simmer for another 2 minutes. Remove from heat and stir in the butter which should help thicken up the glaze a bit.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B1A822DAE7F6344BA3776A05D921F713', X'B297EBF048F64648A6D5DBB0B42B5B63', X'B297EBF048F64648A6D5DBB0B42B5B63', 6, NULL, NULL, 2, NULL, 0, 'Remove galette from the oven and liberally brush the glaze all over the galette. Let cool for 10 minutes before cutting. Garnish with sage, sprinkle with flaky salt, slice and serve the remaining glaze on the side.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7D0D21E0FB2334FA7F45CF618027C97', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 0, NULL, NULL, 2, NULL, 0, 'Bring a large stockpot of salted water to a boil over high heat. Cook the pasta just shy for a minute or two from what the directions say on the package (it''ll finish cooking in the oven). Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'119E690964CC3E45A831F453141CA23A', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 13×9 inch baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'081094950D17C44C8E5DD408E55025FD', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 2, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium heat, melt 4 Tbsp butter and then whisk the 4 Tbsp all-purpose flour into the butter until a thick paste as formed. While whisking, slowly pour the milk into the mixture so that the butter mixture gets incorporated into the milk. Cook for about 10 minutes, stirring often, or until the mix has thickened enough that it coats the pack of a spoon. Remove from heat and stir in the brown sugar, nutmeg, 1/2 tsp salt, and pumpkin puree. Pour the cooked pasta into the pumpkin sauce and stir until coated (alternatively, if your saucepan isn''t big enough, pour into a large mixing bowl and toss until all the pasta is coated).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CEE1D19D0F385448AF0677A4A1C19CA6', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the ricotta, large eggs, 1/2 tsp salt, 1/4 tsp black pepper, minced garlic, and 2 Tbsp chopped sage.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'46223D1826B2A3449E1C724D1AC4E8FB', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 4, NULL, NULL, 2, NULL, 0, 'Pour about 1/3 of the pumpkin pasta mixture into your prepared baking dish and smooth out into an even layer (no need to be exact). Using about half of the ricotta mixture, place large dollops of the mixture over the pumpkin pasta and then top with half of the torn mozzarella. Layer another 1/3 of the pumpkin pasta over that and top with remaining ricotta. Add the final layer of pumpkin pasta and finish with the remaining mozzarella.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'988089D2CF62F1438E011253D9D809E6', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the top is starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7DF5D55E5E471244A5CC200E897CF7FF', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small skillet, heat the remaining 2 Tbsp unsalted butter over medium heat for a few minutes until brown specks start to form. Add the sage leaves and let them fry for 30 seconds or until just starting to turn dark. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E6DC68B07353D34D9442B50852211D83', X'36F13151E1997745A3D7872BA2FFA254', X'36F13151E1997745A3D7872BA2FFA254', 7, NULL, NULL, 2, NULL, 0, 'While the bake is still warm, pour the brown butter and sage over the top of the bake and serve. Feel free to sprinkle red pepper flakes on the top if you''d like a little extra heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80448DD1A8D81642A765DC7B54E008E4', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and grease a 9 inch circle cake pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A9D168311405B54A87EA89585F02F7AD', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan or skillet, warm the 1/4 cup unsalted butter over medium heat. Swirling the butter around the pan slightly, watch it closely and after about two minutes, it should start to bubble. Let it bubble for a minute or two or until you start to see small flecks of brown form on the bottom of the pan. Remove from heat and set aside to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'357C576C4AD05D4AB6ED780B6823B2EC', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the cornmeal, flour, brown sugar, baking powder, cinnamon, nutmeg, ginger, baking soda, and salt until well combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C2BC166F7675DC4687D3787EF0236798', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 3, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients and add in the canned pumpkin, sour cream, eggs, and cooled butter. Whisk the wet ingredients together until combined (it''s okay if some of the dry ingredients get in) and then fold the dry ingredients into the wet just until everything is combined (try to avoid overmixing by only mixing right up until there are no more dry pieces).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26513102B6305E47B842BA400F0646BA', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 4, NULL, NULL, 2, NULL, 0, 'Transfer to the prepared cake pan and smooth the batter so that it''s even in the pan. Bake for 25 minutes or until a toothpick comes out of the center clean.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A35A755A2405264892CA6A267F906873', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 5, NULL, NULL, 2, NULL, 0, 'While the cornbread is baking, toast the pepita seeds in a small dry skillet over medium heat for about four minutes, tossing often, or until they start to darken slightly and become fragnant. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D55409D91184384EBB9C586B4FD36DA5', X'683A57F8A230F74BA03B9F4BBAF07DFD', X'683A57F8A230F74BA03B9F4BBAF07DFD', 6, NULL, NULL, 2, NULL, 0, 'While the cornbread is cooling, use a pastry brush to brush the remaining 1 Tbsp melted butter over the top and sprinkle the pepita seeds. Serve the cornbread on it''s own with more butter or alongside your favorite soups or chilis.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A6DC52F91364B743A0EEF9A383F6FD35', X'D44310CBCEA20144B87619E8CA73AD03', X'D44310CBCEA20144B87619E8CA73AD03', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB3D5F307CFF984285E2BA6C9DABE8B7', X'D44310CBCEA20144B87619E8CA73AD03', X'D44310CBCEA20144B87619E8CA73AD03', 1, NULL, NULL, 2, NULL, 0, 'Add cream cheese, pumpkin, cheddar cheese, paprika, salt, and pepper to a food processor and process until completely combined and a smooth paste remains.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5A8E62017E98B24BBDBCD2C0276FD2CB', X'D44310CBCEA20144B87619E8CA73AD03', X'D44310CBCEA20144B87619E8CA73AD03', 2, NULL, NULL, 2, NULL, 0, 'Using clean and damp hands, divide the dough in half and each half into a circle using the palm of your hands. Transfer to prepared baking sheet and place in the freezer to chill for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'384522AE06509749A9AEB746CA7144CA', X'D44310CBCEA20144B87619E8CA73AD03', X'D44310CBCEA20144B87619E8CA73AD03', 3, NULL, NULL, 2, NULL, 0, 'Mix together the sesame seeds and poppy seeds in a shallow rimmed dish. Remove chilled balls from freezer and roll each ball in the seed mixture until completely coated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'952C9154D959184BB06B562D5CD3E371', X'D44310CBCEA20144B87619E8CA73AD03', X'D44310CBCEA20144B87619E8CA73AD03', 4, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish to serve right away or store in an airtight container in the fridge until ready to serve. Serve with veggies, chips, and crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'71D88354D84FDB46BFCCC229A6C8832C', X'92BF4C032B5FA34F99515D295CB4E197', X'92BF4C032B5FA34F99515D295CB4E197', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 and grease a 9` circle pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'99011F6182C59540B01DC0FA985986C7', X'92BF4C032B5FA34F99515D295CB4E197', X'92BF4C032B5FA34F99515D295CB4E197', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together cornmeal, flour, baking powder, cinnamon, and salt. Make a well in the center of the dry ingredients and add in pumpkin puree, yogurt, honey, butter, and eggs. Whisk together the wet ingredients in the center until combined. Use a spatula to fold the outer dry ingredients into the wet ones just until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9779A5FAA54EBD47805CD0034756C2E7', X'92BF4C032B5FA34F99515D295CB4E197', X'92BF4C032B5FA34F99515D295CB4E197', 2, NULL, NULL, 2, NULL, 0, 'Transfer batter to prepared pan. Lay granny smith apple slices over the top and gently press in (I fanned them out to create a pretty pattern but you can do whatever you’d prefer).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'49706B8D30AF6740A0D3D95FB2B638B4', X'92BF4C032B5FA34F99515D295CB4E197', X'92BF4C032B5FA34F99515D295CB4E197', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes or until the center comes out clean. Cool before slicing into pieces (like you would a cake) and serve with your favorite soup. Drizzle with honey (if desired).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BFD94D9C61A3F74A999CCC8C55390213', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 0, NULL, NULL, 2, NULL, 0, 'Prepare the ziti according to the package direction. Drain and yet aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62FE819FAB3CB14A9272B94893BCAE57', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 1, NULL, NULL, 2, NULL, 0, 'While the pasta is cooking, prepare the caramelized onions: Preheat a large saucepan over medium heat. Add onions, 3 Tbsp olive oil, and a tiny pinch of baking soda (Isa says this will help speed up the caramelization process). Saute, stirring often, for 15 minute or until deeply caramelized. Turn off heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4676DE8B24C89A40B85E87C823E1A08C', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9x13-inch baking dish with olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'47F6852528864D4090FFF18809142293', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 3, NULL, NULL, 2, NULL, 0, 'To make the cashew ricotta: Combine cashews, lemon juice, 2 Tbsp olive oil, garlic cloves, 1 1/2 tsp dried thyme, and 1 1/2 tsp salt in a high speed blender or food processor until a finely chopped mixture is created that looks similar to ricotta.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DFB292E90C588441B4662118770B085B', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together cashew ricotta, maple syrup, paprika, thyme, salt, nutmeg, cayenne, pumpkin puree, milk, and black pepper. Add caramelized onions and pasta and stir until completely combined. Transfer to the prepared basking dish and smooth on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E2C3DDCDB26E641A3FBE33BFCE0BB47', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 5, NULL, NULL, 2, NULL, 0, 'Prepare the breadcrumbs: Combine bread crumbs, walnuts, nutritional yeast, sage, rosemary, and salt. Drizzle with olive oil and toss so that the oil has completely coated the mixture. Sprinkle crumbs over the casserole.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'763C98DEFCE95042B6A8C68110BEE7AB', X'6E2692DB3778BE45A617C6086AF9793C', X'6E2692DB3778BE45A617C6086AF9793C', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, uncovered, or until the crumb is golden brown. Remove from oven, let cool for 10 minutes, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A7498C1EF2C6774093291B3EB4055141', X'8CF8792AB5A68744BE5204FC7C41B543', X'8CF8792AB5A68744BE5204FC7C41B543', 0, NULL, NULL, 2, NULL, 0, 'Shuck the corn and cut the kernels from the core into a medium mixing bowl. Whisk together the corn kernels, basil, scallions, cheese, salt, pepper, and eggs. Fold in the flour just until everything is combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4FD9B13A7825A4597BED8D1CB0E010E', X'8CF8792AB5A68744BE5204FC7C41B543', X'8CF8792AB5A68744BE5204FC7C41B543', 1, NULL, NULL, 2, NULL, 0, 'Heat enough olive oil in a medium pan to cover the bottom over medium heat. Line a dinner plate with parchment paper and set aside. Add 2 heaping tablespoons batter into the warmed oil and press gently to flatten into a fritter. Depending on the size of your pan, you should be able to fit 2-4 fritters into the pan (make sure not to overcrowd – none of the fritters should be touching). Cook until the fritters are golden brown on the bottoms, 1-3 minutes (time will vary depending on how hot your oil gets so check often) and then flip. Cook for an additional 1-3 minutes or until golden on the second side and transfer to the paper towel lined plate. Repeat with the rest of the batter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60B86DB26012544AA0302FF7349A8660', X'8CF8792AB5A68744BE5204FC7C41B543', X'8CF8792AB5A68744BE5204FC7C41B543', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, while the fritters are cooking, use a stand mixer or handheld mixer to whip the butter until light and fluffy. With the mixer on medium, slowly stream in the honey and hot sauce and whip until combined. Transfer to a small serving bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A575D5700A37C48A4BDDBD53367C953', X'8CF8792AB5A68744BE5204FC7C41B543', X'8CF8792AB5A68744BE5204FC7C41B543', 3, NULL, NULL, 2, NULL, 0, 'Serve the fritters warm with dollops of the whipped hot honey butter and a sprinkle of salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AAF5D9618F510B4B9047768B1E2E7D28', X'8484CC2B32D03B44A576F4A3251E9302', X'8484CC2B32D03B44A576F4A3251E9302', 0, NULL, NULL, 2, NULL, 0, 'Bring 4 cups water to a boil over medium heat in a medium saucepan. Add polenta and salt and let come back to a boil. Cover, lower heat to low, and let simmer for 30 minutes, stirring every few minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98730DF83C9009458F005EFB7F9141D5', X'8484CC2B32D03B44A576F4A3251E9302', X'8484CC2B32D03B44A576F4A3251E9302', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, cut a circle opening into the top of each small bell pepper and remove any seeds inside. Transfer to a baking sheet and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76ADBFCE095D1E4D9B6D4F337B24C2B9', X'8484CC2B32D03B44A576F4A3251E9302', X'8484CC2B32D03B44A576F4A3251E9302', 2, NULL, NULL, 2, NULL, 0, 'When polenta is done, remove from heat and stir in 3/4 cup shredded cheddar cheese, cream cheese, mayonnaise, chopped pimentos, garlic powder, and a few grinds of fresh black peper. Taste and add more salt or black pepper, as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'25E1C97C5E2C4049BABD45F98BD196F3', X'8484CC2B32D03B44A576F4A3251E9302', X'8484CC2B32D03B44A576F4A3251E9302', 3, NULL, NULL, 2, NULL, 0, 'Preheat your broiler. Use a large spoon to scoop the polenta into the mini bell peppers so that they are stuffed but not overflowing. Sprinkle the corn and remaining cheddar cheese over the peppers. Place under the broiler for 30 seconds to 1 minute (watch closely!) or until the cheddar cheese on top is browned and crispy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BEC2B5F5604DAC43B32C6EF7DB0ACB2F', X'8484CC2B32D03B44A576F4A3251E9302', X'8484CC2B32D03B44A576F4A3251E9302', 4, NULL, NULL, 2, NULL, 0, 'Remove from heat, sprinkle with dill or chives, and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78F95C42A93BC140B941AB7600ED1C7D', X'68A6C2614D676C45B7960F2AC86DC5E3', X'68A6C2614D676C45B7960F2AC86DC5E3', 0, NULL, NULL, 2, NULL, 0, 'In a high speed blender, blend together the poblano pepper, red onion, garlic clove, vegetable broth, and garlic clove and blend until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40FE5A0737E8E64EAE80F3E2B0521333', X'68A6C2614D676C45B7960F2AC86DC5E3', X'68A6C2614D676C45B7960F2AC86DC5E3', 1, NULL, NULL, 2, NULL, 0, 'Bring salted water to a boil in a medium saucepan. Whisk in the polenta, lower heat to low, and let simmer, stirring often, for 5 minutes or until the polenta is creamy and the liquid is absorbed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F0D707072A4C64F97D489874BF67985', X'68A6C2614D676C45B7960F2AC86DC5E3', X'68A6C2614D676C45B7960F2AC86DC5E3', 2, NULL, NULL, 2, NULL, 0, 'Pour in the poblano mixture and cook for an additional 10 to 15 minutes or until the polenta has thickened, stirring often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D6268980707A7A45922E9896F5856B70', X'68A6C2614D676C45B7960F2AC86DC5E3', X'68A6C2614D676C45B7960F2AC86DC5E3', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat, stir in the cheese, and cover. Let sit for 5 minutes before serving. Taste and season with more salt and pepper, as needed. Feel free to serve with fried eggs, black beans, cilantro, sour cream, tortilla chips, or anything else you might top on tacos and/or nachos!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'88B8ABF173295C4CBF7DEAF669C60BE3', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and grease a 9 inch circle cake pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B00F9D6F880DDA4481EA6F6112E72544', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan or skillet, warm the 1/4 cup unsalted butter over medium heat. Swirling the butter around the pan slightly, watch it closely and after about two minutes, it should start to bubble. Let it bubble for a minute or two or until you start to see small flecks of brown form on the bottom of the pan. Remove from heat and set aside to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA7B5C76D619AF4D902A4D9FB6910963', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the cornmeal, flour, brown sugar, baking powder, cinnamon, nutmeg, ginger, baking soda, and salt until well combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E91B9D0CB8563438CA1572310FC1B2A', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 3, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients and add in the canned pumpkin, sour cream, eggs, and cooled butter. Whisk the wet ingredients together until combined (it''s okay if some of the dry ingredients get in) and then fold the dry ingredients into the wet just until everything is combined (try to avoid overmixing by only mixing right up until there are no more dry pieces).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'92C94E0D537529479F151A86E0B74F11', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 4, NULL, NULL, 2, NULL, 0, 'Transfer to the prepared cake pan and smooth the batter so that it''s even in the pan. Bake for 25 minutes or until a toothpick comes out of the center clean.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C8CC0E99E05494A8F451D0535247D09', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 5, NULL, NULL, 2, NULL, 0, 'While the cornbread is baking, toast the pepita seeds in a small dry skillet over medium heat for about four minutes, tossing often, or until they start to darken slightly and become fragnant. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A2464BFADE837942B17B56C3620EE279', X'7D7D75288FBA4A43A64914F7FA810DF9', X'7D7D75288FBA4A43A64914F7FA810DF9', 6, NULL, NULL, 2, NULL, 0, 'While the cornbread is cooling, use a pastry brush to brush the remaining 1 Tbsp melted butter over the top and sprinkle the pepita seeds. Serve the cornbread on it''s own with more butter or alongside your favorite soups or chilis.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B0703DA921F30241988D40696D620904', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 0, NULL, NULL, 2, NULL, 0, 'Bring a large stockpot of salted water to a boil. Add cauliflower and boil for 5 minutes or until easily pierced with a knife. Use a slotted spoon to remove the cauliflower and transfer to a medium bowl. Add the dried pasta to the same boiling salted water and boil according to the package direction (should be around 10ish minutes). Drain and transfer the pasta to a large mixing bowl.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3003421C7E7FBE4390153D68588AD140', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat olive oil in a large skillet over medium. Add roughly chopped poblano peppers, onions, and garlic and saute for 5 minutes or until starting to soften. Add a dash of salt, smoked paprika, and cumin and saute for another 30 seconds. Remove from heat and transfer to a high speed blender. Set aside and let cool for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CBF13E7D9B60224581222E953E85C226', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 2, NULL, NULL, 2, NULL, 0, 'Once cooled, add the boiled cauliflower, cilantro and 1 cup of milk and blend on high speed until smooth.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EAB9441B346D154E8346166CD27C396F', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 3, NULL, NULL, 2, NULL, 0, 'Wipe clean the large skillet used for the poblano mixture and melt 2 Tbsp butter in it over medium heat. Add flour and whisk until a thick paste has formed. Slowly pour 1/4 cup milk into the skillet to create a thick sauce. Slowly pour the poblano blender mixture and remaining 1/4 cup milk into the sauce. Turn the heat up to medium-high and, stirring contantly, mix until bubbles start to form. Pour in the cheddar cheese and stir until completely melted. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'09D3DF793D97FC44A8018EF9632D70DD', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 4, NULL, NULL, 2, NULL, 0, 'Pour sauce over the pasta and mix to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'95E1ADAA623FBF4B82C4FDD3571C2478', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 5, NULL, NULL, 2, NULL, 0, 'Wipe the large skillet clean and melt 1 Tbsp butter in the pan over medium heat. Add corn and saute for 3 minutes or until starting to brown. Transfer to pasta and mix to combine. Transfer to a oven safe pan (such as a dutch oven).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA1E157E5AE0B54D90D252372467F45E', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 6, NULL, NULL, 2, NULL, 0, 'Add remianing 2 Tbsp butter over medium heat (no need to wipe the pan this time) and add breadcrumbs. Toss until the breadcrumbs are completely coated with the butter and remove from heat. Sprinkle evenly over the pasta.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A5313128A0517A4FAC28CCC70CFAAEBB', X'CD0CBCDF74DA4C45BEFCE0384599DED7', X'CD0CBCDF74DA4C45BEFCE0384599DED7', 7, NULL, NULL, 2, NULL, 0, 'Heat broiler on high and transfer pasta to the top rack of oven. Let broil for 30 seconds (check often!) or until the breadcrumbs have browned. Remove from oven and serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F9E8A04145379347B07788A7F0C8DAD6', X'F7B0CC610C485E45BA97A179BA96A158', X'F7B0CC610C485E45BA97A179BA96A158', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 and grease a 9` circle pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E4B0622E43DBB8438F91B403670680C6', X'F7B0CC610C485E45BA97A179BA96A158', X'F7B0CC610C485E45BA97A179BA96A158', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together cornmeal, flour, baking powder, cinnamon, and salt. Make a well in the center of the dry ingredients and add in pumpkin puree, yogurt, honey, butter, and eggs. Whisk together the wet ingredients in the center until combined. Use a spatula to fold the outer dry ingredients into the wet ones just until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DC67312D17100D41A5E5E632DF59502A', X'F7B0CC610C485E45BA97A179BA96A158', X'F7B0CC610C485E45BA97A179BA96A158', 2, NULL, NULL, 2, NULL, 0, 'Transfer batter to prepared pan. Lay granny smith apple slices over the top and gently press in (I fanned them out to create a pretty pattern but you can do whatever you’d prefer).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E2B3530141869242A94FD9A6D56616A7', X'F7B0CC610C485E45BA97A179BA96A158', X'F7B0CC610C485E45BA97A179BA96A158', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes or until the center comes out clean. Cool before slicing into pieces (like you would a cake) and serve with your favorite soup. Drizzle with honey (if desired).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B6D0CFB391DEA14EB07D69EF933D5122', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 9×13 inch baking pan with butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9963F72BE454CE439086CCFE49CDDE82', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 1, NULL, NULL, 2, NULL, 0, 'Tear large pieces of country bread into blender. Cover and pulse 3-4 times or until large breadcrumbs have formed. Transfer to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'504C3ABBAAD2484B860A739428D7D067', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 2, NULL, NULL, 2, NULL, 0, 'Rinse blender and then add milk, 4 Tbsp melted butter, cornstarch, and eggs into blender. Blend until completely incorporated, about 5 seconds. Add frozen corn, cheese, and cream-style corn and pulse twice to incorporate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'43BED40E30D8F2429D4D89E60D0925DD', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 3, NULL, NULL, 2, NULL, 0, 'Pour mixture into prepared baking pan and bake for 45 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'264E8194DB1ABE40A146C6EAB1F82FB2', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat 4 Tbsp butter in a large skillet over medium until the butter has turned down brown and smells nutty, about 4 minutes (watch closely as it will turn past once it starts!). Remove from heat and whisk in chili garlic sauce and honey. Pour corn flakes and breadcrumbs and toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E9BD03E0C33B474EB259E1B079AC29F0', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 5, NULL, NULL, 2, NULL, 0, 'When the casserole has 10 minutes left, remove from oven, sprinkle crunchy topping over the casserole and put back in the oven for the remaining time.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'53A87611DF85754AB67A971C2018A65D', X'C6D4CDA4BCC79F4284612341034FF45E', X'C6D4CDA4BCC79F4284612341034FF45E', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool slightly before serving. Enjoy!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0BE7FB5CAC77E14E836C35290A327FEF', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp olive oil in a large stockpot over medium heat. Add onions and sauté for 5 minutes or until softened. Add garlic and sauté for another 30 seconds. Then add the ground cumin and smoked paprika and sauté for an additional 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'954A06543A23A541AFDC604DAFD57F2F', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 1, NULL, NULL, 2, NULL, 0, 'Set aside 1/2 cup of the pinto beans. Pour the whole can of chilis (including all the juices in the can) into the pan to deglaze it and then add the fire roasted tomatoes, 2 cups vegetable broth, the remaining pinto beans, and 1 tsp salt. Turn heat to high to bring to a boil. Once the soup is boiling, turn the heat down to medium-low and let simmer for 25 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'663D3CCBD3C76E4F95847087FB4E52E8', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dumplings by whisk toghether the flour, cornmeal, cheddar cheese, baking powder, baking soda, and salt in a medium mixing bowl. Add in the milk and 1 Tbsp olive oil and whisk until a thick dough has formed and no dry ingredients remain. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D5BA5A760EB59E479101A71628FC46C6', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 3, NULL, NULL, 2, NULL, 0, 'Remove the soup from heat and use an immersion blender to blend until smooth. Alternatively, blend the soup in batches in a blender (being careful to leave the lid slightly open so the steam can escape) and return the soup to the stockpot.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26ADB625FD9BB64FA0EA336475B3AFB6', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 cup of vegetable broth and reserved 1/2 cup pinto beans, cover, and bring back to a simmer over medium-low heat, whisking often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'57B3862351C45843B38F61D775812950', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 5, NULL, NULL, 2, NULL, 0, 'Uncover, spoon 1 Tbsp each of the dumpling batter into the soup, and then re-cover. Let the dumplings steam in the soup for 7-10 minutes or until the dumplings are cooked all the way through. If the soup is spitting too much to safely add the dumplings, add a bit more vegetable broth to thin it out.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FBD5312569CEBF4182120346E47509A3', X'BE6DDB64BDDC9643BF3625F5737B7DA7', X'BE6DDB64BDDC9643BF3625F5737B7DA7', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and season with more salt and pepper, as needed. Serve warm with toppings of choice.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E05EADC67281D44DA24499FB494A5F2E', X'5B1A21C4B32F944897EA34B4F2037EC7', X'5B1A21C4B32F944897EA34B4F2037EC7', 0, NULL, NULL, 2, NULL, 0, 'Make sure the parsley is completely dry, Remove leaves from stems and discard stems. Finely chop parsley leaves and then toss with garlic and citrus zest. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9D5A043ECF33F54897B58A87492024E7', X'FCAC7D2191C5524C9193AD4FD45652E1', X'FCAC7D2191C5524C9193AD4FD45652E1', 0, NULL, NULL, 2, NULL, 0, 'Bring 3 cups salted water to a boil in a medium saucepan. Slowly add polenta and reduce heat. Cook, stirring often, for 5 minutes. Remove from heat, cover, and set aside until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C40920E4E2417488129E249E2118FA9', X'FCAC7D2191C5524C9193AD4FD45652E1', X'FCAC7D2191C5524C9193AD4FD45652E1', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. Place veggies and chickpeas on baking sheet and toss with olive oil, smoked paprika, chili powder, salt, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1DB0A8BFD653964AB079BED25C774FBD', X'FCAC7D2191C5524C9193AD4FD45652E1', X'FCAC7D2191C5524C9193AD4FD45652E1', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20-30 minutes, tossing halfway through, or until the veggies are softened and starting to brown around the edges.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'40A53E8AF41FAC44A0E7026E66125A68', X'C66F68788FB51342922BC81C2A13FED6', X'C66F68788FB51342922BC81C2A13FED6', 0, NULL, NULL, 2, NULL, 0, 'Divide the polenta between bowls and then top with roasted veggies, chickpeas, and gremolata. Drizzle with additional olive oil (optional) and season with more salt and pepper. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3BE1E1911806014CB75DA0DA44DA0732', X'D613ACE83E4DD94AADE943CA623B0FCB', X'D613ACE83E4DD94AADE943CA623B0FCB', 0, NULL, NULL, 2, NULL, 0, 'Add tomatoes, corn, bell peppers, and shallots to a large bowl. Stir in salt and let sit for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39651054516A9C4785480E48C58E66D9', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. On the prepared baking sheet, toss the cauliflower with 1 Tbsp olive oil, smoked paprika, Aleppo powder, turmeric, and a sprinkle and salt and pepper. Spread into a single layer and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'549E2D4074153F40867A2D6253E3EB54', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring 1 1/2 cups of salted water to a boil in a medium sauce pan. Add couscous, stir, cover, and remove from heat. Set aside and let steam for 10 minutes before fluffing with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05648CBDD9CD4B4A9FA97FAECD1A1E59', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing ingredients, adding water (1 Tbsp at a time), if the dressing is too thick. It should be easy to pour over the salad. Season with salt and pepper and set aside. Add to the fluffed couscous and toss to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22722399F732EC459071833ADF28962B', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', X'A64E1CAA552C6D4CBABF67B2EFA4E3CE', 3, NULL, NULL, 2, NULL, 0, 'Once everything is done cooking, add roasted cauliflower, dates, parsley, and black sesame seeds to the couscous. Season with salt and pepper. Can be served warmed or chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18C191136987B447823CC5D1F8D5C389', X'EEE5D1D41503E84C9CE3A70EA039E167', X'EEE5D1D41503E84C9CE3A70EA039E167', 0, NULL, NULL, 2, NULL, 0, 'Arrange slices of heirloom tomatoes and cherry tomatoes on a serving platter in a single layer (there can be some overlap but most should be in a single layer). Sprinkle torn basil, nuts, and sesame over the tomatoes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'013703528A60DE46AEA8177CC431EB6C', X'2332D48AC00E85458908CC71E3F6845D', X'2332D48AC00E85458908CC71E3F6845D', 0, NULL, NULL, 2, NULL, 0, 'Whisk together lemon juice, zest, and honey. Drizzle the olive oil into the lemon juice mixture until completely mixed. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7D35824020AF54DA87A6166BEF6A5D9', X'2332D48AC00E85458908CC71E3F6845D', X'2332D48AC00E85458908CC71E3F6845D', 1, NULL, NULL, 2, NULL, 0, 'Drizzle 1/2 the dressing over tomatoes and let sit for at least 10 minutes so they can absorb the flavor. Drizzle remaining dressing over salad right before serving.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FF675A3DC39CD1479F7D6462E8DE35BE', X'2332D48AC00E85458908CC71E3F6845D', X'2332D48AC00E85458908CC71E3F6845D', 2, NULL, NULL, 2, NULL, 0, 'Enjoy as is or serve with toasted bread.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8FC86AE9A31D96439FCFB0043DEEA084', X'F94281739D670148989614921A46DF68', X'F94281739D670148989614921A46DF68', 0, NULL, NULL, 2, NULL, 0, 'Remove the dressing, asiago cheese, and croutons from the the Taylor Farm''s Kale Chopped Salad Kit and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62C9E68ADF00404EB9C1A84A4D365EDE', X'F94281739D670148989614921A46DF68', X'F94281739D670148989614921A46DF68', 1, NULL, NULL, 2, NULL, 0, 'Add chopped salad to a serving bowl and mix in the croutons, tomatoes, and cherries.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0336FA2CD66045468D441E3931E89AB5', X'F94281739D670148989614921A46DF68', X'F94281739D670148989614921A46DF68', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the dressing packet that came with the salad kit over the salad and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F678C18B4D67764CB9AC410E2E1A3D07', X'D54E2E611D00BC42BB5B1018B0D24E60', X'D54E2E611D00BC42BB5B1018B0D24E60', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. In a small mixing bowl, whisk together the smoked paprika, dried garlic, ground mustard, 1/2 tsp chopped fennel seeds, and 1/2 tsp black pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA9868D67CA70B45B776E3C140889284', X'D54E2E611D00BC42BB5B1018B0D24E60', X'D54E2E611D00BC42BB5B1018B0D24E60', 1, NULL, NULL, 2, NULL, 0, 'Place the carrot coins onto the prepared baking sheet and toss with 1 Tbsp olive oil to coat. Add spices and toss until all the carrot coins are evenly coated. Spread carrots into an even layer on the baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'19A3751EA478C24B902207911D338521', X'D54E2E611D00BC42BB5B1018B0D24E60', X'D54E2E611D00BC42BB5B1018B0D24E60', 2, NULL, NULL, 2, NULL, 0, 'Roast on the middle rack for 15 minutes, flipping after 10, or until the coins are easily pierced with a fork (watch them closely as they may cook faster or a little longer depending on how thick you chopped them).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'09BDD1536DF8674CA77EB2B0DAB8F2FA', X'EA36313887FBF1439C6BED9ABB4B7BB0', X'EA36313887FBF1439C6BED9ABB4B7BB0', 0, NULL, NULL, 2, NULL, 0, 'While those roast, in a small bowl, whisk together ¼ cup red wine vinegar, ¼ cup water, and salt. Add thinly sliced onions and set aside to pickle while preparing the rest of the salad.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ABBD08335B98714CA079DC921C5E0875', X'51C7E2FE71DCC34B8E7E41C6F0299C2B', X'51C7E2FE71DCC34B8E7E41C6F0299C2B', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together fennel seeds, orange juice, zest, vinegar, salt, and pepper. While whisking, slowly pour olive oil into the dressing until it’s emulsified. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2AF6D93232DC524A9A1E7A604EB4BCEF', X'9B699F66A4242F4AAB30837A6F9E22E4', X'9B699F66A4242F4AAB30837A6F9E22E4', 0, NULL, NULL, 2, NULL, 0, 'Drain the red onions and toss in a large serving bowl with the iceberg lettuce, carrot coins, olives, banana peppers, artichoke hearts, cherry tomatoes, roasted red pepper, and dressing. Sprinkle with feta and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6DF096B3941E2C439897CEDC2B020F4F', X'9346EB64F7F32A4EB3D9D5F8282F42DB', X'9346EB64F7F32A4EB3D9D5F8282F42DB', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a small skillet over medium heat. Add the garam masala or curry powder and garlic and sauté for 1 minute or just until the garlic starts to brown. Remove from heat and whisk in the lemon juice, vinegar, honey, garlic chili sauce (or red pepper flakes), and a pinch of salt and pepper. Toss in the golden raisins and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F551C26E4AB69B4A8DF703EB58B1CEF1', X'9346EB64F7F32A4EB3D9D5F8282F42DB', X'9346EB64F7F32A4EB3D9D5F8282F42DB', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss together the broccoli, pistachios, and cilantro until combined. Pour the dressing and golden raisin mixture into the broccoli mixture and toss to combine. Taste and season with more salt, pepper, or red pepper flakes, as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9E93515A9AD4BE40ADED93021DEFF654', X'9346EB64F7F32A4EB3D9D5F8282F42DB', X'9346EB64F7F32A4EB3D9D5F8282F42DB', 2, NULL, NULL, 2, NULL, 0, 'Enjoy right away or store in an airtight container in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05038315CE2D054297A1A18C05F5D99B', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil. Add the potatoes and boil for 10 to 15 minutes (this will vary depending on the size) or until they are easily pierced with a fork. Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A62D1D674529914AB87F4E719A1A3F76', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 400 degrees. Rip a piece of aluminum foil double the length of the scallions and lay on a baking sheet. Add scallions and garlic over the foil and toss with 1 Tbsp olive oil and a sprinkle of salt and pepper. Fold the aluminum foil over the scallions and garlic and roast for 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'35BC422E66D7AC4F89CD07E16C3441F8', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 2, NULL, NULL, 2, NULL, 0, 'While the scallions are roasting, toss together the shallot pieces, lime juice, mustard, 1/2 tsp salt, and a dash of pepper into a medium mixing bowl and set aside to marinate together while the scallions roast.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D043BFB1D21C445B440F3B96B32AF7E', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 3, NULL, NULL, 2, NULL, 0, 'Once the scallions are done, remove from the oven and let cool enough to handle. Transfer scallions and garlic (removing the outer skin of the garlic) to a cutting board and finely chop. Add to the shallot mixture. Set a few cilantro and parsley leaves off to the side for garnish and fold the remaining ones into the verde sauce.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C5E934CA66FBE4896452D7ECF7A449F', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss together the potatoes, bell peppers, 1/2 tsp salt, and pepper. Transfer to a serving bowl and drizzle with the scallion salsa verde sauce. Taste and season with more salt and pepper, if needed. Top with the remaining cilantro and parsley leaves as garnish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'705C8A033F8EFC4089321E1B6B6D822E', X'A72128A0081EED41ACDA13062824DE2F', X'A72128A0081EED41ACDA13062824DE2F', 5, NULL, NULL, 2, NULL, 0, 'Serve right away or cover and transfer to the fridge to store until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4C088E846F486742A38106C56475E058', X'7DF575DEBB54BE46A10F11E871A389A9', X'7DF575DEBB54BE46A10F11E871A389A9', 0, NULL, NULL, 2, NULL, 0, 'Start by whisking together the yogurt, whole milk, mayonnaise, salt, and dash of pepper until smooth. Fold in the crumbled blue cheese. Transfer to the fridge while you prepare the rest of the salad.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B3A8A97C3819A34D981C87D7E4C53303', X'7DF575DEBB54BE46A10F11E871A389A9', X'7DF575DEBB54BE46A10F11E871A389A9', 1, NULL, NULL, 2, NULL, 0, 'Transfer the kale, 1 Tbsp olive oil, and 1 tsp lemon juice to a large serving bowl and use clean hands to massage it for a minute or two to softened up and break down the kale a bit.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'123CCF1E44E1C044A638655476A1DD38', X'7DF575DEBB54BE46A10F11E871A389A9', X'7DF575DEBB54BE46A10F11E871A389A9', 2, NULL, NULL, 2, NULL, 0, 'Toss the carrot coins, radish slices, and celery pieces into the mixing bowl with the kale and toss to mix.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AC873627E859774B97B1D40E1EDE5171', X'7DF575DEBB54BE46A10F11E871A389A9', X'7DF575DEBB54BE46A10F11E871A389A9', 3, NULL, NULL, 2, NULL, 0, 'Heat a medium saucepan over medium heat and add in 1 Tbsp olive oil and 3 Tbsp butter. Once melted, add in hot sauce, brown sugar, and 1 Tbsp white wine vinegar and whisk to combine. Let come to a simmer and then toss in the beans. Let cook, stirring often, until the sauce has thickened up and the beans are warmed through, about 3-5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'73FD8D2DBB523B47BFB9E86D4D564158', X'7DF575DEBB54BE46A10F11E871A389A9', X'7DF575DEBB54BE46A10F11E871A389A9', 4, NULL, NULL, 2, NULL, 0, 'Pour the beans (along with any remaining sauce) over the kale salad. Serve right away with the yogurt blue cheese dressing on the side.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B1F2849CED318418D0CFAD38E32A503', X'6ED6428A1E26B04995636D873D055684', X'6ED6428A1E26B04995636D873D055684', 0, NULL, NULL, 2, NULL, 0, 'Add the mango, water, and granulated sugar to a medium saucepan and bring to a low boil over high heat. Once boiling, stir to dissolve the sugar and then remove from heat. Cover and let steep for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A10503059A204042A1817AC77DD40F80', X'6ED6428A1E26B04995636D873D055684', X'6ED6428A1E26B04995636D873D055684', 1, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh strainer to remove the mango solids (which can be discarded). Store syrup in an airtight container in the fridge for up to a week.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3576AF4FA59D94BA78F31D43B960096', X'9002080DEDB8B145BFEC65D2F48AFA23', X'9002080DEDB8B145BFEC65D2F48AFA23', 0, NULL, NULL, 2, NULL, 0, 'Combine the tequila and jalapeño in a shaker and use the back of a spoon or a muddler to smash the jalapeño. If you have the time. let this sit for about 5 minutes to help release the jalepeño juices into the tequila.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4BF1D9C9DA869F4B8A21ED07BABD9D13', X'9002080DEDB8B145BFEC65D2F48AFA23', X'9002080DEDB8B145BFEC65D2F48AFA23', 1, NULL, NULL, 2, NULL, 0, 'Next, fill the shaker with ice and then add in the lime juice and simple syrup. Shake until the shaker is very cold.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6AF1427639E5604DA2EDFAE98363D6FD', X'9002080DEDB8B145BFEC65D2F48AFA23', X'9002080DEDB8B145BFEC65D2F48AFA23', 2, NULL, NULL, 2, NULL, 0, 'Strain into a cocktail glass and garnish. Serve right away!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A8878AC9E68A94CBBF013D19C298C87', X'70EDE84F0D62254CA9373E5690DD80B7', X'70EDE84F0D62254CA9373E5690DD80B7', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a small skillet over medium heat. Add the garam masala or curry powder and garlic and sauté for 1 minute or just until the garlic starts to brown. Remove from heat and whisk in the lemon juice, vinegar, honey, garlic chili sauce (or red pepper flakes), and a pinch of salt and pepper. Toss in the golden raisins and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'75F61C90172E47419FD4BD93443FC7BB', X'70EDE84F0D62254CA9373E5690DD80B7', X'70EDE84F0D62254CA9373E5690DD80B7', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss together the broccoli, pistachios, and cilantro until combined. Pour the dressing and golden raisin mixture into the broccoli mixture and toss to combine. Taste and season with more salt, pepper, or red pepper flakes, as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4891E6D12C3F04FAE2397ED6C4D3CC5', X'70EDE84F0D62254CA9373E5690DD80B7', X'70EDE84F0D62254CA9373E5690DD80B7', 2, NULL, NULL, 2, NULL, 0, 'Enjoy right away or store in an airtight container in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D86A97C0E69DC64192EEDC6C5694F303', X'9413AEA0AA8CFB40B998A624D7D2E064', X'9413AEA0AA8CFB40B998A624D7D2E064', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, gently (be careful not to bruise the peach slices) toss together the shredded cabbage, carrots, peach slices, poblano pieces, and scallions.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4949A8AAD4319B4F8C1B976A26D3F5AD', X'9413AEA0AA8CFB40B998A624D7D2E064', X'9413AEA0AA8CFB40B998A624D7D2E064', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the vinegar, olive oil, lemon juice, hot sauce, honey, 1/4 tsp salt, and dash of pepper. Drizzle over the cabbage mixture and toss until completely coated. Taste and season with more salt and pepper, a needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D13B416B88402E4092602328A63F2590', X'9413AEA0AA8CFB40B998A624D7D2E064', X'9413AEA0AA8CFB40B998A624D7D2E064', 2, NULL, NULL, 2, NULL, 0, 'Cover and let chill for 30 minutes to let the flavors blend. Serve cold.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'24C2FE75D6C0E14A98319694B0222243', X'3E2E670D0C7BB840A7B784EE1392C9EE', X'3E2E670D0C7BB840A7B784EE1392C9EE', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a blender and blend until smooth. If still to thick, add more water (1 Tbsp at a time) until mixture is thin enough to drizzle. Transfer to fridge and let chill until ready to use.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'700D93A8E2492948B1E086B395C7A091', X'2D96ED8394248147AB92DCBA22B7EC11', X'2D96ED8394248147AB92DCBA22B7EC11', 0, NULL, NULL, 2, NULL, 0, 'Heat grill to medium-low.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9CE04BA10A37904CA874B00426EAB4BB', X'2D96ED8394248147AB92DCBA22B7EC11', X'2D96ED8394248147AB92DCBA22B7EC11', 1, NULL, NULL, 2, NULL, 0, 'Either place a vegetable grill rack on the grill or string veggies onto soaked bamboo skewers. Once the grill is heated, add vegetables. If using skewers, flip every 3-5 minutes until browned and cooked to your liking. If using a grill basket, toss the vegetables every 3-5 minutes until cooked to your liking. Once cooked, remove from grill and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CBF82030520F81469D189759986B2015', X'2D96ED8394248147AB92DCBA22B7EC11', X'2D96ED8394248147AB92DCBA22B7EC11', 2, NULL, NULL, 2, NULL, 0, 'Place pita and lemon halves on grill and let cook for 2 minute, rotating the pita after one minute, or until starting to brown. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B0F5D5FAC7DB1499165B5B68959F0FA', X'7CB4B48CCD472D40AB447600D6830D00', X'7CB4B48CCD472D40AB447600D6830D00', 0, NULL, NULL, 2, NULL, 0, 'Divide pita and vegetables between two plates. Drizzle 1/4 cup of sauce onto of each plate of vegetables, squeeze lemon over vegetables, and then garnish with more cilantro. Season with salt and pepper. Serve remaining sauce on the side.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5ECA33644168BF4F8D628391A86D5D82', X'B123755672ECA84CA98E0AD5471367EF', X'B123755672ECA84CA98E0AD5471367EF', 0, NULL, NULL, 2, NULL, 0, 'Bring a small pot of water to a boil and prepare an ice bath. Blanch the basil and mint by dropping it into the boiling water for one minute and then immediately transferring herbs to the ice bath for another minute.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF1F19F0A510DE4FA78F248CA587161F', X'B123755672ECA84CA98E0AD5471367EF', X'B123755672ECA84CA98E0AD5471367EF', 1, NULL, NULL, 2, NULL, 0, 'Remove mint and basil from the water and pat dry with a clean kitchen towel or a few paper towels. Transfer to food processor or blender and add olive oil and a dash of salt. Pulse or blend for 30 seconds or until the herbs are finely chopped. Let sit for at least 5 minutes before straining the oil into a small dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD497DAB1B4B7148AF7AAEBEC91A3B29', X'B123755672ECA84CA98E0AD5471367EF', X'B123755672ECA84CA98E0AD5471367EF', 2, NULL, NULL, 2, NULL, 0, 'Plate the burrata on a serving tray and assemble the plums and tomatoes around it. Garnish with more herbs and drizzle with the basil-mint oil. Finish with pink peppercorn and serve with toasted bread pieces or crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A8B1F7EA4B36140B18776C38378C12B', X'730D481D864C4447A606247E1192A779', X'730D481D864C4447A606247E1192A779', 0, NULL, NULL, 2, NULL, 0, 'Whisk together peanut butter, mascarpone, honey, vanilla extract, and a dash of salt until combined and smoothed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF3F46144E4266438FAD4AE42A0C7390', X'730D481D864C4447A606247E1192A779', X'730D481D864C4447A606247E1192A779', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl and use the back of a spoon to create circular grooves on the top of the dip. Drizzle honey into the grooves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'239094D6C1F7F94B85A418F70FB051B7', X'730D481D864C4447A606247E1192A779', X'730D481D864C4447A606247E1192A779', 2, NULL, NULL, 2, NULL, 0, 'If serving right away, top with dry roasted peanuts and serve with fruit. If storing for later, cover with plastic wrap and stick in the fridge until ready to enjoy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'79D7C311AAB58944B7C7EE0BDE4ABD53', X'45366361625D5946A6C0823759825792', X'45366361625D5946A6C0823759825792', 0, NULL, NULL, 2, NULL, 0, 'Cook the macaroni, salting the water with 2 Tbsp kosher salt, according to instructions on package until tender. Drain, transfer to a large bowl, and set aside for 10 minutes to let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2DDAD51517D6974BAF81745A554D3010', X'45366361625D5946A6C0823759825792', X'45366361625D5946A6C0823759825792', 1, NULL, NULL, 2, NULL, 0, 'Once cooled, fold in onions, 1/2 cup mayonnaise, pickle relish, pickle juice, and a dash of pepper and toss until well coated. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1A3E7B3680B54D498CF3B9B2B1B33958', X'45366361625D5946A6C0823759825792', X'45366361625D5946A6C0823759825792', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, stir in 1/2 cup mayonnaise and the shredded carrot. Taste and add more mayonnaise if it looks dry or more salt if it needs it. Serve chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33601EB50EF956489972A49D5ABCDD83', X'EE951DF199216B468959188E313412EE', X'EE951DF199216B468959188E313412EE', 0, NULL, NULL, 2, NULL, 0, 'Whisk together all dressing ingredients in a small bowl until combined. Your final dressing should be easy to drizzle but not watery. If the dressing is too thick, add 1 tsp of water at a time until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D1294855E25DFE46A0030459030C035E', X'1E16706BA6A2CE4BA54CCEF06EBC1253', X'1E16706BA6A2CE4BA54CCEF06EBC1253', 0, NULL, NULL, 2, NULL, 0, 'Rinse the quinoa thoroughly. Add 2 cups of water and quinoa to a medium saucepan and bring to a boil. Once boiling, cover and turn heat down to low. Let simmer for 15 minutes. Remove from heat and let sit for an additional 5 minutes. Uncover and fluff. Set aside to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'219E0514BB96C94FBCC1E818B14CB530', X'1E16706BA6A2CE4BA54CCEF06EBC1253', X'1E16706BA6A2CE4BA54CCEF06EBC1253', 1, NULL, NULL, 2, NULL, 0, 'Once the quinoa is cooled, toss it with cabbage, sweet pepper, carrot, cilantro, and chives.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3ACE10CA8C8A5F4E8F92C1FF6B8BBCEA', X'1E16706BA6A2CE4BA54CCEF06EBC1253', X'1E16706BA6A2CE4BA54CCEF06EBC1253', 2, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish and drizzle with the dressing. Top with chopped peanuts and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8B712619A185DB4BA664CCE71B759F4F', X'5F35232E231B2C4CA817F7495A55AE5B', X'5F35232E231B2C4CA817F7495A55AE5B', 0, NULL, NULL, 2, NULL, 0, 'Cover and let chill in the fridge for at least 30 minutes before serving to let flavors develop.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5243E809B977F54781016BC5C527DE92', X'432E124E5C5EF746A7F2110506FE65A3', X'432E124E5C5EF746A7F2110506FE65A3', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, fold all the ingredients into each other until completely combined. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2D12609ABD4EB046B5CEC50138C7F175', X'432E124E5C5EF746A7F2110506FE65A3', X'432E124E5C5EF746A7F2110506FE65A3', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4AD9244CAD0F6548A3CEC52A2D8C0F99', X'AAA95EE551DF5046BB3388F03135F9B4', X'AAA95EE551DF5046BB3388F03135F9B4', 0, NULL, NULL, 2, NULL, 0, 'Transfer all ingredients into a food processor and process until completely smooth, about 30 seconds. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3DABD042FF6D4342A23E798D9C222113', X'AAA95EE551DF5046BB3388F03135F9B4', X'AAA95EE551DF5046BB3388F03135F9B4', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl and drizzle with olive oil (optional) and a few turns of freshly ground pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F5D3645F8ED4342A9A366C2FB82AE03', X'4E08FF0EB6A56645B1DD730C165E8E1D', X'4E08FF0EB6A56645B1DD730C165E8E1D', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together cream cheese, yogurt, artichoke hearts, buffalo sauce, and blue cheese.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'46F824468F0E0D4B9631430FF150E58B', X'4E08FF0EB6A56645B1DD730C165E8E1D', X'4E08FF0EB6A56645B1DD730C165E8E1D', 1, NULL, NULL, 2, NULL, 0, 'Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'618B74DBDF2B364391DB5DA2ACFB0489', X'4E08FF0EB6A56645B1DD730C165E8E1D', X'4E08FF0EB6A56645B1DD730C165E8E1D', 2, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl and drizzle with olive oil (optional), buffalo sauce, and a few sprinkles of blue cheese.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA60E84E060C234AAB1627E28D342DBC', X'8F39AE2567133243870C1A38A351B44F', X'8F39AE2567133243870C1A38A351B44F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees and butter 3 miniature muffin tins (you can also work in batches if you don’t have 3). Warm the milk slightly by transferring to a large microwave safe mixing bowl and microwave for about 1 minute.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'11E0F79F613AA0408203814E19BE8C4C', X'8F39AE2567133243870C1A38A351B44F', X'8F39AE2567133243870C1A38A351B44F', 1, NULL, NULL, 2, NULL, 0, 'Add eggs, melted butter, flour, 1/4 cup shredded cheese, and salt and whisk just until combined (it’ll be lighter the less it’s mixed). Transfer to the fridge and let chill while you prep the pans.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'019FDAEBBBEA23489663BA16016782C8', X'8F39AE2567133243870C1A38A351B44F', X'8F39AE2567133243870C1A38A351B44F', 2, NULL, NULL, 2, NULL, 0, 'Place pans in preheated oven for 2 minutes (but not much longer or the butter you greased with will smoke). Once warmed, carefully remove from oven and pour the batter into the muffin tin so each is about 3/4 full. Bake for 18-20 minutes or until brown on the top. If working in batches, transfer the leftover batter to the fridge to chill while the rest of the muffins bake.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'34971EFF7703B348BF63D08EC54CC0F0', X'8F39AE2567133243870C1A38A351B44F', X'8F39AE2567133243870C1A38A351B44F', 3, NULL, NULL, 2, NULL, 0, 'Once browned, remove from oven and transfer to a wire rack. Repeat with remaining batter (if working in batches). Once cooled slightly, transfer to serving platter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'93695ADD9D9B7944820226788575BE53', X'16EFF620D20E7F4C9A181BC5305490DA', X'16EFF620D20E7F4C9A181BC5305490DA', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, use a spatula to fold the lemon zest and dill into the butter. Transfer to a serving dish and place on platter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6804B12C2C8D9243A6B218B6CF05F26D', X'DD1B83644313E949B5AB393028982A06', X'DD1B83644313E949B5AB393028982A06', 0, NULL, NULL, 2, NULL, 0, 'Toss apple slices with lemon juice and transfer to serving platter. Transfer the jam and honey to serving bowls and add to your platter. Transfer cheese to serving platter making sure to leave space between them. Fill in remaining space on the board with veggies and crackers. Serve!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9EA708E197F8394DB5ED2E42D8932174', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F and grease a 9-inch circle baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C87A540C1DB5294489B77FD9CBDB6D52', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine rhubarb, strawberries, granulated sugar, and 1/3 cup flour. Transfer to prepared baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'310434302BCA1E49BB918E300B5A0D6C', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl, mix together remaining flour, brown sugar, oats, cinnamon, and nutmeg.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A504B65BE3384044AC3BDD625902E9BB', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 3, NULL, NULL, 2, NULL, 0, 'Use clean fingers to incorporate the butter into the flour mixture until it has the consistency of course cornmeal. Sprinkle mixture over strawberry-rhubarb mixture making sure to cover the entire top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF1160571E1A034DA1C5290365E7918F', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 4, NULL, NULL, 2, NULL, 0, 'Place pan on a baking sheet (in case anything boils over) and bake for 30-35 minutes, or until the sides are bubbling and the top is browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'885CC98ED26E27488BB65854C478DB19', X'8FE6DEEF9B27224485D552A28F9180BB', X'8FE6DEEF9B27224485D552A28F9180BB', 5, NULL, NULL, 2, NULL, 0, 'Serve warm with ice cream.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69C808B4CBC59B46922EC14FF4F2E9C2', X'023F7B491AAB424FA7D5DB33663B295C', X'023F7B491AAB424FA7D5DB33663B295C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DFA2F37B03E3DB48ACD20D5259B74E99', X'023F7B491AAB424FA7D5DB33663B295C', X'023F7B491AAB424FA7D5DB33663B295C', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix together the strawberries, rhubarb, sugar, vinegar, and salt. Transfer to baking sheet and spread into an even layer');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ACD1946C5B364D49BB57C48B3B042B92', X'023F7B491AAB424FA7D5DB33663B295C', X'023F7B491AAB424FA7D5DB33663B295C', 2, NULL, NULL, 2, NULL, 0, 'Place orange slices and thyme sporadically around the strawberries and rhubarb (it will release juices as it roasts).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60C5D79FE8DC9A4B9DD2B5039963C958', X'023F7B491AAB424FA7D5DB33663B295C', X'023F7B491AAB424FA7D5DB33663B295C', 3, NULL, NULL, 2, NULL, 0, 'Roast for 30 minutes or until the rhubarb is easily pierced with a knife. Remove from oven, transfer the orange slices and thyme to another try (to reserve for garnish), and set aside until the grits are ready.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69FCBBD761C24C4BB65C9D4056EEFE10', X'D3F83EA262F7FB4E8A118103D95801DE', X'D3F83EA262F7FB4E8A118103D95801DE', 0, NULL, NULL, 2, NULL, 0, 'Bring water to a boil in a large saucepan with high edges. Once boiling, add the grits and salt and turn the heat down so they simmer. Cook for about thirty minutes, stirring often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9AB9786188484D4CB403111C345F383C', X'D3F83EA262F7FB4E8A118103D95801DE', X'D3F83EA262F7FB4E8A118103D95801DE', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in the butter until melted. Taste and season with more salt if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E21DDB8A1624C4D8441BFACD4719EF0', X'9DB24951B042354BA82C6A48BD525A0E', X'9DB24951B042354BA82C6A48BD525A0E', 0, NULL, NULL, 2, NULL, 0, 'Divide the grits between three serving bowls and top with the roasted strawberries and rhubarb. Drizzle with heavy cream or yogurt. Sprinkle with toasted pecans and garnish with thyme leaves and an orange slice. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'78F69AD54AB20F47826C303F41F16DD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9 inch baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'28931D6A8EC34140963B32C56D3FC515', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, fold together the strawberries and rhubarb. Add in orange juice, orange zest, sugar, and cornstarch and mix until combined. Transfer to prepared baking pan and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B2DB78ED44634C41A8370E025AB8FD7B', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl (or clean out the one you just used), whisk together the chopped pecans, flour, baking powder, and salt. Add the butter and use cleans hands or a fork to work the butter into the flour mixture until all the butter pieces are pea sized. Drizzle in the milk and mix just until a dough forms.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6754E9B7623FD24EAB8511721C2C9FA6', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 3, NULL, NULL, 2, NULL, 0, 'Scoop the dough over the strawberry rhubarb mixture so that you end up with 7 large biscuits evenly arranged on the top (it''s okay if some of the strawberry rhubarb mixture is still showing).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'151BA1E021AB72448BE8EEA7A4E0E7DB', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 4, NULL, NULL, 2, NULL, 0, 'Place the prepared pan onto a baking sheet (to avoid any bubbling over from falling to the bottom of the oven) and transfer to the oven to bake. Bake for 45-55 minutes or until the biscuits are browned and the filling is bubbly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3582A01509E89742BC5CECAC7E06CB05', X'A46BF54EB9DF4E4CABFADF50A164EDD8', X'A46BF54EB9DF4E4CABFADF50A164EDD8', 5, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool for 10 minutes before serving with Hudsonville''s Dairy Free Birthday cake and more toasted pecans.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB362C3E65B1F94BAC1465367B5695E2', X'16643AE1F4F0A24B966940C11F39C41B', X'16643AE1F4F0A24B966940C11F39C41B', 0, NULL, NULL, 2, NULL, 0, 'In a large dutch oven or skillet, heat oil over medium heat. Add shallots and garlic and saute for 5 minutes or until onions are translucent. Add couscous and 1/2 tsp salt and saute for another minute to toast them.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'807588983871B94BBF986C686989A3D0', X'16643AE1F4F0A24B966940C11F39C41B', X'16643AE1F4F0A24B966940C11F39C41B', 1, NULL, NULL, 2, NULL, 0, 'Add wine and 1 cup of water and let cook until the liquid has been absorbed, stirring often. Add 1 more cup of water and let cook until the liquid has been absorbed, stirring often. Add remaining cup of water and frozen peas and let cool until absorbed, stirring often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F8E46D3E045C245826EBBB2DED9D369', X'16643AE1F4F0A24B966940C11F39C41B', X'16643AE1F4F0A24B966940C11F39C41B', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and fold in the cheese and lemon zest. Taste and add more salt or cheese, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A08D195F8C78F3419DB1ED69BF77029B', X'16643AE1F4F0A24B966940C11F39C41B', X'16643AE1F4F0A24B966940C11F39C41B', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, toss together the radicchio, walnuts, dill, 2 Tbsp olive oil, and lemon juice in a small mixing bowl. Top over the couscous mixture and sprinkle with pepper and red pepper flakes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8811754DFACE2748A73C7733C94759DE', X'16643AE1F4F0A24B966940C11F39C41B', X'16643AE1F4F0A24B966940C11F39C41B', 4, NULL, NULL, 2, NULL, 0, 'Taste and add more salt, pepper, or red pepper flakes, as needed. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F91980572CFD2C41917F342207772E2F', X'53815A77039D284C910FDD6CC99299DF', X'53815A77039D284C910FDD6CC99299DF', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium heat, warm the olive oil. Add the onion and bell pepper pieces and sauté until the vegetables have softened, about 5 minutes. Add the garlic and sauté for another 30 seconds or until the garlic is very fragrant.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1839FABABC68B4ABF04C3236FCDEE6B', X'53815A77039D284C910FDD6CC99299DF', X'53815A77039D284C910FDD6CC99299DF', 1, NULL, NULL, 2, NULL, 0, 'Add the chili powder, brown sugar, smoked paprika, cumin, and cayenne, stirring to coat the vegetables with the spices, and sauté for 30 seconds more. Pour in 1/2 cup vegetable broth and use your spatula or wooden spoon to deglaze the pan so all the spices sticking to the bottom are released. Remove from heat and transfer the mixture into a medium size slow cooker (mine is 7 quarts) and add in the rinsed beans, fire roasted tomatoes (along with their juices), and remaining vegetable broth. Cover and cook over high for 4-5 hours.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6E41986628AFF04BB45A109774C67EB2', X'53815A77039D284C910FDD6CC99299DF', X'53815A77039D284C910FDD6CC99299DF', 2, NULL, NULL, 2, NULL, 0, '30 minutes before serving, season with 1/2 tsp salt and let cook for 30 more minutes. Turn the heat off and squeeze in the lime juice, and sprinkle in black pepper and lime zest. Taste and add more salt or black pepper, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BF4B29BF4EF63D4EAB910D5D9CE819FC', X'53815A77039D284C910FDD6CC99299DF', X'53815A77039D284C910FDD6CC99299DF', 3, NULL, NULL, 2, NULL, 0, 'Serve warm with optional toppings such as sour cream, avocado, cheddar cheese, tortilla chips, hot sauce, chopped chives, chopped cilantro leaves, sliced jalapeño pieces (fresh or pickled).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B68BFDAECA994B4383CB19ADCA32DA0A', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 0, NULL, NULL, 2, NULL, 0, 'Add the onions and carrots with a pinch of salt and let cook for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7F6ACED934731047B24BB2A405E073E1', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 1, NULL, NULL, 2, NULL, 0, 'Next, add the garlic, cumin, and curry powder and continue to cook for another two minutes. Pour in the vegetable stock (or water) and bundle of spices and bring to a boil. Once boiling, lower to a simmer and leave it alone to cook slowly for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'128187CDDFE1CB46B9CADCDDF0DDA5BD', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 2, NULL, NULL, 2, NULL, 0, 'In the meantime, place the chickpeas in the preheated oven and let roast for 20-25 minutes (shaking them around every 10 minutes to ensure they don''t burn). Once crispy, remove from oven and let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CE3E2DF88BB5634FB7DCB7045D06DE6A', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 3, NULL, NULL, 2, NULL, 0, 'For the quick pickled red onion topping: combine the white wine vinegar, water, and 1/2 tsp salt in a small bowl and submerge the thinly sliced onions in the mixture. Let sit while you prepare the rest of the soup (at least 10 minutes and up to an ahour). Drain and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'94E05DFADC921B4FBA04ADA405174366', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 4, NULL, NULL, 2, NULL, 0, 'Return to heat and let it cook for five minutes more while you check the flavor (add more salt or pepper, if needed).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F9F86C1E00D02B43AF05AEB0147A3743', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in the heavy cream or yogurt, if using.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'01A42A721EF8054F9E00A2B057F855B5', X'B21E6B68B7584046BF8F4C0964EC2BF2', X'B21E6B68B7584046BF8F4C0964EC2BF2', 6, NULL, NULL, 2, NULL, 0, 'Serve warm with all the toppings.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6689D525DC617848B5F04157AFDF7E5A', X'40B6BB2870AC394D965F2167FE5B40B1', X'40B6BB2870AC394D965F2167FE5B40B1', 0, NULL, NULL, 2, NULL, 0, 'Add vegetable stock and water and bring to a boil over high heat. Lower heat to medium-low and let simmer for 20 minutes or until the potatoes are soft.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'10E4BACD57BD2146851CA02CAD35C0FE', X'40B6BB2870AC394D965F2167FE5B40B1', X'40B6BB2870AC394D965F2167FE5B40B1', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and use an immersion blender to blend until smooth. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9411BA3A0B28347885EB4306FC69144', X'40B6BB2870AC394D965F2167FE5B40B1', X'40B6BB2870AC394D965F2167FE5B40B1', 2, NULL, NULL, 2, NULL, 0, 'For the coconut bacon: preheat oven to 325 degrees and line a baking sheet with parchment paper. Combine the liquid smoke, soy sauce, and maple syrup in a bowl. Fold the coconut flakes into the liquid smoke mixture until they are completely coated with the sauce. Transfer to the baking sheet and spread out onto a single layer. Baked for 20 minutes, shaking and stirring the flakes every 3-5 minutes to prevent from burning (do it more frequently towards the end as they are very prone to burning right before ready).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C3BDFB7BE03CBE43A429D5E96C7889E3', X'40B6BB2870AC394D965F2167FE5B40B1', X'40B6BB2870AC394D965F2167FE5B40B1', 3, NULL, NULL, 2, NULL, 0, 'To serve: Divide the soup between bowls and top with chives, coconut bacon, sour cream, feta, tomatoes, buffalo sauce, cheese, and whatever other toppings you''ve prepared.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ED21032A9C383241A75C00FACCE264B3', X'CFE93585650D514989FD802FE78E92BA', X'CFE93585650D514989FD802FE78E92BA', 0, NULL, NULL, 2, NULL, 0, 'Using the sauté function, warm the olive oil in the pressure cooker. Add celery, carrots, onion, and bell pepper and cook for 7-10 minutes, stirring often, or until softened. Add garlic clove and cook for another minute.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E3C6F1007E3D949B36DAC1CCB773456', X'CFE93585650D514989FD802FE78E92BA', X'CFE93585650D514989FD802FE78E92BA', 1, NULL, NULL, 2, NULL, 0, 'Stir in the oregano, basil, salt, tomato paste, vegetable broth, diced fire roasted tomatoes, peas, and farro, scraping up any brown bits from the bottom of the pot.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0824AAAF3C130D4083A244B8F6418BC7', X'CFE93585650D514989FD802FE78E92BA', X'CFE93585650D514989FD802FE78E92BA', 2, NULL, NULL, 2, NULL, 0, 'Cover and seal, and make sure the release knob is turned to ''closed''. Turn the pressure cooker setting to ''high'' and set it for 6 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08F874DAD2240E48A24A6EA8E20057AA', X'CFE93585650D514989FD802FE78E92BA', X'CFE93585650D514989FD802FE78E92BA', 3, NULL, NULL, 2, NULL, 0, 'Once the cycle is complete, turn the seal knob to ''quick release'' and let the pressure release before opening up the instant pot.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B9D0D50D2EA6344D80C8F86CFA918CF1', X'CFE93585650D514989FD802FE78E92BA', X'CFE93585650D514989FD802FE78E92BA', 4, NULL, NULL, 2, NULL, 0, 'Divide the soup between seving bowls and sprinkle with grated cheese and red pepper flakes. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C80203C1A7D1014EA182D2E31FA1AE01', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp olive oil in a large stockpot over medium heat. Add onions and sauté for 5 minutes or until softened. Add garlic and sauté for another 30 seconds. Then add the ground cumin and smoked paprika and sauté for an additional 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'79C36D393D362C498CCB14EF5D26CFAF', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 1, NULL, NULL, 2, NULL, 0, 'Set aside 1/2 cup of the pinto beans. Pour the whole can of chilis (including all the juices in the can) into the pan to deglaze it and then add the fire roasted tomatoes, 2 cups vegetable broth, the remaining pinto beans, and 1 tsp salt. Turn heat to high to bring to a boil. Once the soup is boiling, turn the heat down to medium-low and let simmer for 25 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE0F4D40C3BA7843ADBEC3620F445119', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dumplings by whisk toghether the flour, cornmeal, cheddar cheese, baking powder, baking soda, and salt in a medium mixing bowl. Add in the milk and 1 Tbsp olive oil and whisk until a thick dough has formed and no dry ingredients remain. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'45EF463F901EF94291DFC2D5FBAD547C', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 3, NULL, NULL, 2, NULL, 0, 'Remove the soup from heat and use an immersion blender to blend until smooth. Alternatively, blend the soup in batches in a blender (being careful to leave the lid slightly open so the steam can escape) and return the soup to the stockpot.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'02FD0E928619174A9AFBC23CBA975414', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 cup of vegetable broth and reserved 1/2 cup pinto beans, cover, and bring back to a simmer over medium-low heat, whisking often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CF9E29AC3DBEBA4399490379890D5DCB', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 5, NULL, NULL, 2, NULL, 0, 'Uncover, spoon 1 Tbsp each of the dumpling batter into the soup, and then re-cover. Let the dumplings steam in the soup for 7-10 minutes or until the dumplings are cooked all the way through. If the soup is spitting too much to safely add the dumplings, add a bit more vegetable broth to thin it out.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1F719CD80638BB449DE343024CF12395', X'F1BC972CF308D84DA6B4879FE5D92B81', X'F1BC972CF308D84DA6B4879FE5D92B81', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and season with more salt and pepper, as needed. Serve warm with toppings of choice.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9A6D55129DA5543B6E683062CCCF256', X'AC9C4AB697D95D4C86207C0DB2C0761B', X'AC9C4AB697D95D4C86207C0DB2C0761B', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan or Dutch oven over medium heat, warm the olive oil. Add red onion and bell pepper and sauté until vegetables are soft, about 5-7 minutes. Add garlic and sauté for another 30 seconds. Add chili powder, cocoa powder, smoked paprika, cumin, oregano, salt, and chipotle pepper and sauté for another 30 second or until the spices are fragrant and have coated the vegetables. Pour in kidney beans, fire-roasted tomatoes (with their juices), and vegetable stock');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90423246FE6D4B4CA90932CE3274454F', X'AC9C4AB697D95D4C86207C0DB2C0761B', X'AC9C4AB697D95D4C86207C0DB2C0761B', 1, NULL, NULL, 2, NULL, 0, 'Turn heat to medium-high, bring to a boil, then turn heat down to medium-low, cover, and simmer for 20 minutes. Remove the lid and simmer until the chili thickens and most of the liquid has cooked off, about 5-10 more minutes. Remove from heat and let cool slightly. Season to taste with salt and pepper. Ladle into bowls, add toppings, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BD913BD0F8A8B4181ADFF3BB115B648', X'CB1303ABE87B0E4F857D1B57379A314D', X'CB1303ABE87B0E4F857D1B57379A314D', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a high-speed blender and blend until completely smooth. Taste and adjust the seasoning if necessary (add more adobe sauce for extra heat).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C6CEE01BDCBFD428BC4E3DE4B323F97', X'BA25447D554A184380D3D289F9250F87', X'BA25447D554A184380D3D289F9250F87', 0, NULL, NULL, 2, NULL, 0, 'Heat two tablespoons of olive oil in a large pot over medium heat. Add onion, carrots, and celery and saute until softened, about 5 minutes. Add in butternut squash and garlic and saute for another 30 seconds. Add broth, water, and farro and turn heat up to high. Once boiling, lower heat to medium, and let simmer for 20 minutes. Add tomatoes and white beans and let simmer for another 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B470FF190D840A4D8690451182147613', X'BA25447D554A184380D3D289F9250F87', X'BA25447D554A184380D3D289F9250F87', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine sage leaves and olive oil in a high speed blender and blend for 20 seconds or until the sage has been completely blended. Strain through a fine mesh strainer and reserve the green oil for serving.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1933DE8BB0FF8041BF6DEF67EF157F92', X'BA25447D554A184380D3D289F9250F87', X'BA25447D554A184380D3D289F9250F87', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, transfer soup to bowls and drizzle with oil, salty cheese, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA70B34EEF8A7642BCDFEF2326C23727', X'3805478AFC06A04092E4EA31A9A0BDC3', X'3805478AFC06A04092E4EA31A9A0BDC3', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a large stockpot over medium heat. Add in onion, celery, carrots, and a dash of salt and saute for 5-7 minute or until soft.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60ED8B0D430B584C85EE1090D353A90C', X'3805478AFC06A04092E4EA31A9A0BDC3', X'3805478AFC06A04092E4EA31A9A0BDC3', 1, NULL, NULL, 2, NULL, 0, 'Add in garlic, chili powder, paprika, oregano, and cayenne pepper and saute for another 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4597552E1B9FA4095FF0480D3D867EA', X'3805478AFC06A04092E4EA31A9A0BDC3', X'3805478AFC06A04092E4EA31A9A0BDC3', 2, NULL, NULL, 2, NULL, 0, 'Next, add in vegetable broth and 3 cups of water. Bring to a boil and then lower heat to medium-low and let simmer for 30 minutes or until the vegetables are very soft.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90D637E07F69DF47864B923B5D0C3988', X'3805478AFC06A04092E4EA31A9A0BDC3', X'3805478AFC06A04092E4EA31A9A0BDC3', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and use an immersion blender to blend until smooth. Stir in the milk and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E132C794B287DE4598B078DEFDA6857F', X'743170EC8832B848866C0081AD032760', X'743170EC8832B848866C0081AD032760', 0, NULL, NULL, 2, NULL, 0, 'Meanwhile, while the soup is simmering, In a high speed blender or food processor, blend the tahini, cilantro, parsley, garlic, cumin, za''atar, salt, lemon juice, and 1/4 cup water until creamy and no lumps remain. If it''s too thick to drizzle then add more water, 1 Tbsp at a time, until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D8AF980C87BDC4AB13DB78F8B5D6453', X'76B70670FC762F41952DC00502C81BCC', X'76B70670FC762F41952DC00502C81BCC', 0, NULL, NULL, 2, NULL, 0, 'Scoop soup into serving bowls and swirl with green tahini. Top with chopped carrots, sesame seeds, parsley, green onions, and chili pepper flakes. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB2775258D0D5A45821B44DE252119CC', X'BC0A433E9BF15D42BE91B42D854854FC', X'BC0A433E9BF15D42BE91B42D854854FC', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D89603278CE6344D8277F8FB5B9603BF', X'BC0A433E9BF15D42BE91B42D854854FC', X'BC0A433E9BF15D42BE91B42D854854FC', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat oil in a large skillet over medium heat. Add onion, bell pepper, and poblano and cook, stirring occasionally, for 5 to 7 minutes or until onions are translucent.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'27E4137092A34B4998D79A3CABCC31FB', X'BC0A433E9BF15D42BE91B42D854854FC', X'BC0A433E9BF15D42BE91B42D854854FC', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and cook for another minute. Stir in cumin, chili powder, thyme, red pepper flakes, salt, tomatoes, and beans. Lower the heat, cover, and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'807ADA77721BB74B9552203F101B0BAA', X'BC0A433E9BF15D42BE91B42D854854FC', X'BC0A433E9BF15D42BE91B42D854854FC', 3, NULL, NULL, 2, NULL, 0, 'When the water is boiling, stir in the pasta and adjust the heat to maintain a low boil. Cook until pasta is al dente (reference package directions).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8853CC09FF7F89418DDF1082A78D06BD', X'D2720729F9C3AF4CA77E15660B19DA04', X'D2720729F9C3AF4CA77E15660B19DA04', 0, NULL, NULL, 2, NULL, 0, 'Combine all the queso ingredients in a high speed blender and process until completely smooth. If the sauce is too thick to pour, add another tablespoon of water.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D009F0F84024EE408842AD008801CBD7', X'43D98C5E335562499A1447A4B6D3649D', X'43D98C5E335562499A1447A4B6D3649D', 0, NULL, NULL, 2, NULL, 0, 'Fold tomato bean mixture, pasta, and queso together until combined. Top with scallions and season with more salt and pepper (if needed).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EC0A9979610BD6429A333B62B26377F2', X'4FA3CDDE4E5D60478D2B21B6BEF97A27', X'4FA3CDDE4E5D60478D2B21B6BEF97A27', 0, NULL, NULL, 2, NULL, 0, 'Make sure the parsley is completely dry, Remove leaves from stems and discard stems. Finely chop parsley leaves and then toss with garlic and citrus zest. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DAD9A2F38806B94D9271425014646D51', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', 0, NULL, NULL, 2, NULL, 0, 'Bring 3 cups salted water to a boil in a medium saucepan. Slowly add polenta and reduce heat. Cook, stirring often, for 5 minutes. Remove from heat, cover, and set aside until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'87CE03CE6245254088BD1CB923D6DEE5', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. Place veggies and chickpeas on baking sheet and toss with olive oil, smoked paprika, chili powder, salt, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76D2C9FDF3E4154DB3B82B1B848E5EB4', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', X'DDF5AAA7FFB3C6469423C6D7BA667FC8', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20-30 minutes, tossing halfway through, or until the veggies are softened and starting to brown around the edges.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B986B0612D8E449A3174ED5837CD289', X'77D1E5A1C5EC8749BA68EEC8D99A0376', X'77D1E5A1C5EC8749BA68EEC8D99A0376', 0, NULL, NULL, 2, NULL, 0, 'Divide the polenta between bowls and then top with roasted veggies, chickpeas, and gremolata. Drizzle with additional olive oil (optional) and season with more salt and pepper. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F359A307DC6D89459E2D99E53970D3E2', X'6A168724FAFC8143B7F3BE11589DE37E', X'6A168724FAFC8143B7F3BE11589DE37E', 0, NULL, NULL, 2, NULL, 0, 'In a high speed blender, combine poblano, garlic, tahini, lemon juice, olive oil, and 1/4 cup water and blend until smooth. If sauce is too thick, add water as needed to reach desired consistency. Season with salt and pepper and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C1521E73A56ED469C5D0113F4E028D6', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees F and line baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B60D5E24172338499BEDB2F2A0AA45E3', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, cook quinoa according to packaged direction.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F07CFE693C4FC4885D535A8F1941BE3', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine sweet potatoes, coconut oil (or olive oil), maple syrup, orange zest, and 1/4 tsp salt. Mix well and transfer to prepared baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'361DF7A4B3E8FD4EB8577BB0BF59B4DC', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 3, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes, flip, and roast for an additional 15 minutes or until potatoes are tender and beginning to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'081D43A97CF3BE43AEA567B3792DF19D', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small bowl, combine the chickpeas, 2 teaspoons of the olive oil, the paprika, cumin, cayenne, 1/4 teaspoon salt, and 1/8 teaspoon pepper. Mix well until the chickpeas are thoroughly coated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'93222E204BD3484BA9150DA5C61A5508', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 5, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, heat the remaining 1 tablespoon of the olive oil. Add the garlic and red pepper flakes. Cook until the garlic is fragrant, about 3 minutes. Add the chard, cover, and cook, stirring occasionally, until the chard begins to wilt, 2 to 3 minutes. Uncover, add dash of salt and pepper and cook, stirring frequently, until the chard is completely wilted and cooked through, an additional 1 to 2 minutes. Discard the garlic cloves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9B031CEC1049F40A2F3A5FBEE0DBA1C', X'9A31F41932344847BCD90D624BE16328', X'9A31F41932344847BCD90D624BE16328', 6, NULL, NULL, 2, NULL, 0, 'To serve, put 1/2 cup of the cooked grain in four serving bowls. Top each bowl with equal portions of the roasted sweet potatoes, roasted chickpeas, and sautéed chard. Drizzle with the poblano tahini sauce and serve with extra sauce passed separately.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'24E6126205037748B78E2245496002AA', X'4BE49DF31D0F8C4396D0B2407861A5DD', X'4BE49DF31D0F8C4396D0B2407861A5DD', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 degrees and transfer your veggies to one baking sheet (have another sheet ready as well).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3DBE83297FD574DABE1FBF338D3D67A', X'4BE49DF31D0F8C4396D0B2407861A5DD', X'4BE49DF31D0F8C4396D0B2407861A5DD', 1, NULL, NULL, 2, NULL, 0, 'Drizzle the olive oil and chipotle sauce over the veggies and use your clean hands to toss until all the veggies are coated. Sprinkle the smoked paprika, garlic powder, oregano, cumin, salt, cinnamon, coriander, and dash of black pepper over the veggies and toss to coat. Divide the veggies evenly between two baking sheets and spread into a single layer.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E44AE841CF9F5D468015DC68CAC48509', X'4BE49DF31D0F8C4396D0B2407861A5DD', X'4BE49DF31D0F8C4396D0B2407861A5DD', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20-30 minutes (time will vary depending on the size you cut your veggies into and what veggies you used), flipping the veggies every 10 minutes to avoid them sticking to the pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80C20869EAE90F4CBB84869C84644147', X'CFA3EFBA22D5B042B09AA1DE11C6A427', X'CFA3EFBA22D5B042B09AA1DE11C6A427', 0, NULL, NULL, 2, NULL, 0, 'While the veggies roast, transfer the black beans, 1/2 tsp chili powder, 1/2 tsp cumin, and 1/4 tsp salt to a small saucepan and add just enough water to cover the black beans. Place on stove over medium-low heat and let simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E04BE3C7257184DBCE53D7478A94195', X'CFA3EFBA22D5B042B09AA1DE11C6A427', X'CFA3EFBA22D5B042B09AA1DE11C6A427', 1, NULL, NULL, 2, NULL, 0, 'Transfer the cheese, radish slices, avocado pieces, and any other toppings you''ll be serving into small bowls and set on your serving area.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'472EC0D52EC70B41B8B22EC2DB6944EC', X'CFA3EFBA22D5B042B09AA1DE11C6A427', X'CFA3EFBA22D5B042B09AA1DE11C6A427', 2, NULL, NULL, 2, NULL, 0, 'When the veggies and black beans are ready, transfer them and your warmed tortillas to serving bowls or plates and place near your topping ingredients. Serve the black beans with a slotted spoon and the rest of the toppings with a regular spoon or fork. Let your family or guests assemble their own tacos based on the fillings you''ve prepared.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4D8ECC633C572948A26D08D35EB7BC91', X'2EE766CB476EBC4B91B0B4E23BB3EF56', X'2EE766CB476EBC4B91B0B4E23BB3EF56', 0, NULL, NULL, 2, NULL, 0, 'Pulse the walnuts in a food processor until finely crumbled, four or five times (be careful not to over pulse!).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6B7D3E4EC87714FA56FCBF8337C5E9C', X'2EE766CB476EBC4B91B0B4E23BB3EF56', X'2EE766CB476EBC4B91B0B4E23BB3EF56', 1, NULL, NULL, 2, NULL, 0, 'Transfer paprika, chili, and cumin into a saucepan over medium heat and toast for 30 seconds, stirring often. Add walnuts and salt and toss. Add water and lime juice and stir until completely coated. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70FE9BF040AD1C41AB518146749A34B0', X'34E72203E157CE4FB789743F1A4C029A', X'34E72203E157CE4FB789743F1A4C029A', 0, NULL, NULL, 2, NULL, 0, 'Add all salsa ingredients into a bowl and toss to combine. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA43A84BE015AA4FA954933E0FF1E384', X'16AF302C3BAD084195744828E550090E', X'16AF302C3BAD084195744828E550090E', 0, NULL, NULL, 2, NULL, 0, 'Warm the tortillas in a saucepan over medium heat for 10 seconds. Double up the tortillas and place 1/4 of the walnut meat and peach salsa in them. Repeat with the remaining tortillas. Top with avocado, cabbage, and yogurt or sour cream (if using). Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FCB06079794989498CE04EE2C0BF4216', X'3F62C177AFCA6640A7FE7FD78939A567', X'3F62C177AFCA6640A7FE7FD78939A567', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan, heat the olive oil over medium. Add the onions, carrots, and celery and let cook for about 10 minutes (or until they begin to brown). Next, add in the garlic, oregano, and a little bit of salt. Let cook for another minute. Add tomato paste, stir, and cook for 3 more minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9C2AA493C560D34F956DA3C4D321F2A8', X'3F62C177AFCA6640A7FE7FD78939A567', X'3F62C177AFCA6640A7FE7FD78939A567', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and grease a 9x13-inch pan. Using clean hands, roll the mixture into golf ball size balls and place on greased baking sheet. Continue with the rest of the batter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0AC85BEDF6D03949A04E3AB56BB66646', X'04FFA2B8A6F77B45AC7707BECB3F013F', X'04FFA2B8A6F77B45AC7707BECB3F013F', 0, NULL, NULL, 2, NULL, 0, 'Add in garlic and sauté for another thirty seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AEFBDCB1370961409FB002718B0E86FB', X'04FFA2B8A6F77B45AC7707BECB3F013F', X'04FFA2B8A6F77B45AC7707BECB3F013F', 1, NULL, NULL, 2, NULL, 0, 'To serve: divide the rice and beans between four bowls. Garnish with green onions and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'363EFD52D33CDD42900B103A9E3A3914', X'B6B90C7EB0DBD548A0EC3FC926B05734', X'B6B90C7EB0DBD548A0EC3FC926B05734', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp olive oil in a medium non-stick skillet over medium high heat. Once the oil is hot, crack each of your eggs into the skillet and sprinkle with a little salt and pepper. Cook until the white parts are golden brown and bubbly around the edges. If you like your egg yolk on the firmer side, cover with a lid while cooking. Once ready, remove from pan using a spatula and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'178C9451F7E13541AA95506DABDCCF18', X'B6B90C7EB0DBD548A0EC3FC926B05734', X'B6B90C7EB0DBD548A0EC3FC926B05734', 1, NULL, NULL, 2, NULL, 0, 'Turn heat off but keep the used pan on the stove (it should still have a little olive oil on it from frying the eggs). Add pita slices to the pan, flipping every few seconds, until warmed and just starting to brown. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EBE0A99047655440B8DF89F6AF42ACAB', X'B6B90C7EB0DBD548A0EC3FC926B05734', X'B6B90C7EB0DBD548A0EC3FC926B05734', 2, NULL, NULL, 2, NULL, 0, 'In a high speed blender or food processor, blend the tahini, cilantro, parsley, garlic, cumin, za''atar, salt, lemon juice, and 1/4 cup water until creamy and no lumps remain. If it''s too thick to drizzle then add more water, 1 Tbsp at a time, until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0FF14E13565BBB4E9F63069AF1D2E199', X'B6B90C7EB0DBD548A0EC3FC926B05734', X'B6B90C7EB0DBD548A0EC3FC926B05734', 3, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6EDB7C61CD071C4096533E7C377D5283', X'B6B90C7EB0DBD548A0EC3FC926B05734', X'B6B90C7EB0DBD548A0EC3FC926B05734', 4, NULL, NULL, 2, NULL, 0, 'The ''I''ve got my act together'' way to serve: Place pita pieces on two separate plates and top each with an egg, tahini, salt, pepper, parsley, and za''atar. Enjoy right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'06A9E6C12F59CA47AC64F05CBE08EB61', X'C13533C9D889CE4B838CAFB90452DAC9', X'C13533C9D889CE4B838CAFB90452DAC9', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to the package directions. Strain the pastsa, holding back 1 cup of pasta water and set everything aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E4F9B39683464B4FA3A70FC20FAC33B1', X'C13533C9D889CE4B838CAFB90452DAC9', X'C13533C9D889CE4B838CAFB90452DAC9', 1, NULL, NULL, 2, NULL, 0, 'Meahwhile, heat oil in a large skillet over medium-low. Add garlic cloves and sauté for about 3 to 5 minutes or just until starting to get some color (if they start to turn dark before 3 minutes, turn your heat down).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DDD586F0FC44BD4F9D8354528AEFC972', X'C13533C9D889CE4B838CAFB90452DAC9', X'C13533C9D889CE4B838CAFB90452DAC9', 2, NULL, NULL, 2, NULL, 0, 'Add walnuts and red pepper flakes and sauté for another 2-3 minutes or until the nuts have started to turn darker and everything is very fragrant. Remove from heat and sprinkl in 1/2 tsp salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E0D0034CD053394FA2069784788FE4EA', X'C13533C9D889CE4B838CAFB90452DAC9', X'C13533C9D889CE4B838CAFB90452DAC9', 3, NULL, NULL, 2, NULL, 0, 'Add the ricotta to a serving platter and pile the pasta over it. Pour the garlic-walnut mixture (including any remaining oil over the pasta), along with the lemon juice, lemon zest, Parmigiano-Reggiano, and 2 Tbsp pasta water. Toss until combined and a thick sauce has formed. Add more pasta water, 1 Tbsp at a time, if the sauce doesn''t thicken up.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C0C1415C300A7947A7807398792F24A7', X'C13533C9D889CE4B838CAFB90452DAC9', X'C13533C9D889CE4B838CAFB90452DAC9', 4, NULL, NULL, 2, NULL, 0, 'Taste and season with more salt and pepper. Serve with some torn fresh basil or thyme leaves or a dried sprinkle of oregano flakes. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8888034CC220ED409046E9CF29B94368', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 0, NULL, NULL, 2, NULL, 0, 'Add your cauliflower florets into a medium glass bowl or ziplock bag and pour in the coconut milk (reserving any cream that has formed in the can for another use). Toss and set aside to marinate for two hours, tossing every 30 minutes or so to get any pieces not submerged mixed in.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2F5089D89428BB4EBA6E3D71D6A14DF4', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A50344C03ED21F47A5638FD2E07B4442', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 2, NULL, NULL, 2, NULL, 0, 'In either a high speed blender or food processor, pulse the coconut flakes until ground into small crumbs. Transfer to a shallow bowl and add in the panko breadcrumbs. Toss to combine and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'219C181C9A538549979A8510BD894C7F', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 3, NULL, NULL, 2, NULL, 0, 'In another shallow bowl, toss together the flour, salt, and a dash of pepper. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3B8A7C076B9844EA4F2C0DB5E07E811', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 4, NULL, NULL, 2, NULL, 0, 'In a third shallow bowl, add the beaten egg.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A622410FE3518A4183F881D71DA01BAE', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 5, NULL, NULL, 2, NULL, 0, 'Remove the cauliflower florets from the coconut milk and dip into the flour mixture to thoroughly coat. Next, dip into the egg mixture to thoroughly coat and then dip into the breadcrumb mixture to coat. Place coated cauliflower pieces onto your prepared baking sheet, making sure to leave a little space between each floret, and repeat the dipping steps with the remaining florets.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DE0A8B9BA2EF9C45B6F196F0F638720B', X'238B37D79FB4C6418204B960A84B1F67', X'238B37D79FB4C6418204B960A84B1F67', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until starting to brown all over, tossing halfway through. Serve warm with the Creamy Honey Mustard dipping sauce.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5585070CF9E30E4FBBED502C5B45605B', X'D0934382B5FB5847811726065E3C2BD0', X'D0934382B5FB5847811726065E3C2BD0', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the yogurt, mustard, honey, and lemon juice. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD7478987E94674C9A0DFF12A043FBC9', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp olive oil in a large stockpot over medium heat. Add onions and sauté for 5 minutes or until softened. Add garlic and sauté for another 30 seconds. Then add the ground cumin and smoked paprika and sauté for an additional 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF5790261BB0894399D8633F31F4FA33', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 1, NULL, NULL, 2, NULL, 0, 'Set aside 1/2 cup of the pinto beans. Pour the whole can of chilis (including all the juices in the can) into the pan to deglaze it and then add the fire roasted tomatoes, 2 cups vegetable broth, the remaining pinto beans, and 1 tsp salt. Turn heat to high to bring to a boil. Once the soup is boiling, turn the heat down to medium-low and let simmer for 25 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D99114B80CFC4240904D2CAED4E7E371', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dumplings by whisk toghether the flour, cornmeal, cheddar cheese, baking powder, baking soda, and salt in a medium mixing bowl. Add in the milk and 1 Tbsp olive oil and whisk until a thick dough has formed and no dry ingredients remain. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9A396CA97FE59E478A19A20C08A0642E', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 3, NULL, NULL, 2, NULL, 0, 'Remove the soup from heat and use an immersion blender to blend until smooth. Alternatively, blend the soup in batches in a blender (being careful to leave the lid slightly open so the steam can escape) and return the soup to the stockpot.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DCC2AAD1B2572F4CAEFD4764F1283ADC', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 cup of vegetable broth and reserved 1/2 cup pinto beans, cover, and bring back to a simmer over medium-low heat, whisking often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8C9B72DF34E22D489D0A23838DAF916C', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 5, NULL, NULL, 2, NULL, 0, 'Uncover, spoon 1 Tbsp each of the dumpling batter into the soup, and then re-cover. Let the dumplings steam in the soup for 7-10 minutes or until the dumplings are cooked all the way through. If the soup is spitting too much to safely add the dumplings, add a bit more vegetable broth to thin it out.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD7750F768A01043AB6DA219D3EA47DE', X'379798A782A29B44914EE24F9FCF1943', X'379798A782A29B44914EE24F9FCF1943', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and season with more salt and pepper, as needed. Serve warm with toppings of choice.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DB633497E9E94143824916FDCA122939', X'671C62AEB0C78948AA6130C9876A9143', X'671C62AEB0C78948AA6130C9876A9143', 0, NULL, NULL, 2, NULL, 0, 'In a large heavy-bottomed saucepan, warm 5 Tbsp oil over medium heat. Add onions, garlic, and salt, stir to coat in the oil, and cook for 8 to 10 minutes, until the onions become translucent. Reduce heat to the lowest setting and continue to cook for 30 minutes, stirring every 5 minutes, until the onions are caramelized.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5F8596662F7F3040A369A74FB1DFDF2B', X'671C62AEB0C78948AA6130C9876A9143', X'671C62AEB0C78948AA6130C9876A9143', 1, NULL, NULL, 2, NULL, 0, 'Add the chili slices and stock and bring to a boil over high heat. Reduce heat so the mixture is simmering and add in the sake, soy sauce, and miso, stirring well to combine. Add bok choy or choy suy and simmer for two more minutes. Cover and keep warm on the lowest setting while you prepare the noodles.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B18D447D3B20994499C5A1FFEE6BDAC5', X'671C62AEB0C78948AA6130C9876A9143', X'671C62AEB0C78948AA6130C9876A9143', 2, NULL, NULL, 2, NULL, 0, 'Cook the noodles according to the package instructions. Divide the noodles between 4 bowls and ladle the broth and vegetables over the noodles. Top with egg halves and chili saw, if using. Taste and season with more salt, as necessary.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0E76082478AE294994940D8CD502D658', X'BB6560D72C767B4BA3AFBD36DA9F028B', X'BB6560D72C767B4BA3AFBD36DA9F028B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line a baking sheet with parchment paper. Add cauliflower to baking sheet and toss with 2 Tbsp olive oil and 1/2 tsp salt. Bake for 20 minutes or until starting to brown in spots.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B105B7024E427C4E8DC1C4765A5C0131', X'BB6560D72C767B4BA3AFBD36DA9F028B', X'BB6560D72C767B4BA3AFBD36DA9F028B', 1, NULL, NULL, 2, NULL, 0, 'Toss the cooked cauliflower with 1/4 cup buffalo sauce and continue to bake for an additional 5 minutes. Remove from oven and taste. Brush with more buffalo sauce if you''d like it spicier.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3DE6EBD3CB68ED4CB4349E2A79F99217', X'23D737B0A87CDB47AA71F86B6BE25EEF', X'23D737B0A87CDB47AA71F86B6BE25EEF', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the Greek yogurt, buttermilk, 1 Tbsp fresh chives, garlic powder, onion powder, and a dash of pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BA22D805F733B24DBC00C15CE5731C4D', X'42F55915E7873D47A1BED9D83BB5689C', X'42F55915E7873D47A1BED9D83BB5689C', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the white wine vinegar, honey, 1/2 tsp salt, and dash of black pepper. Add cabbage and red pepper to the bowl and toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B3D654BACEFB3249B4B4C492056B5E76', X'9844DE204FA3F84686E942BDD74F2CFB', X'9844DE204FA3F84686E942BDD74F2CFB', 0, NULL, NULL, 2, NULL, 0, 'Divide the rice between 4 serving bowls and spread to cover the bottom of each bowl. Top each bowl with 1/4 of the spicy slaw, buffalo cauliflower, cherry tomatoes, and scallions/chives. Drizzle with greek yogurt ranch and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C062BA520387C488BCC0DFB7B162A9D', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 0, NULL, NULL, 2, NULL, 0, 'Boil and cook spaghetti pasta according to package directions and drain.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E3193CA2D551C949B998E4AE6D9DA7F9', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the peanut butter, tamari, honey, rice vinegar, chili garlic sauce, and sesame oil (if using) until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7DC950A550D13C4C8D3C9A75ED9D93F5', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, warm the olive oil over medium. Add the white parts of the scallions, garlic, and ginger and saute for 30 seconds or until started to become fragrant.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'08DD2E9753F55240BF2C49735CFC3B65', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 3, NULL, NULL, 2, NULL, 0, 'Add the bell pepper strips, carrots, and snow peas and cook for another 2 to 3 minutes (you want them to still be crunchy).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D2D7ABFC601774FB12701178EB0A276', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 4, NULL, NULL, 2, NULL, 0, 'Add the creamy peanut sauce and the pasta and toss to coat. Drizzle in the water to de-glaze the pan and toss until glossy sauce has formed on all the pasta and vegetables, about 30 seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'437CB23FC6F225488D3192A6857DF5F1', X'A10F92752C574045B04371B001FF6896', X'A10F92752C574045B04371B001FF6896', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat and transfer to a serving bowl. Top with the green parts of the scallions, cilantro, and toasted sesame seeds. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D334F22455BED4B88497E31A68D5396', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 0, NULL, NULL, 2, NULL, 0, 'Bring a large stockpot of salted water to a boil over high heat. Cook the pasta just shy for a minute or two from what the directions say on the package (it''ll finish cooking in the oven). Drain and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FF61D5C6A4F13146B3A5A625986D1738', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 13×9 inch baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'086B377C2BB3934CBCCCCFD014E06DBB', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 2, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium heat, melt 4 Tbsp butter and then whisk the 4 Tbsp all-purpose flour into the butter until a thick paste as formed. While whisking, slowly pour the milk into the mixture so that the butter mixture gets incorporated into the milk. Cook for about 10 minutes, stirring often, or until the mix has thickened enough that it coats the pack of a spoon. Remove from heat and stir in the brown sugar, nutmeg, 1/2 tsp salt, and pumpkin puree. Pour the cooked pasta into the pumpkin sauce and stir until coated (alternatively, if your saucepan isn''t big enough, pour into a large mixing bowl and toss until all the pasta is coated).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8347491AA5C4824191AE0DEF0B801169', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the ricotta, large eggs, 1/2 tsp salt, 1/4 tsp black pepper, minced garlic, and 2 Tbsp chopped sage.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BBB3FC160EE6B047BC544D6BF4AA5855', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 4, NULL, NULL, 2, NULL, 0, 'Pour about 1/3 of the pumpkin pasta mixture into your prepared baking dish and smooth out into an even layer (no need to be exact). Using about half of the ricotta mixture, place large dollops of the mixture over the pumpkin pasta and then top with half of the torn mozzarella. Layer another 1/3 of the pumpkin pasta over that and top with remaining ricotta. Add the final layer of pumpkin pasta and finish with the remaining mozzarella.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC85337E2467924E87F7D231A29569EC', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the top is starting to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E68EEAC6592B3A41AF1580529CE18065', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small skillet, heat the remaining 2 Tbsp unsalted butter over medium heat for a few minutes until brown specks start to form. Add the sage leaves and let them fry for 30 seconds or until just starting to turn dark. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5E3FC3546C5B1546BF1E455C7874ADB5', X'E90FEA3437AB7640B1E5B71BF10F0F4F', X'E90FEA3437AB7640B1E5B71BF10F0F4F', 7, NULL, NULL, 2, NULL, 0, 'While the bake is still warm, pour the brown butter and sage over the top of the bake and serve. Feel free to sprinkle red pepper flakes on the top if you''d like a little extra heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CE8A015A16A90640A4F711335AB9488B', X'A32BD058DA8926488B76DE116CC69DF4', X'A32BD058DA8926488B76DE116CC69DF4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. In a small mixing bowl, whisk together the smoked paprika, dried garlic, ground mustard, 1/2 tsp chopped fennel seeds, and 1/2 tsp black pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A98F9A5BF6A9EB41B609A6C2CB5CB625', X'A32BD058DA8926488B76DE116CC69DF4', X'A32BD058DA8926488B76DE116CC69DF4', 1, NULL, NULL, 2, NULL, 0, 'Place the carrot coins onto the prepared baking sheet and toss with 1 Tbsp olive oil to coat. Add spices and toss until all the carrot coins are evenly coated. Spread carrots into an even layer on the baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0149066419295C4C8D8101AE1D2C6CEB', X'A32BD058DA8926488B76DE116CC69DF4', X'A32BD058DA8926488B76DE116CC69DF4', 2, NULL, NULL, 2, NULL, 0, 'Roast on the middle rack for 15 minutes, flipping after 10, or until the coins are easily pierced with a fork (watch them closely as they may cook faster or a little longer depending on how thick you chopped them).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'409529C8F2F03C4983F0D20F837356E8', X'6175768AA02C0042A123049F2D535DE2', X'6175768AA02C0042A123049F2D535DE2', 0, NULL, NULL, 2, NULL, 0, 'While those roast, in a small bowl, whisk together ¼ cup red wine vinegar, ¼ cup water, and salt. Add thinly sliced onions and set aside to pickle while preparing the rest of the salad.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A019E546A735DB4A8F4291EF97776EF4', X'3D47A76689708344BC3A1AC1A734153E', X'3D47A76689708344BC3A1AC1A734153E', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together fennel seeds, orange juice, zest, vinegar, salt, and pepper. While whisking, slowly pour olive oil into the dressing until it’s emulsified. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'735C912BC454B845B55AF028FA983A4F', X'42022F0DFADC854EADB67F04C5741050', X'42022F0DFADC854EADB67F04C5741050', 0, NULL, NULL, 2, NULL, 0, 'Drain the red onions and toss in a large serving bowl with the iceberg lettuce, carrot coins, olives, banana peppers, artichoke hearts, cherry tomatoes, roasted red pepper, and dressing. Sprinkle with feta and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E12B6E0ED05C05478ABC4B76A71A93C3', X'0A154184EC364C4CADA4704B835E1EC4', X'0A154184EC364C4CADA4704B835E1EC4', 0, NULL, NULL, 2, NULL, 0, 'Combine the cherries, 1/4 cup Amaretto, and brown sugar in a small bowl and toss to mix. Set aside and let marinate while you prepare the rest of the cheesecake.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56A9D6D02395B34FA821B162E3F2C426', X'230C8B93ED0A6C479EC0474315801ABC', X'230C8B93ED0A6C479EC0474315801ABC', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, mix together the graham cracker crumbs, melted butter, sugar, and pinch of salt until all the crumbs are wet and starting to stick together. Divide the mixture evenly into 4 small glasses or serving bowl and press down to compact the crust. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EE0E67315212E5429D10F07435B5C7DA', X'6565DCE577DDFE47AC053C6542B6B650', X'6565DCE577DDFE47AC053C6542B6B650', 0, NULL, NULL, 2, NULL, 0, 'In the bowl of a mixer fitted with the whisk attachment, whisk together the cream cheese, sour cream, and milk on high speed for one minute or until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50BDBEAB2F9D5B49BE9A8B7147AD506C', X'6565DCE577DDFE47AC053C6542B6B650', X'6565DCE577DDFE47AC053C6542B6B650', 1, NULL, NULL, 2, NULL, 0, 'Turn the mixer speed down to low and add in the powdered sugar, vanilla extract, Amaretto and salt and mix until combined, about 10 seconds. Increase the speed up to medium and whisk until light and fluffy, about another minute.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'04E16328CB13D9408529231501A17752', X'6565DCE577DDFE47AC053C6542B6B650', X'6565DCE577DDFE47AC053C6542B6B650', 2, NULL, NULL, 2, NULL, 0, 'Divid the filling evenly into the 4 glasses with the crusts and smooth down the filing so it is even. If serving right away, move to the next step. If serving later, cover both the glasses and the bowl with the cherries with plastic wrap and place in the fridge until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'05715BACE3FC184EADF9180537390AA5', X'A078C5CEFA22634B913B06B625954233', X'A078C5CEFA22634B913B06B625954233', 0, NULL, NULL, 2, NULL, 0, 'Divide the topping between the 4 glasses and garnish with mint or basil leaves and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4160094990C3D243BAC07B178BFD5360', X'5645EEDC6BBDF244A19984FBDCE6405A', X'5645EEDC6BBDF244A19984FBDCE6405A', 0, NULL, NULL, 2, NULL, 0, 'In a food processor fitted with the blade attachment, pulse together the sun dried tomatoes, basil leaves, oil, walnuts, garlic cloves, parmesan, salt, and pepper until everything is finely chopped. Taste and season with more salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'950FAF7935377041B716A281E2CA0323', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 0, NULL, NULL, 2, NULL, 0, 'Add your cauliflower florets into a medium glass bowl or ziplock bag and pour in the coconut milk (reserving any cream that has formed in the can for another use). Toss and set aside to marinate for two hours, tossing every 30 minutes or so to get any pieces not submerged mixed in.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'19B03CD053C1BD49A080F1ADE134A46F', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE86462C603EC7438DD8E6DF0D141503', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 2, NULL, NULL, 2, NULL, 0, 'In either a high speed blender or food processor, pulse the coconut flakes until ground into small crumbs. Transfer to a shallow bowl and add in the panko breadcrumbs. Toss to combine and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'247FD3B1ED927A42A026AB702723F92B', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 3, NULL, NULL, 2, NULL, 0, 'In another shallow bowl, toss together the flour, salt, and a dash of pepper. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0A2A284447D97142905968E78A478BB7', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 4, NULL, NULL, 2, NULL, 0, 'In a third shallow bowl, add the beaten egg.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A37B3B63A471245A44E493B632A3DFC', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 5, NULL, NULL, 2, NULL, 0, 'Remove the cauliflower florets from the coconut milk and dip into the flour mixture to thoroughly coat. Next, dip into the egg mixture to thoroughly coat and then dip into the breadcrumb mixture to coat. Place coated cauliflower pieces onto your prepared baking sheet, making sure to leave a little space between each floret, and repeat the dipping steps with the remaining florets.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'41F526EA6BAE3D49B0B662B2B2A0D77F', X'1B331659DC2E394A8C5701A2D8AF2064', X'1B331659DC2E394A8C5701A2D8AF2064', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until starting to brown all over, tossing halfway through. Serve warm with the Creamy Honey Mustard dipping sauce.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D63CD237FBA2AF4B9ED90BC9D9395598', X'DC2541276C291C4FB7190581E2972F63', X'DC2541276C291C4FB7190581E2972F63', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the yogurt, mustard, honey, and lemon juice. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0BB3F964031FC342B711137604309B2B', X'FD99B897C658F941817CDE8D5577EC2A', X'FD99B897C658F941817CDE8D5577EC2A', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the mango preserve, mustard, white wine vinegar, and hot sauce. While whisking, slowly pour in the olive oil until the dressing is emulsified. Add in a dash of salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C98FD0F72510A43816C11E032BCD64C', X'F9728FF49EBD0242A14B8D364A95CE51', X'F9728FF49EBD0242A14B8D364A95CE51', 0, NULL, NULL, 2, NULL, 0, 'Divide the rice between 4 shallow serving bowls. Top with cauliflower bites, black beans, kale, tomatoes, and bell pepper. Garnish with fresh herbs and a squeeze of lemon juice. Drizzle dressing over each bowl and season with more salt and pepper (if needed). Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BFAB6301F567434B88D749BC83162E4E', X'7B5B5861AD702D47BF9913D9924B7BF7', X'7B5B5861AD702D47BF9913D9924B7BF7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4DC0080CF119824784317614CF1F1083', X'7B5B5861AD702D47BF9913D9924B7BF7', X'7B5B5861AD702D47BF9913D9924B7BF7', 1, NULL, NULL, 2, NULL, 0, 'Place Earthbound Farm''s Organic Broccoli & Cauliflower on the prepared baking sheet and drizzle 1 Tbsp olive oil, 1/4 tsp salt, and dash of black pepper over them. Toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F55CD9DA1FAC444C8930B65760C0ED51', X'7B5B5861AD702D47BF9913D9924B7BF7', X'7B5B5861AD702D47BF9913D9924B7BF7', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes or until the vegetables are easily pierced with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D2B3C02EF18D904BB0BF01E87794616D', X'1722086F467F884CA27C57947C9CA6DD', X'1722086F467F884CA27C57947C9CA6DD', 0, NULL, NULL, 2, NULL, 0, 'Whisk all spicy peanut sauce ingredients together in a small bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E03B78DEF5F0944A87BA80A122FE8787', X'6E7F6B281473794BA65965001694AE4D', X'6E7F6B281473794BA65965001694AE4D', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the jalapeño juice, vinegar, olive oil, honey / maple syrup, salt, and pepper until combined and emulsified. Add in the pickled jalepenos and broccoli slaw and toss to coat. Taste and add more salt / pepper, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5BB5373299288046B0FCA43BC2FC4652', X'35F721060DA03E4296AEFF681446311C', X'35F721060DA03E4296AEFF681446311C', 0, NULL, NULL, 2, NULL, 0, 'Divide the quinoa evenly between 4 bowls and top with the roasted vegetables, slaw, and a drizzle of sauce. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'17CE29B78E0250478C66015E596F8AD3', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees F and grease a 9×13-inch baking pan with 1 tsp olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9D72AF252EE82C408A023E7426DF8455', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 1, NULL, NULL, 2, NULL, 0, 'In a shallow bowl, whisk together 2 eggs and 2 Tbsp water. In another shallow bowl, spread out the panko breadcrumbs.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'220E1BE04E3AA24BB0017C70406BC59E', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 2, NULL, NULL, 2, NULL, 0, 'Dip both sides of each cauliflower slice into the egg mixture and then into the panko mixture (making sure it''s completely coated). Repeat with the rest of the cauliflower pieces and place in a single layer on prepared baking sheet. Drizzle with 1 Tbsp olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D09FDA8F5A3C6B46B7FFB2985D21A5D1', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 3, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until browned and a knife can easily pierce the cauliflower. Remove from oven and transfer breaded cauliflower onto a plate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D10CB8B0FB25149BB678CE8854582C5', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 4, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together ricotta, oregano, thyme, 1/4 cup Parmesan cheese, garlic, salt, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA5238A8A79E4A4D99C73DF49DC349DF', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 5, NULL, NULL, 2, NULL, 0, 'Using the same baking sheet, pour half the jar of marinara sauce in the bottle of the baking sheet and spread out into an even layer. Add cauliflower over the sauce and divide the ricotta evenly over each piece of ricotta. Dot each piece with mozzarella, more marinara (using up the rest of the jar), and the remaining 1/4 cup Parmesan. Drizzle it all with 2 Tbsp olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'45ADE7BA92A89A40B5EC8847B2BB27D0', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 6, NULL, NULL, 2, NULL, 0, 'Cover with aluminum foil and bake for 30 minutes. Remove foil and bake for another 10 to 15 minutes or until the top is starting to brown and the sauce is bubbly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'21E94ECEE4D0AA409F83A97B7773D39C', X'7C9942E52923204AAAA5954A86285D1A', X'7C9942E52923204AAAA5954A86285D1A', 7, NULL, NULL, 2, NULL, 0, 'Let cool slightly for five minutes before serving.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B96565C65D650C4D888D6321C2AAE339', X'77793B633B1C4F4EB585466A61DF1FD6', X'77793B633B1C4F4EB585466A61DF1FD6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 500 degrees F. Remove the green leaves from head of cauliflower and trim the stems. Cut 1 1/2 inch thick steaks (I got 4 from two heads plus a bunch of florets which I saved for another use).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'11AD4F8438770D48B412157EE09DAB0A', X'77793B633B1C4F4EB585466A61DF1FD6', X'77793B633B1C4F4EB585466A61DF1FD6', 1, NULL, NULL, 2, NULL, 0, 'Transfer slices to a baking sheet and brush olive oil on each side of each piece. Sprinkle spices on all steaks and then cover the baking sheet with aluminum foil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B50A7CF3F03DD844B2650D96CE08D516', X'77793B633B1C4F4EB585466A61DF1FD6', X'77793B633B1C4F4EB585466A61DF1FD6', 2, NULL, NULL, 2, NULL, 0, 'Bake for 5 minutes, remove foil, and bake for an additional 10. Flip steaks and bake for another 5-10 minutes or until the tops are browned. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B52603A440BF3043B4F0382985AC7454', X'513456878AD16D4192F084E86F643604', X'513456878AD16D4192F084E86F643604', 0, NULL, NULL, 2, NULL, 0, 'Combine carrot tops, garlic, pecans, basil leaves, and asiago cheese in a high speed blender or food processor. Pulse until everything is chopped into small pieces. Turn the machine on and drizzle in 1/4 cup olive oil to incorporate. If the mixture is too dry, drizzle more olive oil until you reach your desired consistency.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF2BE190A276C240A560E5E83229D038', X'7A0A330EBFF0A84B84C903258DA8916F', X'7A0A330EBFF0A84B84C903258DA8916F', 0, NULL, NULL, 2, NULL, 0, 'Bring a medium saucepan of water to a boil over high heat. Salt and add in carrot pieces. Let blanch for 5 minutes. Meanwhile, add ice to a large mixing bowl and add water to create an ice bath. Once the carrots are ready, use tongs to transfer from the boiling water to ice bath.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D60A5A369602214A87A925DFDA804BFA', X'7A0A330EBFF0A84B84C903258DA8916F', X'7A0A330EBFF0A84B84C903258DA8916F', 1, NULL, NULL, 2, NULL, 0, 'Once carrots are cooled, transfer to a high speed blender along with miso, vinegar, yogurt, olive oil, and a dash of salt and pepper. Blend until completely combined and a thick dressing forms. You want the dressing to be thin enough that you can spread it but quick enough that it won''t run. If it''s too thick, add 1 Tbsp of water at a time until you''ve received a desired consistency.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51CA71AF3CCF4A44A7BF15EEA1C198CC', X'497591C2FBFAE54889D26C0C82CF38C0', X'497591C2FBFAE54889D26C0C82CF38C0', 0, NULL, NULL, 2, NULL, 0, 'Spread carrot miso dressing on a serving platters and place cauliflower steaks over it. Top with pesto, a drizzle of olive oil, salt, and pepper. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7A59ECC7EDD3C14E9B540DBAA7CB1A7F', X'BFC2DF19657C16459E45149AFD28F1B1', X'BFC2DF19657C16459E45149AFD28F1B1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line a baking sheet with parchment paper. Add cauliflower to baking sheet and toss with 2 Tbsp olive oil and 1/2 tsp salt. Bake for 20 minutes or until starting to brown in spots.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4758A804C9644845B1CACAF6D735C6EE', X'BFC2DF19657C16459E45149AFD28F1B1', X'BFC2DF19657C16459E45149AFD28F1B1', 1, NULL, NULL, 2, NULL, 0, 'Toss the cooked cauliflower with 1/4 cup buffalo sauce and continue to bake for an additional 5 minutes. Remove from oven and taste. Brush with more buffalo sauce if you''d like it spicier.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6A145FBCB5902849AE14AF7E1F09865D', X'F075268075786A449074CB3FE81C95C4', X'F075268075786A449074CB3FE81C95C4', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the Greek yogurt, buttermilk, 1 Tbsp fresh chives, garlic powder, onion powder, and a dash of pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B8E944FA4B2C94B9CB13465E9EF5AF3', X'D4D30865651A6649A57A2601D0A20C4A', X'D4D30865651A6649A57A2601D0A20C4A', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the white wine vinegar, honey, 1/2 tsp salt, and dash of black pepper. Add cabbage and red pepper to the bowl and toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98CFEEC632F84947A717FD292FAC07D9', X'694FE38100DD7D4A9DF868E1664EFA29', X'694FE38100DD7D4A9DF868E1664EFA29', 0, NULL, NULL, 2, NULL, 0, 'Divide the rice between 4 serving bowls and spread to cover the bottom of each bowl. Top each bowl with 1/4 of the spicy slaw, buffalo cauliflower, cherry tomatoes, and scallions/chives. Drizzle with greek yogurt ranch and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6B8556E4647934180504EB51D2A94B1', X'2BE8183043002644AE9F8B931665CA09', X'2BE8183043002644AE9F8B931665CA09', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. On the prepared baking sheet, toss the cauliflower with 1 Tbsp olive oil, smoked paprika, Aleppo powder, turmeric, and a sprinkle and salt and pepper. Spread into a single layer and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D65D4DC2400A0439F5FDF235E830775', X'2BE8183043002644AE9F8B931665CA09', X'2BE8183043002644AE9F8B931665CA09', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring 1 1/2 cups of salted water to a boil in a medium sauce pan. Add couscous, stir, cover, and remove from heat. Set aside and let steam for 10 minutes before fluffing with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC045694D3701E49B9201CD4DB2F5A19', X'2BE8183043002644AE9F8B931665CA09', X'2BE8183043002644AE9F8B931665CA09', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing ingredients, adding water (1 Tbsp at a time), if the dressing is too thick. It should be easy to pour over the salad. Season with salt and pepper and set aside. Add to the fluffed couscous and toss to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA1FB3E738DD0C4D9720DB84D368E9DE', X'2BE8183043002644AE9F8B931665CA09', X'2BE8183043002644AE9F8B931665CA09', 3, NULL, NULL, 2, NULL, 0, 'Once everything is done cooking, add roasted cauliflower, dates, parsley, and black sesame seeds to the couscous. Season with salt and pepper. Can be served warmed or chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4774DC5F8090FA4385C3CC0FBFF918C9', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', 0, NULL, NULL, 2, NULL, 0, 'Add vegetable stock and water and bring to a boil over high heat. Lower heat to medium-low and let simmer for 20 minutes or until the potatoes are soft.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F3639AE8A11AE348947F044D8D3E5DC6', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and use an immersion blender to blend until smooth. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8894709FCF7983418B2D3BF1115C6A2A', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', 2, NULL, NULL, 2, NULL, 0, 'For the coconut bacon: preheat oven to 325 degrees and line a baking sheet with parchment paper. Combine the liquid smoke, soy sauce, and maple syrup in a bowl. Fold the coconut flakes into the liquid smoke mixture until they are completely coated with the sauce. Transfer to the baking sheet and spread out onto a single layer. Baked for 20 minutes, shaking and stirring the flakes every 3-5 minutes to prevent from burning (do it more frequently towards the end as they are very prone to burning right before ready).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DA37752FED2E7C41A993DC8C950C2A9C', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', X'DDB6DA6EA3CEC944849CFEC49E8BCADF', 3, NULL, NULL, 2, NULL, 0, 'To serve: Divide the soup between bowls and top with chives, coconut bacon, sour cream, feta, tomatoes, buffalo sauce, cheese, and whatever other toppings you''ve prepared.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B67E19B5EF7ACC42AA66DC3C4FA2D915', X'21BE47A0AF27F34CB55ACE97721E2F9B', X'21BE47A0AF27F34CB55ACE97721E2F9B', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to the package directions. Strain the pastsa, holding back 1 cup of pasta water and set everything aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'912C577B46C1F74EA738DE158A2F4FF6', X'21BE47A0AF27F34CB55ACE97721E2F9B', X'21BE47A0AF27F34CB55ACE97721E2F9B', 1, NULL, NULL, 2, NULL, 0, 'Meahwhile, heat oil in a large skillet over medium-low. Add garlic cloves and sauté for about 3 to 5 minutes or just until starting to get some color (if they start to turn dark before 3 minutes, turn your heat down).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EBF63589B899CD44B22E1388DB9F4EF6', X'21BE47A0AF27F34CB55ACE97721E2F9B', X'21BE47A0AF27F34CB55ACE97721E2F9B', 2, NULL, NULL, 2, NULL, 0, 'Add walnuts and red pepper flakes and sauté for another 2-3 minutes or until the nuts have started to turn darker and everything is very fragrant. Remove from heat and sprinkl in 1/2 tsp salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'93B71B12A623A449A23C969E9ACA32B6', X'21BE47A0AF27F34CB55ACE97721E2F9B', X'21BE47A0AF27F34CB55ACE97721E2F9B', 3, NULL, NULL, 2, NULL, 0, 'Add the ricotta to a serving platter and pile the pasta over it. Pour the garlic-walnut mixture (including any remaining oil over the pasta), along with the lemon juice, lemon zest, Parmigiano-Reggiano, and 2 Tbsp pasta water. Toss until combined and a thick sauce has formed. Add more pasta water, 1 Tbsp at a time, if the sauce doesn''t thicken up.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E144F93B82F3734AAB40D6397A20FCD8', X'21BE47A0AF27F34CB55ACE97721E2F9B', X'21BE47A0AF27F34CB55ACE97721E2F9B', 4, NULL, NULL, 2, NULL, 0, 'Taste and season with more salt and pepper. Serve with some torn fresh basil or thyme leaves or a dried sprinkle of oregano flakes. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6D2C52CCDD40014B845B75A724C82727', X'CA94DAFFF972EB498431B90BE596F658', X'CA94DAFFF972EB498431B90BE596F658', 0, NULL, NULL, 2, NULL, 0, 'In a large dutch oven or skillet, heat oil over medium heat. Add shallots and garlic and saute for 5 minutes or until onions are translucent. Add couscous and 1/2 tsp salt and saute for another minute to toast them.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AA773B224E2D6E4F817679B86ACF9DA6', X'CA94DAFFF972EB498431B90BE596F658', X'CA94DAFFF972EB498431B90BE596F658', 1, NULL, NULL, 2, NULL, 0, 'Add wine and 1 cup of water and let cook until the liquid has been absorbed, stirring often. Add 1 more cup of water and let cook until the liquid has been absorbed, stirring often. Add remaining cup of water and frozen peas and let cool until absorbed, stirring often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6EEEC9490C3214D8442C2DA8B7276B0', X'CA94DAFFF972EB498431B90BE596F658', X'CA94DAFFF972EB498431B90BE596F658', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and fold in the cheese and lemon zest. Taste and add more salt or cheese, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F7E14C31162FF747B23B391056803516', X'CA94DAFFF972EB498431B90BE596F658', X'CA94DAFFF972EB498431B90BE596F658', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, toss together the radicchio, walnuts, dill, 2 Tbsp olive oil, and lemon juice in a small mixing bowl. Top over the couscous mixture and sprinkle with pepper and red pepper flakes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6235CC685E8F144EA44E1A484F3983E1', X'CA94DAFFF972EB498431B90BE596F658', X'CA94DAFFF972EB498431B90BE596F658', 4, NULL, NULL, 2, NULL, 0, 'Taste and add more salt, pepper, or red pepper flakes, as needed. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'03129AC6E5A755478FAE301E92626426', X'A5ACA01E6E9A434B87EB2CD9133F7463', X'A5ACA01E6E9A434B87EB2CD9133F7463', 0, NULL, NULL, 2, NULL, 0, 'Cook the macaroni, salting the water with 2 Tbsp kosher salt, according to instructions on package until tender. Drain, transfer to a large bowl, and set aside for 10 minutes to let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'43D09EE831AF9D409ADC28400485A7DB', X'A5ACA01E6E9A434B87EB2CD9133F7463', X'A5ACA01E6E9A434B87EB2CD9133F7463', 1, NULL, NULL, 2, NULL, 0, 'Once cooled, fold in onions, 1/2 cup mayonnaise, pickle relish, pickle juice, and a dash of pepper and toss until well coated. Taste and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'170012580799D64E88F6AF742AD8CB94', X'A5ACA01E6E9A434B87EB2CD9133F7463', X'A5ACA01E6E9A434B87EB2CD9133F7463', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, stir in 1/2 cup mayonnaise and the shredded carrot. Taste and add more mayonnaise if it looks dry or more salt if it needs it. Serve chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5FFE6C1377ED2D48896C5C693F5C54DA', X'5F950219DF5CAC40A67B4A90F47102A3', X'5F950219DF5CAC40A67B4A90F47102A3', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E125B0D98852694C899237CA426A8574', X'5F950219DF5CAC40A67B4A90F47102A3', X'5F950219DF5CAC40A67B4A90F47102A3', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat oil in a large skillet over medium heat. Add onion, bell pepper, and poblano and cook, stirring occasionally, for 5 to 7 minutes or until onions are translucent.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D62022F97A3795409140EDE819F8577D', X'5F950219DF5CAC40A67B4A90F47102A3', X'5F950219DF5CAC40A67B4A90F47102A3', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and cook for another minute. Stir in cumin, chili powder, thyme, red pepper flakes, salt, tomatoes, and beans. Lower the heat, cover, and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9227AE6F9ECFA048BB88CFABF59A1356', X'5F950219DF5CAC40A67B4A90F47102A3', X'5F950219DF5CAC40A67B4A90F47102A3', 3, NULL, NULL, 2, NULL, 0, 'When the water is boiling, stir in the pasta and adjust the heat to maintain a low boil. Cook until pasta is al dente (reference package directions).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'65FF999E0FD88D43ACDB3DF90642B320', X'D569662ABEB94D4B85B34FF6136D23BB', X'D569662ABEB94D4B85B34FF6136D23BB', 0, NULL, NULL, 2, NULL, 0, 'Combine all the queso ingredients in a high speed blender and process until completely smooth. If the sauce is too thick to pour, add another tablespoon of water.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'60A211847DE3B14B894DB619CBEA3FEA', X'CA345DBC1AD20D409F65F2CF32B67063', X'CA345DBC1AD20D409F65F2CF32B67063', 0, NULL, NULL, 2, NULL, 0, 'Fold tomato bean mixture, pasta, and queso together until combined. Top with scallions and season with more salt and pepper (if needed).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F5A020334A02C4DBFD27B908A13ED74', X'99CCA8C02C46954393D4D7E7D9A76002', X'99CCA8C02C46954393D4D7E7D9A76002', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. On the prepared baking sheet, toss the cauliflower with 1 Tbsp olive oil, smoked paprika, Aleppo powder, turmeric, and a sprinkle and salt and pepper. Spread into a single layer and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'967B3FF27B63BE43898EB98CFE766BAA', X'99CCA8C02C46954393D4D7E7D9A76002', X'99CCA8C02C46954393D4D7E7D9A76002', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring 1 1/2 cups of salted water to a boil in a medium sauce pan. Add couscous, stir, cover, and remove from heat. Set aside and let steam for 10 minutes before fluffing with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5691494670F29746A1B486567F32AD3A', X'99CCA8C02C46954393D4D7E7D9A76002', X'99CCA8C02C46954393D4D7E7D9A76002', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing ingredients, adding water (1 Tbsp at a time), if the dressing is too thick. It should be easy to pour over the salad. Season with salt and pepper and set aside. Add to the fluffed couscous and toss to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0AD7DF177B5FC84FB8F92999821C7AF4', X'99CCA8C02C46954393D4D7E7D9A76002', X'99CCA8C02C46954393D4D7E7D9A76002', 3, NULL, NULL, 2, NULL, 0, 'Once everything is done cooking, add roasted cauliflower, dates, parsley, and black sesame seeds to the couscous. Season with salt and pepper. Can be served warmed or chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9759E95038134346BBECC1679B737C68', X'DD645666D4B2B1458B09600BE501E215', X'DD645666D4B2B1458B09600BE501E215', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan, heat the olive oil over medium. Add the onions, carrots, and celery and let cook for about 10 minutes (or until they begin to brown). Next, add in the garlic, oregano, and a little bit of salt. Let cook for another minute. Add tomato paste, stir, and cook for 3 more minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5E3F3A48C012C949AE98791EC541E724', X'DD645666D4B2B1458B09600BE501E215', X'DD645666D4B2B1458B09600BE501E215', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and grease a 9x13-inch pan. Using clean hands, roll the mixture into golf ball size balls and place on greased baking sheet. Continue with the rest of the batter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE5505310A85DC4EB4B6CE986DCFA14F', X'ACA0F8D050B79D4884BCB7AC805691A2', X'ACA0F8D050B79D4884BCB7AC805691A2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven 450 degrees F and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'210994E3F54DC7428892D3958EF51DD7', X'3AF215124D764642B6695EB6F6B13AD1', X'3AF215124D764642B6695EB6F6B13AD1', 0, NULL, NULL, 2, NULL, 0, 'Lower oven heat to 400 degrees F and grease a 9` baking dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6AC0F3D32F056E4CA18F23D7240D7F62', X'3AF215124D764642B6695EB6F6B13AD1', X'3AF215124D764642B6695EB6F6B13AD1', 1, NULL, NULL, 2, NULL, 0, 'In a medium skillet, heat olive oil over medium heat. Add zucchini, onion, and bell pepper. Sauté for 5 to 7 minutes, or until softened.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CA6EB192E042414F8046E35FF06A98D1', X'3AF215124D764642B6695EB6F6B13AD1', X'3AF215124D764642B6695EB6F6B13AD1', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and sauté for another 30 seconds. Add tomatoes and sauté until they begin to soften, an additional 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4CEB416F5641014D8188E07C7826B556', X'3AF215124D764642B6695EB6F6B13AD1', X'3AF215124D764642B6695EB6F6B13AD1', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in pasta, chickpeas, ricotta mixture, and mozzarella.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCEFC52BFA30DB4BAFF2EF410AFF9BDD', X'3AF215124D764642B6695EB6F6B13AD1', X'3AF215124D764642B6695EB6F6B13AD1', 4, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes, or until the cheese is bubbly and the top has browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'382530DF88095840AB175BC4066193D3', X'EC57312D0FF44B48BFC3D0792AE2B296', X'EC57312D0FF44B48BFC3D0792AE2B296', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan or Dutch oven over medium heat, warm the olive oil. Add red onion and bell pepper and sauté until vegetables are soft, about 5-7 minutes. Add garlic and sauté for another 30 seconds. Add chili powder, cocoa powder, smoked paprika, cumin, oregano, salt, and chipotle pepper and sauté for another 30 second or until the spices are fragrant and have coated the vegetables. Pour in kidney beans, fire-roasted tomatoes (with their juices), and vegetable stock');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BCBFCAF0583FC646A1A344D6AE2EEC26', X'EC57312D0FF44B48BFC3D0792AE2B296', X'EC57312D0FF44B48BFC3D0792AE2B296', 1, NULL, NULL, 2, NULL, 0, 'Turn heat to medium-high, bring to a boil, then turn heat down to medium-low, cover, and simmer for 20 minutes. Remove the lid and simmer until the chili thickens and most of the liquid has cooked off, about 5-10 more minutes. Remove from heat and let cool slightly. Season to taste with salt and pepper. Ladle into bowls, add toppings, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C9F711BB9D5AFD4E895FFD98C963EB63', X'C36D05B83C63EE428F6C591E71CB3D65', X'C36D05B83C63EE428F6C591E71CB3D65', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a high-speed blender and blend until completely smooth. Taste and adjust the seasoning if necessary (add more adobe sauce for extra heat).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62FA46D85BD1204CAE5A2CFD9700F331', X'7930D66629527A4E95D737B7833E4D72', X'7930D66629527A4E95D737B7833E4D72', 0, NULL, NULL, 2, NULL, 0, 'Heat two tablespoons of olive oil in a large pot over medium heat. Add onion, carrots, and celery and saute until softened, about 5 minutes. Add in butternut squash and garlic and saute for another 30 seconds. Add broth, water, and farro and turn heat up to high. Once boiling, lower heat to medium, and let simmer for 20 minutes. Add tomatoes and white beans and let simmer for another 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'159D25F36A4C83428F71E6DC35F969A7', X'7930D66629527A4E95D737B7833E4D72', X'7930D66629527A4E95D737B7833E4D72', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine sage leaves and olive oil in a high speed blender and blend for 20 seconds or until the sage has been completely blended. Strain through a fine mesh strainer and reserve the green oil for serving.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FEECD94F2CCB6446AE89E81592724364', X'7930D66629527A4E95D737B7833E4D72', X'7930D66629527A4E95D737B7833E4D72', 2, NULL, NULL, 2, NULL, 0, 'When ready to serve, transfer soup to bowls and drizzle with oil, salty cheese, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E715FEBF4F508F48B6380C17888CDDC8', X'91D2F571F3B2C34CA3C81AE533CD2B3B', X'91D2F571F3B2C34CA3C81AE533CD2B3B', 0, NULL, NULL, 2, NULL, 0, 'In a high speed blender, combine poblano, garlic, tahini, lemon juice, olive oil, and 1/4 cup water and blend until smooth. If sauce is too thick, add water as needed to reach desired consistency. Season with salt and pepper and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'615E203F15B11444B33186A5342B9F75', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees F and line baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'87A6352CF10361478D3924446A1592C7', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, cook quinoa according to packaged direction.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0CB0043B071FA945B764C55212DA6FE2', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine sweet potatoes, coconut oil (or olive oil), maple syrup, orange zest, and 1/4 tsp salt. Mix well and transfer to prepared baking sheet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3CA6D8641D0414AA5DC033C2FCF9B98', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 3, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes, flip, and roast for an additional 15 minutes or until potatoes are tender and beginning to brown.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4F97C945F9977408783D6961D5E326F', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small bowl, combine the chickpeas, 2 teaspoons of the olive oil, the paprika, cumin, cayenne, 1/4 teaspoon salt, and 1/8 teaspoon pepper. Mix well until the chickpeas are thoroughly coated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'90D4896FC02E6448BF82A57C1499BA4D', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 5, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, heat the remaining 1 tablespoon of the olive oil. Add the garlic and red pepper flakes. Cook until the garlic is fragrant, about 3 minutes. Add the chard, cover, and cook, stirring occasionally, until the chard begins to wilt, 2 to 3 minutes. Uncover, add dash of salt and pepper and cook, stirring frequently, until the chard is completely wilted and cooked through, an additional 1 to 2 minutes. Discard the garlic cloves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE882798261C034785FC338CC5711D02', X'3D4348F2C4B80740958B62DDCC5D5DF2', X'3D4348F2C4B80740958B62DDCC5D5DF2', 6, NULL, NULL, 2, NULL, 0, 'To serve, put 1/2 cup of the cooked grain in four serving bowls. Top each bowl with equal portions of the roasted sweet potatoes, roasted chickpeas, and sautéed chard. Drizzle with the poblano tahini sauce and serve with extra sauce passed separately.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9E9D09A924329F44B689BA16F2058D9E', X'F6462EC7BC3C714AAF667446BD8E46E2', X'F6462EC7BC3C714AAF667446BD8E46E2', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'22BF9BFF5320FE4BA2528C7B61002AAE', X'F6462EC7BC3C714AAF667446BD8E46E2', X'F6462EC7BC3C714AAF667446BD8E46E2', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat oil in a large skillet over medium heat. Add onion, bell pepper, and poblano and cook, stirring occasionally, for 5 to 7 minutes or until onions are translucent.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D938B74E78CDB84F84A823270422B021', X'F6462EC7BC3C714AAF667446BD8E46E2', X'F6462EC7BC3C714AAF667446BD8E46E2', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and cook for another minute. Stir in cumin, chili powder, thyme, red pepper flakes, salt, tomatoes, and beans. Lower the heat, cover, and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7679030BE304EB409568686E9632AFD0', X'F6462EC7BC3C714AAF667446BD8E46E2', X'F6462EC7BC3C714AAF667446BD8E46E2', 3, NULL, NULL, 2, NULL, 0, 'When the water is boiling, stir in the pasta and adjust the heat to maintain a low boil. Cook until pasta is al dente (reference package directions).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'399BA2D76A6AA14DBBD35B06FEBCE5AD', X'F3751E08E0CE424290A24B3D15FF43CB', X'F3751E08E0CE424290A24B3D15FF43CB', 0, NULL, NULL, 2, NULL, 0, 'Combine all the queso ingredients in a high speed blender and process until completely smooth. If the sauce is too thick to pour, add another tablespoon of water.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7FD0A59588705448F60D54D8B955779', X'F0C710D11032C7468CE073D00300226A', X'F0C710D11032C7468CE073D00300226A', 0, NULL, NULL, 2, NULL, 0, 'Fold tomato bean mixture, pasta, and queso together until combined. Top with scallions and season with more salt and pepper (if needed).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BEE2B7E3F40BAB4B836FF6EC21F56E03', X'1FC29E9821D37040990CA6D6C5BEFC48', X'1FC29E9821D37040990CA6D6C5BEFC48', 0, NULL, NULL, 2, NULL, 0, 'Make sure the parsley is completely dry, Remove leaves from stems and discard stems. Finely chop parsley leaves and then toss with garlic and citrus zest. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A4BB0C1C52ED34A83F2D05B8B03C973', X'B4C2E38D658B634697F24E946FB9BE0C', X'B4C2E38D658B634697F24E946FB9BE0C', 0, NULL, NULL, 2, NULL, 0, 'Bring 3 cups salted water to a boil in a medium saucepan. Slowly add polenta and reduce heat. Cook, stirring often, for 5 minutes. Remove from heat, cover, and set aside until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E02743B9E6D2014AB1576214AA449962', X'B4C2E38D658B634697F24E946FB9BE0C', X'B4C2E38D658B634697F24E946FB9BE0C', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. Place veggies and chickpeas on baking sheet and toss with olive oil, smoked paprika, chili powder, salt, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'098856DECA764D41AE8B50844B933815', X'B4C2E38D658B634697F24E946FB9BE0C', X'B4C2E38D658B634697F24E946FB9BE0C', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20-30 minutes, tossing halfway through, or until the veggies are softened and starting to brown around the edges.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F70E9860720D974C8025457CC58FB4EB', X'337EE959A07F5C4481F34DBDDD7D9517', X'337EE959A07F5C4481F34DBDDD7D9517', 0, NULL, NULL, 2, NULL, 0, 'Divide the polenta between bowls and then top with roasted veggies, chickpeas, and gremolata. Drizzle with additional olive oil (optional) and season with more salt and pepper. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D9DC8A95719DD746B720A0F7938F1EC0', X'EC925454B1ED634BBD60100049C068CB', X'EC925454B1ED634BBD60100049C068CB', 0, NULL, NULL, 2, NULL, 0, 'Add in garlic and sauté for another thirty seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'873F058A9632FD48A48669388A1D0C85', X'EC925454B1ED634BBD60100049C068CB', X'EC925454B1ED634BBD60100049C068CB', 1, NULL, NULL, 2, NULL, 0, 'To serve: divide the rice and beans between four bowls. Garnish with green onions and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'186080BDFEDE5843AD33324A3DCB3DB1', X'4C44E081A0364D4CB5DE4F7B7FD11E72', X'4C44E081A0364D4CB5DE4F7B7FD11E72', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the yogurt, dill, 1/4 tsp garlic powder, 1/4 tsp kosher salt, and 3 to 4 Tbsp water until a loose sauce forms.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DE3E7E965B09E54FB4D4016FE7BA8597', X'803C0D06122265439E8FA0A4C8626708', X'803C0D06122265439E8FA0A4C8626708', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, combine 1 tsp garlic powder, cumin, paprika, cinnamon, red pepper flakes, black pepper, and 3/4 tsp salt. In a large skillet, heat olive oil over medium heat and add in the chickpeas and spices. Saute, stirring often, until chickpeas are warm and coated evenly with the spices (about 5 minutes).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'01D7F35054BF154C97D5BA2B3D084BFF', X'ECD8C356340BF143887ED0C0085A590B', X'ECD8C356340BF143887ED0C0085A590B', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BC3A65113D789C43A49BE2443E9B1ED7', X'0AEDD80C4DEF7B40941E5150DD0023CF', X'0AEDD80C4DEF7B40941E5150DD0023CF', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the olive oil, lemon juice, cinnamon, garlic cloves, tahini, and salt in a food processor until smooth. With the processor running, add in the chickpeas, 1/2 cup at a time, until smooth. Next, add in the butternut squash and pulse until completely smooth. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF7C50BEFB8F8948A08DB9ADEF95F817', X'0AEDD80C4DEF7B40941E5150DD0023CF', X'0AEDD80C4DEF7B40941E5150DD0023CF', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, melt the butter over medium heat. Once melted, watch closely and swirl the pan often until it bubbles and specks of brown butter start to form. Remove from heat and add in sage leaves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AAA7728C40FF8446A0BD0294B348D7EC', X'0AEDD80C4DEF7B40941E5150DD0023CF', X'0AEDD80C4DEF7B40941E5150DD0023CF', 2, NULL, NULL, 2, NULL, 0, 'To assemble: Transfer hummus to a small serving bowl and use the back of the spoon to create an indent in the center. Pour browned butter and sage leaves over the hummus. Sprinkle with a dash of salt and pepper. Serve right away with crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'44A8458E0F351743BA31F1ABE2E6ADC3', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F and line a 9×5-inch bread pan with parchment paper and grease with butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0C33EC64C23164192092831E1577F5D', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 1, NULL, NULL, 2, NULL, 0, 'Wrap the shredded zucchini in a clean kitchen towel and ring out all excess water. Set aside and ring out one more time before using.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F24B430998E21440B803239CAE34760F', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together flour, sugar, baking soda, paprika, and salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2FC7E7E91B67EB46A1AB04DDA5E9EF72', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 3, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients and add egg and buttermilk. Whisk just the buttermilk and egg in the center until combined and then fold the dry ingredients into the wet just until combined (too much mixing will cause the final bread to be tough so you''ll want to be careful here).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'850349AACB674D44A7B92348104A1C84', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 4, NULL, NULL, 2, NULL, 0, 'Mix in cheese, zucchini, and dill and transfer to prepared baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'985C1CA9CC2F9F4DA867940F4C9E244E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 5, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes to 1 hour, or until a toothpick comes out clean from the center and the top is browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'33F7211DAF47B24098FEA2CC933E2448', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', X'CCF5A42FD63C5D4FAF4DEBBEDB30D08E', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool for 15 minutes before slicing. Serve slices with whipped goat cheese and fresh dill.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'88592D26B418344B86B77DFD5A62D1FB', X'ABCE382FE0227A44B540F61F283D96F4', X'ABCE382FE0227A44B540F61F283D96F4', 0, NULL, NULL, 2, NULL, 0, 'Using a stand mixer with the whisk attachment, whisk together the cream cheese and goat cheese until light and creamy. With the stand mixer running, slowly add in 1 Tbsp of milk. The end result should be fluffy and spreadable. If it’s still too thick, add in another tablespoon of milk and whisk until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7F05C8BF5085C4BA6B5434C0E8A1EF7', X'ABCE382FE0227A44B540F61F283D96F4', X'ABCE382FE0227A44B540F61F283D96F4', 1, NULL, NULL, 2, NULL, 0, 'Use right away or store in an airtight container in the fridge.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56D34A47A34C664992FD60190F1BB4AD', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9 inch baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'520BA7CF2FEB914A88E4AD98CB7DBF24', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, fold together the strawberries and rhubarb. Add in orange juice, orange zest, sugar, and cornstarch and mix until combined. Transfer to prepared baking pan and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3C647207F0CAF4408610C2CC1C9C3281', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl (or clean out the one you just used), whisk together the chopped pecans, flour, baking powder, and salt. Add the butter and use cleans hands or a fork to work the butter into the flour mixture until all the butter pieces are pea sized. Drizzle in the milk and mix just until a dough forms.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E11E1B205824AC498A765E11B1D93331', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 3, NULL, NULL, 2, NULL, 0, 'Scoop the dough over the strawberry rhubarb mixture so that you end up with 7 large biscuits evenly arranged on the top (it''s okay if some of the strawberry rhubarb mixture is still showing).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B0C88348C708644D986C117846A80F64', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 4, NULL, NULL, 2, NULL, 0, 'Place the prepared pan onto a baking sheet (to avoid any bubbling over from falling to the bottom of the oven) and transfer to the oven to bake. Bake for 45-55 minutes or until the biscuits are browned and the filling is bubbly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3451C7022149DD409C1687E4D542A5D9', X'F959581B5028F342B8E6F662FE676A6E', X'F959581B5028F342B8E6F662FE676A6E', 5, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool for 10 minutes before serving with Hudsonville''s Dairy Free Birthday cake and more toasted pecans.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F453B7DA32ACAB40A9DF1F6359341267', X'59E63C2B1D11474CB4CAF563F5C668A0', X'59E63C2B1D11474CB4CAF563F5C668A0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees and butter 3 miniature muffin tins (you can also work in batches if you don’t have 3). Warm the milk slightly by transferring to a large microwave safe mixing bowl and microwave for about 1 minute.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0AF4F3316181E34C81BF9DFD176C4E2F', X'59E63C2B1D11474CB4CAF563F5C668A0', X'59E63C2B1D11474CB4CAF563F5C668A0', 1, NULL, NULL, 2, NULL, 0, 'Add eggs, melted butter, flour, 1/4 cup shredded cheese, and salt and whisk just until combined (it’ll be lighter the less it’s mixed). Transfer to the fridge and let chill while you prep the pans.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B393CB70A076040905D9CAFBB2A3E4B', X'59E63C2B1D11474CB4CAF563F5C668A0', X'59E63C2B1D11474CB4CAF563F5C668A0', 2, NULL, NULL, 2, NULL, 0, 'Place pans in preheated oven for 2 minutes (but not much longer or the butter you greased with will smoke). Once warmed, carefully remove from oven and pour the batter into the muffin tin so each is about 3/4 full. Bake for 18-20 minutes or until brown on the top. If working in batches, transfer the leftover batter to the fridge to chill while the rest of the muffins bake.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2BBD9456D288B94BBA9F51C996835BEE', X'59E63C2B1D11474CB4CAF563F5C668A0', X'59E63C2B1D11474CB4CAF563F5C668A0', 3, NULL, NULL, 2, NULL, 0, 'Once browned, remove from oven and transfer to a wire rack. Repeat with remaining batter (if working in batches). Once cooled slightly, transfer to serving platter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6651B2DBFCF7D84AAC4A2311962CB89B', X'59F5CE4D8A872045A5D5D3CE695DC1AD', X'59F5CE4D8A872045A5D5D3CE695DC1AD', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, use a spatula to fold the lemon zest and dill into the butter. Transfer to a serving dish and place on platter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'145165270CDE8A45B9C934729340956F', X'FC384268F64E674F858518173CCDC2A3', X'FC384268F64E674F858518173CCDC2A3', 0, NULL, NULL, 2, NULL, 0, 'Toss apple slices with lemon juice and transfer to serving platter. Transfer the jam and honey to serving bowls and add to your platter. Transfer cheese to serving platter making sure to leave space between them. Fill in remaining space on the board with veggies and crackers. Serve!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CBB7627D9B50E84FA72124AE1A3730BB', X'1670348DA63563439A1EE5ECE1FD9170', X'1670348DA63563439A1EE5ECE1FD9170', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, combine all compote ingredients and bring to a simmer over medium. Let simmer for 20 minutes, stirring occasionally, until thickened. Remove from heat, transfer to a bowl and cover. Chill for at least 2 hours or overnight.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C8CD629AC9BE7743B5DABCFD10FD0495', X'8525A4DB096C6B4D960A6A28E2B0C4D2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a small saucepan over low heat. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'59107D6ACB15894482036A6E926FA107', X'8525A4DB096C6B4D960A6A28E2B0C4D2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together flour, brown sugar, baking powder, salt, and baking soda.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1AE6ADE27DFE224C9FE9D72119281001', X'8525A4DB096C6B4D960A6A28E2B0C4D2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', 2, NULL, NULL, 2, NULL, 0, 'In another bowl, whisk flour, brown sugar, baking powder, salt, and baking soda. Make a well in the center of the dry ingredients and pour in milk, buttermilk, eggs, and butter. Whisk the wet ingredients and then whisk wider to incorporate dry ingredients into wet until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E45765A0572584E9E3CEF0703B871F2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', 3, NULL, NULL, 2, NULL, 0, 'Heat waffle iron according to package directions and lightly coat with butter. Make waffles, again according to the waffle irons directions.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B3D867A6AF1F2844AA28A23840BED940', X'8525A4DB096C6B4D960A6A28E2B0C4D2', X'8525A4DB096C6B4D960A6A28E2B0C4D2', 4, NULL, NULL, 2, NULL, 0, 'Serve with compote and orange slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'12D9B2229B4DDB4B9CF1D912A06DAE1A', X'3D90D5CB7D8C66419BD1C6F5F60281BB', X'3D90D5CB7D8C66419BD1C6F5F60281BB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. Place 1 Tbsp sugar in a small shallow bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E1F032964EA2A04AAE3F0D28678110E1', X'3D90D5CB7D8C66419BD1C6F5F60281BB', X'3D90D5CB7D8C66419BD1C6F5F60281BB', 1, NULL, NULL, 2, NULL, 0, 'In a stand mixer bowl fitted with the paddle attachment, beat together the butter and sugar on medium speed until fluffy and pale in color, about 3 minutes. Beat in egg, almond extract (if using), and orange zest until incorporated, scraping down the sides of the bowl as needed. Beat in sour cream until incorporated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EA85B1FD5D41654FAF600400A5FA304B', X'3D90D5CB7D8C66419BD1C6F5F60281BB', X'3D90D5CB7D8C66419BD1C6F5F60281BB', 2, NULL, NULL, 2, NULL, 0, 'In a separate mixing bowl, whisk together all-purpose flour, almond flour, cardamom, baking powder, salt, and baking soda. Slowly pour into the mixer bowl while the mixer is running on low speed and mix until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'724A8C5B28FAD24AAB6D139B3880A1F9', X'3D90D5CB7D8C66419BD1C6F5F60281BB', X'3D90D5CB7D8C66419BD1C6F5F60281BB', 3, NULL, NULL, 2, NULL, 0, 'Drop tablespoons of dough onto prepared baking sheet, keeping 2 inches in between each cookie. Wet the bottom of a glass with water and dip it into the sugar in the shallow bowl. Press into each cookie to flatten, re-wetting and dipping in sugar after each cookie to keep it from sticking.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD00BFFECC299241B30E07D65FE4EA33', X'3D90D5CB7D8C66419BD1C6F5F60281BB', X'3D90D5CB7D8C66419BD1C6F5F60281BB', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10 to 12 minutes or until starting to brown around edges. Remove from oven and let cool for a few minutes before transferring onto a cooling rack. Let cool completely before adding frosting.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2532A09AF7EACC418E748BB8D9E0F135', X'08FB564813887948AA5CAD8907590E58', X'08FB564813887948AA5CAD8907590E58', 0, NULL, NULL, 2, NULL, 0, 'In a stand mixer fitted with the whisk attachment, cream butter until smooth and fluffy, about 3 minutes. Gradually beat in powdered sugar and vanilla extract until incorporated. Add milk, 1 Tbsp at a time, and beat for an additional 3 minutes or until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EEFD995C58B9A340B590F16A9CA16601', X'CDCD50447871D74AA8A249FDFA3AF9AA', X'CDCD50447871D74AA8A249FDFA3AF9AA', 0, NULL, NULL, 2, NULL, 0, 'Use a peeler to remove off long strips of the orange peel until all of the outer layer is removed. Use a sharp pairing knife to slice peel into 1/4 inch wide strips.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'522766F17118C24799F51291F7FA545B', X'CDCD50447871D74AA8A249FDFA3AF9AA', X'CDCD50447871D74AA8A249FDFA3AF9AA', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat 1 cup sugar and 1 cup water to a boil over high heat. Add orange peel strips and let boil until translucent, about 10 minutes. Remove peels with a slotted spoon and transfer to a wire rack, separating each piece so they don’t stick together as they dry.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C3C07221AB3E0439B507FAC95683152', X'CDCD50447871D74AA8A249FDFA3AF9AA', X'CDCD50447871D74AA8A249FDFA3AF9AA', 2, NULL, NULL, 2, NULL, 0, 'When ready to assemble. frost each cookie using a spatula and add 3-5 strips of orange peel (depending on how many your orange yielded).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70E513539CA2E2478403FF770AE563DD', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 and grease a 9` circle pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'232FE766BEAF6D4C8AF194FFE3C62DAA', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together cornmeal, flour, baking powder, cinnamon, and salt. Make a well in the center of the dry ingredients and add in pumpkin puree, yogurt, honey, butter, and eggs. Whisk together the wet ingredients in the center until combined. Use a spatula to fold the outer dry ingredients into the wet ones just until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C36DBA9991F6F441880178FB79A3AF18', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', 2, NULL, NULL, 2, NULL, 0, 'Transfer batter to prepared pan. Lay granny smith apple slices over the top and gently press in (I fanned them out to create a pretty pattern but you can do whatever you’d prefer).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F098E39255B09E47B1767F07F5DA37EF', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', X'2F0B105FCFAAEF4B82898D4BEAADEFE6', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes or until the center comes out clean. Cool before slicing into pieces (like you would a cake) and serve with your favorite soup. Drizzle with honey (if desired).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E7C84B7EE0041743A06BA1FB79F0C62F', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 9 inch heat-proof skillet.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'525E439C94F7BC4CA19056A983684001', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together all-purpose flour, baking powder, baking soda, salt, and brown sugar until combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FD26A8637E06724A815D3169E5B97238', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 2, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients and add in the olive oil and milk into the center. Whisk together the milk and olive oil until combined (it’s okay if a little of the dry ingredients gets in there) and then switch to a wooden spoon to mix everything together until just combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9CF113DFBAFE1F46B86C8080FE0AF456', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 3, NULL, NULL, 2, NULL, 0, 'Add 3/4 of the chocolate chips and mix until incorporated. Transfer to greased pan and press down dough evenly down onto pan. Sprinkle remaining 1/4 cup chocolate chips on the top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B6EAA4F7C5E75E4F91CB802B62BD8EF5', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 4, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until golden brown. Remove from oven and let cool slightly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E698C39E652E4E458A6E35A18403FAB9', X'3B196D9054C9E54D9AAD65C4697B7789', X'3B196D9054C9E54D9AAD65C4697B7789', 5, NULL, NULL, 2, NULL, 0, 'When ready to serve, scoop Hudsonville Dairy-Free Caramel Cookie Dough Ice Cream over the top, add sprinkles, chocolate chips or chocolate syrup, and top with cherries. Pass around spoons and dig in!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D81A7696ADC7414C8EB1279F76FAA76D', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9 inch round cake pan with butter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'30B603796F808E46B2D6692E2C0362C6', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 1, NULL, NULL, 2, NULL, 0, 'Whisk together almond flour, all-purpose flour, salt, and orange zest in a medium mixing bowl. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7018E98FBA156447A7FF4A67860B195F', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 2, NULL, NULL, 2, NULL, 0, 'Using a stand mixer fitted with the paddle attachment, beat together the beat together the butter and sugar on medium-high until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'37A8C770A04691458F9D1C2147510444', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 3, NULL, NULL, 2, NULL, 0, 'Add the eggs, one at a time, beating between each addition until completely incorporated. Increase speed and beat until mixture has doubled in size and is pale, about 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1D21B443422C994C9E88981226CC20F1', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 4, NULL, NULL, 2, NULL, 0, 'Fold in the flour mixture just until incorporated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9727F55BF9D35F41A3BC49DE91B09F8B', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 5, NULL, NULL, 2, NULL, 0, 'Transfer batter to prepared cake pan and bake for 30 minutes or until the top browns. Remove from oven and let cool. Dust with powdered sugar.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A5C9F056D2E5A4983762D04422F04B7', X'F1319D360BE8BE4BA2C97C5FEE9F4009', X'F1319D360BE8BE4BA2C97C5FEE9F4009', 6, NULL, NULL, 2, NULL, 0, 'To serve, dollop with yogurt, fresh citrus, and mint.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8BE11CDC5D8CB443BF358810316A645C', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line two baking sheets with parchment paper. Remove frozen puff pastry from the freezer and let thaw at room temperature for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0FC6C69F2516E4E93E4699CCE68D126', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 1, NULL, NULL, 2, NULL, 0, 'Add almond butter to a food processor and turn it on. With it running, slowly pour in Oregon Chai Tea Latte Concentrate and continue to process until completely blended. The end result should be smooth and spreadable but not runny. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0C396ED468020C458E64CDABB7FDDD43', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 2, NULL, NULL, 2, NULL, 0, 'Core the apples and thinly slice them (I kept each side of the apples slices grouped together so I could easily fan them onto the tarts (see photos). Squirt the lemon juice over the apple slices to keep them from browning.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6BB7581C8FB0F94FA0ACA7DEEF3B3C6E', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 3, NULL, NULL, 2, NULL, 0, 'Unfold the puff pastry sheets so that they both lay flat. Use a 3` biscuit cutter to cut circular tart bases and transfer them to prepared baking sheets.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'00CB94B757B533449C0F147C4065C6EF', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 4, NULL, NULL, 2, NULL, 0, 'Place 1 Tbsp. almond butter mixture into the center of each round pastry piece and then top with 1/4 of apple slices (fanning them as scene in the photo above). Dust each pastry with a dash of cinnamon.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1212B3BF9B2EF54BA5B4D18CBB5C3A52', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 5, NULL, NULL, 2, NULL, 0, 'Brush the egg wash around the edges of the tarts and place in the freezer to chill for 10 minutes (you''ll want your pastry to be very cold when it goes into the oven to avoid spreading).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B410E7458E35B04C9883B91541DE7AC8', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 6, NULL, NULL, 2, NULL, 0, 'Bake for 20-25 minutes or until the edges are browned and golden. Remove from oven and let cool for 5-10 minutes. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4AD21C56E669A34A8CD4B6D9EB83AE22', X'6C7B8D3F9D11E64D8E506A71B2321AB7', X'6C7B8D3F9D11E64D8E506A71B2321AB7', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the powdered sugar and 2 Tbsp Oregon Chai Tea Latte Concentrate. If needed, whisk in an additional 1 or 2 Tbsp of concentrate until you end up with a thick but pour-able glaze. Drizzle the glaze evenly over the tarts and serve tarts warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'95F2D195F6592C428D0921B87A39DCAD', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F and grease a 9-inch circle baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1828B0561467734C87904F8DC9EF9312', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine rhubarb, strawberries, granulated sugar, and 1/3 cup flour. Transfer to prepared baking pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3CC88FA8F9A5544FA0F1A00309463B09', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl, mix together remaining flour, brown sugar, oats, cinnamon, and nutmeg.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F0EA7FE932489B49B24136A466DF831C', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 3, NULL, NULL, 2, NULL, 0, 'Use clean fingers to incorporate the butter into the flour mixture until it has the consistency of course cornmeal. Sprinkle mixture over strawberry-rhubarb mixture making sure to cover the entire top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B008FBAF429D6547A0D2AB974756C5C0', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 4, NULL, NULL, 2, NULL, 0, 'Place pan on a baking sheet (in case anything boils over) and bake for 30-35 minutes, or until the sides are bubbling and the top is browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E329940EAEAAA4BB1AF1D9F4A39F39E', X'F18FA4B463A21C40898ECB0DB42010C5', X'F18FA4B463A21C40898ECB0DB42010C5', 5, NULL, NULL, 2, NULL, 0, 'Serve warm with ice cream.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BE0C19A3789B594FBD00FB74BD28F570', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a 8 inch loaf pan with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'63339BBFCE8B874180C2B53F30C2F296', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, mix together 2 Tbsp sesame seeds, 2 Tbsp poppy seeds, granulated sugar, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D34B3B2322F90240927F76CA0A0A2534', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 2, NULL, NULL, 2, NULL, 0, 'Make a well in the center of the dry ingredients and add the mashed bananas, eggs, oil, and vanilla extract. Whisk together the wet ingredients (it''s okay if a little bit of the dry ingredients get in there) in the center until evenly mixed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1713F2376417CA4EB6E21881DC5F6722', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 3, NULL, NULL, 2, NULL, 0, 'Once the wet ingredients are mixed, use a spatula to fold the dry ingredients into the wet ingredients just until combined. Fold in the blueberries and transfer to the prepared loaf pan.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7C000663D1D0814EA504457C09831796', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the remaining 2 tsp sesame seeds, 2 tsp poppy seeds, and coarse sugar. Sprinkle evenly over the batter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2780A80ACE16F641BAEEAA71989C1C81', X'B3CD4B92F343DB4E969FD328A8CF8C8F', X'B3CD4B92F343DB4E969FD328A8CF8C8F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 50 minutes or until a toothpick comes out of the center clean and the seeds on the top are toasted. Let cool for 15 minutes before slicing and enjoying.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80B8C71E6D59D342B21C3488FD2E3852', X'CDA2AAB1AE66D644811CC7EB478AF4C5', X'CDA2AAB1AE66D644811CC7EB478AF4C5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees F and line a baking sheet with parchment paper. In a medium bowl, whisk together the flour, baking soda, and fine salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DF9B2D0D6681CB49A616E10E31FD4072', X'CDA2AAB1AE66D644811CC7EB478AF4C5', X'CDA2AAB1AE66D644811CC7EB478AF4C5', 1, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with a paddle attachment, beat the butter and brown sugar on medium-high speed until pale and fluffy. Add tahini, egg, and vanilla and beat until incorporated. Reduce speed to low and add in the dry ingredients beating until combined. Use a spatula to fold in the chocolate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6F2C216C649146458CA2C67B3F1B9919', X'CDA2AAB1AE66D644811CC7EB478AF4C5', X'CDA2AAB1AE66D644811CC7EB478AF4C5', 2, NULL, NULL, 2, NULL, 0, 'Use a 1-inch scoop to scoop out dough into balls onto prepared baking sheet, keeping 2 inches between each cookie. Repeat with the rest of the dough and then sprinkle thick sea salt over the cookies.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE86B155AC44704B97E4794FFAF5DC9F', X'CDA2AAB1AE66D644811CC7EB478AF4C5', X'CDA2AAB1AE66D644811CC7EB478AF4C5', 3, NULL, NULL, 2, NULL, 0, 'If baking right away, transfer to preheated oven and bake for 12 to 15 minutes or until brown around the edges. Let cool on pan for 10 minutes and then serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0D5A57E3DECFF4C9DDD93C236EBA27D', X'CDA2AAB1AE66D644811CC7EB478AF4C5', X'CDA2AAB1AE66D644811CC7EB478AF4C5', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, transfer dough balls into the freezer for one hour and then transfer all the dough into a freezer bag, seal, and store in the freezer for up to 3 months. When wanting one, just bake at 375 for 15 to 18 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DE9FE14F5AEF1F44AD90868B397557F6', X'46E0CD0DDEE6F14BB707E6FD93134D70', X'46E0CD0DDEE6F14BB707E6FD93134D70', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E08A0187EE97B5458B4A2AB8B8A3D7E1', X'46E0CD0DDEE6F14BB707E6FD93134D70', X'46E0CD0DDEE6F14BB707E6FD93134D70', 1, NULL, NULL, 2, NULL, 0, 'Place Earthbound Farm''s Organic Broccoli & Cauliflower on the prepared baking sheet and drizzle 1 Tbsp olive oil, 1/4 tsp salt, and dash of black pepper over them. Toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5006C6997505DD428E571EECFF494B9E', X'46E0CD0DDEE6F14BB707E6FD93134D70', X'46E0CD0DDEE6F14BB707E6FD93134D70', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes or until the vegetables are easily pierced with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'753C03F4086EAD4AAB03A1065869AE3F', X'EEDC3438A7616641B07C94987AE5725C', X'EEDC3438A7616641B07C94987AE5725C', 0, NULL, NULL, 2, NULL, 0, 'Whisk all spicy peanut sauce ingredients together in a small bowl and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ECBF2A8D1449064FB222FC32D1D6EA33', X'7FA4D47BC81A9D4DA7EC281056B40104', X'7FA4D47BC81A9D4DA7EC281056B40104', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the jalapeño juice, vinegar, olive oil, honey / maple syrup, salt, and pepper until combined and emulsified. Add in the pickled jalepenos and broccoli slaw and toss to coat. Taste and add more salt / pepper, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5AD53A574296C748935FF9E4FC3C053C', X'F4131177286BB4419E79CA701A17DCB6', X'F4131177286BB4419E79CA701A17DCB6', 0, NULL, NULL, 2, NULL, 0, 'Divide the quinoa evenly between 4 bowls and top with the roasted vegetables, slaw, and a drizzle of sauce. Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F4666859DFFC4145AEFBA19479944563', X'D072841D41150641817690388C523C40', X'D072841D41150641817690388C523C40', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line a baking sheet with parchment paper. Add cauliflower to baking sheet and toss with 2 Tbsp olive oil and 1/2 tsp salt. Bake for 20 minutes or until starting to brown in spots.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8D72E6C5CF751746AFFDB115CBC4B0C5', X'D072841D41150641817690388C523C40', X'D072841D41150641817690388C523C40', 1, NULL, NULL, 2, NULL, 0, 'Toss the cooked cauliflower with 1/4 cup buffalo sauce and continue to bake for an additional 5 minutes. Remove from oven and taste. Brush with more buffalo sauce if you''d like it spicier.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'70712D1371A61649A219464EF5994789', X'C0539D4433F3D844BBEDE45C4DEB7233', X'C0539D4433F3D844BBEDE45C4DEB7233', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, whisk together the Greek yogurt, buttermilk, 1 Tbsp fresh chives, garlic powder, onion powder, and a dash of pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F7E5444A4EED1F468FD2071B50E20985', X'E18CC6504C514442A0DFAB96D12DF066', X'E18CC6504C514442A0DFAB96D12DF066', 0, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk together the white wine vinegar, honey, 1/2 tsp salt, and dash of black pepper. Add cabbage and red pepper to the bowl and toss to coat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C9C1B9BE3EE2046891605C95BACA789', X'A67263F379DBBC448E73675BF8687451', X'A67263F379DBBC448E73675BF8687451', 0, NULL, NULL, 2, NULL, 0, 'Divide the rice between 4 serving bowls and spread to cover the bottom of each bowl. Top each bowl with 1/4 of the spicy slaw, buffalo cauliflower, cherry tomatoes, and scallions/chives. Drizzle with greek yogurt ranch and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AD57DE5DD1DAB446BF6ADC599BAB8148', X'97FEA02EC8CFDD439C6821B573F7D730', X'97FEA02EC8CFDD439C6821B573F7D730', 0, NULL, NULL, 2, NULL, 0, 'Whisk together all dressing ingredients in a small bowl until combined. Your final dressing should be easy to drizzle but not watery. If the dressing is too thick, add 1 tsp of water at a time until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E24530FB351F084990E7585A8BB664EA', X'F6A868FD55A752459C1AC7327122FCBF', X'F6A868FD55A752459C1AC7327122FCBF', 0, NULL, NULL, 2, NULL, 0, 'Rinse the quinoa thoroughly. Add 2 cups of water and quinoa to a medium saucepan and bring to a boil. Once boiling, cover and turn heat down to low. Let simmer for 15 minutes. Remove from heat and let sit for an additional 5 minutes. Uncover and fluff. Set aside to cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E851BD846F62F4F895EA4D8FB7FA829', X'F6A868FD55A752459C1AC7327122FCBF', X'F6A868FD55A752459C1AC7327122FCBF', 1, NULL, NULL, 2, NULL, 0, 'Once the quinoa is cooled, toss it with cabbage, sweet pepper, carrot, cilantro, and chives.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E55D2582B9DEA74D8DB43CAAB486E1D1', X'F6A868FD55A752459C1AC7327122FCBF', X'F6A868FD55A752459C1AC7327122FCBF', 2, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish and drizzle with the dressing. Top with chopped peanuts and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB78199190D20B46A5D4654FF014C3C7', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 0, NULL, NULL, 2, NULL, 0, 'Bring 2 cups of salted water to a boil in a medium saucepan. Once boiling, add quinoa and reduce to a simmer. Cover and let simmer for 15 minutes. Remove from heat and let sit, covered, for an additional 10 minutes or until most of the liquid is absorbed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D5CC294804E04C4A8912B2A884669B06', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 375 degrees F and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F36755E7ABA194BA15C1C4C45BD18D1', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine 1/2 cup salsa, nutritional yeast (if using), olive oil, cumin, chili powder, paprika, oregano, and a dash of salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A8F95CE950FD854A8C3187C3B0909279', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 3, NULL, NULL, 2, NULL, 0, 'Once the quinoa has sat, fluff with a fork and transfer to mixing bowl. Fold until the quinoa is completed coated with the salsa mixture and transfer to prepared baking sheet. Spread out into an level layer on the baking sheet to ensure even baking.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B175CB7F4D01845A534DBB25820EAA8', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 4, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 35 minutes (time varies so much because different salsas will have different amounts of water content), stirring halfway through, or until most of the quinoa has browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'81D64417A48E784C818CC39C592173D5', X'E4301821CE17D94AAF56098825074941', X'E4301821CE17D94AAF56098825074941', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF77FDE54E9BE841BC2279F64E3B32CE', X'FAB646D15AFBE148891EBB379FB1D820', X'FAB646D15AFBE148891EBB379FB1D820', 0, NULL, NULL, 2, NULL, 0, 'Turn broiler to high. Place half the bag of chips on a baking sheet along with half the cheese, salsa, and jalapeno slices. Place remaining chips on top and top with remaining cheese, salsa, and jalapeno slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F68FA120E439CB44A76C8970426087B1', X'FAB646D15AFBE148891EBB379FB1D820', X'FAB646D15AFBE148891EBB379FB1D820', 1, NULL, NULL, 2, NULL, 0, 'Place baking sheet in oven and broil until cheese is melted and gooey.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5CA87040A27E1A46B607CE5CA53EA859', X'FAB646D15AFBE148891EBB379FB1D820', X'FAB646D15AFBE148891EBB379FB1D820', 2, NULL, NULL, 2, NULL, 0, 'Remove from oven and finish with tomatoes, cilantro, radish slices, lime juice, and hot sauce. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0760D8E45619FF4285E96921885D2ADA', X'FB8E16AF8C0AC0439F1CCF75EE0EC331', X'FB8E16AF8C0AC0439F1CCF75EE0EC331', 0, NULL, NULL, 2, NULL, 0, 'Using a food processor fitted with an S-blade, combine beans, beet pieces, tahini, maple syrup, lemon juice, salt, and pepper and process until completely smooth, 3 to 5 minutes. Season with more salt, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'39639D0FB08E714DABD497A3BD3460D0', X'FB8E16AF8C0AC0439F1CCF75EE0EC331', X'FB8E16AF8C0AC0439F1CCF75EE0EC331', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl, drizzle olive oil and hazelnut over it and then sprinkle with rosemary. Serve with fresh bread, crackers, or veggies.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B5AE810A03D9164484D96E477E8A9EA0', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the olive oil, lemon juice, cinnamon, garlic cloves, tahini, and salt in a food processor until smooth. With the processor running, add in the chickpeas, 1/2 cup at a time, until smooth. Next, add in the butternut squash and pulse until completely smooth. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D5ED44006A46AC40B24799F61DB51D45', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, melt the butter over medium heat. Once melted, watch closely and swirl the pan often until it bubbles and specks of brown butter start to form. Remove from heat and add in sage leaves.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D1A6C22BC80434E9E9D3822649C1BD4', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', X'0B2AD002B2D1AB42B306B9A36CBCE9E4', 2, NULL, NULL, 2, NULL, 0, 'To assemble: Transfer hummus to a small serving bowl and use the back of the spoon to create an indent in the center. Pour browned butter and sage leaves over the hummus. Sprinkle with a dash of salt and pepper. Serve right away with crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6101D707631A5C439DF48313D7EB36BA', X'78ED1AF749C3E74CBECD055BB9F99DD0', X'78ED1AF749C3E74CBECD055BB9F99DD0', 0, NULL, NULL, 2, NULL, 0, 'Pulse the walnuts in a food processor until finely crumbled, four or five times (be careful not to over pulse!).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AE774A6F195A2E4C809EEA5F3483C1BF', X'78ED1AF749C3E74CBECD055BB9F99DD0', X'78ED1AF749C3E74CBECD055BB9F99DD0', 1, NULL, NULL, 2, NULL, 0, 'Transfer paprika, chili, and cumin into a saucepan over medium heat and toast for 30 seconds, stirring often. Add walnuts and salt and toss. Add water and lime juice and stir until completely coated. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6AA95A6A3B1EE54D95B1682090E67234', X'7BB34C7EA456154BA6E8AC81F9E69D8E', X'7BB34C7EA456154BA6E8AC81F9E69D8E', 0, NULL, NULL, 2, NULL, 0, 'Add all salsa ingredients into a bowl and toss to combine. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4BC9E1BEBEB0AB4AA436E61FB708433E', X'CA40E5F25C8F3249A38A521C919FFB92', X'CA40E5F25C8F3249A38A521C919FFB92', 0, NULL, NULL, 2, NULL, 0, 'Warm the tortillas in a saucepan over medium heat for 10 seconds. Double up the tortillas and place 1/4 of the walnut meat and peach salsa in them. Repeat with the remaining tortillas. Top with avocado, cabbage, and yogurt or sour cream (if using). Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9C5126243C6B7B419D38456BD0259958', X'D449E2D163920F4F911DB7FC6B1F82AD', X'D449E2D163920F4F911DB7FC6B1F82AD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'642FA6E8C137894ABABBE1EA8A5EBB1E', X'D449E2D163920F4F911DB7FC6B1F82AD', X'D449E2D163920F4F911DB7FC6B1F82AD', 1, NULL, NULL, 2, NULL, 0, 'Brush both sides of the pita with olive oil and slightly sprinkle with salt. Stack pitas evenly on top of each other and use a sharp chef''s knife to slice into 8 small pieces each.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'41EBF6A0DC7AE341A1CD4959F1AC41B7', X'D449E2D163920F4F911DB7FC6B1F82AD', X'D449E2D163920F4F911DB7FC6B1F82AD', 2, NULL, NULL, 2, NULL, 0, 'Arrange pitas evenly on the baking sheet and bake for 10 minutes or until they are lightly golden on both sides, flipping halfway through.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'81BC489C1811BF4684E33753D83C2F8E', X'C84DFCC8F1761F40BDDDE6CA1A05842B', X'C84DFCC8F1761F40BDDDE6CA1A05842B', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine all of the salad ingredients and toss to combine. Taste and season with more salt (if needed). Set aside to marinate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'865347111EB5C640A1B19E3689FFFF60', X'0558816283BBC24CB99F1CDE99210D54', X'0558816283BBC24CB99F1CDE99210D54', 0, NULL, NULL, 2, NULL, 0, 'In a small food processor, combine the tahini, lemon juice, herbs, water, and salt and blend until smooth. If the sauce is not pour-able, add more water (1 Tbsp at a time) and continue to blend until it''s easy to drizzle.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FE20B79D93AB714FB8DED75582E38779', X'AE246DCB32EFEC4A98294041092217C9', X'AE246DCB32EFEC4A98294041092217C9', 0, NULL, NULL, 2, NULL, 0, 'Arrange the pita pieces on a large serving tray. Stir the salad once more and then spoon over pita wedges. Drizzle tahini sauce over it all and garnish with more fresh herbs. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AEFA6F5CA778A144B034209237C68CCA', X'5CFFF5C81C9CCB40AF5974035ADA7B06', X'5CFFF5C81C9CCB40AF5974035ADA7B06', 0, NULL, NULL, 2, NULL, 0, 'Make sure the parsley is completely dry, Remove leaves from stems and discard stems. Finely chop parsley leaves and then toss with garlic and citrus zest. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A6AB7D80D6A5B7429347773955CB7EE0', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', 0, NULL, NULL, 2, NULL, 0, 'Bring 3 cups salted water to a boil in a medium saucepan. Slowly add polenta and reduce heat. Cook, stirring often, for 5 minutes. Remove from heat, cover, and set aside until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA6AA741370D5D4989E3D51BAE98610A', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees and line a baking sheet with parchment paper. Place veggies and chickpeas on baking sheet and toss with olive oil, smoked paprika, chili powder, salt, and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2A50E6BA801A0941AE0D8E6F71CF059A', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', X'AD0E5F414D9E884BB4CA5EA1701B8EDA', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20-30 minutes, tossing halfway through, or until the veggies are softened and starting to brown around the edges.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB73C36619706947B003D059244B9294', X'4D7324E45CD94C489925D2CC40F4295F', X'4D7324E45CD94C489925D2CC40F4295F', 0, NULL, NULL, 2, NULL, 0, 'Divide the polenta between bowls and then top with roasted veggies, chickpeas, and gremolata. Drizzle with additional olive oil (optional) and season with more salt and pepper. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1586A0D2C9156B478DFCA5A6ED8738AD', X'6E4BA585E82DC74F9151AFA3A2F139AD', X'6E4BA585E82DC74F9151AFA3A2F139AD', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan, heat the olive oil over medium. Add the onions, carrots, and celery and let cook for about 10 minutes (or until they begin to brown). Next, add in the garlic, oregano, and a little bit of salt. Let cook for another minute. Add tomato paste, stir, and cook for 3 more minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4870F750C6C92A4F8FFCD4B14FC4A05D', X'6E4BA585E82DC74F9151AFA3A2F139AD', X'6E4BA585E82DC74F9151AFA3A2F139AD', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and grease a 9x13-inch pan. Using clean hands, roll the mixture into golf ball size balls and place on greased baking sheet. Continue with the rest of the batter.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18854812F6F27D44A9D5C84650D24B69', X'283B73CBD21BF843BD717E2A7FB5E548', X'283B73CBD21BF843BD717E2A7FB5E548', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp olive oil in a medium non-stick skillet over medium high heat. Once the oil is hot, crack each of your eggs into the skillet and sprinkle with a little salt and pepper. Cook until the white parts are golden brown and bubbly around the edges. If you like your egg yolk on the firmer side, cover with a lid while cooking. Once ready, remove from pan using a spatula and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3FA50E3F52308949A9ACBCD44B2A06E6', X'283B73CBD21BF843BD717E2A7FB5E548', X'283B73CBD21BF843BD717E2A7FB5E548', 1, NULL, NULL, 2, NULL, 0, 'Turn heat off but keep the used pan on the stove (it should still have a little olive oil on it from frying the eggs). Add pita slices to the pan, flipping every few seconds, until warmed and just starting to brown. Remove from heat.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9987DFD38EBA864FA31ADB613826FECE', X'283B73CBD21BF843BD717E2A7FB5E548', X'283B73CBD21BF843BD717E2A7FB5E548', 2, NULL, NULL, 2, NULL, 0, 'In a high speed blender or food processor, blend the tahini, cilantro, parsley, garlic, cumin, za''atar, salt, lemon juice, and 1/4 cup water until creamy and no lumps remain. If it''s too thick to drizzle then add more water, 1 Tbsp at a time, until desired consistency is reached.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9741938188DD0D4CB9A41969A85A2721', X'283B73CBD21BF843BD717E2A7FB5E548', X'283B73CBD21BF843BD717E2A7FB5E548', 3, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'30E4B32E78717E4BB0A048D873C8BEF8', X'283B73CBD21BF843BD717E2A7FB5E548', X'283B73CBD21BF843BD717E2A7FB5E548', 4, NULL, NULL, 2, NULL, 0, 'The ''I''ve got my act together'' way to serve: Place pita pieces on two separate plates and top each with an egg, tahini, salt, pepper, parsley, and za''atar. Enjoy right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'977DDCAFB3ED6C4F804AD1A6FC0C3224', X'15A2761C865CA04C8BC7D56D52173C73', X'15A2761C865CA04C8BC7D56D52173C73', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven 450 degrees F and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'96DAB0FA5A13EA40BC5A6089F3867D5D', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', 0, NULL, NULL, 2, NULL, 0, 'Lower oven heat to 400 degrees F and grease a 9` baking dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CFED81D4E9961449A84AE8423D17C7D9', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', 1, NULL, NULL, 2, NULL, 0, 'In a medium skillet, heat olive oil over medium heat. Add zucchini, onion, and bell pepper. Sauté for 5 to 7 minutes, or until softened.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'106F6A6C7942DA47917D560334E72683', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and sauté for another 30 seconds. Add tomatoes and sauté until they begin to soften, an additional 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'198824B64AD1D647AC7E76F4B5BD640F', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in pasta, chickpeas, ricotta mixture, and mozzarella.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7AAA7CAF820D594A92151A8E89A72439', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', X'2AC0C9DFDAF0D34B970FCDC04FFB0ACC', 4, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes, or until the cheese is bubbly and the top has browned.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2C1BD16C36B27A4CA414A8A595BC2596', X'88FFDE278EC84F42B2356603AE67A519', X'88FFDE278EC84F42B2356603AE67A519', 0, NULL, NULL, 2, NULL, 0, 'Add in garlic and sauté for another thirty seconds.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AB1B8B5D594C064BA020FCEF42727554', X'88FFDE278EC84F42B2356603AE67A519', X'88FFDE278EC84F42B2356603AE67A519', 1, NULL, NULL, 2, NULL, 0, 'To serve: divide the rice and beans between four bowls. Garnish with green onions and serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56F5E6A3BEDAB147B22B56DDF20155A0', X'B41DE1EA61F5F24D8F8C360FAC283A65', X'B41DE1EA61F5F24D8F8C360FAC283A65', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A723A0C5FDE11A43998945538F86A739', X'B41DE1EA61F5F24D8F8C360FAC283A65', X'B41DE1EA61F5F24D8F8C360FAC283A65', 1, NULL, NULL, 2, NULL, 0, 'Brush both sides of the pita with olive oil and slightly sprinkle with salt. Stack pitas evenly on top of each other and use a sharp chef''s knife to slice into 8 small pieces each.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A0029315AA02784D913969AD707A106A', X'B41DE1EA61F5F24D8F8C360FAC283A65', X'B41DE1EA61F5F24D8F8C360FAC283A65', 2, NULL, NULL, 2, NULL, 0, 'Arrange pitas evenly on the baking sheet and bake for 10 minutes or until they are lightly golden on both sides, flipping halfway through.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'ECF2A6442B71EB4BA5F958B5C2A99583', X'750F2568AEE8754D9155366CC1CC641E', X'750F2568AEE8754D9155366CC1CC641E', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine all of the salad ingredients and toss to combine. Taste and season with more salt (if needed). Set aside to marinate.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6CD415083412544883018CDD446CDB94', X'D4281DBF644F5E4C855DE2A0F4EF5B41', X'D4281DBF644F5E4C855DE2A0F4EF5B41', 0, NULL, NULL, 2, NULL, 0, 'In a small food processor, combine the tahini, lemon juice, herbs, water, and salt and blend until smooth. If the sauce is not pour-able, add more water (1 Tbsp at a time) and continue to blend until it''s easy to drizzle.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E463D30E2DE83742A0C9F724F71513E2', X'4AA9C137C64E46479263584AFF152933', X'4AA9C137C64E46479263584AFF152933', 0, NULL, NULL, 2, NULL, 0, 'Arrange the pita pieces on a large serving tray. Stir the salad once more and then spoon over pita wedges. Drizzle tahini sauce over it all and garnish with more fresh herbs. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'18C2A4C12539BF46BD4800AE5AAE8242', X'CCE136A82CADF84B93186855A8CB4E18', X'CCE136A82CADF84B93186855A8CB4E18', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Add farro and cook until farro is tender, about 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'98FED2C54F3D034E88E57E3BC19B2FB1', X'CCE136A82CADF84B93186855A8CB4E18', X'CCE136A82CADF84B93186855A8CB4E18', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk together the olive oil, orange zest, orange juice, ginger, and garlic paste.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D267418D42F48E42AA0D2D18F2A17F2E', X'CCE136A82CADF84B93186855A8CB4E18', X'CCE136A82CADF84B93186855A8CB4E18', 2, NULL, NULL, 2, NULL, 0, 'Once the farro is ready, drain and transfer to a mixing bowl. Fold in the orange dressing, apricots, pistachios, and cilantro. Season with salt and pepper and garnish with jalapeno slices (if you like heat, skip if not).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'00F970F5D5F5FE4A9379FA5C3C529790', X'CCE136A82CADF84B93186855A8CB4E18', X'CCE136A82CADF84B93186855A8CB4E18', 3, NULL, NULL, 2, NULL, 0, 'This can be served warm but I like to transfer it to the fridge for a few hours so the dressing has time to soak into the farro.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B58CDB19824AD54E97B173A617685492', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', 0, NULL, NULL, 2, NULL, 0, 'Bring a small saucepan of water to a boil. Use a spoon to carefully lower the eggs into the boiling water. Turn heat down to medium-low and let the water boil at a low simmer for 9 minutes. Drain and rinse eggs under cold running water until cool enough peel. Once peeled, slice eggs into quarters and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3DE54ED72A69BE4BADEB258C143036CC', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', 1, NULL, NULL, 2, NULL, 0, 'While the eggs are cooking, whisk together the mayo and beet juice in a small bowl (starting with 1/2 tsp and adding more if you want it tangier).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8A48105E33E7644EB31C32140CDE0BB0', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', X'DFCFCF3A3218CB4FA7E192B1F8DACC05', 2, NULL, NULL, 2, NULL, 0, 'Once the eggs are ready and cut, place them on the toasted pita along with the pickled beets and celery. Drizzle pink mayo, sprinkle chopped mayo, and season with a dash of salt and pepper. Taste and then season with more salt and pepper, as needed. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'99BC5EA72AABC84CB287FE21BCEBB499', X'0E8A98D6E180DA4AA799DAAF6E698C8F', X'0E8A98D6E180DA4AA799DAAF6E698C8F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. On the prepared baking sheet, toss the cauliflower with 1 Tbsp olive oil, smoked paprika, Aleppo powder, turmeric, and a sprinkle and salt and pepper. Spread into a single layer and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'16EAFE5C6D89144FA020DE2B55BF84C4', X'0E8A98D6E180DA4AA799DAAF6E698C8F', X'0E8A98D6E180DA4AA799DAAF6E698C8F', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring 1 1/2 cups of salted water to a boil in a medium sauce pan. Add couscous, stir, cover, and remove from heat. Set aside and let steam for 10 minutes before fluffing with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'596A6498F5053641A667C54D1BE048AF', X'0E8A98D6E180DA4AA799DAAF6E698C8F', X'0E8A98D6E180DA4AA799DAAF6E698C8F', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing ingredients, adding water (1 Tbsp at a time), if the dressing is too thick. It should be easy to pour over the salad. Season with salt and pepper and set aside. Add to the fluffed couscous and toss to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F2982470C055D14E884910E8BCD92E20', X'0E8A98D6E180DA4AA799DAAF6E698C8F', X'0E8A98D6E180DA4AA799DAAF6E698C8F', 3, NULL, NULL, 2, NULL, 0, 'Once everything is done cooking, add roasted cauliflower, dates, parsley, and black sesame seeds to the couscous. Season with salt and pepper. Can be served warmed or chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C497472A25F03468F6583D80302A0AA', X'59586D0249891941847D135005463A9C', X'59586D0249891941847D135005463A9C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven 450 degrees and line a baking sheet with parchment paper. Add broccoli to prepared sheet and toss with 2 Tbsp olive oil. Sprinkle with a little salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6303A2760C93FB48826E823CB61960D8', X'59586D0249891941847D135005463A9C', X'59586D0249891941847D135005463A9C', 1, NULL, NULL, 2, NULL, 0, 'Roast for 20 - 25 minutes, tossing often, until browned and tender. Remove from oven and transfer to a serving dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1E5938250E41A348A385D10892372A22', X'FEB03705A344194BA5744FF3A9BF0D0A', X'FEB03705A344194BA5744FF3A9BF0D0A', 0, NULL, NULL, 2, NULL, 0, 'While the broccoli roasts, bring 2 cups of salted water to a boil in a medium saucepan. Once boiling, add quinoa and reduce to a simmer. Cover and let simmer for 15 minutes. Remove from heat and let sit, covered for an additional 10 minutes or until most of the liquid is absorbed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'93CC58F5FA061C419936C5427D61272F', X'FEB03705A344194BA5744FF3A9BF0D0A', X'FEB03705A344194BA5744FF3A9BF0D0A', 1, NULL, NULL, 2, NULL, 0, 'Once the quinoa has sat, warm 2 Tbsp olive oil in a large frying pan over medium heat. Fluff quinoa with a fork and then add to the frying pan. Toast the quinoa by tossing it often and letting the heat dry it out for about 15 minutes or until it has turned golden and crunchy. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'735F17C97E29DD4993F6C646B0F746B5', X'E294F15FB45F554FB98587E7690CC024', X'E294F15FB45F554FB98587E7690CC024', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium heat (you can re-use the one you made the quinoa in if you rinse it out), whisk together 2 Tbsp butter and 2 Tbsp flour until a thick dough has formed. Slowly add the milk, whisking the entire time, fully incorporating it into the flour mixture. Let the mixture cook for about 5 minutes or until thickened, whisking often. Remove from heat and whisk in the cheese and a dash of salt and pepper. Taste and add more salt, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7DB0C49A60223141B654336814FD3F4C', X'1E1ED2F51662FC45AD5B2D8036C36050', X'1E1ED2F51662FC45AD5B2D8036C36050', 0, NULL, NULL, 2, NULL, 0, 'Pour the cheese sauce over the broccoli and top with crispy quinoa. Sprinkle with roasted red pepper pieces, if using. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'909939EE5C38254183B200E7AA2F3918', X'53C41EB513DC5F459B75DB5050879E6E', X'53C41EB513DC5F459B75DB5050879E6E', 0, NULL, NULL, 2, NULL, 0, 'Using a food processor fitted with an S-blade, combine beans, beet pieces, tahini, maple syrup, lemon juice, salt, and pepper and process until completely smooth, 3 to 5 minutes. Season with more salt, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B10C786D1B81E41B5623F3E3545042A', X'53C41EB513DC5F459B75DB5050879E6E', X'53C41EB513DC5F459B75DB5050879E6E', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a serving bowl, drizzle olive oil and hazelnut over it and then sprinkle with rosemary. Serve with fresh bread, crackers, or veggies.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0B4BBCF0DA7AEA4EB14EDD33CD484DDB', X'4547BE967E29AD469CE5410E2489C2E3', X'4547BE967E29AD469CE5410E2489C2E3', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'796AA16256515A46B99A41B21187B759', X'4547BE967E29AD469CE5410E2489C2E3', X'4547BE967E29AD469CE5410E2489C2E3', 1, NULL, NULL, 2, NULL, 0, 'Add cream cheese, pumpkin, cheddar cheese, paprika, salt, and pepper to a food processor and process until completely combined and a smooth paste remains.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B9C9EA070AF3442B36484E816D1C882', X'4547BE967E29AD469CE5410E2489C2E3', X'4547BE967E29AD469CE5410E2489C2E3', 2, NULL, NULL, 2, NULL, 0, 'Using clean and damp hands, divide the dough in half and each half into a circle using the palm of your hands. Transfer to prepared baking sheet and place in the freezer to chill for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50C90E51F3566E48A254EECB5F64D93F', X'4547BE967E29AD469CE5410E2489C2E3', X'4547BE967E29AD469CE5410E2489C2E3', 3, NULL, NULL, 2, NULL, 0, 'Mix together the sesame seeds and poppy seeds in a shallow rimmed dish. Remove chilled balls from freezer and roll each ball in the seed mixture until completely coated.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FA5B565F9DC82D47B9F993724263176D', X'4547BE967E29AD469CE5410E2489C2E3', X'4547BE967E29AD469CE5410E2489C2E3', 4, NULL, NULL, 2, NULL, 0, 'Transfer to a serving dish to serve right away or store in an airtight container in the fridge until ready to serve. Serve with veggies, chips, and crackers.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0D541265178993478BBE04575398963A', X'F3504D4DA1F74B47A059C80D02B82ED8', X'F3504D4DA1F74B47A059C80D02B82ED8', 0, NULL, NULL, 2, NULL, 0, 'Cut the goat cheese into 1-inch discs (you can also roll them into small balls if the cheese falls apart).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4A489AAAC083604A9893B3CF37134135', X'F3504D4DA1F74B47A059C80D02B82ED8', X'F3504D4DA1F74B47A059C80D02B82ED8', 1, NULL, NULL, 2, NULL, 0, 'In a small but deep bowl, combine the olive oil, green olives, sun-dried tomatoes, and rosemary. Gently set the goat cheese into the olive oil mixture so that it''s completely immersed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'89ADDA04537D72469CE1AF43F0165F88', X'F3504D4DA1F74B47A059C80D02B82ED8', X'F3504D4DA1F74B47A059C80D02B82ED8', 2, NULL, NULL, 2, NULL, 0, 'Transfer to the fridge and let marinate for at least 1 hour.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B4B3AD921BFF8428631368E87905B13', X'F3504D4DA1F74B47A059C80D02B82ED8', X'F3504D4DA1F74B47A059C80D02B82ED8', 3, NULL, NULL, 2, NULL, 0, 'Right before serving, transfer the goat cheese onto a serving platter. Spoon the green olives, sun-dried tomatoes, and rosemary over the goat cheese and drizzle with a little bit of the olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'632DE396355C9640839E2F58217913AF', X'F3504D4DA1F74B47A059C80D02B82ED8', X'F3504D4DA1F74B47A059C80D02B82ED8', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt and pepper. Serve with crackers and a bottle of Day Owl.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7D8837DAD230744398C176E97935E0CC', X'02E31E3CE55C4A45AFF61BB8276B7DF7', X'02E31E3CE55C4A45AFF61BB8276B7DF7', 0, NULL, NULL, 2, NULL, 0, 'Combine all of the chai concentrate ingredients, except the black tea, into a small saucepan and bring to a boil over high heat. Once boiling, reduce heat to medium-low, cover, and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'75C16DCFE3767D43BFD27286154ABFF0', X'02E31E3CE55C4A45AFF61BB8276B7DF7', X'02E31E3CE55C4A45AFF61BB8276B7DF7', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tea bags, and let steep for 5 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E40543447DCEC64D930CB7AAA9DDF94B', X'02E31E3CE55C4A45AFF61BB8276B7DF7', X'02E31E3CE55C4A45AFF61BB8276B7DF7', 2, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture through a strainer and discard the spices (or save some for garnish). Let cool to room temperature.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'647EB04E36C7A6489143CD0E28C63B84', X'02E31E3CE55C4A45AFF61BB8276B7DF7', X'02E31E3CE55C4A45AFF61BB8276B7DF7', 3, NULL, NULL, 2, NULL, 0, 'Pour 1.5 ounces of spiced rum into 4 cocktail glasses. Add 1/4 cup of both the chai concentrate and apple cider into each glass and garnish with a star anise. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'54C7A8012A6F6C47A4794FE10465A963', X'02E31E3CE55C4A45AFF61BB8276B7DF7', X'02E31E3CE55C4A45AFF61BB8276B7DF7', 4, NULL, NULL, 2, NULL, 0, 'Alternatively, you can warm the cider and chai in a small saucepan and serve the cocktails warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C8DD3B96D3816499AA2116738B5AD6A', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 0, NULL, NULL, 2, NULL, 0, 'Prepare the ziti according to the package direction. Drain and yet aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'885BD5FD960DF444B4CA1FC417E35469', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 1, NULL, NULL, 2, NULL, 0, 'While the pasta is cooking, prepare the caramelized onions: Preheat a large saucepan over medium heat. Add onions, 3 Tbsp olive oil, and a tiny pinch of baking soda (Isa says this will help speed up the caramelization process). Saute, stirring often, for 15 minute or until deeply caramelized. Turn off heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D3F898FC4CE8EE42B2BB1B9689D89D1E', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9x13-inch baking dish with olive oil.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'84F8DF16AE499D4AB2A57F1F32A00006', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 3, NULL, NULL, 2, NULL, 0, 'To make the cashew ricotta: Combine cashews, lemon juice, 2 Tbsp olive oil, garlic cloves, 1 1/2 tsp dried thyme, and 1 1/2 tsp salt in a high speed blender or food processor until a finely chopped mixture is created that looks similar to ricotta.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D4EAA04C2C79B340A50C119DF116FC56', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together cashew ricotta, maple syrup, paprika, thyme, salt, nutmeg, cayenne, pumpkin puree, milk, and black pepper. Add caramelized onions and pasta and stir until completely combined. Transfer to the prepared basking dish and smooth on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B81894F97C0D7140BAF9328285F86B40', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 5, NULL, NULL, 2, NULL, 0, 'Prepare the breadcrumbs: Combine bread crumbs, walnuts, nutritional yeast, sage, rosemary, and salt. Drizzle with olive oil and toss so that the oil has completely coated the mixture. Sprinkle crumbs over the casserole.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'347DAC6F94690C4E903EE784EB2564D0', X'5C7C6C881D6DE149BA1D165144001BBB', X'5C7C6C881D6DE149BA1D165144001BBB', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, uncovered, or until the crumb is golden brown. Remove from oven, let cool for 10 minutes, and serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E201195CF624F843A75B02C9C7E3F2DF', X'8AF310310269B547BFEEA48323F40EE1', X'8AF310310269B547BFEEA48323F40EE1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper. On the prepared baking sheet, toss the cauliflower with 1 Tbsp olive oil, smoked paprika, Aleppo powder, turmeric, and a sprinkle and salt and pepper. Spread into a single layer and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B83AAAA0B1810940BA7C77FB02BBBEDE', X'8AF310310269B547BFEEA48323F40EE1', X'8AF310310269B547BFEEA48323F40EE1', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring 1 1/2 cups of salted water to a boil in a medium sauce pan. Add couscous, stir, cover, and remove from heat. Set aside and let steam for 10 minutes before fluffing with a fork.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3ACABD8B915EAD49ABA6796A9995D602', X'8AF310310269B547BFEEA48323F40EE1', X'8AF310310269B547BFEEA48323F40EE1', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing ingredients, adding water (1 Tbsp at a time), if the dressing is too thick. It should be easy to pour over the salad. Season with salt and pepper and set aside. Add to the fluffed couscous and toss to combine.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A2BD1FB2722DB347B611BD9944054A24', X'8AF310310269B547BFEEA48323F40EE1', X'8AF310310269B547BFEEA48323F40EE1', 3, NULL, NULL, 2, NULL, 0, 'Once everything is done cooking, add roasted cauliflower, dates, parsley, and black sesame seeds to the couscous. Season with salt and pepper. Can be served warmed or chilled.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A34CB3D7FAAD904DAB0F98530F63B845', X'2F5E2BB02DAF2B49B66427C4D440EAC1', X'2F5E2BB02DAF2B49B66427C4D440EAC1', 0, NULL, NULL, 2, NULL, 0, 'Add vegetable stock and water and bring to a boil over high heat. Lower heat to medium-low and let simmer for 20 minutes or until the potatoes are soft.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E308448ACBB0574C8B3CE39086CC38ED', X'2F5E2BB02DAF2B49B66427C4D440EAC1', X'2F5E2BB02DAF2B49B66427C4D440EAC1', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and use an immersion blender to blend until smooth. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'AF00FA38B879CC4EB603BE4CA9AF2BC8', X'2F5E2BB02DAF2B49B66427C4D440EAC1', X'2F5E2BB02DAF2B49B66427C4D440EAC1', 2, NULL, NULL, 2, NULL, 0, 'For the coconut bacon: preheat oven to 325 degrees and line a baking sheet with parchment paper. Combine the liquid smoke, soy sauce, and maple syrup in a bowl. Fold the coconut flakes into the liquid smoke mixture until they are completely coated with the sauce. Transfer to the baking sheet and spread out onto a single layer. Baked for 20 minutes, shaking and stirring the flakes every 3-5 minutes to prevent from burning (do it more frequently towards the end as they are very prone to burning right before ready).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4C609341FEE2EC41BFC39AB5F66DC22E', X'2F5E2BB02DAF2B49B66427C4D440EAC1', X'2F5E2BB02DAF2B49B66427C4D440EAC1', 3, NULL, NULL, 2, NULL, 0, 'To serve: Divide the soup between bowls and top with chives, coconut bacon, sour cream, feta, tomatoes, buffalo sauce, cheese, and whatever other toppings you''ve prepared.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'52C1029DACA46C4696A536A6D29BB6E3', X'2C665A3BC48A6B408139859A0D26B680', X'2C665A3BC48A6B408139859A0D26B680', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven 450 degrees and line a baking sheet with parchment paper. Add broccoli to prepared sheet and toss with 2 Tbsp olive oil. Sprinkle with a little salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5348553EC9551041892CF83ED6E95D8C', X'2C665A3BC48A6B408139859A0D26B680', X'2C665A3BC48A6B408139859A0D26B680', 1, NULL, NULL, 2, NULL, 0, 'Roast for 20 - 25 minutes, tossing often, until browned and tender. Remove from oven and transfer to a serving dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'03B22619ABE0AA4784F8047CA91EF0FA', X'CE15FB827CCE224EAE5E7A7D07BF8B16', X'CE15FB827CCE224EAE5E7A7D07BF8B16', 0, NULL, NULL, 2, NULL, 0, 'While the broccoli roasts, bring 2 cups of salted water to a boil in a medium saucepan. Once boiling, add quinoa and reduce to a simmer. Cover and let simmer for 15 minutes. Remove from heat and let sit, covered for an additional 10 minutes or until most of the liquid is absorbed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'51808FC0ADD1E447A02DCB7577AD6B8B', X'CE15FB827CCE224EAE5E7A7D07BF8B16', X'CE15FB827CCE224EAE5E7A7D07BF8B16', 1, NULL, NULL, 2, NULL, 0, 'Once the quinoa has sat, warm 2 Tbsp olive oil in a large frying pan over medium heat. Fluff quinoa with a fork and then add to the frying pan. Toast the quinoa by tossing it often and letting the heat dry it out for about 15 minutes or until it has turned golden and crunchy. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DDC3F7D656A5AD449201F2C6D3557FD0', X'087C6C3F728794438DB95E0DA6AB7726', X'087C6C3F728794438DB95E0DA6AB7726', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium heat (you can re-use the one you made the quinoa in if you rinse it out), whisk together 2 Tbsp butter and 2 Tbsp flour until a thick dough has formed. Slowly add the milk, whisking the entire time, fully incorporating it into the flour mixture. Let the mixture cook for about 5 minutes or until thickened, whisking often. Remove from heat and whisk in the cheese and a dash of salt and pepper. Taste and add more salt, if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3D950B1DC4F1B7428D2B777702E80AEF', X'336601A8FA041948B6D2313462926955', X'336601A8FA041948B6D2313462926955', 0, NULL, NULL, 2, NULL, 0, 'Pour the cheese sauce over the broccoli and top with crispy quinoa. Sprinkle with roasted red pepper pieces, if using. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'76FF652A5FABC04AB2FA7E44754A20B8', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and grease a 10` round baking dish.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F960DA2D2F3C624897353412F898F548', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the flours, baking soda, 1 tsp cinnamon, 1/2 tsp nutmeg, and 1/2 tsp salt. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'450AF4758E870A43B536D725AA753723', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 2, NULL, NULL, 2, NULL, 0, 'In the mixing bowl of a stand attachment, cream together 1/2 cup butter and 1/2 cup brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'85662934A4453641BAB2D7030A541F9B', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, one at a time, beating after each addition.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C07FFADBB2935A4387CFDAC967F918DD', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 4, NULL, NULL, 2, NULL, 0, 'With the mixer on low, alternative between adding the flour mixture, maple syrup, and Greek yogurt. Beat in vanilla extra.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'62C32B6A1D1BCE4089C7E14FCD3DF383', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 5, NULL, NULL, 2, NULL, 0, 'Turn mixer up to medium and beat until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'61DE8310635436469FF114F44D38950E', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 6, NULL, NULL, 2, NULL, 0, 'Fold in apple chunks and transfer to greased baking dish. Spread out evenly.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6591E56982908B44885B1E8A87D68F5E', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 7, NULL, NULL, 2, NULL, 0, 'Combine 1/2 cup brown sugar, 1/2 cup all-purpose flour, walnuts, and cinnamon in a small bowl. Use your clean hands to work the cold butter into it until a coarse consistency has formed. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5D473EA2ABAA964AA7DF060F1250061F', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 8, NULL, NULL, 2, NULL, 0, 'Grab 1/4 thinly sliced apple (trying to keep the pieces grouped together) and lightly press it down on top of the batter. Repeat with the rest of the apple slices, alternating directions and filling any gaps with extra slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E5611103B937254EBAAE3B8C724E7B02', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the brown sugar topping mixture over the top and transfer to the oven.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD54AA69DE11714BB29DD151D83E6940', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 to 35 minutes or until browned completely on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'36E8D5F1553585488B53B9C3B18CEE46', X'260FA7EB69EE2E41B8FF8479FBD87E57', X'260FA7EB69EE2E41B8FF8479FBD87E57', 11, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool on an wire rack for at least ten minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4E6141EA017D9D429572DCCD1CE1F326', X'2C60809EC6B9FA4DBE90CA74B616CC1B', X'2C60809EC6B9FA4DBE90CA74B616CC1B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees. Pour the blueberries into a 6` baking dish and place the plums in a single layer in the dish (cut sides facing up).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'17E23841D97A064289091BA521B6A3D6', X'2C60809EC6B9FA4DBE90CA74B616CC1B', X'2C60809EC6B9FA4DBE90CA74B616CC1B', 1, NULL, NULL, 2, NULL, 0, 'Drizzle with honey, olive oil, salt, and pepper and toss to coat.Add on the cardamom pods and vanilla bean pod.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F16760D50B7C944BB839D03D8E81F2D5', X'2C60809EC6B9FA4DBE90CA74B616CC1B', X'2C60809EC6B9FA4DBE90CA74B616CC1B', 2, NULL, NULL, 2, NULL, 0, 'Roast for 10-12 minutes or until the plums are easily pierced with a fork. Remove from oven and discard the pods (vanilla and cardamom).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'902CD70071C29D489B3B99DE0FEAAC56', X'2C60809EC6B9FA4DBE90CA74B616CC1B', X'2C60809EC6B9FA4DBE90CA74B616CC1B', 3, NULL, NULL, 2, NULL, 0, 'Let cool slightly before topping with yogurt or one of the variations I mentioned previously.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'82E77334A238BE49B4B8F52F5C132CB4', X'7CEB0DD8ACA2AA4DB1BE9E9370901673', X'7CEB0DD8ACA2AA4DB1BE9E9370901673', 0, NULL, NULL, 2, NULL, 0, 'Pulse the walnuts in a food processor until finely crumbled, four or five times (be careful not to over pulse!).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CC049B9640A5164CB6DD937B65866B81', X'7CEB0DD8ACA2AA4DB1BE9E9370901673', X'7CEB0DD8ACA2AA4DB1BE9E9370901673', 1, NULL, NULL, 2, NULL, 0, 'Transfer paprika, chili, and cumin into a saucepan over medium heat and toast for 30 seconds, stirring often. Add walnuts and salt and toss. Add water and lime juice and stir until completely coated. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B5E537C028B2444D9C9B06EB0E60C424', X'206957E3F551614EB37AB897DFB8679E', X'206957E3F551614EB37AB897DFB8679E', 0, NULL, NULL, 2, NULL, 0, 'Add all salsa ingredients into a bowl and toss to combine. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB51460FA8B44D4D9FEE41FE4C242431', X'4BFC2DFE40D46249BF7DF6094DB85E99', X'4BFC2DFE40D46249BF7DF6094DB85E99', 0, NULL, NULL, 2, NULL, 0, 'Warm the tortillas in a saucepan over medium heat for 10 seconds. Double up the tortillas and place 1/4 of the walnut meat and peach salsa in them. Repeat with the remaining tortillas. Top with avocado, cabbage, and yogurt or sour cream (if using). Serve warm.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'79E08FEBAB2EB142B863EB97E57886B7', X'79D41701FF8DFC4BB0D22C42E69C260C', X'79D41701FF8DFC4BB0D22C42E69C260C', 0, NULL, NULL, 2, NULL, 0, 'Place melon slices in a line along a large serving tray (you may need to use several trays depending on the size you have available).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'06342D9F9F7C8845AC60409257D20DB4', X'79D41701FF8DFC4BB0D22C42E69C260C', X'79D41701FF8DFC4BB0D22C42E69C260C', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the feta, lime zest, lime juice, and fresh mint evenly across all of the lemon slices.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'355FFDD352C71448ABBEE60828C7D785', X'79D41701FF8DFC4BB0D22C42E69C260C', X'79D41701FF8DFC4BB0D22C42E69C260C', 2, NULL, NULL, 2, NULL, 0, 'Garnish with sesame seeds. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B95A75BC1B2E3048A41ADB93D18BABB4', X'C1BD2E4E923A3B449B07F042D4034E91', X'C1BD2E4E923A3B449B07F042D4034E91', 0, NULL, NULL, 2, NULL, 0, 'Whisk all the ingredient together and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7EBC4252519A0F41ADB4A4AA993C0048', X'1FB52CDFD8C7A04E8EC4B4225D37813D', X'1FB52CDFD8C7A04E8EC4B4225D37813D', 0, NULL, NULL, 2, NULL, 0, 'Stir all ingredients together until combined. Season with salt, pepper, and more lemon juice (if needed). Store in the fridge until ready to use.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'26494338D602BA44B7440FAE1DA43CEA', X'341FA3E77E108A4AB4D92A440C91E640', X'341FA3E77E108A4AB4D92A440C91E640', 0, NULL, NULL, 2, NULL, 0, 'Spread the yogurt tahini sauce evenly across the bottom of a small platter. Lay eggplants over the tahini sauce and top with plum salsa. Garnish with fresh cilantro or basil and sesame seeds. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9C2596DF142FEA47B91A3F203E0E15F1', X'415524927B0C334E9EB95157CE2B8DFA', X'415524927B0C334E9EB95157CE2B8DFA', 0, NULL, NULL, 2, NULL, 0, 'Toss together cherry tomatoes, fruit, and basil. Season with salt and pepper. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B57E26346C04EE4BB3B8D63D9929CE7A', X'415524927B0C334E9EB95157CE2B8DFA', X'415524927B0C334E9EB95157CE2B8DFA', 1, NULL, NULL, 2, NULL, 0, 'Using a stand mixer or hand mixer, whisk together the feta and heavy cream until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'BD39A9F8C7CBE242963028443E0491E0', X'B96A803E20162E47A0D7323F76BBF2F9', X'B96A803E20162E47A0D7323F76BBF2F9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees and grease a 9-inch circle tart pan with a removable bottom.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CFE56A5B0622E45A68005EC3F94A9A9', X'AD7054924A07DA45B3AC933DCA122951', X'AD7054924A07DA45B3AC933DCA122951', 0, NULL, NULL, 2, NULL, 0, 'Combine flour, almonds, salt, sugar and almond extract in a food processor and pulse a few times to combine. Add the butter and then run the processor until a dough ball forms.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'879A2894F71D6B42B33CF3D3366E2742', X'AD7054924A07DA45B3AC933DCA122951', X'AD7054924A07DA45B3AC933DCA122951', 1, NULL, NULL, 2, NULL, 0, 'Transfer dough to prepared pan and press dough evenly across the pan. Bake for 15 minutes or until just starting to turn a light golden brown. Remove from oven and let cool completely (you can also stick it in the fridge or freezer to speed this process up).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'50719E7A0744B64CA01A0D3B8BE1328C', X'9A022421CA98C84F883BC03A05920223', X'9A022421CA98C84F883BC03A05920223', 0, NULL, NULL, 2, NULL, 0, 'Add almond paste to the food processor (no need to clean the bowl out from last time) and pulse a few times to break it up into tiny pieces. Spread evenly over the prepared crust.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'20136FC55BC3434D9C6B734AA2A99570', X'9A022421CA98C84F883BC03A05920223', X'9A022421CA98C84F883BC03A05920223', 1, NULL, NULL, 2, NULL, 0, 'Toss together the fruit, sugar, and cornstarch. Pour and spread evenly over the prepared crust.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'11EEEE784451AE48BA239D651024F6E8', X'0FD69788ECFA404A9FB2EED3B5484720', X'0FD69788ECFA404A9FB2EED3B5484720', 0, NULL, NULL, 2, NULL, 0, 'Add flour, almond paste, sugar, and salt into the food processor and pulse a few times to combine. Add butter and pulse until a coarse crumble forms. Use a spatula to mix in almonds. Sprinkle over the crumble.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C597BAA10304D14B945B51EF585B8627', X'0FD69788ECFA404A9FB2EED3B5484720', X'0FD69788ECFA404A9FB2EED3B5484720', 1, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until browned on top.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DD2472C7319850459AE14B00CD187968', X'0FD69788ECFA404A9FB2EED3B5484720', X'0FD69788ECFA404A9FB2EED3B5484720', 2, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool completely before slicing.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7E4539F759DD024AA5D8BF9F84963326', X'D5FDA067B87AEB4A97ADEDA232DA3900', X'D5FDA067B87AEB4A97ADEDA232DA3900', 0, NULL, NULL, 2, NULL, 0, 'Toss together cherry tomatoes, fruit, and basil. Season with salt and pepper. Set aside.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'56828DAFBCAF4C4CB0102AC5FCBE9967', X'D5FDA067B87AEB4A97ADEDA232DA3900', X'D5FDA067B87AEB4A97ADEDA232DA3900', 1, NULL, NULL, 2, NULL, 0, 'Using a stand mixer or hand mixer, whisk together the feta and heavy cream until completely combined.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6B4D3D0E0BAD634BB093A569A5D5FDAB', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', 0, NULL, NULL, 2, NULL, 0, 'Add golden beet chunks, granulated sugar, and water into a saucepan over medium heat. Bring to a boil, lower to a simmer, and let simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'690B80687DED8448B66EB0117825913B', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tarragon, cover, and let sit for another 15 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5865ED1010721748BABE7B6610B41028', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', X'58D7EFCAD8C53F47A788DE8B4ABB0F61', 2, NULL, NULL, 2, NULL, 0, 'Strain syrup into a clean jar. Let cool completely before using.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'66B20B7C6809124FA412F4B4101AE0F6', X'582DF1E8F047CC419229CAFEBA292790', X'582DF1E8F047CC419229CAFEBA292790', 0, NULL, NULL, 2, NULL, 0, 'Add syrup and strawberries to a large pitcher. Use a muddler or handle of a wooden spoon to smash the strawberries in the bottom of the pitcher. Add lime juice, vodka, and sparkling water.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'033070F15992CB4F91D5A79B7BED45A3', X'582DF1E8F047CC419229CAFEBA292790', X'582DF1E8F047CC419229CAFEBA292790', 1, NULL, NULL, 2, NULL, 0, 'Garnish with beets, strawberries, tarragon, and mint.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'163429E5E5B7AE40BFAD1015F0C20CDA', X'016610EDBFF88945A4EAB3BF2D5B868F', X'016610EDBFF88945A4EAB3BF2D5B868F', 0, NULL, NULL, 2, NULL, 0, 'Fill four glasses with ice.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'610250E9DE216A468E0DEF8211E8625E', X'016610EDBFF88945A4EAB3BF2D5B868F', X'016610EDBFF88945A4EAB3BF2D5B868F', 1, NULL, NULL, 2, NULL, 0, 'Divide the ingredients evenly between 4 glasses and serve right away with lemon slices!');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'917D859D831F2B45BD3421AE7A30B777', X'1A0CCF602F32D74FB2960272C53B58DF', X'1A0CCF602F32D74FB2960272C53B58DF', 0, NULL, NULL, 2, NULL, 0, 'Add golden beet chunks, granulated sugar, and water into a saucepan over medium heat. Bring to a boil, lower to a simmer, and let simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'88F0CED57EEA1445837A36AB34CA32D5', X'1A0CCF602F32D74FB2960272C53B58DF', X'1A0CCF602F32D74FB2960272C53B58DF', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat, add tarragon, cover, and let sit for another 15 minutes.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3A0666E74285E74FB8BDA0A879DF5C51', X'1A0CCF602F32D74FB2960272C53B58DF', X'1A0CCF602F32D74FB2960272C53B58DF', 2, NULL, NULL, 2, NULL, 0, 'Strain syrup into a clean jar. Let cool completely before using.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'80B798D2C0607B41B623D438C5376988', X'F413168D05B2F941BFED5B7F10FF5CF2', X'F413168D05B2F941BFED5B7F10FF5CF2', 0, NULL, NULL, 2, NULL, 0, 'Add syrup and strawberries to a large pitcher. Use a muddler or handle of a wooden spoon to smash the strawberries in the bottom of the pitcher. Add lime juice, vodka, and sparkling water.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'347063D119469A4AA41403C5584E3190', X'F413168D05B2F941BFED5B7F10FF5CF2', X'F413168D05B2F941BFED5B7F10FF5CF2', 1, NULL, NULL, 2, NULL, 0, 'Garnish with beets, strawberries, tarragon, and mint.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'89B91E391C32514489360CC0EDEE6AF9', X'6F59554D4566614B8F650574ABC1467A', X'6F59554D4566614B8F650574ABC1467A', 0, NULL, NULL, 2, NULL, 0, 'Bring the water to a boil in a tea kettle or small saucepan over the stove. Place the matcha in a heat safe bowl and slowly pour the water in, one tablespoon at a time, while whisking the matcha until dissolved.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2285B25BAC16594884C73DC895D7D88F', X'6F59554D4566614B8F650574ABC1467A', X'6F59554D4566614B8F650574ABC1467A', 1, NULL, NULL, 2, NULL, 0, 'Transfer matcha mixture to a large pitcher and add in the lime juice, lime slices, grapes, apple slices, cucumber slices, and half the mint.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B7C58B9364D7E744B49DC030ADFE961D', X'6F59554D4566614B8F650574ABC1467A', X'6F59554D4566614B8F650574ABC1467A', 2, NULL, NULL, 2, NULL, 0, 'Pour white wine into the pitcher and transfer to the fridge until ready to serve.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'DB5FBCDB1B23904D95A51EEE82D97E04', X'6F59554D4566614B8F650574ABC1467A', X'6F59554D4566614B8F650574ABC1467A', 3, NULL, NULL, 2, NULL, 0, 'Right before serving, add a handful of ice and the sparkling soda.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'270AB476E8D4DF4F94A5AEBCC9C14F01', X'DC4055405B3D7B4E8FDBD52152A1A0D7', X'DC4055405B3D7B4E8FDBD52152A1A0D7', 0, NULL, NULL, 2, NULL, 0, 'Bring the water, honey, elderflowers, and lemon juice to a boil in a small saucer.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2479954F43B737499F5E409716AFD313', X'DC4055405B3D7B4E8FDBD52152A1A0D7', X'DC4055405B3D7B4E8FDBD52152A1A0D7', 1, NULL, NULL, 2, NULL, 0, 'Once boiling and the honey has dissolved, remove from heat, cover, and let cool.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'55DBBEA33858994F924DAB24CFC800F0', X'DC4055405B3D7B4E8FDBD52152A1A0D7', X'DC4055405B3D7B4E8FDBD52152A1A0D7', 2, NULL, NULL, 2, NULL, 0, 'Once cooled, place in a sealed container and refrigerate overnight. Strain and discard the elderflowers (or save them for garnish).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9BE15817C342A24F95F6CE150585E49C', X'136DE5774082D047A638FFEAE2E2928D', X'136DE5774082D047A638FFEAE2E2928D', 0, NULL, NULL, 2, NULL, 0, 'Juice the lemons using whatever method you would like and cut the garnish lemons into thin slices. In a large pitcher, stick the fresh lemon slices, lemon juice, water, and simple syrup. Adjust flavoring to your likening by adding more honey or agave syrup.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2B7FD31A0526F0499BC411022944DC66', X'96A6E9780426CD47AFFE344504063C75', X'96A6E9780426CD47AFFE344504063C75', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees and line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0557AACFBE9DC14996DA3D92F72BF3D0', X'96A6E9780426CD47AFFE344504063C75', X'96A6E9780426CD47AFFE344504063C75', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix together the strawberries, rhubarb, sugar, vinegar, and salt. Transfer to baking sheet and spread into an even layer');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D01FE58B1FFDCF4F9080FD0B1568A382', X'96A6E9780426CD47AFFE344504063C75', X'96A6E9780426CD47AFFE344504063C75', 2, NULL, NULL, 2, NULL, 0, 'Place orange slices and thyme sporadically around the strawberries and rhubarb (it will release juices as it roasts).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'66077335AB82754CBEFEEDF92712CC5A', X'96A6E9780426CD47AFFE344504063C75', X'96A6E9780426CD47AFFE344504063C75', 3, NULL, NULL, 2, NULL, 0, 'Roast for 30 minutes or until the rhubarb is easily pierced with a knife. Remove from oven, transfer the orange slices and thyme to another try (to reserve for garnish), and set aside until the grits are ready.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9F10631D0BFCCB4D8508DC766CF472EE', X'D4887EE9519EAD40BB384443729F592F', X'D4887EE9519EAD40BB384443729F592F', 0, NULL, NULL, 2, NULL, 0, 'Bring water to a boil in a large saucepan with high edges. Once boiling, add the grits and salt and turn the heat down so they simmer. Cook for about thirty minutes, stirring often.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B23FDFEBC8CE684E9E8D5F0BF7FC91DC', X'D4887EE9519EAD40BB384443729F592F', X'D4887EE9519EAD40BB384443729F592F', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in the butter until melted. Taste and season with more salt if needed.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F5A50E9D8082F0458F8E9C5C412E98AC', X'4BC828566361864DB9F3EE4766C3B1F1', X'4BC828566361864DB9F3EE4766C3B1F1', 0, NULL, NULL, 2, NULL, 0, 'Divide the grits between three serving bowls and top with the roasted strawberries and rhubarb. Drizzle with heavy cream or yogurt. Sprinkle with toasted pecans and garnish with thyme leaves and an orange slice. Serve right away.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'E769DAA46B218342B6559310F578C37D', X'94E848E31CA8264588059D1FCD254DD5', X'94E848E31CA8264588059D1FCD254DD5', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the almond flour, all-purpose flour, baking powder, poppyseeds, and salt in a medium bowl.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1CB3A1857A5EA147B98DF3ECE73572E3', X'94E848E31CA8264588059D1FCD254DD5', X'94E848E31CA8264588059D1FCD254DD5', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, while the cakes are cooling, combine the rhubarb, 1 cup water, and sugar in a small saucepan over medium heat. Let simmer for 20 to 30 minutes or until thickened. Remove from heat and let cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'82C1116186E9B2408EE7F60D48F906CB', X'1579B69B7A864342909C6725E057E4E3', X'1579B69B7A864342909C6725E057E4E3', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 Tbsp butter in a small saucepan over medium and add ramps. Let sauté for about 5 minutes or until completely softened. Remove from heat and let cool completely.');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'82C0FDDFECF64F4FA437FD4CFF76B707', X'1579B69B7A864342909C6725E057E4E3', X'1579B69B7A864342909C6725E057E4E3', 1, NULL, NULL, 2, NULL, 0, 'Transfer ramps, remaining butter, lemon juice, and a dash of salt and pepper to a food processor. Process until a green butter has formed (I like to keep a few chunks of ramps in my butter but you can process longer if you''d rather have it creamier).');
INSERT INTO `VWR INTERNAL Vegetarianventures howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1C9C09D581BD324B8ED7C703A7F09E76', X'1579B69B7A864342909C6725E057E4E3', X'1579B69B7A864342909C6725E057E4E3', 2, NULL, NULL, 2, NULL, 0, 'Serve right away or transfer to an airtight container and store in the fridge for up to 1 week.');
